CREATE TABLE `school` (
  `rank` int(11) DEFAULT NULL,
  `scid` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) DEFAULT NULL,
  `areaname` varchar(20) DEFAULT NULL,
  `areaid` int(11) DEFAULT NULL,
  `description` longtext,
  `acronym` varchar(10) DEFAULT NULL,
  `region` varchar(20) DEFAULT NULL,
  `reid` int(10) DEFAULT NULL,
  `usedname` varchar(300) DEFAULT NULL,
  `type` varchar(60) DEFAULT NULL,
  `foundingYear` int(11) DEFAULT NULL,
  `department` varchar(60) DEFAULT NULL,
  `iscombine` varchar(60) DEFAULT NULL,
  `is985` int(10) DEFAULT NULL,
  `is211` int(10) DEFAULT NULL,
  `isDoubleFirstClass` int(60) DEFAULT NULL,
  `firstClassNum` int(10) DEFAULT NULL,
  `facultyNum` int(10) DEFAULT NULL,
  `academicianNum` int(10) DEFAULT NULL,
  `changjiangScholarNum` int(10) DEFAULT NULL,
  `teachersNum` int(10) DEFAULT NULL,
  `undergraProNum` int(10) DEFAULT NULL,
  `postgraProNum` int(10) DEFAULT NULL,
  `doctorProNum` int(10) DEFAULT NULL,
  `mainLabNum` int(10) DEFAULT NULL,
  `undergraEnrollNum` int(10) DEFAULT NULL,
