  PRIMARY KEY (`scid`),
  KEY `school_ibfk_1` (`areaid`)
) ENGINE=InnoDB AUTO_INCREMENT=6938 DEFAULT CHARSET=gbk;

/*Data for the table `school` */

insert  into `school`(`rank`,`scid`,`name`,`areaname`,`areaid`,`description`,`acronym`,`region`,`reid`,`usedname`,`type`,`foundingYear`,`department`,`iscombine`,`is985`,`is211`,`isDoubleFirstClass`,`firstClassNum`,`facultyNum`,`academicianNum`,`changjiangScholarNum`,`teachersNum`,`undergraProNum`,`postgraProNum`,`doctorProNum`,`mainLabNum`,`undergraEnrollNum`,`postgraEnrollNum`,`schoolWeb`) values (191,1,'西安科技大学','陕西',6,'西安科技大学（Xi’an University of Science and Technology）位于世界历史名城西安，学校是中华人民共和国应急管理部和陕西省人民政府共建高校，入选教育部卓越工程师教育培养计划高校、国家建设高水平大学公派研究生项目实施高校、国家特色重点学科项目实施高校、国家中西部高校基础能力建设工程实施高校、国家级大学生创新创业训练计划实施高校、国家级新工科研究与实践项目实施高校、教育部本科教学工作水平评估优秀学校、陕西省省属高水平大学、陕西省“一流大学、一流学科”建设高校，CDIO工程教育联盟成员。','XUST','西北地区',7,'西安矿业学院','理工',1895,'陕西省','是',0,0,0,0,20,1,2,1300,59,85,32,0,3480,0,'http://www.xust.edu.cn/'),(41,2,'西安电子科技大学','陕西',6,'西安电子科技大学（Xidian University）是中华人民共和国教育部直属的全国重点大学，由教育部与工业和信息化部、国家国防科技工业局、中国电子科技集团公司、陕西省、西安市共建，国家“世界一流学科建设高校”、“211工程”、“985工程优势学科创新平台”，入选“2011计划”、“111计划”、卓越工程师教育培养计划、国家建设高水平大学公派研究生项目、新工科研究与实践项目、中国政府奖学金来华留学生接收院校、国家双创示范基地、国家大学生文化素质教育基地、国家国际科技合作基地、国家创新人才培养示范基地，\'\'两电一邮\'\'、新丝绸之路大学联盟、CDIO工程教育联盟成员，中国电子信息领域科学研究和人才培养的核心基地，中国雷达、信息论、密码学、电子对抗、微波天线等学科的发源地。','XDU','西北地区',7,'中国军委无线电学校','理工',1931,'教育部','否',0,211,1,2,18,4,30,2100,59,101,50,2,1137,0,'http://www.xidian.edu.cn/'),(33,3,'北京科技大学','北京',1,'北京科技大学于1952年由天津大学（原北洋大学）、清华大学等6所国内著名大学的矿冶系科组建而成，现已发展成为以工为主，工、理、管、文、经、法等多学科协调发展的教育部直属全国重点大学，是全国首批正式成立研究生院的高等学校之一。1997年5月，学校首批进入国家“211工程”建设高校行列。2006年，学校成为首批“985工程”优势学科创新平台建设高校。2014年，学校牵头的，以北京科技大学、东北大学为核心高校的“钢铁共性技术协同创新中心”成功入选国家“2011计划”。2017年，学校入选国家“双一流”建设高校。2018年，学校获批国防科工局、教育部共建高校。\n\n建校六十七年来，学校逐步形成了“学风严谨，崇尚实践”的优良传统，为社会培养各类人才20余万人，大部分已成为国家政治、经济、科技、教育等领域尤其是冶金、材料行业的栋梁和骨干。党和国家领导人罗干、刘淇、徐匡迪、黄孟复、范长龙、郭声琨、刘晓峰等都曾在校学习，另有38名校友当选为中国科学院或中国工程院院士，一大批校友走上了省长、市长的领导岗位，一大批校友担任中国一重、鞍钢集团、宝武集团、中国铝业、中国五矿、中国钢研和中国黄金等国家特大型企业的董事长或总经理。学校被誉为“钢铁摇篮”。\n\n学校位于高校云集的北京市海淀区学院路，占地约80.39万平方米（包括管庄校区），校舍建筑总面积97万平方米（包括管庄校区）。学校现有1个国家科学中心，1个“2011计划”协同创新中心，2个国家重点实验室，2个国家工程（技术）研究中心，2个国家科技基础条件平台，2个国家级国际科技合作基地，50个省、部级重点实验室、工程研究中心、国际合作基地、创新引智基地等。特别是2007年，学校作为第一所教育部直属高校牵头承担了国家重大科技基础设施项目——重大工程材料服役安全研究评价设施，并负责筹建国家材料服役安全科学中心。图书馆实体馆藏230万余册（件）。定期出版《工程科学学报》《北京科技大学学报（社会科学版）》《International Journal of Minerals, Metallurgy and Materials》《思想教育研究》《物流技术与应用》《金属世界》《粉末冶金技术》等重要学术刊物。\n\n学校由土木与资源工程学院、冶金与生态工程学院、材料科学与工程学院、机械工程学院、能源与环境工程学院、自动化学院、计算机与通信工程学院、数理学院、化学与生物工程学院、东凌经济管理学院、文法学院、马克思主义学院、外国语学院、高等工程师学院，以及研究生院、体育部、管庄校区、融合创新研究院、天津学院、顺德研究生院等组成。现有20个一级学科博士学位授权点，30个一级学科硕士学位授权点，80个二级学科博士学位授权点，138个二级学科硕士学位授权点，另有MBA（含EMBA）、MPA、法律硕士、会计硕士、翻译硕士、社会工作、文物与博物馆和工程硕士等8个专业学位授权点，16个博士后科研流动站，53个本科专业。学校冶金工程、材料科学与工程、矿业工程、科学技术史4个全国一级重点学科学术水平蜚声中外（2017年进入国家世界一流学科建设行列；在第四轮学科评估，冶金工程、科学技术史获评A+，材料科学与工程获评A），安全科学与工程、环境科学与工程、控制科学与工程、动力工程与工程热物理、机械工程、计算机科学与技术、土木工程、化学、外国语言文学、管理科学与工程、工商管理、马克思主义理论等一批学科具有雄厚实力，力学、物理学、数学、信息与通信工程、仪器科学与技术、纳米材料器件、光电信息材料与器件等基础学科与交叉学科焕发出勃勃生机。\n\n截至2018年底，全日制在校生2.5万余人，其中本专科生13683人，各类研究生10441人（其中硕士生7165人、博士生3276人），国际学生912人；学历继续教育学生71449人。在站博士后283人。已形成研究生教育、全日制本科、高职教育和继续教育多层次、较完整的人才培养体系。\n\n学校拥有一支治学严谨的师资队伍。教职工总数3368人，具有正高级专业技术职务的教职工504人，具有副高级专业技术职务的教职工814人，其中专任教师1836人。现有中国科学院院士5人（双聘2人），中国工程院院士6人（双聘3人），国务院学位委员会委员1人，国务院学位委员会学科评议组成员5人，国家973项目首席科学家3人，国家级有突出贡献专家15人，省部级有突出贡献专家10人，“长江学者奖励计划”特聘教授15人、青年学者5人，“国家杰出青年科学基金”获得者19人，“万人计划”领军人才14人、青年拔尖人才4人，国家级教学名师2人，国家百千万人才工程人选17人，国家优秀青年科学基金获得者15人，北京市教学名师31人，教育部跨世纪/新世纪优秀人才103人。\n\n学校的科研实力十分雄厚。1978年至2018年12月底，共申请专利7812项，授权专利4630项；有2000余项科研成果获国家、省、部委级等各种奖励，其中国家级奖励176项。1999年教育部编辑的《中国高等学校科技50年高校获奖重大成果一览表》中收录北京科技大学12项重大科研成果，在全国高校中名列前茅。据教育部统计，1978～2011年，学校获国家科技进步一等奖4项，列全国高校第4。学校“块体非晶合金的结构和强韧化研究”、“一维氧化锌的界面调控及其应用基础研究”的科研成果在基础研究和应用基础研究领域，阐明了自然现象、特征和规律，作出了重大科学贡献。近五年学校 “高性能铜铝复合材料连铸直接成形技术与应用”“露天转地下高效转型建设大型数字化地下金属矿山的研究与实践”“电弧炉炼钢复合吹炼技术的研究应用”“复杂组分战略金属再生关键技术创新及产业化”等大批科研成果在国民经济建设中发挥了重要作用，获得了巨大的经济效益和社会效益。据2018年发布数据，2017年学校师生发表论文被“SCIE”“EI”收录数量分别居全国高校第31位和21位。\n\n学校不断拓展社会服务领域和发展空间，与国内150多个省市区政府、大型企事业单位签署了全面合作协议。同时，学校瞄准世界前沿，加强国际合作，先后与德国亚琛工业大学、美国橡树岭国家实验室、英国牛津大学等180多所著名大学和科研机构建立了合作关系，并开展了实质性的合作。\n\n以培养德智体美劳全面发展的社会主义建设者和接班人为根本任务，学校注重学生综合素质和创新、创业能力培养。学校每年被评为全国高校社会实践先进单位，学校学生在历年国家及北京市的各种竞赛中多次获得殊荣。特别是学生团队在“互联网+”大学生创新创业大赛、“挑战杯”全国大学生科技学术竞赛、“创青春”全国大学生创业大赛、全国大学生智能汽车竞赛、全国大学生机器人大赛、全国大学生节能减排与社会实践大赛等竞赛中屡创佳绩，MEI机器人团队、智能车队先后获评大学生“小平科技创新团队”称号。学校积极营造“科学与艺术共融，人文与创新并存”的校园氛围，学生艺术团屡获佳绩，原创校史话剧《绽放》获得校园戏剧最高奖。学校高度重视学生思想品德教育，努力营造培养学生爱国主义、集体主义和社会主义精神的校园文化氛围，先后被授予“北京市文明校园”、“北京市党建和思想政治工作先进普通高校”及“首都文明单位标兵”等荣誉称号。2018年，学校入选教育部首批“三全育人”综合改革试点高校。\n\n学校的体育竞技水平和群众性体育活动在北京乃至全国享有盛誉，涌现了一批以李敏宽、楼大鹏为代表的国家优秀运动员、教练员和体育官员。学生田径代表队在全国及北京市高校竞赛中数度摘桂；女篮代表队在北京市高校联赛中连续12次夺冠，并于2005年挺进CUBA全国八强。学校目前拥有约8.2万平方米的现代化体育场地。学校体育馆作为2008年奥运会、残奥会竞赛场馆，圆满完成了北京奥运会柔道、跆拳道，残奥会轮椅篮球、轮椅橄榄球四项赛事，学校体育馆团队被党中央国务院授予“北京奥运会、残奥会先进集体”荣誉称号。\n\n今天，北京科技大学全体师生正满怀信心，迈着坚定的步伐，向着“把北京科技大学建设成为以工为主，工、理、管、文、经、法等多学科协调发展，规模适度，特色突出，国内一流，国际知名的高水平研究型大学”的目标而奋进。','USTB','华北地区',1,'北京钢铁工业学院、北京钢铁学院','理工',1952,'教育部','是',0,211,0,4,14,11,15,1836,53,30,20,2,173,0,'http://www.ustb.edu.cn/'),(66,4,'西南大学','重庆',3,'西南大学（Southwest University）简称西大，坐落于重庆市，是中华人民共和国教育部直属高校，由教育部、农业农村部与重庆市人民政府共建，是世界一流学科建设高校，位列211工程、985工程优势学科创新平台，入选111计划、2011计划、卓越农林人才教育培养计划、卓越教师培养计划、百校工程、国家建设高水平大学公派研究生项目、国家大学生文化素质教育基地，开办师范生免费教育的7所高校之一，全国自主选拔录取改革试点高校，中国政府奖学金来华留学生接收院校，重庆市大学联盟创始成员。西南大学由原教育部直属西南师范大学与原农业部直属西南农业大学于2005年合并而成。原两校毗邻而建，均溯源于1906年建立的川东师范学堂。1936年更名为四川省立教育学院。1950年，四川省立教育学院师范、农学相关系科分别与1940年创办的国立女子师范学院、1946年创办的私立相辉文法学院等合并建立西南师范学院、西南农学院。1985年，两校分别更名为西南师范大学、西南农业大学。此后，重庆轻工业职工大学、四川畜牧兽医学院、中国农业科学院柑桔研究所相继并入。','SWU','西南地区',6,'','综合',1906,'教育部','是',0,211,0,1,26,0,0,2968,105,204,106,1,0,0,'http://www.swu.edu.cn/'),(12,5,'哈尔滨工业大学','黑龙江',7,'哈尔滨工业大学（简称哈工大），隶属于工业和信息化部，拥有哈尔滨、威海、深圳三个校区，是一所以理工为主，理、工、管、文、经、法、艺等多学科协调发展的国家重点大学。\n\n学校始建于1920年，1951年被确定为全国学习国外高等教育办学模式的两所样板大学之一，1954年进入国家首批重点建设的6所高校行列（京外唯一一所），是新中国第一所本科五年制、研究生三年制、毕业生直接被授予工程师称号的理工科大学，被誉为工程师的摇篮。学校于1996年进入国家“211工程”首批重点建设高校，1999年被确定为国家首批重点建设的9所大学之一，2000年与同根同源的哈尔滨建筑大学合并组建新的哈工大，2017年入选“双一流”建设A类高校名单。 \n\n在长期的办学过程中，学校全面贯彻党的教育方针，坚持马克思主义指导地位，模范执行党委领导下的校长负责制，坚持立德树人根本使命，坚持师德师风第一标准，形成了“规格严格，功夫到家”的校训，培育了精神引领、典型引路、品牌带动的思想政治工作传统，涌现出一大批全国先进典型，曾被授予全国先进基层党组织、全国五一劳动奖状、全国五四红旗团委、工业和信息化部“一提三优”工程特别优秀学校等荣誉称号。\n\n学校充分发挥学科交叉、融合的优势，形成了由重点学科、新兴学科和支撑学科构成的较为完善的学科体系，涵盖了理学、工学、管理学、文学、经济学、法学、艺术学等多个学科门类。学校现有9个国家重点学科一级学科，6个国家重点学科二级学科。在教育部第三轮学科评估中，学校有10个一级学科排名位居全国前五位，其中力学学科排名全国第一。在全国第四轮学科评估中，哈工大共有17个学科位列A类，学科优秀率（A类学科占授权学科的比例）位列全国第六位，A类学科数量位列全国第八位，工科A类数量位列全国第二位。材料科学、工程学、物理学、化学、计算机科学、环境与生态学、数学、生物学与生物化学、农业科学、临床医学、社会科学总论11个学科领域进入ESI全球前1%行列，材料科学进入全球前1‰行列，工程学学科连续多年进入ESI全球前万分之一行列。\n\n学校大力弘扬“铭记责任，竭诚奉献的爱国精神；求真务实，崇尚科学的求是精神；海纳百川，协作攻关的团结精神；自强不息，开拓创新的奋进精神”的哈工大精神和“铭记国家重托，肩负艰巨使命，扎根东北，艰苦创业，拼搏奉献，把毕生都献给了共和国的工业化事业”的哈工大“八百壮士”精神，建成了一支高素质师资队伍，为学校创建中国特色世界一流大学奠定了良好的人才基础。学校有两院院士36人(含共享院士16人)，入选国家级教学名师9人，“长江学者”特聘教授47人、青年学者10人，国家杰出青年科学基金获得者52人，国家自然基金委优秀青年基金获得者45人，国家“万人计划”领军人才44人、青年拔尖人才23人，“百千万人才工程”国家级人选31人，“973计划”首席科学家12人，创新人才推进计划38人；国家自然科学基金委创新群体6个，教育部科技创新团队12个，科技部重点领域创新团队5个，国家级教学团队6个，工信部研究型教学创新团队4个。\n\n学校坚持立足航天、服务国防、面向国民经济主战场的办学定位，创立了中国高校第一个航天学院，发射了中国第一颗由高校牵头自主研制的小卫星，在中国首次实现了星地激光链路通信、首次实现了激光全自动束靶耦合引导（神光-Ⅲ靶场光电及控制系统），诞生了中国第一台会下棋能说话的计算机、第一部新体制雷达、第一台弧焊机器人和点焊机器人、第一颗由高校学子自主设计研制管控的纳卫星、第一支登上春晚舞台的大学生机器人舞蹈队，实现了国际首次高轨卫星对地高速激光双向通信试验，突破了世界最大口径射电望远镜的支撑结构系统关键技术、支持中国“天眼”成功“开眼”，研制成功的 空间机械手在天宫二号上实现了国际首次人机协同在轨维修科学试验，研制成功的新一代磁聚焦型霍尔电推力器在国际上首次实现空间应用，首次揭示了艾滋病病毒毒力因子结构、让中国艾滋病结构生物学研究跻身世界前列，成功发射的“龙江二号”成为全球首个独立完成地月转移、近月制动、环月飞行的微卫星，正在建设中国首个用于模拟太空极端环境的大科学工程，参与了探月工程等14个国家重大科技专项，年授权专利数、科研经费总额和国家科技三大奖数量位居全国高校前列，刘永坦院士荣获2018年度国家最高科学技术奖，连续6年有7个项目入选中国高校十大科技进展，一大批成果助力“长征七号”“长征五号”火箭首飞、天宫二号和神舟十一号载人飞行等重大任务，曾获“中国载人航天工程协作贡献奖”“中国载人航天工程突出贡献集体奖”“中国载人航天工程突出贡献者奖”等多个奖项，正在成为享誉国内外的理工强校、航天名校。\n\n站在新时代新的历史起点上，哈工大坚持以习近平新时代中国特色社会主义思想为指引，牢固树立“四个意识”，自觉坚定“四个自信”，忠诚履行“四个服务”，坚决做到“两个维护”，努力走出一条中国特色、世界一流、哈工大规格的强校之路，为中华民族伟大复兴、人类文明进步做出新的更大贡献！','HIT','东北地区',2,'哈尔滨中俄工业学校、中俄工业大学校','理工',1920,'国防科工委','否',985,211,1,7,23,38,44,3850,86,42,28,10,1680,0,'http://www.hit.edu.cn/'),(31,6,'湖南大学','湖南',4,'湖南大学位于中国历史文化名城长沙，校区坐落在湘江之滨、岳麓山下，享有“千年学府，百年名校”之誉。学校办学历史悠久、教育传统优良，是教育部直属全国重点大学、国家“211工程”“985工程”重点建设高校、国家“世界一流大学”建设高校。\n\n湖南大学办学起源于公元976年创建的岳麓书院，历经宋、元、明、清等朝代的变迁，始终保持着文化教育教学的连续性。1903年改制为湖南高等学堂，1926年定名为湖南大学，1937年成为国民政府教育部16所国立大学之一。中华人民共和国成立后，中共“一大”代表、教育家李达任新中国第一任湖南大学校长，毛泽东同志亲笔题写校名。2000年，湖南大学与湖南财经学院合并组建成新的湖南大学。\n\n在长期的办学历程中，学校继承和发扬“传道济民、爱国务实、经世致用、兼容并蓄”的优良传统，积淀了以校训“实事求是、敢为人先”、校风“博学、睿思、勤勉、致知”为核心的湖大精神，确立了“基础扎实、视野开阔、德才兼备”的人才培养总目标。形成了传统文化教育和强化实践教学的办学特色，为国家和社会培养了一大批“基础扎实、思维活跃、适应能力强、综合素质高”的高级专门人才，许多毕业生成长为著名专家学者、杰出企业家和优秀党政管理人才，师生中先后有36人当选为“学部”委员和“两院”院士。学校现有全日制在校学生36000余人，其中本科生20000余人，研究生15000余人。毕业生初次就业率一直保持在95％以上。\n\n学校设有研究生院和25个学院，学科专业涵盖哲学、经济学、法学、教育学、文学、历史学、理学、工学、管理学、医学、艺术学等11大门类。拥有博士学位授权一级学科27个、博士专业学位授权1个、硕士学位授权一级学科36个、硕士专业学位授权20个、本科招生专业60个，建有国家重点学科一级学科2个、国家重点学科二级学科14个、博士后科研流动站25个。目前7个学科进入ESI排名前1%，其中工程学、化学学科跻身前1‰，7个学科入选国防特色学科，化学、机械工程学科进入教育部“世界一流建设学科”建设行列。\n\n学校现有教职工近4000人，其中专任教师2045人，含教授和副教授1428人，院士11人、国务院学位委员会学科评议组成员6人、“千人计划”学者51人、“万人计划”学者20人(含教学名师3人)、获国家级教学名师奖3人、“长江学者”特聘教授、讲座教授及青年学者18人、国家杰出青年基金获得者19人。入选国家百千万人才工程23人、国家创新人才推进计划中青年创新领军人才2人、教育部新世纪优秀人才支持计划134人。拥有国家自然科学基金“创新研究群体”4个、教育部创新团队8个。\n\n学校获批建设国家工科(化学)基础课教学基地、国家理科(化学)基础科学研究与教学人才培养基地、国家示范性软件学院教学基地、全国大学生文化素质教育基地、国家深化创新创业教育改革示范高校、全国创新创业典型经验高校、全国高校实践育人创新创业基地。拥有国家级教学团队7个、双语教学示范课程6门、人才培养模式创新实验区6个、国家级实验教学示范中心4个、卓越计划国家级试点专业5个、国家级大学生校外实践基地3个、国家级虚拟仿真实验教学中心1个。近年来，学校获得国家级教学成果奖一等奖2项、二等奖7项；获批国家精品视频公开课10门、国家级精品资源共享课21门、国家级精品在线开放课程13门。\n\n学校拥有国家重点实验室2个、国家工程技术研究中心2个、国家级国际合作基地3个、国家工程实验室1个、国防科工局国防重点学科实验室1个、教育部重点实验室5个、教育部工程技术研究中心4个、教育部高等学校学科创新引智基地3个，参与组建国家“2011协同创新中心”3个。\n\n学校建有国家级大学科技园和“两山一湖”创新创业基地，在长沙高新区“麓谷”建立产学研基地，包括56个高水平校地企产学研平台，与32个省(市、区)和上千家企业建立了产学研合作关系。学校运营管理的国家超级计算(长沙)中心是第三家国家超级计算中心，为高校、科研机构、政府和企事业等数百家用户单位提供高性能计算、云计算及大数据处理服务。\n\n学校与世界上160余所高校和科研机构建立了合作与交流关系，招收了来自108个国家和地区的各类留学生，并建有学生海外实习基地，近2000名学生赴国(境)外合作院校交流学习或联合培养。分别在美国、加拿大、韩国设立孔子学院。\n\n学校校园占地面积153.196万平方米，校舍建筑面积124.056万平方米。截至2018年底，全校拥有藏书728.877万册、中外文数据库127个。全网支持下一代互联网IPV6，万兆骨干地面有线和无线网已覆盖所有的教学楼、办公楼和学生公寓，结合移动技术实现了多渠道的信息化教学、管理及服务。\n\n“麓山巍巍，湘水泱泱，宏开学府，济济沧沧；承朱张之绪，取欧美之长”，历经千年沧桑的湖南大学，将继承和发扬岳麓书院优秀教育和文化传统，遵循现代大学办学规律，重点突破，整体提升，改革创新，强化特色，为创建世界一流大学奋力前行，为实现中华民族伟大复兴的中国梦做出应有的贡献!','HNU','华中地区',4,'岳麓书院、湖南高等学堂、省立湖南大学、国立湖南大学','综合',1903,'教育部','是',985,211,1,2,23,8,15,1800,66,36,27,2,233,0,'http://www.hnu.cn/'),(132,7,'西安建筑科技大学','陕西',6,'西安建筑科技大学（Xi’an University of Architecture and Technology），简称西安建大，坐落于古都西安，由陕西省人民政府、中华人民共和国住房和城乡建设部和教育部共建 [1]  ，中国“建筑老八校”之一，原冶金工业部直属重点大学，国家“中西部高校基础能力建设工程”、国家建设高水平大学公派研究生项目、国家“特色重点学科项目”高校，陕西省省属高水平大学，全国首批博士、硕士和学士学位授权单位，入选首批国家卓越工程师教育培养计划、国家国际科技合作基地、全国工程硕士研究生教育创新院校、全国深化创新创业教育改革示范高校、。是一所以土木建筑、环境市政、材料冶金等相关学科为特色，以工程技术学科为主体，多学科协调发展的大学。','XAUAT','西北地区',7,'西安冶金学院','理工',1895,'陕西省','是',0,0,0,0,20,5,2,1700,63,94,30,1,0,0,'http://www.xauat.edu.cn/'),(107,8,'河北大学','河北',2,'河北大学（Hebei University），位于国家历史文化名城保定市，是列入教育部直属高校序列并与国家国防科技工业局和河北省人民政府“部局省合建”的重点综合性大学，“一省一校”国家重点建设大学，河北省重点支持的国家一流大学建设一层次高校；入选国家中西部高校综合实力提升工程、全国首批深化创新创业教育改革示范高校等，还是中国中部地区重点支持的五所大学之一、中西部高校联盟成员、河北省首家中国政府奖学金来华留学生接收院校、全国高校首个延安精神教育基地。','HBU','华北地区',1,'天津工商大学','综合',1921,'河北省','是',0,0,0,0,9,0,0,3278,94,190,37,0,0,0,'http://www.hbu.edu.cn/'),(34,9,'重庆大学','重庆',3,'重庆大学（Chongqing University），简称重大（CQU），是教育部直属的全国重点大学，中共中央直管副部级，由教育部和重庆市共同建设；“建筑老八校”之一，是国家世界一流大学建设高校A类、“211工程”和”985工程”首批重点建设的高水平研究型综合性大学；卓越大学联盟、中俄工科大学联盟、一带一路高校联盟、中俄“长江—伏尔加河”高校联盟、CDIO工程教育联盟成员单位，入选国家“2011计划”、“111计划”、卓越工程师教育培养计划、卓越法律人才教育培养计划、国家建设高水平大学公派研究生项目，设有研究生院和国家大学科技园。1929年刘湘创办重庆大学，1935年批准为省立大学，抗战期间和西迁的中央大学合作办学，1942年更名为国立重庆大学，成为有文、理、工、商、法、医6个学院的国立综合性大学。1960年成为全国重点大学，改革开放后，学校大力发展人文、经管、艺术、教育等学科专业。2000年，原重庆大学、重庆建筑大学、重庆建筑高等专科学校三校合并组建新重庆大学，使得一直以机电、能源、材料、信息、生物、经管等学科优势著称的重大，在建筑、土木、环保等学科方面也处于全国较高水平。','CQU','西南地区',6,'','综合',1929,'教育部','是',985,211,1,3,36,0,31,5300,96,55,31,3,0,0,'http://www.cqu.edu.cn/'),(347,10,'云南财经大学','云南',5,'云南财经大学（Yunnan University of Finance and Economics，YNUFE） ，简称 “云财大”，创建于1951年，位于春城昆明盘龙江之畔，云南省重点大学、云南省高水平大学整体建设高校、全国大学生社会实践重点组织单位、全国深化创新创业教育改革示范高校、中国政府奖学金来华留学生接收院校，是一所办学历史悠久、财经特色鲜明的以经济学、管理学为主干学科，法学、哲学、文学、理学、工学、艺术等9大学科门类交叉渗透、协调发展的多学科性财经类高水平教学研究型大学。云南财经大学前身是始建于1951年的云南省财政干部学校；1981年成立云南财贸学院，开始全日制本科教育；1998年合并云南经济管理干部学院；1995年被云南省政府确定为省属重点大学；2006年更名为云南财经大学，是云南省覆盖经济学和管理学博士、硕士授权点最集中最全面的大学。','YNUFE','西南地区',6,'云南财贸学院','财经',1951,'云南省','是',0,0,0,0,16,0,3,1914,66,85,21,0,0,0,'http://www.ynufe.edu.cn/'),(61,11,'西北大学','陕西',6,'西北大学（Northwest University），简称“西大”，坐落于古都西安，是中华人民共和国教育部与陕西省人民政府共建的综合性全国重点大学，位列首批国家“世界一流学科建设高校”、国家“211工程”重点建设高校，入选中西部高校基础能力建设工程、高等学校学科创新引智计划、国家建设高水平大学公派研究生项目、中国政府奖学金来华留学生接收院校、国家大学生创新性实验计划、国家级大学生创新创业训练计划、全国首批深化创新创业教育改革示范高校、国家创新人才培养示范基地、国家国际科技合作基地、国家大学生文化素质教育基地、首批民政部社会工作专业人才培训基地、首批高等学校科技成果转化和技术转移基地，中日人文交流大学联盟、新丝绸之路大学联盟、长安联盟创始成员，全国首批博士、硕士、学士学位授予单位，是中国西北地区历史最为悠久的高等学府。','NWUPL','西北地区',7,'国立西北大学','综合',1902,'陕西省','是',0,211,1,0,23,10,20,2800,89,37,189,1,1391,0,'http://www.nwu.edu.cn/'),(11,12,'西安交通大学','陕西',6,'西安交通大学（Xi’an Jiaotong University）简称西安交大，位于古都西安，是教育部直属、中央直管的综合性研究型全国重点大学，由教育部与国家国防科技工业局共建，位列世界一流大学建设高校A类、国家“七五”“八五”重点建设高校、国家“211工程”和“985工程”首批重点建设高校，“2011计划”牵头高校，九校联盟（C9）、“珠峰计划”、“111计划”、卓越计划、中国大学校长联谊会、全球能源互联网大学联盟、中俄交通大学联盟、CDIO工程教育联盟成员，新丝绸之路大学联盟发起高校，中国人工智能教育联席会理事长单位，中国三所开设少年班高校之一。','XJTU','西北地区',7,'交通大学','理工',1896,'教育部','是',985,211,1,8,26,44,93,3081,87,242,154,5,1094,0,'http://www.xjtu.edu.cn/'),(28,13,'西北工业大学','陕西',6,'西北工业大学（Northwestern Polytechnical University）简称“西工大”，位于古都西安，直属中华人民共和国工业和信息化部，中央直管副部级建制，是中国唯一一所以同时发展航空、航天、航海（三航）工程教育和科学研究为特色的全国重点大学，世界一流大学建设高校A类，“985工程”、“211工程”重点建设高校；入选2011计划、111计划、卓越工程师教育培养计划、国家大学生创新性实验计划、新工科研究与实践项目、国家建设高水平大学公派研究生项目、中国政府奖学金来华留学生接收院校；卓越大学联盟、中俄工科大学联盟、中俄交通大学联盟、中英大学工程教育与研究联盟、“一带一路”航天创新联盟牵头成员，学位授权自主审核单位，欧盟QB50项目亚洲区唯一发起单位与亚洲区总协调单位。','NPU','西北地区',7,'西北工学院','理工',1960,'国防科工委','否',985,211,1,0,20,31,37,4000,66,122,69,1,823,0,'http://www.nwpu.edu.cn/'),(128,14,'西安理工大学','陕西',6,'西安理工大学（Xi’an University of Technology）简称“西安理工”，坐落于古都西安，是中央与陕西省共建高校，国家中西部高校基础能力建设工程实施院校，陕西省“国内一流大学建设高校”，新丝绸之路大学联盟成员，陕西省省属高水平大学，24所中国研究生院院长联席会扩大高校之一，全国首批博士、硕士、学士学位授予单位，教育部专业学位研究生教育、研究生课程建设综合改革试点单位，入选国家建设高水平大学公派研究生项目、国家特色重点学科项目、卓越工程师教育培养计划、新工科研究与实践项目、国家级大学生创新创业训练计划、全国深化创新创业教育改革示范高校、中国人民解放军海军后备军官选拔和培训基地，中国西北地区水利水电、装备制造、印刷包装行业高级专门人才的重要培养基地和科研中心之一',NULL,'西北地区',7,'陕西机械学院','理工',1958,'陕西省','是',0,0,0,0,17,4,2,2500,67,91,41,0,2836,0,'http://www.xaut.edu.cn/'),(263,15,'西安工业大学','陕西',6,'西安工业大学（Xi’an Technological University）简称“西安工大”，位于世界历史名城古都西安，是中国西北地区唯一一所以兵工为特色，多学科协调发展的教学研究型大学。原中华人民共和国兵器工业部直属的七所本科院校之一（“兵工七子”），由国家国防科技工业局、中国兵器工业集团、中国兵器装备集团与陕西省政府共建。是中国兵器协同创新联盟单位、陕西省省属高水平大学、教育部“卓越工程师教育培养计划”试点高校、国家级大学生创新创业训练计划实施高校、教育部“新工科研究与实践项目（卓越工程师教育培养计划2.0）”入选高校、陕西省大学生创新能力培养综合改革试点学校。学校是国家二级保密资格单位，是一所以\"军民结合，寓军于民\"的国防科研高校，CDIO工程教育联盟成员单位。','XATU','西北地区',7,'西安仪器工业学校','理工',1955,'陕西省','否',0,0,0,0,14,0,0,1800,56,62,8,0,0,0,'http://www.xatu.cn/'),(257,16,'西安石油大学','陕西',6,'西安石油大学（Xi’an Shiyou University），简称“西石大”，位于世界历史名城古都西安，学校是中国西北地区唯一一所以石油石化为特色的多科性普通高等学校，陕西省人民政府和中国石油天然气集团公司、中国石油化工集团公司、中国海洋石油总公司国家三大石油公司共建院校，教育部卓越工程师教育培养计划、陕西省省属高水平大学、中国政府奖学金来华留学生自主招生院校，推荐优秀应届本科毕业生免试攻读硕士学位研究生院校。具有本科、硕士研究生到博士研究生完整的人才培养体系。','XSYU','西北地区',7,'西安石油学院','理工',1957,'陕西省','否',0,0,0,0,14,0,0,1260,57,58,0,0,3250,0,'http://www.xapi.edu.cn/'),(161,17,'陕西科技大学','陕西',6,'陕西科技大学（Shaanxi University of Science & Technology）位于世界历史名城古都西安，是中国西部地区唯一一所以轻工为特色的多科性大学，由中国轻工业联合会、中国轻工集团公司与陕西省政府共建，国家“中西部高校基础能力建设工程”建设高校、陕西省省属高水平大学之一，入选教育部“卓越工程师计划”试点高校、教育部“卓越工程师教育培养计划2.0”建设高校、国家首批“新工科研究与实践项目”入选高校、教育部“互联网+中国制造2025”产教融合促进计划试点院校和陕西省首批深化创新创业教育改革示范高校，CDIO工程教育联盟成员单位。','SUST','西北地区',7,'西北轻工业学院','理工',1958,'陕西省','是',0,0,0,0,15,0,0,1200,59,71,18,0,0,0,'http://www.sust.edu.cn'),(258,18,'西安工程大学','陕西',6,'西安工程大学（Xi’an Polytechnic University）简称“西工程大”，位于古都西安，是中国西部地区唯一以纺织服装为特色的高校，教育部“卓越工程师教育培养计划”高校、教育部“新工科研究与实践项目“（卓越工程师教育培养计划2.0）高校、陕西省省属高水平大学、首批“陕西省2011协同创新中心”立项建设高校，拥有硕士学位授予权。','XPU','西北地区',7,'西安工程科技学院','理工',1928,'陕西省','否',0,0,0,0,15,0,0,1000,63,50,1,0,0,0,'http://www.xpu.edu.cn/'),(91,19,'长安大学','陕西',6,'长安大学（Chang’an University）座落于古都西安，直属中华人民共和国教育部，是教育部和交通运输部、自然资源部、住房和城乡建设部、陕西省人民政府共建的国家“211工程”重点建设大学，国家“985工程优势学科创新平台”建设高校，国家世界一流学科建设高校。入选国家“111计划”、教育部“卓越工程师教育培养计划”、“国家建设高水平大学公派研究生项目”、”国家级大学生创新创业训练计划“、”国家级新工科研究与实践项目“。是高水平行业特色大学优质资源共享联盟、中俄交通大学联盟成员高校，是中国最早承担援外教育和和首批招收港、澳、台学生的高校之一，是中国政府奖学金来华留学生接收院校、国家大学生文化素质教育基地、中国人民武装警察部队后备警官选拔培训基地、首批高等学校科技成果转化和技术转移基地，拥有世界唯一的高校汽车综合试验场。',NULL,'西北地区',7,'西安公路学院','理工',1951,'教育部','是',985,211,1,0,21,5,6,2027,77,34,118,1,1157,0,'http://www.chd.edu.cn/'),(68,20,'西北农林科技大学','陕西',6,'西北农林科技大学（Northwest A&F University）简称“西农”或“西北农大”，坐落于中国农科城杨凌，是中华人民共和国教育部直属全国重点大学、中管32所副部级建制重点大学之一，国家“双一流”世界一流大学建设高校 [1]  ，“985工程”、“211工程”重点建设高校，“111计划”、“2011计划”、“千人计划”、“卓越农林人才教育培养计划”、“国家建设高水平大学公派研究生项目”、“中国政府奖学金来华留学生接收院校”、“国家大学生创新性实验计划”成员，教育部“援疆学科建设计划”40所全国重点大学之一，由教育部与中国科学院、农业部、水利部、国家林业局等16个部委和陕西省共建，是中国西北地区现代高等农业教育的发源地，也是全国农林水学科最为齐备的高等农业院校，葡萄酒专业稳居全国第一。',NULL,'西北地区',7,'国立西北农林专科学校','农林',1934,'教育部','是',985,211,1,0,26,3,6,2183,67,119,68,2,1339,0,'http://www.nwsuaf.edu.cn'),(441,21,'陕西中医药大学','陕西',6,'陕西中医药大学（Shaanxi University of Chinese Medicine）简称“陕中大”，坐落于陕西咸阳，前身是1952年创建于西安的西北中医进修学校，1959年升格为陕西中医学院，1961年迁至咸阳，2015年更名为陕西中医药大学，是陕西惟一一所培养高级中医药人才的普通高等院校，是1978年中共中央56号文件确定的全国8所重点建设的中医院校之一。','SUCM','西北地区',7,'西北中医进修学校','医科',1953,'陕西省','否',0,0,0,0,14,0,0,2501,24,31,0,0,0,0,'http://www.sntcm.edu.cn'),(79,22,'陕西师范大学','陕西',6,'陕西师范大学（Shaanxi Normal University）简称陕西师大、陕师大，坐落于古都西安，是中华人民共和国教育部直属重点综合性师范大学，国家“211工程”、“985工程优势学科创新平台”重点建设大学，是国家首批“双一流”世界一流学科建设高校 [1]  ，入选“111计划”、“2011计划”、“卓越教师培养计划”、”海外高层次人才引进计划“、“国家建设高水平大学公派研究生项目”，中俄教育类高校联盟、新丝绸之路大学联盟、长安联盟成员，国家师范生免费教育试点院校，中国政府奖学金来华留学生接收院校，是国家培养高等院校、中等学校师资和教育管理干部以及其他高级专门人才的重要基地，被誉为“教师的摇篮 ','SNNU','西北地区',7,'西安师范学院','师范',1944,'教育部','是',0,211,1,0,21,0,0,1800,68,231,118,0,1041,0,'http://www.snnu.edu.cn/'),(316,23,'延安大学','陕西',6,'延安大学（Yan’an University）简称“延大”，坐落于革命圣地延安，是毛泽东同志亲自命名、中国共产党创办的第一所综合性大学，由中华人民共和国教育部与陕西省人民政府共建。是“卓越医生教育培养计划”试点高校、陕西省属重点大学、陕西省省属高水平大学、国家大学生文化素质教育基地，是中国人民大学、北京理工大学、北京化工大学等国家重点高校对口支援高校','Yanda','西北地区',7,NULL,'综合',1941,'陕西省','是',0,0,0,0,19,0,0,1481,61,12,0,0,0,0,'http://www.yau.edu.cn/'),(390,24,'陕西理工大学','陕西',6,'陕西理工大学（Shaanxi University of Technology），简称“陕理工”，创建于1958年，坐落于中国历史文化名城汉中，是一所涵盖文学、经济学、法学、教育学、历史学、理学、工学和管理学的多科性省属普通高等学校，也是全国首批具有学士学位授予权的高校之一，硕士学位授予单位。','SNUT','西北地区',7,'陕西理工学院','理工',1958,'陕西省','是',0,0,0,0,17,0,0,1180,65,27,0,0,0,0,'http://www.snut.edu.cn/'),(397,25,'宝鸡文理学院','陕西',6,'宝鸡文理学院（Baoji University of Arts and Sciences）简称“宝文理”，位于宝鸡。学校是陕西省“国内一流学科建设高校”、硕士学位授予单位、博士学位授予单位立项建设单位、普通高等学校本科教学工作水平评估优秀学校。学校前身是1958年创办的省属本科宝鸡大学，1963年停办；1975年陕西师范大学宝鸡分校在宝鸡大学旧址成立，1978年改建为宝鸡师范学院，办学层次依然为本科；1984年职业大学宝鸡大学成立；1992年经原国家教委批准宝鸡师范学院与宝鸡大学合并定名为宝鸡文理学院；2018年成为陕西省“国内一流学科建设高校”，同年成为教育部在线教育研究中心“智慧教学试点项目”单位。','BUAS','西北地区',7,'宝鸡大学','师范',1958,'陕西省','是',0,0,0,0,17,0,0,1100,65,11,0,0,0,0,'http://www.bjwlxy.edu.cn/'),(501,26,'咸阳师范学院','陕西',6,'咸阳师范学院（Xianyang Normal University）简称“咸阳师院”，位于陕西咸阳，是一所以教师教育为特色的普通省属本科院校，是陕西省一流应用型本科院校建设单位、省级硕士学位授予单位立项建设单位。学校肇始于1978年5月的“陕西师范大学咸阳专修科”。1978年12月，经教育部批准，成立“咸阳师范专科学校”。2001年5月，与陕西省咸阳教育学院合并，成立咸阳师范学院。2004年10月，陕西广播电视大学咸阳市分校并入。2016年7月，陕西省机电工程学校划归学校。','','西北地区',7,'咸阳师范专科学校','师范',1978,'陕西省','是',0,0,0,0,16,0,0,1240,55,0,0,0,0,0,'http://www.xysfxy.cn/'),(493,27,'渭南师范学院','陕西',6,'渭南师范学院（Weinan Normal University）简称“渭南师院”，位于陕西渭南，是一所以教师教育为主要特色的多科性普通高等院校。具有学士学位授予权，与中国国内外15所高校合作培养硕士、博士研究生。学校肇始于中国共产党陕西党团组织创始人之一、著名教育家王尚德1923年创立的赤水职业学校及之后“抗大式”的赤水农业职业学校，经历了原渭南师范学院、陕西师范大学渭南专修科、渭南师范专科学校三个时期。2000年，经教育部批准，渭南师范专科学校与1983年创立的渭南教育学院合并为本科层次的渭南师范学院。','WNU','西北地区',7,'赤水职业学校','师范',1923,'陕西省','是',0,0,0,0,12,0,0,1300,64,0,0,0,0,0,'http://www.wntc.edu.cn/'),(328,28,'西安外国语大学','陕西',6,'西安外国语大学（Xi’an International Studies University）简称“西外大”、“西外”，位于世界历史名城古都西安，是新中国最早建立的四所外语院校之一，西北地区唯一一所主要外语语种齐全的高等学府，陕西省省属高水平大学、新丝绸之路大学联盟、长安联盟成员之一，中国政府奖学金来华留学生接收院校 [1]  ，博士学位授予单位。是一所以外语教育和人文学科为主体，社会学科、管理学科和理学学科协调发展的多学科外国语大学，国家重要的外语人才培养基地。','XISU','西北地区',7,'西北俄文专科学校','语言',1951,'陕西省','是',0,0,0,0,21,0,0,1300,56,59,13,0,0,0,'http://www.xisu.edu.cn/'),(297,29,'西北政法大学','陕西',6,'西北政法大学（Northwest University of Political Science and Law）简称“西法大”，坐落于世界历史名城古都西安，由中央与陕西省共建，是新中国最早建立的高等政法学府之一，具有光荣的革命传统和丰厚的文化底蕴，是一所法学特色鲜明，哲学、经济、管理、文学等多学科相互支撑、协调发展的多科性大学。入选首批“卓越法律人才教育培养计划”，是法学传统“五院四系”成员、“立格联盟”创始成员、长安联盟成员、西北地区法学研究中心、人文社会科学研究的重要基地、陕西省省属高水平大学。','NWUPL','西北地区',7,'西安政法学院','政法',1937,'陕西省','是',0,0,0,0,18,0,0,1000,32,39,1,0,0,0,'http://www.nwupl.cn/'),(331,30,'西安体育学院','陕西',6,'西安体育学院（Xi’an Physical Education University）简称“西体”，坐落于世界历史名城古都西安，创建1954年，是新中国最早建立的6大体育院校之一，原隶属国家体育总局（国家体委），2001年实行中央与地方共建，以地方管理为主，陕西省人民政府与国家体育总局共建院校。 是西北地区唯一一所体育高等学府。','XPEU','西北地区',7,'西北体育学院','体育',1954,'陕西省','否',0,0,0,0,8,0,0,832,21,11,0,0,0,0,'http://www.xaipe.edu.cn/'),(342,31,'西安音乐学院','陕西',6,'西安音乐学院（Xi’an Conservatory of Music）简称“西音”， 坐落于世界历史名城古都西安，学校创建于1949年10月，是中国西北地区唯一独立建制的高水平高等音乐学府。东临巍巍大雁塔，西通丝路起点，北依长安古城南门，南接大兴善寺学校是硕士学位授予单位。截至2016年10月，占地地总面积89547.1平方米，建筑面积约28.90万平方米；有2个一级省级优势学科、1个省级特色学科、12个二级学科硕士学位授权点；开设音乐教育学院、现代音乐学院等12个教学单位，10个本科专业；设有西北民族音乐研究中心、“一带一路”音乐文化高等研究院、西安鼓乐学术馆、陕西爱乐乐团、西安鼓乐艺术团、秦筝艺术团等社科研究基地或艺术实践团体；有教职工527人，其中专任教师325人。专任教师中，具有副高级以上专业技术职称160人，具有硕士、博士学位的254人，享受“国务院特殊津贴”专家4人；全日制在校生4500余人。','XACM','西北地区',7,'陕西艺术学院','艺术',1949,'陕西省','否',0,0,0,0,12,0,0,527,10,10,0,0,0,0,'http://www.xacom.edu.cn/'),(200,32,'西安美术学院','陕西',6,'西安美术学院（Xi’an Academy of Fine Arts）简称“西安美院”或“西美”，位于世界历史名城古都西安，是中国三大拥有博士学位授权点的专业美术学院之一，居全国八大美院第三。是中国西北地区唯一一所高等专业美术学府，陕西省省属高水平大学。学校前身是西北人民艺术学院二分部，1949年7月由山西临汾迁至西安兴国寺，更名为西北军政大学艺术学院，后历经西北艺术专科学院、西北美术专科学院等阶段，于1960年5月正式定名为西安美术学院，1994年学院整体搬至雁塔区。2007年在教育部本科教学工作水平评估中获得“优秀”。2011年被列入陕西省高水平大学建设行列。','XAFA','西北地区',7,'西北艺术专科学院','艺术',1949,'陕西省','否',0,0,0,0,15,0,0,744,27,8,3,0,0,0,'http://www.xafa.edu.cn/'),(482,33,'西安文理学院','陕西',6,'西安文理学院（Xi’an University）简称西文理，位于古都西安。是2003年经教育部批准，由西安市政府主办、省市共建、面向全国招生的一所全日制普通高等院校 。先后由西安大学、西安师范专科学校、西安教育学院、西安幼儿师范学校和西安师范学校合并而成，办学历史可追溯至1903年设立的陕西师范学堂。学校与西安交通大学、电子科技大学、陕西师范大学、宝鸡文理学院、西安体育学院、延安大学、陕西理工大学、延安大学鲁迅艺术学院联合培养硕士研究生','','西北地区',7,'','综合',2003,'陕西省教育厅','是',0,0,0,0,12,0,0,1179,47,0,0,0,0,0,'http://www.xawl.org/'),(511,34,'榆林学院','陕西',6,'榆林学院（Yulin University）坐落在陕西榆林，是一所以工科为主，工、管、文、理、农、法等多学科协调发展的省属本科院校，是陕西省最北部的高等院校，也是榆林唯一的一所省属本科院校。入选卓越农林人才教育培养计划。学校前身是创建于1923年的陕西省立第四师范学校，1958年改名为西安师范学院绥德分院，1978年更名为陕西师范大学榆林专修科，80年代初期北迁至榆林西沙，1984年更名为榆林师范专科学校，1991年与榆林农林专科学校合并，整合为榆林高等专科学校，2003年经教育部批准升格为本科院校，定名为榆林学院。2015年1月，陕西省教育厅和榆林市人民政府签订了省市共建协议，合力推进榆林大学建设。2017年，绥德师范学校并入。2018年，学校被教育部确定为新增硕士学位授权单位，被陕西省确定为一流应用型本科院校建设单位。',NULL,'西北地区',7,'榆林高等专科学校','师范',1923,'陕西省','是',0,0,0,0,15,0,0,1004,55,0,0,0,0,0,'http://www.yulinu.edu.cn/'),(517,35,'商洛学院','陕西',6,'商洛学院（Shangluo University）是教育部批准、陕西省政府举办的全日制普通本科院校，坐落于陕西商洛。学校肇始于1976年成立的陕西省商洛地区五七师范学院，1979年更名陕西师范大学商洛专修科，1984年更名为商洛师范专科学校，2005年与陕西省商洛农业学校合并，2006年经教育部批准升本改建为商洛学院，2011年成为省市共建高校，2014年被列入陕西省首批转型发展试点高校，2017年入选全国百所产教融合发展工程应用型高校，2018年被确定为陕西省“一流学院”建设单位。与西安交通大学、陕西师范大学、西北大学、浙江万里学院等国内高校建立了对口支援与合作办学友好关系。',NULL,'西北地区',7,'商洛师范专科学校','综合',1976,'陕西省教育厅','是',0,0,0,0,11,0,0,723,40,0,0,0,0,0,'http://www.slxy.cn/'),(530,36,'安康学院','陕西',6,'安康学院（Ankang University）是陕西省安康市唯一一所省属全日制普通本科院校，以培养具有较高综合素质和创新精神的应用型人才为主要任务。学院是首批卓越农林人才教育培养计划项目成员、汉江流域大学联盟成员之一，具有学士学位授予权，与多所高校联合培养硕士、博士研究生。前身是创建于1958年的安康大学，1963年因国家经济困难停办。1978年重新设立“陕西师范大学安康专修科”，1984年经陕西省人民政府批准，更名为“安康师范专科学校”。2004年原安康教育学院、陕西省安康农业学校并入安康师范专科学校，2006年2月经教育部批准升格为本科院校。',NULL,'西北地区',7,'安康教育学院','综合',1958,'陕西省教育厅','否',0,0,0,0,13,0,0,755,41,0,0,0,0,0,'http://www.aktc.net.cn/'),(9999,37,'西安培华学院','陕西',6,'西安培华学院（Xi’an Peihua University）位于世界历史名城古都西安，是中华人民共和国教育部批准的具有学士学位授予权的民办高校。学院历史可追溯到1928年由陕西女子职业教育促进会筹办、陕西省教育厅批准设立的西安第一女子平民职业学校，其后几经变迁，1984年由中国教育家姜维之教授在恢复原培华女职的基础上创建西安培华女子大学，成为国内首家专门开展女性教育的普通高等院校。2003年经国家教育部批准升格为本科高校，并更名为西安培华学院（男女兼收），2007年成为西部首家拥有学士学位授予权的民办高校。2018年成为陕西省一流民办高校建设单位。',NULL,'西北地区',7,'西安培华女子大学','综合',1928,'陕西省','否',0,0,0,0,9,0,0,1200,58,0,0,0,0,0,'http://www.peihua.cn/'),(232,39,'西安邮电大学','陕西',6,'西安邮电大学（Xi’an University of Posts & Telecommunications）简称“西邮”，位于世界历史名城古都西安，创建于1950年，是中华人民共和国工业和信息化部与陕西省政府共建高校、陕西省省属高水平大学、教育部“卓越工程师教育培养计划”实施高校、国家级大学生创新创业训练计划立项高校、教育部”新工科研究与实践项目“入选高校、长安联盟、CDIO工程教育联盟成员单位，中国人民解放军后备军官选拔培养基地、全国首批试办边防军人子女预科班的高校、西北地区唯一承担亚太电信组织和东盟培训任务的高校，已发展成一所以工学为主，以信息科学技术为特色，工、理、管、经、文、法、艺多学科协调发展的教学研究型大学。','XUPT','西北地区',7,'西安邮电学院','理工',1950,'陕西省','否',0,0,0,0,18,0,0,965,46,63,0,0,0,0,'http://www.xiyou.edu.cn/'),(564,40,'西安航空学院','陕西',6,'西安航空学院（Xi’an Aeronautical University）位于世界历史名城古都西安，是一所以工科为主、多学科协调发展的全日制普通高等学校。学校创建于1955年，原名为西安航空工业学校，隶属原航空工业部；1957年合并兰州航空工业学校；1960年升格为专科学校，更名为西安航空工业专科学校；1985年经原国家教委批准升格为西安航空工业技术专科学校；1993年更名为西安航空技术高等专科学校。1999年划转地方，隶属陕西省人民政府，为中央与地方共建院校。2012年经教育部批准升格为普通本科院校，更名为西安航空学院。','','西北地区',7,'西安航空工业学校','理工',1955,'陕西省','否',0,0,0,0,17,0,0,900,51,0,0,0,0,0,' http://www.xihangzh.com/'),(562,41,'西安医学院','陕西',6,'西安医学院（Xi’an Medical University）简称“西医”，位于古都西安，是陕西省人民政府举办的一所全日制普通本科院校，硕士学位授予单位。创建于1951年，其前身是陕西省卫生学校。1959年省政府设陕西省卫生干部学院建制。1994年国家教委批准成立陕西医学高等专科学校。2006年2月经教育部批准，升格为本科院校，更名为西安医学院。','XMU','西北地区',7,'陕西省卫生学校','医科',1951,'陕西省','否',0,0,0,0,14,0,0,879,20,16,0,0,0,0,'http://www.xiyi.edu.cn/'),(9999,42,'西安欧亚学院','陕西',6,'西安欧亚学院（Xi’an Eurasia University）位于世界历史名城古都西安，是一所经中华人民共和国教育部批准，以管理、经济为主，艺术、文学、教育、工学等协调发展的国际化应用型普通本科高校；入选民办教育领域改革试点单位、国家教育体制改革试点项目、国家第一批教育信息化试点单位、教育部首批新工科研究与实践项目、陕西省一流民办高校建设单位。','XAEU','西北地区',7,'西安涉外人才培训学院','财经',1995,'陕西省','否',0,0,0,0,11,0,0,1433,39,0,0,0,0,0,'http://www.eurasia.edu/'),(9999,44,'西安翻译学院','陕西',6,'西安翻译学院（Xi’an FanYi University）简称“西译”，位于世界历史名城古都西安，是一所以文、商科为主，以外语为特色，多学科协调发展、具有重要影响和鲜明特色的高水平民办大学。学校前身是创建于1987年的西安翻译培训学院。2000年经陕西省人民政府批准成立西安翻译职业学院。2005年经教育部批准升格为本科高校，更名为西安翻译学院。2009年获得学士学位授予权。2014年成为陕西省首批应用型转型试点院校。2016年获批陕西省“管办评”试点院校。2018年成为陕西省一流民办高校建设单位','XFU','西北地区',7,'西安翻译培训学院','语言',1987,'陕西省教育厅','否',0,0,0,0,12,0,0,1000,36,0,0,0,0,0,'http://www.xfuedu.org/'),(9999,45,'西京学院','陕西',6,'西京学院（Xijing University）是一所具有研究生教育资格的高水平民办高校，位于古都西安。2005年升格为本科院校，定名为“西京学院”，2009年取得学士学位授予权，2011年获得专业学位研究生教育资格。2018年2月成为陕西省一流民办高校建设单位。 [1]学校是首批获得硕士研究生教育专业点的全国五所民办高校之一。2018年5月学校官网显示，学校有5个专业硕士学位授权点；占地近1400亩，建筑面积60多万平方米。在长期的发展过程中，形成了以机械工程、控制工程、土木工程为代表的工科，以会计学、电子商务为代表的现代服务业，以服务于产业的工业设计、视觉传达为代表的艺术设计“三大专业集群。设有15个院系、7个书院；35个本科专业，23个高职专业。有全日制在校学生2.1万余人。','XJU','西北地区',7,NULL,'理工',1994,'陕西省教育厅','否',0,0,0,0,15,0,0,1787,37,5,0,0,0,0,'http://www.xijing.com.cn/'),(9999,46,'西安思源学院','陕西',6,'西安思源学院（Xi\'an Siyuan University）位于世界历史名城古都西安，是中华人民共和国教育部批准设立的民办普通本科高校。学校成立于1998年，前身是西安交通大学机械工程学院培训中心，已发展成为一所以本科教育为主、高等职业教育为辅，工、文、经、管、教、法、艺、医等学科齐全的综合性院校。2012年，西安交大与学院建立了对口帮扶关系。\r\n','','西北地区',7,'西安交通大学机械工程学院','理工',1998,'陕西省教育厅','是',0,0,0,0,13,0,0,1000,70,0,0,0,0,0,'http://www.xasyu.cn/'),(9999,47,'陕西国际商贸学院','陕西',6,'陕西国际商贸学院由陕西步长制药有限公司1997年5月投资创办， 2002年6月经陕西省人民政府批准，教育部备案，升格为具有国家统招资格的职业学院。同时更名为“陕西国际商贸职业学院”。2007年6月通过了教育部 “人才培养工作水平评估”，取得了“良好”格次，同年12月通过了“升本”评估。2008年5月，经教育部和省人民政府同意，学校升格为全日制民办本科高校，同时更名“陕西国际商贸学院”。','','西北地区',7,'','综合',1997,'陕西省教育厅','否',0,0,0,0,10,0,0,744,34,0,0,0,0,0,' http://www.csiic.com/'),(9999,48,'陕西服装工程学院','陕西',6,'陕西服装工程学院创建于1994年，是国家教育部批准设立具有学士学位授予权的普通本科大学，学校以工为主，工、艺、文、管、经、医、教育相结合的多学科协调发展的全日制特色本科高校。\r\n学院位于西安市西咸新区沣西新城，占地729.1亩，建筑面积30.1万平方米。 [1]  设10个教学单位，开设15个本科专业33个高职专业，在校生10000余人，专任教师390多名。（据2015年12月学院官网信息显示）。','','西北地区',7,'','综合',1994,'陕西省教育厅','否',0,0,0,0,11,0,0,400,28,0,0,0,0,0,' http://www.sxfu.org/'),(9999,49,'西安交通工程学院','陕西',6,'西安交通工程学院（Xi\'an Traffic Enginering Institute）是经国家教育部批准的全日制普通本科高等院校，创办于1994年，是陕西省唯一一所以轨道交通类专业为主的普通本科高等院校。\r\n学校“教育部-中兴通讯ICT产教融合创新基地”是教育部与中兴通讯在全国30所高校共建的创新应用技术人才培养模式基地之一，是陕西高校中唯一一所基地共建签约学校。是中国职教学会轨道交通专业委员会唯一民办院校理事单位，“中俄交通大学校长联盟—中国轨道交通应用技术人才培养联盟”成员单位，中国城市轨道交通协会会员单位。','','西北地区',7,'西安科技商贸职业学院','理工',1994,'陕西省教育厅','否',0,0,0,0,8,0,0,0,18,0,0,0,0,0,'http://www.xikemao.com/'),(9999,50,'西安交通大学城市学院','陕西',6,'西安交通大学城市学院?[1]??（Xi\'an Jiaotong University City College）是2004年5月经国家教育部批准设立的全日制本科层次的独立学院。学院由西安经发集团控股的交大博通公司投资，西安交通大学负责对学院的教学进行组织和管理，任课教师和管理干部主要从西安交通大学选聘。',NULL,'西北地区',7,NULL,'综合',2004,'陕西省教育厅','否',0,0,0,0,9,0,0,577,38,0,0,0,0,0,'http://www.xjtucc.cn'),(9999,51,'西北大学现代学院','陕西',6,'西北大学现代学院（Modern College of Northwest University）创建于2003年，由国家211工程西北大学申办、国家教育部批准设立的从事统招本科学历教育的独立学院。西大现代继承百年名校的光荣传统，以创新模式兴教办学，适应了中国高等教育的发展方向，是由国家教育部批准设立的从事统招本科学历教育的独立学院，现代学院按照普通高等学校独立学院体制运行，实行普通类全日制本科教育，具有学士学位授予权力。','','西北地区',7,'','综合',2003,'陕西省教育厅','否',0,0,0,0,0,0,0,0,14,0,0,0,0,0,'http://www.xdxd.cn/ '),(9999,52,'西安建筑科技大学华清学院','陕西',6,'西安建筑科技大学华清学院（Xian University of Architecture and Technology Huaqing College）始建于2004年，位于陕西省西安市，学院是经中华人民共和国教育部批准、由西安建筑科技大学联合社会力量举办的一所全日制普通高等院校',NULL,'西北地区',7,NULL,'综合',2004,'陕西省教育厅','否',0,0,0,0,6,0,0,600,30,0,0,0,0,0,'http://www.xauat-hqc.com'),(9999,54,'陕西科技大学镐京学院','陕西',6,'陕西科技大学镐京学院是全国重点大学——陕西科技大学按照新机制、新模式与陕西斯卡雷 博德制衣有限公司合作举办的本科层次独立学院。学院成立于2004年，是陕西省首批设立的八所独立学院之一。镐京学院全面贯彻党的十八大和十八届三中、四中、五中、六中全会精神，扎实推进“两学一做”学习教育；全面贯彻党的教育方针，依法治校，依规办学，立德树人，提高质量。全面深化教育综合改革，坚持以人才培养为中心，注重内涵发展，加快推进应用转型和教育现代化；全面推进“三大工程”建设，加强两个队伍建设，落实教师一岗双任，全面提高教育教学质量，深化人才培养模式改革,保持英语教学特色，强化专业技能培养,完善就业实习实训，推进竞赛考证，注重文化建设，优化育人环境，努力创建和谐文明和平安校园，为社会主义事业培养更多合格的建设者和可靠接班人。陕西科技大学镐京学院是华为技术有限公司战略合作方。','','西北地区',7,'','综合',2004,'陕西省教育厅','否',0,0,0,0,0,0,0,460,27,0,0,0,0,0,'http://www.kdhj-edu.net'),(9999,58,'西北工业大学明德学院','陕西',6,'西北工业大学明德学院是国家教育部批准设立的全日制本科层次独立学院，其前身是于1999年成立的西北工业大学金叶信息技术学院。2005年，陕西金叶科教集团股份有限公司与西北工业大学在金叶信息技术学院办学的基础上，共同申办本科层次的独立学院——西北工业大学明德学院。',NULL,'西北地区',7,NULL,'综合',1999,'陕西省教育厅','否',0,0,0,0,9,0,0,767,30,0,0,0,0,0,'http://www.npumd.edu.cn'),(9999,59,'长安大学兴华学院','陕西',6,'长安大学兴华学院是2006年4月12日经教育部批准（教发函[2006]72号），由长安大学联合社会力量，以新机制、新模式成立的全日制普通本科独立学院，校园位于西安高新技术产业开发区富裕路。',NULL,'西北地区',7,NULL,'综合',2006,'陕西省教育厅','否',0,0,0,0,0,0,0,0,15,0,0,0,0,0,'http://www.chdxhxy.com'),(9999,60,'西安理工大学高科学院','陕西',6,'西安理工大学高科学院是国家教育部批准的全日制普通本科独立学院，是学士学位授予权单位。学院有位于陕西省西安市长安区大学城的长安校区和位于西咸新区泾河新城的西咸校区，长安校区占地400余亩，建筑面积15万平方米。新建西咸校区占地500亩，建筑面积56万平方米，在长安校区的基础上规划建设了大型图书馆、体育馆、教学楼、现代化的实验实训室集群、信息化公寓，按照功能完善、环境优美、风格别具的高等院校要求，结合现代科技新发展，建设成产、学、研一体的现代化新校区。第一批6000余名新生于2018年9月正式入驻西咸校区。全日制在校生13000人。','','西北地区',7,'','综合',2006,'陕西省教育厅','否',0,0,0,0,0,0,0,0,14,0,0,0,0,0,'http://www.xthtc.com'),(9999,61,'西安科技大学高新学院','陕西',6,'西安科技大学高新学院 [1]  （Xi\'an Kedagaoxin University），位于古都西安，是经教育部批准，由西安科技大学与陕西西科美芯科技有限责任公司、骊山微电子公司合作举办的全日制普通本科独立学院。学校的办学历史可追溯到2003年，原名叫西安科技大学电子信息学院。经教育部批准，2006年西安科技大学高新学院正式成立。 [2]','','西北地区',7,'','综合',2003,'陕西省教育厅','否',0,0,0,0,8,0,0,500,37,0,0,0,0,0,'http://www.gaoxinedu.com'),(540,62,'陕西学前师范学院','陕西',6,'陕西学前师范学院（Shaanxi Xueqian Normal University）简称\"陕师院\"，位于古都西安，是一所由陕西省人民政府主办的全日制公办省属普通本科高校。学校前身是陕西教育学院，1963年由陕西省西安师范学校（1906年建立）和陕西省教师进修学校（1956年建立）合并成立。2012年3月，经国家教育部和陕西省人民政府批准，改制更名为陕西学前师范学院。2013年，学校成为陕西省学前教育师范生免费教育唯一试点院校；2014年，学校被陕西省确定为陕西省转型发展试点院校。',NULL,'西北地区',7,'陕西教育学院','师范',1963,'陕西省教育厅','否',0,0,0,0,14,0,0,890,28,0,0,0,0,0,'http://www.snsy.edu.cn/'),(43,102,'北京交通大学','北京',1,'北京交通大学是教育部直属，教育部、交通运输部、北京市人民政府和中国国家铁路集团有限公司共建的全国重点大学，“211工程”“985工程优势学科创新平台”项目建设高校和具有研究生院的全国首批博士、硕士学位授予高校。学校牵头的“2011计划”“轨道交通安全协同创新中心”是国家首批14个认定的协同创新中心之一。2017年，学校正式进入国家“双一流”建设行列，将围绕优势特色学科，重点建设“智慧交通”世界一流学科领域。\n\n北京交通大学作为交通大学的三个源头之一，历史渊源可追溯到1896年，前身是清政府创办的北京铁路管理传习所，是中国第一所专门培养管理人才的高等学校，是中国近代铁路管理、电信教育的发祥地。1917年改组为北京铁路管理学校和北京邮电学校，1921年与上海工业专门学校、唐山工业专门学校合并组建交通大学。1923年交通大学改组后，北京分校更名为北京交通大学。1950年学校定名北方交通大学，毛泽东主席题写校名，著名桥梁专家茅以升任校长。1952年，北方交通大学撤销，学校改称北京铁道学院。1970年恢复“北方交通大学”校名。2000年与北京电力高等专科学校合并，由铁道部划转教育部直属管理。2003年恢复使用“北京交通大学”校名。学校曾培养出中国第一个无线电台创建人刘瀚、中国第一台大马力蒸汽机设计者应尚才、中国第一本铁路运输专著作者金士宣、中国铁路运输经济学科的开创者许靖、中国最早的四大会计师之一杨汝梅，以及中国现代作家、文学评论家、文学史家郑振铎等一大批蜚声中外的杰出人才。“东京审判”担任首席检察官的向哲浚，中国著名的经济学家、人口学家马寅初等都曾在学校任教。\n\n学校始终瞄准科技发展前沿和国家重大战略需求，依托信息、管理和交通科学与技术等优势特色学科，通过智力支持、人才保障和专业服务，全面参与了铁路大提速、青藏铁路建设、大秦铁路重载运输、高速铁路建设和城市轨道交通核心技术自主研发等中国轨道交通发展的重大历史事件，取得了一系列具有完全自主知识产权、处于国际先进水平的一系列原创性重大成果，为服务国家交通、物流、信息、新能源等行业以及北京经济社会发展作出了积极贡献，成为支撑和引领国家、行业和区域科技创新发展的重要力量。近年来，学校科技创新取得重大突破，CBTC地铁运行控制系统的研发和成功应用，使我国成为第4个掌握该项技术的国家，实现自主可控；FAO全自动无人驾驶列控系统已达到城市轨道交通列车运行自动化水平的国际最高等级；复杂环境下高速铁路无缝线路关键技术是轨道结构近百年来最突出的改进与创新；“标识网络体系及关键技术”“智慧协同网络”提高了我国下一代互联网产业自主创新能力和核心竞争力；“基于行为的城市交通流时空分布规律与数值计算”对建立现代交通科学理论体系具有重要指导意义；“复杂路网条件下高速铁路列控系统互操作和可靠运用关键技术”为我国高速铁路列控系统设计研发、测试和运用提供了保障；“大型屋盖及围护体系抗风防灾理论、关键技术”攻克了大型屋盖及其围护体系抗风难题，提出的风荷载公式，填补了国内外空白，纳入国家标准。目前，学校正在全面推进实施学校“十三五”规划和《综合改革方案》各项任务，坚持“三步走”战略，到本世纪中叶基本建设成为特色鲜明世界一流大学。\n\n学校在被称为“学府胜地”的北京市海淀区建有东西两个校区，总面积近1000亩，建筑面积100余万平方米。2015年9月，位于山东省威海市的威海校区正式投入使用。学校各校区均具有完善的教学、科研设施，校园环境优美。\n\n历经双甲子发展，学校形成了以信息、管理等学科为优势，以交通科学与技术为特色，工、管、经、理、文、法、哲等多学科协调发展的完备的学科培养体系。学校设有电子信息工程学院、计算机与信息技术学院、经济管理学院、交通运输学院、土木建筑工程学院、机械与电子控制工程学院、电气工程学院、理学院、语言与传播学院、软件学院、马克思主义学院、建筑与艺术学院、法学院、国家保密学院等14个学院；设有研究生院以及远程与继续教育学院；与企业合作在河北省黄骅市创办独立学院——北京交通大学海滨学院。\n\n学校在四大世界大学排行榜中稳步提升，在U.S.News世界大学排行、上海软科世界大学学术排行中进入500+。在最具影响力的世界大学学科排行榜均有学科上榜，工程与技术、计算机科学、商科与经济学、物理学等4个学科入围THE世界大学学科排名，电气与电子工程，计算机科学与信息系统，机械、航空与制造工程，材料科学，数学，物理学与天文学，商业与管理研究，统计与运筹学等8个学科进入QS世界顶尖学科，交通运输工程学科位列上海软科世界一流学科第一，工程学接近ESI前1‰，工程学、计算机科学、材料科学进入ESI前3‰。系统科学学科在全国学科评估中连续四次蝉联全国第一；系统科学、交通运输工程、信息与通信工程、计算机科学与技术、工商管理等5个学科进入全国第四轮学科评估前10%（A类）；应用经济学、土木工程、统计学、马克思主义理论、机械工程、管理科学与工程、软件工程等7个学科进入全国第四轮学科评估前20%（B+类）。学校有交通运输工程、信息与通信工程2个一级学科国家重点学科，产业经济学、桥梁与隧道工程2个二级学科国家重点学科，包括一级学科所涵盖的二级学科国家重点学科总数达到8个；建有博士后科研流动站15个；有一级学科博士点21个，一级学科硕士点33个，有工程博士、MBA、工程硕士、会计硕士、法律硕士等14类专业学位。\n\n学校把建设高素质的教师队伍作为提高办学实力的关键，大力实施人才强校战略。全校在职教职工2972人，其中专任教师1877人（具有副高级及以上专业技术职称的1286人，具有硕士及以上学历的1774人）。学校有中国科学院院士4人，中国工程院院士9人 ，国家级教学名师5人 ，国务院学位委员会学科评议组成员6人，国家“万人计划”专家13人 ，长江学者特聘教授6人、讲座教授1人、青年学者3人 ，百千万人才工程国家级人选11人 ，国家杰出青年基金获得者12人、优秀青年基金获得者17人，享受政府特殊津贴专家156人。\n\n学校始终把人才培养作为办学的根本任务，素质教育成绩斐然，向国家输送了大量人才。学校1997年、2006年参加全国本科教学工作水平评估均获得优秀。学校有在校本科生15534人，博士研究生2861人，硕士研究生8169人，非全日制硕士研究生1580人，在职专业学位研究生2182人，成人学生6249人，外国留学生全年累计2217人。近三届本科教学成果奖评选中，获得国家级一等奖2项、二等奖9项。近三届中国学位与研究生教育学会研究生教育成果奖评选中，获得一等奖1项，二等奖2项。近5年，学校新增1名国家“万人计划”教学名师，10名北京市教学名师、4名北京市青年教学名师。学校拥有11个国家级特色专业、7个国家级综合改革试点专业、8个国家级卓越工程师教育培养计划专业，11个专业通过国家工程教育专业认证，2个专业通过国家土建类专业评估。学校建有国家级实验教学示范中心6个、国家级虚拟仿真实验教学中心3个、国家级虚拟仿真实验教学项目2个、国家级大学生校外实践基地3个、国家级工程实践教育中心7个；建有国家级教师教学发展示范中心。学校获评国家级精品在线开放课程8门、获评国家级精品资源共享课19门、国家级精品视频公开课6门；获批“十二五”普通高等教育本科国家级规划教材34种42册；获评“全国高校创新创业教育工作50强”“国家级大学生创新创业训练计划实施工作先进单位”“全国高校实践育人创新创业基地”。通过设立理科试验班（思源班）、基础学科拔尖人才试点班（知行班）、本硕（博）连读班、詹天佑班、茅以升班、国际班等多样化人才培养模式，加强了拔尖创新人才的培养。学校生源质量和培养水平逐年提高，本科生和研究生就业率稳定在98%以上，本科生深造率超过55%，学校获评教育部全国首批创新创业典型经验高校和北京市创新创业教育改革示范高校。学生艺术团在全国和首都历次大学生艺术展演比赛中取得优异成绩，曾获全国第一、二、三、四、五届大学生艺术展演器乐组一等奖和多项北京市一等奖，并多次赴海外进行文化交流演出。学校学生艺术团排演的大学生版《长征组歌》在人民大会堂和国安剧院演出，在社会上引起强烈反响，原创话剧《茅以升》成功入选“共和国的脊梁—科学大师名校宣传工程”，在校内外演出多场，赢得社会各界广泛好评。学校体育运动蓬勃发展，高水平运动队和普通学生代表队成绩斐然，截至2018年底在国家级及以上各类比赛中共获冠军133项（次），其中羽毛球87项、跆拳道19项、田径17项、男子排球1项、射击1项、棒垒球2项、桥牌1项、舞蹈队4项。\n\n学校把加强科技创新作为发展的战略重点，不断提升社会服务能力。学校拥有省部级以上科研平台61个，其中包括国家重点实验室1个，国家工程研究中心1个，国家工程实验室6个（其中5个参与），轨道交通安全协同创新中心（牵头）1个，国家能源研发中心1个，国家国际科技合作基地2个，国家认可实验室4个，国家大学科技园1个，教育部重点实验室/工程研究中心9个，北京实验室2个，北京市重点实验室/工程技术研究中心17个，首批首都高端智库1个，北京市哲学社会科学研究基地4个，建有北京市习近平新时代中国特色社会主义思想研究中心北京交通大学研究基地，以及交通运输行业重点实验室2个，其他省部级科研平台8个。近5年，学校承担了原“973”计划、“863”计划、国家重点研发计划项目（课题）、国家社会科学基金重大项目、国家自然科学基金项目以及有关部委的各类科研课题1万余项，科研经费38亿元。发表SCIE检索论文4942篇、EI检索论文7516篇、ISTP检索论文2627篇。申请专利2743项，获授权专利1418项。创立学术交流品牌“中国交通高层论坛”“交大大讲堂”，主办和承办高水平国际学术会议60余场。获得国家级奖励10项，省部级科技奖励155项，其中主持完成项目获国家科学技术进步奖一等奖1项，国家科技进步奖二等奖2项和国家技术发明奖二等奖2项。主持完成的4项人文社会科学研究成果获高等学校科学研究优秀成果奖（人文社会科学）。拥有教学、科研仪器设备资产12.9亿元；图书馆纸本藏书、电子图书、网络资源等总量约1189万册，建有交通运输特色数据库。\n\n学校把加强合作交流作为提高办学水平的重要途径，国内外影响力不断提升。与美、英、德、法等45个国家的253所大学及著名跨国企业建立了合作关系。在比利时鲁汶、美国休斯顿、巴西坎皮纳斯和波兰华沙等地共办有4所孔子学院，积极传播中国文化。学校加入国际铁路合作组织（OSJD），提升了在国际铁路领域的影响力和话语权；加入中国-中东欧高校联合会，为拓展与中东欧国家教育合作交流奠定了基础。学校有国家级示范学院1个，学科创新引智基地（ “111基地”）6个，“111基地”培育项目1个，“一带一路”教科文卫引智项目1个；累计有31位专家入选高端外国专家项目；有中外合作办学项目5个、涉外办学机构3个，开展本科、研究生层次学位教育，提升学校国际化办学水平。充分发挥校友会、基金会、董事会的作用，深化“政产学研用”协同创新，在海内外成立地方校友会51个，吸纳董事单位83家，与交通、物流、信息、能源等行业企业及地方政府等单位建立战略合作关系，在人才培养、科研合作等领域开展长期、广泛的合作。\n\n“饮水思源，爱国荣校”。如今，有着122年辉煌历史的北京交通大学，秉承“知行”校训，肩负新的使命，正以更加开拓进取的精神向着特色鲜明世界一流大学的目标迈进。','NJTU','华北地区',1,'北京铁路管理传习所、交通部交通传习所','理工',1896,'教育部','是',0,211,0,1,14,4,0,1868,59,33,21,1,125,0,'http://www.njtu.edu.cn/'),(13,103,'北京航空航天大学','北京',1,'京航空航天大学（简称北航）成立于1952年，由当时的清华大学、北洋大学、厦门大学、四川大学等八所院校的航空系合并组建，是新中国第一所航空航天高等学府，现隶属于工业和信息化部。学校所在地北京，分为学院路校区、沙河校区，占地3000多亩，总建筑面积170余万平方米。建校以来，北航一直是国家重点建设的高校，是全国第一批16所重点高校之一，也是80年代恢复学位制度后全国第一批设立研究生院的22所高校之一，首批进入“211工程”，2001年进入“985工程”，2013年入选首批“2011计划”国家协同创新中心，2017年入选国家“双一流”建设高校名单。学校第十六次党员代表大会提出以建设扎根中国大地的世界一流大学为发展愿景目标。\n\n近年来，学校着力提升办学境界和格局，大力促进空天信融合发展的学科态势，持续优化创新人才培养体系，积极推进一流师资队伍建设，加快推动科研创新转型发展，务实开展高水平的国际交流合作，大力推进办学条件和民生改善，全面加强党的建设和思想政治工作，各项事业发展取得了突出的成绩。综合办学能力和核心竞争力不断增强，跻身国内高水平大学的第一方阵，国际影响力显著提升。\n\n学校学科繁荣，特色鲜明。有工、理、管、文、法、经、哲、教育、医和艺术10个学科门类。有8个一级学科国家重点学科（并列全国高校第7名），28个二级学科国家重点学科，10个北京市重点学科，10个国防特色学科，14个A类学科，其中航空宇航科学与技术、仪器科学与技术、材料科学与工程、软件工程为A+学科。有60个本科专业，23个博士学位授权一级学科点，40个硕士学位授权一级学科点，20个博士后科研流动站。学校突出学科基础地位，构建空天信融合、理工文交叉、医工结合的一流学科体系，形成珠峰引领、高峰集群、高原拓展的良性学科生态。在航空、航天、动力、信息、材料、仪器、制造、管理等学科领域具有明显的比较优势，形成了航空航天与信息技术两大优势学科群，国防科技主干学科达到国内一流水平，工程学、材料科学、物理学、计算机科学、化学五个学科领域的ESI排名进入全球前1%，工程学进入全球前1‰，具备了建设世界一流学科的基础。在2018年“软科世界一流学科排名”中，航空航天工程学科为世界第一。\n\n学校名师荟萃，人杰地灵。目前学校教职工总数达到3928人，其中专任教师2172人。专任教师中，73.75%具有高级职称，83.60%具有博士学位。汇聚了以23位两院院士、31位“973”计划首席科学家、67位“长江学者奖励计划”教授、52位“国家杰出青年科学基金”获得者、3位国家级教学名师等为代表的高层次人才，全职在校工作的青年长江学者11位，国家优秀青年科学基金获得者52位，涌现出众多国家一等奖获得者、领域专家和型号总师，以及一大批年轻有为、造诣精深的专家学者。\n\n学校精育良才，立德树人。有全日制在校生30000余人，其中本科生与研究生比例为1:1，在校攻读学位的外国留学生近1300人。近年来各省招生生源高考成绩平均排名在前3‰，并有多个省份已进入全省排名前1‰，学校理工类生源整体质量稳居全国第9名。学校坚持立德树人根本任务，突出人才培养中心地位，把培养拔尖人才与强化爱国担当相结合，在知识创造中培养人才，在人才培养中创造知识，着力培养理想高远、学识一流、胸怀寰宇、致真唯实的领军领导人才。打造一流本科生教育，推进大类招生，扩大专业自主选择权，突出厚基础、个性化、导师制、小班化培养，健全完全学分制。推进通识教育、书院育人，发挥华罗庚班、吴大观班、人文社会科学实验班及高等理工学院、中法工程师学院等实验班和荣誉学院的引领辐射作用。构建知识学习、能力实践、国际交流三位一体大课堂，强化跨学科实践、社会实践和创新创业实践。研究生教育坚持面向国家战略需求，立足国际学术前沿，改革创新教育教学机制，重点实施尖端优质生源汇聚、精品课程体系构筑、国际交流深度拓展、多方聚力协同育人、创新激励分类优秀、卓越导师队伍建设等行动计划，着力打造“重创新、强能力、高规格”的人才培养质量品牌，探索出大飞机班、发动机班、信息安全班等定制化高层次人才联合培养模式。拥有国家级人才培养模式创新实验区6个、实验教学示范中心5个、教学团队5个、特色专业建设点11个和精品课程37门。人才培养硕果累累，涌现出了以“北航1号”、“北航2号”和“北航3号”探空火箭为代表的一大批学生科技创新作品。2010年以来，研究生有近400人次获省部级及以上科技成果奖，其中国家级科技奖励署名达70余人次，在国际顶级学术刊物上发表论文的数量显著增长，全国工程硕士实习实践优秀成果获得者、全国做出突出贡献的工程硕士学位获得者等国家级荣誉称号数量处于领先地位。历史悠久的北航“冯如杯”竞赛已连续举办28届，吸引了6万5千余名学生开展创新研究。学校是在全国“挑战杯”竞赛中唯一连续15届捧得“优胜杯”的高校，并将在2019年承办第十六届“挑战杯”竞赛。学生创新创业作品连续三届获得中国“互联网+”大学生创新创业大赛金奖，获得冠军、亚军各一项。春华秋实，芬芳桃李满天下。建校66载，北航为国家培养了大批学术精英、兴业人才和治国栋梁，为国家主流行业和骨干单位输送了20多万优秀毕业生，毕业生一次就业率保持在98%以上。近6年北航有18名校友当选为院士。\n\n学校服务国家，锐意创新。学校以国家重大战略需求为先导，强化基础性、前瞻性和战略高技术研究，引导和支持创新要素向关键瓶颈技术汇聚、与产学研用深度融合，打造顶级创新平台和一流科研团队，科研总量不断扩大，科研经费人均位居全国高校第一。有1个国家实验室（航空科学与技术国家实验室（筹），9个国家级重点实验室（含4个国防科技重点实验室），4个国家级工程研究中心，3个北京市高精尖创新中心，70余个省部级重点实验室；有7个国家自然科学基金委创新研究群体，12个教育部创新团队，6个国防科技创新团队。学校突出自主创新，强化协同创新，积极搭建国家级创新平台，组建大团队，承担重大科研项目，重大科技成果不断涌现。建校60余年来，北航创造了40多项国内第一的科研成果，在尖端技术研究领域始终居于国内高校前列，研制发射（试飞）成功的多种型号飞行器填补了国内多项空白，如中国第一架轻型旅客机“北京一号”、亚洲第一枚探空火箭“北京二号”、中国第一架无人驾驶飞机“北京五号”、“蜜蜂”系列飞机、共轴式双旋翼无人驾驶直升机等。近年来，学校持续强化服务国家战略能力，在航空动力、关键原材料、核心元器件等瓶颈领域的研究取得突破，多项标志性成果直接应用于国防建设。学校团队参与论证并助力研制的国产大飞机C919成功首飞，研制成功我国首个新型临近空间飞艇。学校牵头设计研制的某型无人机定型并执行重要任务，在纪念抗战胜利70周年阅兵中位居无人机方队阵首。“十五”以来，北航共获国家三大科技奖励60余项，其中，近14年获得14项国家级科技奖励一等奖、4项国家自然科学二等奖，创造了一所大学连续获国家高等级科技奖的纪录，被社会誉为科技创新的“北航模式”。学校学术论文数量和质量协同增长，实现了在《Nature》《Science》等顶级期刊发表文章的突破。学校大力推进协同创新，与航空、航天、发动机、船舶等国家重点骨干企业开展战略合作。“先进航空发动机协同创新中心”获“2011计划”首批认定。联合中国工程院成立中国航空工程科技发展战略研究院，打造我国航空工程科技领域首个国家级智库。全面融入首都全国科技创新中心建设，打造“环北航知识经济圈”，北航大学科技园被认定为优秀国家大学科技园。\n\n学校面向全球，开放交融。通过实施“UPS国际化发展战略”，构建北航国际交流合作网络和平台，形成全方位、多层次、宽领域对外开放格局，有力提升了学校的国际影响力和竞争力。先后与国外近200所著名高等院校、一流研究机构和知名跨国公司建立了长期稳定的合作关系。加入了国际宇航联合会、“T.I.M.E.联盟”、“中俄综合性大学联盟”等国际联盟和学术组织。创设了“中德软件联合研究所”、“中英空间科学与技术联合实验室”等一批高端国际合作平台。与法国中央理工大学集团联合创办中法工程师学院，融合中法两国工程教育优势，被誉为中法高等教育合作典范。成立“国际通用工程学院”，努力建设全新高水平国际化通用工程教育示范学院。实施“全球校园计划”，建立高水平、特色鲜明的学生交换、联合培养、双学位、国际暑期学校项目，把北航校园延伸到世界的各个角落，让北航学子充分享受全球优质教育资源。在全球范围内吸引优秀学生，国际学生规模、生源质量、培养层次、教育水平进入全国高校前列。获批设立“联合国附属空间科学与技术教育亚太区域中心”、“亚太空间合作组织教育培训中国中心”和“北斗国际交流培训中心”。联合“一带一路”沿线国家顶尖高校共同建设“北斗丝路学院”，开启北斗导航服务于“一带一路”的新起点。师资队伍国际化程度不断加深，国际科研合作成果显现，建立了8个国家级“高等学校学科创新引智基地”，“月宫一号”入选“新中国65年十大引智成果”。\n\n学校文化育人，传承创新。学校实施“文化校园建设计划”和“大爱润航工程”，传承创新“空天报国、敢为人先”的北航精神和“开放包容、务实担当”的北航文化。注重以文化人以文育人，以航空航天博物馆、音乐厅、艺术馆、校史馆、艺文雅苑、致真创享空间、咏曼剧场、艺文空间等文化场馆为阵地，以感动北航、我爱我师、北航大讲堂、驻校艺术家/作家计划、大学生艺术团等为载体，开展形式多样、健康向上、格调高雅的校园文化活动，形成了传统承续品牌、典型引路品牌、文化景观品牌和人文滋养品牌，在全国高校起到探索和示范作用。原创音乐剧《罗阳》已成功演出30场，覆盖观众6万余人。教育部社科中心组编的《中国美育年鉴》，以近3万字的篇幅，系统介绍了北航的美育实践和文化育人典型案例。2011年以来，学校先后获全国高校文化建设优秀成果特等奖2项、一等奖2项，文化育人示范项目1项、特色展示项目2项，并获北京高校“十佳美丽校园”称号。\n\n云空高远启征程，星光璀璨映华章。迈入新时代、谱写新篇章，学校将以坚定的信心、豪迈的气魄、百倍的努力，加快建设扎根中国大地的世界一流大学，以“北航梦”助力实现中华民族伟大复兴的“中国梦”！','BUAA','华北地区',1,'北京航空学院','理工',1952,'国防科工委','是',985,211,1,7,36,3,27,2172,60,40,23,9,0,4715,'http://www.buaa.edu.cn/'),(20,104,'北京理工大学','北京',1,'北京理工大学1940年诞生于延安，是中国共产党创办的第一所理工科大学，是新中国成立以来国家历批次重点建设的高校，首批进入国家“211工程”和“985工程”，首批进入“世界一流大学”建设高校A类行列。毛泽东同志亲自题写校名，李富春、徐特立、李强等老一辈无产阶级革命家先后担任学校主要领导。学校现隶属于工业和信息化部，全体师生员工正对标国家“两个一百年”奋斗目标，全力朝着中国特色世界一流大学的建设目标迈进。\n\n栉风沐雨，北京理工大学伴随着国家教育事业的发展历久弥坚。1949年，学校迁入北京；1952年，学校更名为北京工业学院，成为新中国第一所国防工业院校；1988年，学校更名为北京理工大学。学校现设有18个专业学院以及徐特立学院、前沿交叉科学研究院、先进结构技术研究院、医工融合研究院等教学科研单位，工程、材料科学、化学、物理、数学、计算机科学、社会科学先后进入ESI国际学科排名前1%，其中工程学科进入前1‰。近年来，学校结合传统优势和长期发展需要，重点建设“5+3”个学科群——“高效毁伤及防护”学科群、“新材料科学与技术”学科群、“复杂系统感知与控制”学科群、“运载装备与制造”学科群、“信息科学与技术”学科群和“特色理科”学科群、“医工融合”学科群、“新领域与创新发展”学科群，形成“优势工科引领带动、特色理科融合推动、精品文科辅助联动、前沿交叉创新互动”的学科整体建设布局。学校现有教职工3300余名，汇聚了22名两院院士、39名“长江学者奖励计划”教授、38名“国家杰出青年科学基金”获得者、33名“万人计划”领军人才、4名国家级教学名师等高层次人才和6个国家级教学团队、4个国家自然科学基金创新研究群体、10个教育部“长江学者”创新团队；拥有2个国家级协同创新中心、9个国家级重点实验室及工程研究中心、6个国家级实验教学中心。\n\n立德树人，北京理工大学在全面提升人才培养质量中传承文明。学校致力于培养“胸怀壮志、明德精工、创新包容、时代担当”的领军领导人才。在建校78年以来的30余万毕业生中，有李鹏、曾庆红、叶选平等党和国家领导人，以及120余位省部级以上党政领导和将军；有国家最高科学技术奖获得者王小谟、我国第一艘核潜艇总设计师彭士禄等40余位院士；有各个行业、各个层面的大批领袖人才和优秀建设者，被誉为“红色国防工程师的摇篮”。学校累计获得国家级教学成果奖19项、国家级精品课程10门、国家精品视频公开课10门、精品资源共享课9门、精品在线开放课15门、国家虚拟仿真实验教学项目2门。学校现有全日制在校生2.7万余人；在教育部“全国大学生创新创业年会”上，连续九届共15件作品获“十佳作品”奖，在全国高校排名第一。2017年，学生在阿联酋首都阿布扎比举行的无人机及机器人领域国际顶级赛事中获冠军；2018年，学生荣获“创青春”全国大学生创业大赛专项赛金奖，包揽“互联网+”大学生创新创业大赛（北京赛区）冠亚军且获得五项“第一”；2019年，学生在加拿大蒙特利尔举行的ICRA 2019 RoboMaster人工智能国际挑战赛中获总冠军。近五年，学校本科生就业率一直保持在97%以上，研究生就业率达98%以上。面向国际，学校已经与6大洲74个国家和地区的270多所高校签订校级合作协议，与德国慕尼黑工业大学、亚琛工业大学、俄罗斯鲍曼国立技术大学、荷兰代尔夫特理工大学、日本东京工业大学、以色列理工大学、美国伊利诺理工大学等50多所合作院校设立学生交换项目，形成人才培养国际化的全球网络。\n\n矢志创新，北京理工大学在服务国家战略和奉献世界科技发展中展现担当。学校始终与党和国家同呼吸、共命运，坚持瞄准国家重大战略需求和世界科技发展前沿锐意进取。学校曾创造了新中国科技史上多个“第一”：第一台电视发射接收装置、第一枚二级固体高空探测火箭、第一辆轻型坦克、第一部低空测高雷达、第一台 20 公里远程照相机等，在精确打击、高效毁伤、机动突防、远程压制、军用信息系统与对抗等国防科技领域代表了国家水平，在智能仿生机器人、绿色能源、现代通信、工业过程控制等军民两用技术方面具有明显优势。在国庆60周年阅兵的30个方阵中，学校参与了22个方阵的装备设计和研制；在“9·3”大阅兵的27个地面装备方队和10个空中飞行方队中，学校参与了17个地面方队和8个空中方队的装备研制工作；在建军90周年朱日和阅兵的45个方（梯）队中，参与了8个作战群、29个方队的装备研制工作；参与数量和深度位居全国高校第一。在北京奥运会、上海世博会、广州亚运会、“神舟八号”与“天宫一号”交会对接等过程中，学校研发的多项技术均有优异表现。“十二五”期间，学校科技投入总量近100亿元，共获得国家科学技术奖23项，牵头获奖总数位列全国高校第15位，在国家科学技术奖专用项目获奖数目方面位列全国高校第3位，获得国防科学技术奖总数、授权国防专利和有效国防专利数均位列全国高校第1位。2016年，学校获得5项国家科学技术奖；2017年，学校获得9项国家科学技术奖；2018年，学校牵头获得6项国家科学技术奖。多项科技成果直接服务国计民生重大项目中。\n\n号角激昂，北京理工大学承载着光荣与梦想扬帆起航。“延安根、军工魂”，是镌刻进北京理工大学的红色基因；“德以明理、学以精工”的校训，“团结、勤奋、求实、创新”的校风，徐特立老院长倡导的“实事求是、不自以为是”的学风，共同铸就了学校独特的精神气质和文化内核。从1940年，在延安诞生为抗战建国服务，到21世纪的今天，为创新型国家建设培养高层次人才；从露天课堂、吃住窑洞，到现代化校区、国家重点实验室。面向未来，学校将继续坚持以习近平新时代中国特色社会主义思想为指导，紧紧围绕立德树人这一根本任务，坚持办学正确政治方向，努力建设高素质教师队伍，努力形成高水平人才培养体系，培养中国特色社会主义建设者和接班人！继续坚持“四个服务”，扎根中国大地建设世界一流大学，为实现“两个一百年”的宏伟目标和中华民族伟大复兴的中国梦提供有力支撑！','BIT','华北地区',1,'延安自然科学院、华北大学工学院、北京工业学院、北京理工大学','理工',1940,'国防科工委','是',985,211,1,3,18,22,39,3300,67,36,31,9,3194,4000,'http://www.bit.edu.cn/'),(162,106,'北方工业大学','北京',1,'毓秀工大\n\n北方工业大学创办于1946年，前身是国立北平高级工业职业学校，1985年更名为北方工业大学，1998年9月起以北京市管理为主。历经七十年风雨沧桑，逐步发展成为一所以工为主、文理兼融，具有学士、硕士、博士培养层次的多科性高等学府。学校位于北京城西，东接五环，北倚西山，占地面积近500亩，建筑面积40余万平方米。校园整洁雅致，景色宜人，绿化面积超过50%，是北京市十佳美丽校园之一。\n\n广聚英才 \n\n学校在校生15000多人，其中全日制本科生10000余人，研究生2000余人，成人高等教育学生3000余人，留学生600余人。教师千余名，拥有双聘院士1名，国家“千人计划”、“万人计划”等高层次人才7名，全国优秀教师1名，享受国务院政府特殊津贴专家26人，北京市有突出贡献专家2人。获得北京市级优秀教师及教育工作者表彰、教学名师称号近50余人次。获批北京市级高层次人才项目300余项。拥有北京市级学术创新团队、优秀教学团队等24个。\n\n因材施教\n\n“因材施教，素质为先，全面发展”是学校的育人特色。学校实行弹性学分制，建立了“分层教学、分流培养、分类成才”的培养体系，充分满足学生自主学习、全面发展、个性化成才的需要。学校确立了以提高学生的工程实践能力、工程设计能力、就业能力与科技创新创业能力培养为核心的实践教学理念。学校是国家级人才培养模式创新实验区和教育部“卓越工程师教育培养”院校。学校具有硕士研究生免试推荐资格和高水平棒垒球运动员招收资格。\n\n多科发展\n\n学校设有11个学院、20个系、48个本科专业，涵盖理、工、文、经、管、法、艺七大学科门类。学校建有1个服务国家特殊需求博士人才培养项目，20个一级学科硕士学位授权点（涵盖57个二级学科），15个专业学位硕士授权点。学校建有国家特色专业建设点3个，北京市一流专业1个，北京市特色专业建设点5个，北京市重点建设学科7个。学校建有1个国家级实验教学示范中心，1个国家级校外人才培养基地，6个北京市级实验教学示范中心，9个省部级重点实验室/工程研究中心。\n\n科研鼎新\n\n学校科研工作以增强自主创新能力为主线，主动服务国家和首都经济社会发展，充分重视科技创新，在高新技术开发与应用研究及其产业化等方面取得了显著成就，在一些领域形成了比较优势。近年来，科研成果获国家科技进步二等奖6项，鲁迅文学奖1项，省部级奖励50多项。学校是首都科技条件平台重要基地高校。学校坚持创新驱动，推动产学研结合，打造了创新创业基地，建立了“首都世界城市顺畅交通”、“军民融合智能装备”、“机器人”、“海绵城市与地下空间”、“新兴风险研究院”等多个协同创新中心或研究院。\n\n人文滋养\n\n学校文化积淀深厚，孕育了“敦品励学，才德并懋”的校训，树立了“严肃，严格，严谨”的校风，形成了以“专业精神，人文精神，进取精神”为主旨的学校精神。学校是北京市第一家“文明校园”，建有全国理工科院校第一所艺术馆，是国家和北京市大学生文化素质教育基地，连续5次获评“首都文明单位”。学校开展的学生文化、艺术、科学、体育四大节已赓续30年，成为校园文化品牌。学校开全国高校之先河创立了学校形象识别系统（UIS），校园文化全方位、多角度融入校园生活。\n\n臻育栋梁\n\n学校面向全国27个省（市、区）招生，招生地区均进入当地本科重点批次。学校扎实实施“四航”工程，深入推进学风建设“一院一特色一品牌”活动，激发学生成才动力。学生在全国和北京市各类重大竞赛中屡创佳绩，每年有千余人次获得各类奖励；大学英语四级首次通过率连续多年远超北京地区高校平均水平；本科毕业生就业率稳居北京高校平均水平之上；本科生国内外升学率超过30%。\n\n放眼寰宇\n\n学校大力推进开放办学战略，不断拓展国际交往的广度和深度。学校已同世界 35 个国家（地区）的80多所高校建立了校际合作关系。学校创办了国际学院，大力开展教学科研、师生交流、联合培养等多种形式的合作交流。现今，学校每年选派400余名学生赴美、欧、日、韩等国高校学习，吸引 了74个国家的1000余名外国留学生来校进行长短期学习深造。','NCUT','华北地区',1,'国立北平高级工业职业学校、北京钢铁工业学校、北京冶金机电专科学院、北京冶金机电学院','理工',1946,'北京市','是',0,0,0,0,12,1,0,725,49,20,0,0,1202,0,'http://www.ncut.edu.cn/'),(64,107,'北京化工大学','北京',1,'北京化工大学创办于1958年，原名北京化工学院，是新中国为“培养尖端科学技术所需求的高级化工人才”而创建的一所高水平大学。作为教育部直属的全国重点大学，国家“211工程”和“‘985’优势学科创新平台”重点建设院校，国家“一流学科”建设高校，北京化工大学肩负着高层次创新人才培养和基础性、前瞻性科学研究以及原创性高新技术开发的使命。\n\n北京化工大学经过60年的建设，已经发展成为理科基础坚实，工科实力雄厚，管理学、经济学、法学、文学、教育学、哲学、医学等学科富有特色的多科性重点大学，形成了从本科生教育到硕士研究生、博士研究生、博士后流动站以及留学生教育等多层次人才培养格局。目前，学校共设有15个学院，在校全日制本科生15331人，研究生7055人（其中博士953人），函授、夜大等继续教育学生3350人，学历留学生358人。\n\n学校学科实力稳步增强。化学学科进入ESI 排名的前1‰，材料科学、工程学以及生物学与生物化学3 个学科进入ESI 排名的前1%。化学工程与技术一级学科在全国第四轮学科评估中排名并列第三。2017 年我校“绿色化学化工及材料”学科群正式列入一流学科建设之列。学校现有8个一级学科博士点，21个一级学科硕士点，2个二级学科硕士点，6个硕士专业学位授权类别，8个工程硕士授权领域；1个一级学科国家重点学科（涵盖5个二级重点学科），2个二级学科国家重点学科，1个国家重点（培育）学科，3个一级学科北京市重点学科（涵盖14个二级重点学科），2个北京市交叉重点学科，3个二级学科北京市重点学科；50个本科专业，8个国家级特色专业建设点，14个北京市级特色专业建设点，13个国家综合改革试点专业，6个教育部“卓越工程师教育培养计划”试点专业，5个教育部工程教育专业认证专业；2个国家重点实验室，1个国家工程技术研究中心，1个国家工程实验室，14个省、部级重点实验室，15个省、部级工程技术研究中心，2个社科类省部级基地；7个博士后流动站。\n\n学校师资队伍实力雄厚。学校现有教职工2500余人，其中两院院士8人，外籍院士5人，教育部“长江学者奖励计划”特聘教授13人，国家杰出青年基金获得者23人，“万人计划”教学名师3人，“973”首席科学家8人次，享受政府特殊津贴128人（其中在岗29人），国家“万人计划”科技创新领军人才7人，国家“万人计划”青年拔尖人才4人，国家优秀青年科学基金获奖者13人，国家级高等学校教学名师5人,教育部“全国优秀教师”8人，全国杰出专业技术人才1人，中国青年女科学家奖获得者2人，国家百千万人才9人，中国青年科技奖获得者8人，科技北京百名领军人才获得者4人，教育部跨（新）世纪优秀人才69人。\n\n学校教学改革成果不断涌现。目前已经拥有2个国家级教学基地，1个国家大学生文化素质教育基地，2个国家级实验教学示范中心，3个国家级虚拟仿真教学实验中心，12个国家级大学生校外实践教学基地，1个国家级人才培养模式创新实验区，1个教育部全国高校实践育人创新创业基地，7个北京市级实验教学示范中心，5个北京市级校外人才培养基地，2个北京市级示范性校内创新实践基地，1个北京市级文化素质教育基地，1个北京市哲学社会科学研究基地。15 门国家级精品课程，15 门国家级精品资源共享课，2门国家级精品视频公开课，5 门省部级双语示范课程，37 门北京市级精品课程。教育部普通高等教育精品教材1 部，北京市高等教育精品教材10 部，“十二五”规划教材14 部；国家级教学成果奖一等奖1 项，国家级教学成果奖二等奖9 项，北京市教学成果一等奖37项，北京市教学成果二等奖47项；国家级教学团队5 个，北京市优秀教学团队10 个。\n\n学校科研工作发展迅速。承担重大项目、解决国家经济社会发展重大需求的能力进一步增强，科技经费不断创出历史新高，人均科研经费名列全国高校前茅。2001年以来，学校已有27个科研项目获得国家科技三大奖，拥有3个国家自然科学基金委员会创新研究群体，6个教育部长江学者创新团队，1个国家国防科工局第二批国防科技创新团队，5个国家引智基地，位居全国高校前列。是入选国防科工局和教育部“十三五”期间共建高校中仅有的3所“211工程”和“985优势学科创新平台”行业院校之一。一大批科研成果在Nature、Science 等国际顶尖学术期刊上发表，各类科研成果应用于国家尖端科技领域。2018年学校科技经费到款6.24亿元，获专利授权419项，鉴定成果9项。\n\n学校科技成果转化不断加强。学校坚持服务创新型国家建设，引领企业转型升级，促进经济社会发展，推进“三权”改革。建设有北京软物质科学与工程高精尖创新中心，北京化工大学科技园被认定为“国家大学科技园”“北京市中小企业创业基地”和第四批“国家技术转移示范机构”。学校的科技产业拥有约30个与教学、科研紧密结合的科技产业实体，依托学校科技和人才优势，以科技成果产业化为经营宗旨，形成了具有北化特色的高科技产业，在生物化工、日用化工、精细化工、化工新材料等领域已形成系列技术和多种产品。近年来，结合校内优势学科、地方经济发展战略和企业科技创新能力提升的需求，学校还重点在珠三角、长三角和环渤海地区，相继成立先进材料、工业生物技术等5个校地合作地方研究院，在威海、安庆、沧州、嘉兴、济源等地成立了6个技术转移中心，以及200余个校企联合研发机构。\n\n学校国际交流合作日益广泛。我校实施“全球化”战略，不断拓展全球战略合作伙伴关系，与美、英、法、德、澳、韩、日等国131所大学和机构建立了合作伙伴关系。建有1个北京市高精尖创新中心、3个北京市国际合作联合实验室(研究中心)，3个北京市国际科技合作基地，2个校级国际联合研究中心。着力推进“一带一路”建设，举办了北京化工大学高校技术转移全球校长论坛，与“一带一路”沿线国家37所高校和机构建立了合作伙伴关系，成立了北京化工大学“一带一路”学院、中国石油和化工行业国际产能合作企业联盟“一带一路”国际人才培养中心。坚持高端引领和重点推进相结合，积极推进国际教育科技合作，统筹规划学校外专引智项目，重点支撑我校“绿色化学化工及材料”一流学科建设。坚持举办大型国际会议和学术论坛，承办对台交流项目，邀请诺贝尔奖获得者等知名教授走进我校“宏德讲坛”，与新校区本科生深入交流。持续推动我校师资国际化水平提升。2018年，因公临时出访团组数为138个，共计218人次，涉及出访国家41个,赴港、澳、台湾地区团组数为6个，共计12人次；接待境外短期专家621人次, 在校任职的长期外籍专家共24人，其中包含外籍教师13人，外籍博士后11人；聘请外国专家学者来华短期工作达26人。积极推进学生国际化交流。学校已与20个国家42所高校和机构签署64项学生赴海外学习项目合作协议，2018年学生海外学习项目共派出418人。举办了首届北京化工大学海外学习国际教育展，来自海外15所知名大学的21名代表参展，400多名学生参加了教育展。同时，我校今年启动“北化-世界百强高校本硕博精英计划”，选拔6名本科生和7名研究生赴世界百强高校或世界高水平大学进行暑期学习、短期研修以及攻读博士学位。进一步促进中外人文交流，我校今年先后与美国，匈牙利等高校完成人文交流项目，即“中美人文交流营Project-GO”“北化-伍斯特理工大学中美科学营”“北化-匈牙利米什科尔茨大学孔子学院夏令营”等，40多名国外大学生来华进行文化、科技交流活动。\n\n学校高度重视学生就业工作。举全校之力推进就业工作，建立了完善的就业指导和服务体系，重视对学生的全过程就业指导，积极发挥学校在毕业生与用人单位供需见面、双向选择过程中的主导作用，学校有25位入选教育部“全国万名优秀创新创业导师人才库”的老师悉心指导学生自主创业，多年来毕业生一次性就业率一直名列全国高校前茅。2018年本科毕业生就业率为98.71%，毕业研究生就业率为97.73%。\n\n今天的北京化工大学，坚持以习近平新时代中国特色社会主义思想为指导，坚持以立德树人为根本任务，继续秉承“宏德博学、化育天工”的校训和“团结奉献、艰苦奋斗、务实力行、博学创新”的化大精神，以“双一流”建设为契机，加快建设成为特色鲜明、在国际上有影响的高水平研究型大学，为实现“两个一百年”的奋斗目标，实现中华民族伟大复兴的中国梦贡献北化力量。','BUCT','华北地区',1,'北京化工学院','理工',1958,'教育部','是',0,211,0,1,16,8,13,2500,55,26,8,2,129,0,'http://www.buct.edu.cn/'),(55,108,'北京邮电大学','北京',1,'六十余载风雨砥砺，六十余载春华秋实。明光之北、蓟门之南，古老的城墙，见证了永不消逝的电波；鸿雁翱翔、银杏巍巍，坚实的土地，承载了信息黄埔的传奇。\n\n北京邮电大学是教育部直属、工业和信息化部共建、首批进行“211工程”建设的全国重点大学，是“985优势学科创新平台”项目重点建设高校，是一所以信息科技为特色、工学门类为主体、工管文理协调发展的多科性、研究型大学，是我国信息科技人才的重要培养基地。2017年，“信息网络科学与技术学科群”和“计算机科学与网络安全学科群”两个学科群进入一流学科建设行列。\n\n学校坚持以习近平新时代中国特色社会主义思想为指导，坚持立德树人根本任务，扎根中国大地办好中国特色社会主义大学。学校始终肩负“传邮万里国脉所系”的家国情怀。自1955年建校以来，经过60多年的建设与发展，学校全日制教育已经形成了信息背景浓郁、专业特色鲜明、学科优势突出的办学格局。学校现设有信息与通信工程学院、电子工程学院、计算机学院、自动化学院、软件学院、数字媒体与设计艺术学院、现代邮政学院、网络空间安全学院、光电信息学院、理学院、经济管理学院、人文学院、马克思主义学院、国际学院、网络教育学院、继续教育学院、民族教育学院、体育部等18个教学单位，以及网络技术、信息光子学与光通信、感知技术与产业3个研究院，可信网络通信2011协同创新中心，并设有研究生院。目前，学科专业已经涵盖理学、工学、文学、法学、经济学、管理学、教育学、哲学、艺术学等9个学科门类，涉及22个一级学科。学校信息与通信工程、计算机科学与技术以及电子科学与技术三个一级学科在教育部第四轮学科评估中被评为A类学科，其中信息与通信工程取得了A+的优异成绩。\n\n学校现有西土城路校区、沙河校区、宏福校区和小西天校区，在江苏无锡和广东深圳分别设有研究院。全日制本、硕、博学生及留学生近23000名，正式注册的非全日制学生近55000名。\n\n近年来，北京邮电大学聚焦国家重大战略需求，以网络强国战略目标为导向，遵循高等教育的办学规律，按照“综改拉动、科学发展”的战略思路，牢固树立事业型思维，启动学校教育事业综合改革。在全校党员、全体师生的共同努力下，学校的改革与发展进入了一个新的阶段。\n\n——牢固确立人才培养是高等学校的根本任务的思想，大力推进实施“质量工程”，积极推进本科教育教学改革和研究生培养机制创新，实现了质量与规模的协调发展。2008年3月，教育部正式公布我校以“优秀”成绩通过“本科教学工作水平评估”，标志着我校人才培养质量踏上了新的台阶。近年，我校在全国百篇优秀博士学位论文、国家精品课程、北京市精品课程、国家级双语教学示范课程、普通高等教育精品教材、国家级教学团队、北京市优秀教学团队、国家级教育成果奖、北京市教育成果奖等各级各类评选工作中均取得了良好的成绩。长期以来，我校学生就业率一直保持在全国高校领先水平。在以英语、电子、数学和物理竞赛为代表的国内外大学生重大赛事中成绩优异，位居全国重点高校前列。连续九年创办大学生创新创业成果展示交流会暨创新创业论坛，创办研究生创新创业成果展，学生创新创业实践活动取得突出成绩。\n\n——始终坚持办学以教师为根本的指导思想，积极推进“人才工程”，人才队伍不断壮大，师资结构不断优化。目前，我校拥有着一支以中国科学院院士、中国工程院院士、外籍院士、973项目首席科学家、国家自然科学基金委创新研究群体、“长江学者和创新团队发展计划”创新团队、“长江学者”、“千人计划”、“新世纪百千万人才工程”国家级人选、国家级突出贡献专家、“国家杰出青年科学基金”获得者、教育部“跨世纪优秀人才计划”获得者、“新世纪优秀人才支持计划”获得者、北京市优秀教学团队、北京市科技新星、省部级“青年学科带头人”、省部级“优秀青年骨干教师”、政府特殊津贴专家、国家级教学名师等为骨干的实力雄厚的师资队伍。具有博士、硕士学位的教师占专任教师总数的88%，外籍教师近百人。\n\n——以“211工程”建设和“985优势学科创新平台”建设为重点，不断加强学科建设，学科布局进一步优化。目前，学校具有博士学位授权一级学科点10个，硕士学位授权一级学科点22个（含一级学科博士点10个），有7类专业硕士学位授权点，有43个本科专业，建立博士后科研流动站6个。学科涵盖了理、工、文、法、哲、经济、管理、教育、艺术等9个学科门类，初步形成了信息学科优势突出、工管文理相互支撑的多科性学科架构。在重点学科建设方面，我校现有一级学科国家级重点学科2个、北京市重点学科7个、部级重点学科8个。\n\n——加强基地建设，科研工作良性发展。我校建有以国家重点实验室、国家工程实验室、教育部重点实验室、教育部国防重点实验室、教育部工程研究中心、教育部学科创新引智基地为代表的若干高水平的科研基地。学校现有国家重点实验室2个、国家工程实验室5个（其中2个为牵头、3个为合作）、教育部工程研究中心2个、教育部“111创新引智基地”5个、北京实验室1个、各类部级重点实验室9个。以基地建设和团队建设为基础，我校科研工作在质和量两个方面呈现出快速增长态势。近年来共获得国家科技发明奖、国家科技进步奖、省部级科研奖励数十项。同时，我校建有国家级大学科技园，为推进科技成果转化奠定了良好基础。\n\n——加强国际合作与交流，国际化办学水平进一步提高。2004年，经教育部批准，我校开始实施与伦敦大学玛丽女王学院的学士学位联合培养项目，成立了国际学院。近年来，国际学院以其严格的管理和高水平的人才培养质量在全社会引起广泛好评。2011年，我校与斐济南太平洋大学合作建设的孔子学院。2017年，库克群岛孔子课堂荣获“先进孔子课堂”称号。我校与150余所国（境）外知名大学、科研机构、知名企业建立了深厚的合作与交流关系。\n\n我校建设了“通信与网络核心技术创新引智基地”、“高等智能与网络服务创新引智基地”、“无线网络融合创新引智基地”、“信息网络体系构建与融合学科创新引智基地”、“信息网络体系构建与融合创新学科创新引智基地”等五个国家“111”创新引智基地，引进了以诺贝尔物理学奖获得者若列斯.阿尔费罗夫为代表的世界一流学术大师和科研学者，形成了实力强大的国际化研究阵容。积极主办高层次、高规格国际会议，搭建学校与国际高水平大学的学术交流平台，增进了学校与国际高水平大学的学术交流与对话，进一步提升了学校的国际影响力。\n\n北京邮电大学在“团结勤奋严谨创新”的校风、“厚德博学敬业乐群”的校训和“崇尚奉献追求卓越”的北邮精神的引领下，正朝着建成特色鲜明、优势突出、世界著名的高水平研究型大学这一宏伟目标而阔步前进。','BUPT','华北地区',1,'北京邮电学院','理工',1955,'教育部','是',0,211,0,2,17,9,4,1435,43,22,10,1,276,0,'http://www.bupt.edu.cn/'),(261,109,'北京印刷学院','北京',1,'北京印刷学院隶属于北京市，是由北京市人民政府和原国家新闻出版总署共建的全日制普通高等院校。学校的前身是1958年文化部建立的文化学院；1961年文化学院撤销，其印刷工艺系并入中央工艺美术学院；1978年，经国务院批准，在中央工艺美术学院印刷工艺系基础上组建北京印刷学院，由原国家出版事业管理局管理；2000年，学校划归北京市。经过60年的建设和发展，已经成为在印刷与包装、出版与传播、设计与艺术三个领域具有明显优势和特色，工、文、艺、管协调发展，国内唯一专门为出版传媒全产业链培养人才的多科性高等院校。\n\n学校现有13个二级学院（教学部），分别是印刷与包装工程学院、新闻出版学院、设计艺术学院、新媒体学院、机电工程学院、信息工程学院、经济管理学院、马克思主义学院、职业与继续教育学院、国际教育学院、基础部、外语部、体育部。学校共有26个本科专业招生，其中有2个国家级特色专业建设点、4个北京市特色专业建设点、印刷工程专业被评为北京市首批一流本科建设专业；有17部国家级精品和规划教材；拥有国家级实验教学示范中心、国家级大学生校外实践教育基地等。\n\n学校现有教职工806人，其中专任教师507人，高级职称教师占专任教师比例57.14%。形成了以国家“千人计划”特聘教授、北京“海聚工程”特聘教授、国务院政府特殊津贴专家、北京市人才强校计划高层次人才等为代表的高层次人才团队；以全国和北京市新闻出版行业领军人才、中国出版政府奖、毕昇印刷杰出成就奖获得者等为代表的行业领军人才团队；以北京市科技新星、青年拔尖人才等为代表的青年人才团队；以全国优秀教师、北京市优秀教师等为代表的优秀教学人才团队。\n\n学校坚持特色发展，以特色学科建设提升核心竞争力。学校现有12个一级学科硕士学位授权点，5个专业学位授权点，4个北京市重点建设学科，2个北京高校高精尖学科。学校有国家绿色印刷包装产业协同创新基地、北京市印刷电子工程技术研究中心、国家数字复合出版系统工程实验室、数字媒体艺术北京市重点实验室、新闻出版领域关键技术应用研究与服务综合实验室、高端印刷装备信号与信息处理北京市重点实验室、北京文化产业与出版传媒研究基地等11个省部级重点实验室。2018年，学校获批北京市新增博士学位授予立项建设单位。2019年，学校设计学、新闻传播学两个一级学科入选北京高校高精尖学科建设名单。\n\n学校大力推进科研工作，实施科技创新驱动工程。近5年，主持国家级及省部级科研项目169项，委办局委托项目284项，横向科研项目457项，科研经费累计超过3亿元，发表SCI、EI、ISTP、CSSCI等检索论文1200余篇，出版著作、教材500余部，获得授权专利600余件，获省部级科研成果奖励18项。学校2013年获批博士后科研工作站，具有独立招收和培养博士后资格。\n\n学校强化产学研结合，建立了覆盖印刷出版产业链的科研平台。围绕“创意设计 艺工融合”的办学特色，形成了以北京绿色印刷包装产业技术研究院的科研成果转化平台为基础，大学科技园、文化创意产业园和大学生创新创业园相互支撑、融合发展的“一院三园”双创发展模式，打造“北印科创”、“北印文创”双亮点，建设产学研用一体化产业综合体。学校主动适应国家战略性新兴产业发展和北京“四个中心”建设，密切跟踪印刷出版产业转型升级和融合发展，开展系列科技攻关和咨询决策研究，取得一批高水平研究成果。\n\n学校坚持开放办学，大力加强对外交流合作。学校先后与美国、俄罗斯等30 多个国家和地区的50余所大学和科研机构建立了校际合作与交流关系，签订了本科、研究生以及本科与研究生联合培养、共同授予学位的协议。近5年，560余名师生赴国（境）外合作院校开展学习交流。近年来，学校留学生教育取得突破，累计共招收来自40多个国家500余名国际学生，为我国印刷包装企业走向“一带一路”沿线地区和国家提供人才支撑。2017年学校入选北京市首批“一带一路”国家人才培养基地，并成为基地秘书处单位。\n\n办学60年来，学校累计培养毕业生5万余名，得到社会广泛认可。学校是原国家新闻出版总署认定的国家出版印刷高级人才培养基地、国家绿色印刷包装产业协同创新基地、北京市认定的北京市文化创意产业人才培养基地；高等学校出版专业教学指导委员会常务副主任委员单位和秘书处挂靠单位；中国印刷高等教育联盟、全国数字出版专业教育联盟、全国印刷电子产业技术创新联盟、全国传统印刷产业技术创新联盟、京津冀设计产业联盟等组织的发起单位和理事长单位。\n\n进入新时代，学校坚持不忘初心、牢记使命，继往开来、开拓创新，为建设特色鲜明、高水平出版传媒大学而不懈奋斗。',NULL,'华北地区',1,'文化学院','理工',1958,'北京市','否',0,0,0,0,13,0,0,806,27,7,0,0,995,0,'http://www.bigc.edu.cn/'),(118,110,'北京建筑大学','北京',1,'北京建筑大学是北京市和住房城乡建设部共建高校、教育部“卓越工程师教育培养计划”试点高校和北京市党的建设和思想政治工作先进高校，是一所具有鲜明建筑特色、以工为主的多科性大学，是“北京城市规划、建设、管理的人才培养基地和科技服务基地”、“北京应对气候变化研究和人才培养基地”和“国家建筑遗产保护研究和人才培养基地”，是北京地区唯一一所建筑类高等学校。\n\n学校源于1907年清政府成立的京师初等工业学堂，1933年更名为北平市市立高级职业学校，1936年增设土木工程科，后历经北京市市立工业学校、北京市建筑专科学校、北京市土木建筑工程学校、北京建筑工程学校、北京建筑工程学院等发展阶段。2013年4月经教育部批准更名为北京建筑大学。学校1977年恢复本科招生，1982年被确定为国家首批学士学位授予高校，1986年获准为硕士学位授予单位。2011年被确定为教育部“卓越工程师教育培养计划”试点高校。2012年“建筑遗产保护理论与技术”获批服务国家特殊需求博士人才培养项目，成为博士人才培养单位。2014年获批设立“建筑学”博士后科研流动站。2015年10月北京市人民政府和住房城乡建设部签署共建协议，学校正式进入省部共建高校行列。2016年5月，学校“未来城市设计高精尖创新中心”获批“北京高等学校高精尖创新中心”。 2017年获批推荐优秀应届本科毕业生免试攻读研究生资格。2018年5月，获批博士学位授予单位，建筑学、土木工程获批一级学科博士学位授权点。\n\n学校有西城和大兴两个校区。西城校区占地12.3万平方米，大兴校区占地50.1万平方米；校舍建筑总面积48.8万平方米。目前，学校正按照“大兴校区建成高质量本科人才培养基地，西城校区建成高水平研究生培养、科技协同创新及成果转化基地”的“两高”布局目标加快推进两校区建设。学校图书馆纸质藏书152.9万册、电子图书185万册，大型电子文献数据库52个，与住建部共建中国建筑图书馆，是全国建筑类图书种类最为齐全的高校。\n\n学校学科专业特色鲜明，人才培养体系完备。学校现有10个学院和1个基础教学单位，另设有继续教育学院、国际教育学院和创新创业教育学院。现有35个本科专业，其中国家级特色专业3个——建筑学、土木工程、建筑环境与能源应用工程；北京市特色专业7个——建筑学、土木工程、建筑环境与设备工程、给水排水工程、工程管理、测绘工程、自动化。学校设有研究生院，学科设置以工学为主，兼有管理学、理学和艺术学。有一级学科博士学位授权点2个（建筑学、土木工程），服务国家特殊需求博士人才培养项目1个，博士后科研流动站1个，一级学科硕士学位授权点14个，有交叉学科硕士学位授权点1个，专业学位类别硕士学位授权点7个。拥有一级学科北京市重点学科3个（建筑学、土木工程、测绘科学与技术），一级学科北京市重点建设学科2个（管理科学与工程、城乡规划学）。我校全部工学学科参加了教育部第四轮学科评估并获档次评价，其中建筑学、土木工程2个学科评估结果为B。\n\n学校名师荟萃、师资队伍实力雄厚。学校现有教职工1019人，其中专任教师644名。专任教师中具有硕士学位的教师194人，具有博士学位的教师401人，具有高级专业技术职务的教师387人，教授106人，博士生导师24人。拥有长江学者1人，国家杰出青年科学基金获得者1人，国家“千人计划”入选者1人，国家级教学名师1人，全国优秀教师1人，百千万人才工程国家级人选3人，中科院“百人计划”1人，北京学者2人，北京市高创计划杰出人才获得者1人，教育部新世纪优秀人才计划入选者1人，科技部中青年科技领军人才1人，科技北京百名领军人才1人，北京市留学人员创新创业特别贡献奖获得者1人、百千万人才工程市级人才8人，北京市长城学者8人，北京市科技新星8人，北京市委组织部青年拔尖个人4人，享受政府特殊津贴专家9人，教育部、住建部专业指导和评估委员会委员9人，省部级优秀教师、教学名师、高层次人才、学术创新人才、青年拔尖人才等80余人。拥有教育部创新团队1个，北京市委组织部青年拔尖团队、北京市教委学术创新团队、优秀教学团队、管理创新团队等各类团队28个。\n\n学校坚持质量立校，教育教学成果丰硕。学校2014年获得国家教学成果一等奖，近年来获得省部级教学成果奖29 项，其中一等奖13 项。学校是首批国家级工程实践教育中心建设高校，拥有国家级实验教学示范中心、国家级土建类人才培养模式创新试验区、国家级虚拟仿真实验教学中心、国家级校外人才培养基地等10个国家级本科教学工程项目。另有4个北京市实验教学示范中心、7个市级校外人才培养基地、2个市级校内创新实践基地、1 个北京高等学校市级人才培养模式创新试验区、1 个北京市“一带一路”国家人才培养基地。95个校内外实践教学基地。近五年来，学生在全国和首都高校“挑战杯”、“创青春”等科技文化活动中获得国家级奖项14 项、省部级以上奖励743项。\n\n学校坚持立德树人，培育精英良才。现有各类在校生11842人，其中全日制本科生7717人，硕士研究生2171人，博士研究生20人，成人教育学生1842人，留学生92人，已形成从本科生、硕士生到博士生，从全日制到成人教育、留学生教育全方位、多层次的办学格局和教育体系。多年来，学校遵循“立德树人、开放创新”的办学理念和“团结、勤奋、求实、创新”的校风，秉承“实事求是、精益求精”的校训和“爱国奉献 坚毅笃行 诚信朴实 敢为人先”的北建大精神，为国家培养了6万多名优秀毕业生，他们参与了北京60年来重大城市建设工程，成为国家和首都城市建设系统的骨干力量。校友中涌现出了被称为“当代鲁班”的李瑞环，核工业基地建设的奠基人赵宏、中国工程院院士张在明、全国工程勘察设计大师刘桂生、沈小克、张宇、罗玲、胡越、包琦玮、高士国、杨伯钢，在国际上有重要影响的中国建筑师马岩松等一大批优秀人才。学校毕业生全员就业率多年来一直保持在95%以上，2014年进入“全国高校就业50强”行列。\n\n学校坚持科技兴校，科学研究硕果累累。学校始终坚持科技兴校，不断强化面向需求办学的特色，形成了建筑遗产保护、城乡规划与建筑设计、城市交通基础设施及地下工程、海绵城市建设、现代城市测绘、固体废弃物资源化技术、绿色建筑与节能技术为代表的若干在全国具有比较优势的特色学科领域、科研方向和创新团队。学校现有北京未来城市设计高精尖创新中心以及城市雨水系统与水环境教育部重点实验室、代表性建筑与古建筑数据库教育部工程研究中心、现代城市测绘国家测绘地理信息局重点实验室、北京市应对气候变化研究及人才培养基地等26个省部级重点实验室、工程研究中心和研究基地。近年来，我校教师荣获省部级以上科技成果奖励70余项，其中国家技术发明奖、国家科技进步奖共13项，2010、2011、2012连续三年以第一主持单位获得国家科技进步二等奖,2014年以第一主持单位获得国家技术发明奖。近五年，新增各类科技项目2000余项，其中国家重大专项、国家重点研发计划项目、国家自然科学基金项目、国家社科基金项目、教育部人文社科项目、北京市自然科学基金项目、北京市科技计划项目、国际合作项目等省部级及以上科研项目490余项；科技服务经费连年超过2亿元。学校重视科技成果转化，不断提高服务社会的能力和水平，建设具有建筑行业特色的大学科技园，是中关村国家自主创新示范区股权激励改革工作首批试点的2所高校之一。\n\n学校面向国际，办学形式多样。学校始终坚持开放办学战略，广泛开展国际教育交流与合作。目前已与美国、法国、英国、德国等28个国家和地区的63所大学建立了校际交流与合作关系。　\n\n学校全面加强党的建设，党建和思想政治工作成效显著。近年来，获评北京市“党的建设和思想政治工作先进高等学校”、“首都高校平安校园示范校”、“全国厂务公开民主管理先进单位”、“北京市厂务公开民主管理示范单位”、“首都文明单位标兵”、“北京市文明校园”等荣誉称号。　\n\n站在新的历史起点上，学校将以党的十九大精神为指引，深入学习贯彻习近平新时代中国特色社会主义思想，按照“提质、转型、升级”的工作方针，围绕立德树人的根本任务，全面推进内涵建设，全面深化综合改革，全面实施依法治校，全面加强党的建设，持续增强学校的办学实力、核心竞争力和社会影响力，以首善标准推动学校各项事业上层次、上水平，向着把学校建设成为国内一流、国际知名、具有鲜明建筑特色的高水平、开放式、创新型大学的宏伟目标奋进。','BUCEA','华北地区',1,'京师初等工业学堂、北平市市立高级职业学校、北京市市立工业学校、北京市建筑专科学校、北京市土木建筑工程学校、北京建筑工程学校、北京建筑工程学院','理工',1936,'北京市','否',0,0,0,0,10,0,1,660,44,14,2,0,1810,0,'http://www.bucea.edu.cn/'),(30,111,'中国农业大学','北京',1,'中国农业大学是国家“211工程”和“985工程”重点建设的教育部直属高校。\n\n中国农业大学是我国现代农业高等教育的起源地，其历史 起自于1905年成立的京师大学堂农科大学。1949年9月，由北 京大学农学院、清华大学农学院和华北大学农学院合并为北京 农业大学。1952年10月，北京农业大学农业机械系与中央农业部机耕化农业专科学校、华北农业机械专科学校、平原省农学院合并成立北京 农业机械化学院，1985年10月更名为北京农业工程大学。1995 年9月，北京农业大学与北京农业工程大学合并成立中国农业大 学，江泽民同志亲自题写校名。长期以来，学校的建设与发展得 到了国家的高度重视。北京农业大学于1954年被国务院列为全国6所重点院校之一，于1984年被列为全国重点建设的10所高等院校之一。北京农业机械化学院于1960年被列为全国重点大学之一。学校于1995年进入首批“211工程”建设行列，于2004年被确定为“985工程”重点建设的高水平研究型大学。2003年5月4日，国务院总理温家宝视察中国农业大学，发表了重要讲话。2005年10月13日，全国人大常委会吴邦国委员长视察中国农业大学，祝贺农大建校百年，鼓励学校再创辉煌。2009 年5月2日，中共中央总书记、国家主席胡锦涛视察中国农业大学，与师生代表亲切座谈，对青年学生提出了殷切期望，对学校提出了“加快建设世界一流农业大学步伐”的要求。2010年5月2日，胡锦涛总书记给中国农业大学师生回信，鼓励全校师生为发展现代农业作出更大贡献。2012年9月15日，习近平等党和国家领导同志来校参加全国科普日北京主场活动，在视察学校时要求学校教书育人和党的建设“两手抓两促进两不耽误”，“真正建成有中国特色的、具有农业特色的一流大学”，为学校发展进一步指明了方向，极大激发了全校师生的责任感和使命感。\n\n历经百年的世纪风雨，中国农业大学已经发展成为一所以农学、生命科学、农业工程和食品科学为特色和优势的研究型大学，形成了特色鲜明、优势互补的农业与生命科学、资源与环境科学、信息与计算机科学、农业工程与自动化科学、经济管理与社会科学等学科群。学校共设有16个学院，涉及农学、工学、理学、经济学、管理学、法学、文学、医学、哲学等9大学科门类；设有研究生院、继续教育学院和体育与艺术教学部。在第三轮一级学科水平评估中，6个一级学科蝉联全国第一，排名第一的学科数量居全国高校第四位；拥有3个国家级重点实验室，1个国家工程实验室，2个国家工程技术研究中心，91个省部级重点实验室（中心、基地）；1个国家野外科学观测研究站，6个部级野外科学观测实验站。\n\n中国农业大学拥有一支实力雄厚、结构合理的师资队伍。现有专任教师1654人，其中教授（含研究员）588人、副教授（含副研究员）852人。研究生导师1398人，其中博士生导师810人。学校有中国科学院院士5人、中国工程院院士7人，“ 长江学者奖励计划”特聘教授26人，“国家杰出青年科学基金”获得者42人，国家“973计划”项目首席科学家15人，新世纪“百千万人才工程”国家级 人选26人，教育部“新世纪优秀人才支持计划”人选143人，享受政府特殊津贴专家76人。聘请了包括诺贝尔生理或医学奖获得者、“DNA之父”James Watson 和我国杰出的农业科学家、杂交水稻之父袁隆平院士在内的一批国内外著名学者担任名誉教授。\n\n中国农业大学具备培养学士、硕士、博士的完整教育体系。学校拥有15个博士后流动站，20个博士学位授权一级学科，95 个博士学位授权点，29个硕士学位授权一级学科，144个硕士学位授权点，9个专业 学位类型，33个专业学位领域；65个本科专业，其中生物学、化学两个学科被列入“国家理科基础学科研究和教育人才培养基地”。全日制本科生11395名，全日制研究生7661名，其中全日制硕士研究生4478名，全日制博士研究生3183名；在站博士后研究人员215名。\n\n中国农业大学坚持德育为先、能力为重、全面发展的人才培养理念，培养具有爱国情怀和社会责任感、熟练掌握专业知识与技能、综合能力强的拔尖创新人才。学校拥有1名国家高层次人才特殊支持计划（“万人计划”）教学名师，2名国家级教学名师和32名北京市教学名师，5个国家级优秀教学团队和10个北京市优秀教学团队，14个国家级特色专业和2个国家人才培养模式创新实验区。学校不断深化教学内容和教学方法改革，建成了23门国家精品课程、15门精品资源共享课、2门精品视频公开课和25门北京市精品课程，6门“中国大学慕课”课程；122项教材入选国家级“十一五”规划教材；17项教材入选国家级“十二五”规划教材；进一步加强实验室、实习基地建设，拥有3个国家实验教学示范中心、3个国家级虚拟仿真实验教学中心和6个北京市实验教学示范中心；通过强化实践创新能力培养，构建了创新人才培养体系，为开展高水平的研究性教学奠定了良好的基础，大学生科技创新活动活跃。2008年以来，我校本科生在各类创新项目支持下，共发表高水平学术论文342篇，其中Science 1篇，SCI 268篇，EI 67篇，ISTP 7篇；发明专利184项，实用新型专利33项，外观设计专利10项；学生获省部级及以上学科竞赛奖励1464项。深入推进研究生教育教学改革，全面实施博士生“申请考核制”；进一步完善研究生学位论文的质量保障体系，促进学位论文的质量不断提高。学校总计有23篇博士学位论文入选“全国优秀博士学位论文”，在全国高校中位居前列。学校已培养了13万余名合格的毕业生，取得了一大批教育教学成果，以优异的成绩通过了教育部本科教学工作水平评估，办学质量和办学水平得到了社会各界的广泛认可。2016年，学校本科毕业生2801人，硕士毕业生1688人，博士毕业生632人。\n\n中国农业大学围绕人类的营养与健康，以国家农业科技重大需求和国际学术前沿为导向，开展高水平科学研究、社会服务和文化传承与创新。在生物与农业高技术领域的研究居国内领先水平。主持国家重点基础研究计划（“973计划”）项目22项（含重大科学问题导向项目和重大科学研究计划项目各1项），有国家现代农业产业技术体系首席科学家6名。2001年以来，获国家级科技奖励78项，获省部级科技奖励455项，获得授权专利3420件。石元春院士主持完成的“黄淮海平原中低产地区综合治理的研究与开发”研究成果曾获国家科技进步特等奖。先后有23篇高水平研究论文在国际著名杂志《SCIENCE》、《CELL》、《NATURE》和《NATURE GENETICS》上发表，奠定了我校在基础研究方面的领先地位。我校2016年度被 SCI、EI和SSCI收录论文数量达到2310篇。在ESI学科评价指标中，我校的农业科学、植物与动物科学、环境/生态学、生物学与生物化学、化学、微生物学、工程学\\分子生物学与遗传学和社会科学总论9个学科和所有学科论文总引用量进入了全球前1％，其中农业科学、植物与动物科学两个学科的总引用量保持在全球前1‰。学校加强科技成果转化与推广服务，涉及食品科学与营养工程、动物医学、动物科技、资源与环境、农学与生物技术、工学、信息与电气工程等领域的实用技术成果推广覆盖全国。科技服务重点围绕国家重大需求、区域发展和产业发展需求，立足京津冀，以顶层设计、统筹布局、引领带动的工作思路，针对国家、地方、企业等重大需求，结合我校科研、人才培养和服务社会的需求，在全国相关区域建设综合实验站（教授工作站）53个，为区域经济和社会发展提供规划设计、技术指导社会管理等综合服务；进一步开拓成果转化和社会服务领域，拓展转化渠道，扩大服务地区，努力探索科技服务地方的新机制和新模式，已与十余个省、自治区、直辖市和七十余个地市签署了科技合作协议，为地方经济的发展提供技术支撑；我校每年与地方签署科技研发及成果转化方面的合同500余项，加快了科技成果向企业的转移；依托现代农业产业技术体系专家及其团队成员解决产业发展中的技术难题，依托实验站、教授工作站、科技小院等围绕地方和农民产业发展需求，面向基层农业干部、农业企业经理人、基层技术人员、农民等开展5万余人次的技能培训，为我校科技成果的推广示范和转化起到了突出的作用。\n\n中国农业大学广泛开展国际交流与合作，努力扩大开放，加速国际化进程。学校与美国、加拿大、德国、英国、荷兰、挪威、丹麦、日本、韩国、澳大利亚、俄罗斯、以色列、坦桑尼亚、几内亚等41个国家和地区的209所大学、研究单位建立了友好合作关系。校内设有与美国普度大学、荷兰瓦赫宁根大学等合作的11个联合研究中心，还设有国际学院、中德综合农业发展中心、中以国际农业培训中心等国际教育、科研与培训机构。学校十分重视培养本科生的国际视野和创新精神，与海外大学广泛开展交换生项目、短期交流项目、交换实习项目等各类本科生交流项目。学校与美国的康奈尔大学、科罗拉多州立大学、普度大学、伊利诺依大学、奥本大学，加拿大的滑铁卢大学、阿尔伯特大学等国外著名高校在互认学分的前提下开展各种形式的本科生联合培养项目。\n\n中国农业大学十分重视学生综合素质的培养，积极开展第二课堂教育活动，校园文化生活丰富多彩。充分发挥学生会、研究生会自我管理、自我教育、自我服务的作用，引导学生规范有序的参与学校民主管理。学校现有校级学生社团90个，院级学生社团15个，包含公益类、理论学术类、科技实践类、文化艺术类、体育类等多种类型。学校峰云社的成员曾代表当代大学生成为奥运珠峰火炬手；农村发展研究会、挚友社曾被评为“全国优秀学生社团”。学校每年组织5000余名学生参与社会实践活动，在服务我国“三农”事业发展的过程中将自己的论文写在祖国的大地上，连续10年获得首都大学生暑期社会实践优秀组织单位。大学生科技创新与创业活动蓬勃开展，依托大学生科技协会和创业联合协会开展“双创”工作，多次获“挑战杯”全国大学生课外学术科技作品竞赛、“创青春”中国大学生创业计划竞赛、“溢达”创意大赛、全国电子设计大赛、全国机械创新设计大赛、全国大学生数学建模与计算机应用竞赛等赛事中取得优异成绩，大学生创新创业能力和热情不断提高。极推进志愿服务工作，融入新生入学教育，培养学生担当精神与奉献意识。积极组织学生参与奥运会、国庆60周年、园博会、世锦赛、北京半程马拉松、110周年校庆等大型赛会志愿服务，多次获得上级表彰。我校现有常态化志愿服务项目65个，覆盖扶贫、济困、扶老、救孤、助残、助学等多个领域，我校“青春悦动温馨家园”项目在第三届中国青年志愿服务项目大赛中荣获银奖。中国农业大学橄榄球队是中国大陆第一支橄榄球队，多次代表国家参加国际比赛，为祖国争光。2010年11月，在广州亚运会上，以我校学生为主的中国橄榄球女队夺得银牌，男队获得第四名。2016年，我校男子橄榄球队蝉联全国七人制大学生橄榄球锦标赛四连冠。2014年，我校足球队在全国大学生五人制足球争霸赛总决赛中获得冠军。2016年，我校第四次夺得首都大学生足球甲级联赛冠军。田径队也在北京高校顶级比赛中多人多次获得冠军，在全国大学生比赛中取得优异成绩。经过多年努力，我校艺术教育蓬勃发展，在艺术教育、艺术团管理等方面形成了自己的特色。目前有建制完善的大学生艺术团，包括合唱团、管乐团、舞蹈团、戏剧团、民乐团、弦乐团和音乐剧团。各团在北京市大学生艺术展演中多次取得第一名的优异成绩。其中，我校合唱团、管乐团先后成为北京市大学生艺术团。合唱团多次参加央视春晚的录制工作及国内外的艺术交流活动；管乐团多次在解放军军乐厅成功举办专场演出。这些艺术团体充分展现了我校独特的艺术特点和魅力。\n\n国富民殷、强农为本。解民生之多艰，育天下之英才，是我校百年不变的追求。数代农大人情系乡土，忧患苍生，为实现中国人千百年来的温饱和富庶之梦不遗余力，与祖国和人民保持着最紧密的血肉联系，形成了学校特有的勤勉持重、爱国忧民的精神传统和严谨求实、厚德博学的办学传统。今天的中国农业大学，保持着农业优势学科，发展着多种新兴学科，同瞬息万变的世界紧密相连，与日新月异的科技同步发展，正朝着具有中国特色、农业特色的世界一流大学的目标阔步迈进。','CAU','华北地区',1,'京师大学堂农科大学','农林',1905,'教育部','是',985,211,1,9,14,11,24,1641,65,39,20,3,205,0,'http://www.cau.edu.cn/'),(227,112,'北京农学院','北京',1,'北京农学院的前身河北省通县农业学校，创建于1956年。1958年，河北省通县农业学校与北京市农业合作干部学校合并，更名为北京市农业学校。1965年，北京市在北京市农业学校的基础上建立半农半读的北京农业劳动大学。1978年，经国务院批准更名为北京农学院。学校现占地面积约1000亩，包括校本部、东校区、北校区、大学科技园市场。另外，学校建有千亩大学科技园农场、万亩大学科技园林场。学校先后被授予北京市花园式单位、北京市文明校园、首都文明单位标兵、全国文明单位等称号。\n\n学校现有11个二级学院和3个教学部，拥有11个一级学科硕士学位授权点和7类专业硕士学位授权点。现有31个本科专业，其中园艺、动物医学、农林经济管理3个专业为国家级特色建设专业。学校拥有2个国家级（植物类、动物类）和4个北京市级实验教学示范中心（植物类、动物类、食品加工类、管理类）；建有1个国家级、5个北京市级、172个校级签约的校外人才培养基地，满足学校植物类、动物类、园林生态类、食品加工类、生物与信息技术类、经管文法类所有专业需求；拥有2个北京市级校内实践基地（植物类、食品加工类）；另有8个校级实验教学中心，实现了所有专业实验教学全覆盖。\n\n学校现有在编教职员工700余人，其中专任教师500余人，50余人次入选国务院政府特殊津贴、教育部新世纪优秀人才、新世纪百千万人才工程市级人选、“科技北京”百名领军人才、北京市高层次创新创业人才、北京市属高校“长城学者培养计划”、北京市现代农业产业技术体系创新团队首席专家与岗位专家。\n\n学校共有全日制在校生8000余人，图书馆纸本藏书近90万册，电子馆藏近240万册。建校60余年来，学校培养的4万余名优秀毕业生，扎根基层，踏实勤勉，成为首都经济社会发展特别是农业行业发展中的骨干力量，在他们当中涌现出了一批优秀的政府管理人员、杰出的农业领域专家学者、颇具影响力的涉农企业家。北京农学院现已成为北京市都市型现代农业人才培养的重要基地和国家现代农业示范区技术服务中心。\n\n六秩风雨，岁月如歌。新时期，学校将以习近平新时代中国特色社会主义思想为指导，深入贯彻落实全国教育大会和北京市教育大会精神，聚焦首都“四个中心”战略定位和建设国际一流的和谐宜居之都现实需求，全面服务首都区域经济社会发展、乡村振兴和京津冀协同发展，与时代脉搏共振、与首都发展同行，努力把学校建设成为立足首都、服务“三农”、面向京津冀、辐射全国的高水平都市型现代农林大学！','BUA','华北地区',1,'河北省通县农业学校、北京市农业学校、北京农业劳动大学','农林',1956,'北京市','是',0,0,0,0,12,0,0,400,35,18,0,1,1570,0,'http://www.bac.edu.cn/'),(77,113,'北京林业大学','北京',1,'北京林业大学是教育部直属、教育部与国家林业和草原局共建的全国重点大学。学校办学历史可追溯至1902年的京师大学堂农业科林学目。1952年全国高校院系调整，北京农业大学森林系与河北农学院森林系合并，成立北京林学院。1956年，北京农业大学造园系和清华大学建筑系部分并入学校。1960年被列为全国重点高等院校，1981年成为首批具有博士、硕士学位授予权的高校。1985年更名为北京林业大学。1996年被国家列为首批“211工程”重点建设的高校。2000年经教育部批准试办研究生院，2004年正式成立研究生院。2005年获得本科自主选拔录取资格。2008年，学校成为国家“优势学科创新平台”建设项目试点高校。2010年获教育部和国家林业局共建支持。2011年与其他10所行业特色高校参与组建北京高科大学联盟。2012年，牵头成立中国第一个林业协同创新中心——“林木资源高效培育与利用”协同创新中心。2016年，学校“林木分子设计育种高精尖创新中心”入选北京市第二批高精尖创新中心。2017年，学校入选世界一流学科建设高校行列，林学和风景园林学两个学科入围“双一流”建设学科名单。2018年，我校有5个学科进入ESI全球排名前1%。\n\n学校以生物学、生态学为基础，以林学、风景园林学、林业工程、草学和农林经济管理为特色，是农、理、工、管、经、文、法、哲、教、艺等多门类协调发展的全国重点大学。学校是国务院学位委员会、教育部授权可自行审定教授任职资格的高校，是国务院学位委员会授权一级学科内可自主设置博士、硕士二级学科及交叉学科的高校。经全国第四轮学科评估，林学和风景园林学两个一流建设学科位居A+档位。学校现有16个学院，61个本科专业及方向，72个硕士生招生学科专业、30个博士生招生学科专业，有7个博士后流动站，9个一级学科博士学位授权点，24个一级学科硕士学位授权点，2个二级学科硕士学位授权点和16个专业硕士学位类别，1个一级学科国家重点学科（含7个二级学科国家重点学科）、2个二级学科国家重点学科、1个国家重点（培育）学科、6个国家林业和草原局重点学科（一级）、3个国家林业和草原局重点培育学科、3个北京市重点学科（一级）（含重点培育学科）、4个北京市重点学科（二级）、1个北京市重点交叉学科。\n\n学校形成了“知山知水，树木树人”的办学理念，为国家培养了10多万名高级专门人才和一批外国留学生，其中包括15名两院院士等为代表的一大批杰出科技专家和管理人才，他们为我国林业事业和经济社会发展做出了卓越贡献。\n\n截至2018年12月，学校在校生25347人，其中本科生13309人，全日制研究生5931人，在职攻读硕士学位356人，各类继续教育学生5751人。学校教职工1888人，其中专任教师1204人，包括正教授302人、副教授541人。中国工程院院士3人，中组部“千人计划”入选者3人，“万人计划”国家级教学名师1人，科技创新领军人才3人，哲学社会科学领军人才1人，青年拔尖人才入选者2人，国家特聘专家1人，教育部“长江学者奖励计划”入选者7人，国家“973”首席科学家1人，“863”首席专家1人，国家社科基金重大项目首席科学家1人，国家百千万人才工程（新世纪百千万人才工程）入选者10人，中宣部文化名家暨“四个一批”人才1人，科技部“中青年科技创新领军人才”2人，环保部“国家环境保护专业技术青年拔尖人才”1人，“国家杰出青年科学基金”获得者6人，“国家优秀青年科学基金”获得者5人，“中国青年科技奖”获得者8人，“中国青年女科学家奖”获得者1人，“科技北京”百名领军人才1人，北京市优秀青年人才1人，北京市高创人才支持计划青年拔尖人才1人，北京高校青年英才计划入选者50人，北京市优秀人才支持计划入选者27人，国家有突出贡献专家8人，省部级有突出贡献专家23人，享受政府特殊津贴专家146人，教育部“创新团队发展计划”3支。教师获奖众多，其中有1人获全国创新争先奖，2人获何梁何利科技进步奖，1人获国际环境突出贡献奖，1人获Luc Hoffmann湿地科学与保护奖，3人获全国优秀科技工作者称号，2人获全国模范教师称号，3人获全国优秀教师称号，1人获全国高校思想政治理论课优秀教师称号,1人获北京市人民教师奖，25人获北京市高等学校教学名师奖，2人获北京市高等学校青年教学名师奖，2人获全国林业教学名师称号。\n\n学校高度重视教学工作，实施导师制、主辅修制，拥有国家理科基地、国家卓越农林人才培养计划、梁希实验班、创新实验班、中外合作办学等多种拔尖创新人才和复合型人才的培养模式。学校获得国家级教学成果一等奖3项、二等奖7项、优秀奖1项和省部级教学成果奖51项。5篇博士学位论文入选“全国优秀博士论文”，5篇博士学位论文入选“北京市优秀博士论文”。研究生58人次在国际风景园林师联合会国际学生风景园林设计竞赛（IFLA-UNESCO）、国际大学生建筑设计大赛、中日韩大学生风景园林设计竞赛中获得金奖。近三年本科毕业生就业率在94%以上，研究生就业率在96%以上。\n\n学校科学研究和科技平台建设实力雄厚、成果丰硕。现有国家、省（部）级重点实验室、工程中心及野外站台共43个。其中，国家花卉工程技术研究中心1个、林木育种国家工程实验室1个、国家野外观测科学研究站1个、国家能源非粮生物质原料研发中心1个、林业生物质能源国际科技合作基地1个、国家水土保持科技示范园区2个、教育部重点实验室3个、教育部工程中心3个、国家林业和草原局重点实验室7个、国家林业和草原局工程技术研究中心3个、国家林业和草原局质检中心1个、国家林业和草原局野外观测研究站6个、北京实验室1个、北京市高精尖创新中心1个、北京市重点实验室8个、北京市工程技术研究中心3个。“十二五”以来，学校获国家科技奖励8项，其中国家科技进步二等奖5项，国家技术发明二等奖3项；获省部级科技奖励62项。以第一作者单位发表论文被SCI收录5424篇，EI收录2610篇，承担国家重点研发计划、国家科技支撑计划、“863”计划、国家自然科学基金重点项目等重大科技计划课题，到账科研经费总计19.7亿元。\n\n学校积极推进国际合作和开放办学，先后与30余个国家和地区的110余所高等院校、科研院所和非政府组织建立了教育与科技合作关系。自2005年起，每年承办商务部援外培训项目，培养了70余个发展中国家近600名林业领域高级管理人才和技术骨干；共举办国际学术会议50余次，接待国外专家学者千余人次，派出大量教师出国交流合作，执行国际合作项目40余项。与加拿大不列颠哥伦比亚大学联合举办木材科学与工程、生物技术专业合作办学项目，同时与美国、法国等国高校开展多个联合培养项目。亚太地区林业教育协调机制办公室在学校设立，为学校国际化办学搭建了新平台。2016年，学校成为丝绸之路农业教育科技创新联盟成员院校，与“一带一路”沿线国家高校积极开展交流合作。学校积极开展与港澳台地区的交流与合作，与港澳台地区多所高校建立了伙伴关系。学校是中国政府奖学金、商务部援外学历项目奖学金、北京市政府奖学金、北京市“一带一路”专项奖学金以及亚太森林组织奖学金项目院校。目前共有来自50多个国家的近300名留学生在校进行本科、硕士、博士、汉语学习，其中学历生的比例占80%以上。\n\n校本部现有校园面积696亩，学校实验林场占地面积12480亩，学校总占地面积13176亩。图书馆建筑面积23400平方米，藏书190.72万册，电子文献48900GB，数据库70种。建成了“万兆骨干、部分千兆到桌面”的数字校园网络。\n\n目前，学校正以办人民满意的高等教育为宗旨，按照学校第十一次党员代表大会提出的新时代“三步走”战略，全面贯彻党的教育方针，开启北林崛起新征程，为建设成为扎根中国大地的世界一流林业大学而努力奋斗。','BJFU','华北地区',1,'北京林学院','农林',1952,'教育部','是',0,211,0,2,16,3,1,1806,61,40,9,0,216,1840,'http://www.bjfu.edu.cn/'),(46,114,'首都医科大学','北京',1,'首都医科大学创建于1956年，是北京市重点高等院校，是北京市政府、国家卫生健康委员会、教育部共建院校。已故著名泌尿外科专家、两院院士、全国人大常委会副委员长吴阶平教授为首任校长。现任党委书记为呼文亮研究员，校长为我国著名分子神经生物学家饶毅教授。\n\n学校校本部设有10个学院、1个学部、1个研究中心和1所附属卫生学校，包括基础医学院、药学院、公共卫生学院、护理学院、生物医学工程学院、中医药学院、医学人文学院、全科医学与继续教育学院、马克思主义学部、国际学院、燕京医学院，脑重大疾病研究中心，附属卫生学校。学校现有21所临床医学院（其中19所为附属医院），包括首都医科大学宣武医院（第一临床医学院）、附属北京友谊医院（第二临床医学院）、附属北京朝阳医院（第三临床医学院）、附属北京同仁医院（第四临床医学院）、附属北京天坛医院（第五临床医学院）、附属北京安贞医院（第六临床医学院）、附属复兴医院（第八临床医学院）、附属北京佑安医院（第九临床医学院）、附属北京胸科医院（第十临床医学院）、首都医科大学三博脑科医院（第十一临床医学院）、附属北京地坛医院（第十二临床医学院）、附属北京儿童医院（儿科医学院）、附属北京口腔医院（口腔医学院）、附属北京安定医院（精神卫生学院）、附属北京妇产医院（妇产医学院）、附属北京中医医院（中医药临床医学院）、附属北京世纪坛医院（肿瘤医学院）、附属北京康复医院（北京康复医学院）、附属北京潞河医院（潞河临床医学院）、中国康复研究中心（康复医学院）、中日友好医院（中日友好临床医学院），以及预防医学教学基地（北京市疾病预防控制中心）。学校还设有38个临床专科学院、专科学系，32个临床诊疗中心。\n\n学校现有全日制在校生12422人，其中研究生4648人，本科生5309人，高专高职生1765人，留学生700人；成教生3121人。学校和附属医院现有教职员工和医务人员42791人（校本部1470人，附属医院41321人）,其中有院士7人；正高职称2596人，副高职称4164人；教授944人，副教授1303人；博士生导师689人，硕士生导师1124人。\n\n学校现有8个国家重点学科，2个国家重点（培育）学科；60个国家临床重点专科（含中医）；14个国家中医药管理局重点（培育）学科；4个北京市一级重点学科，6个北京市二级重点学科，1个北京市重点交叉学科，1个北京地区高等学校学科群。学校临床医学、神经科学与行为学、药理学与毒理学、免疫学、生物学与生物化学、分子生物学与遗传学、社会科学总论7个学科进入ESI学科全球前1%排名，其中，临床医学进入ESI学科前1‰，神经科学与行为学、药理学与毒理学进入全球前5‰。\n\n有8个一级学科博士学位授权点和13个一级学科硕士学位授权点；按照三级学科统计，有59个博士学位授权点和77个硕士学位授权点；具有所有医学及医学相关学科的硕士、博士专业学位授予权。有9个博士后科研流动站。学校设置的长学制专业有临床医学、儿科学和口腔医学；本科专业有临床医学、基础医学、口腔医学、儿科学、预防医学、精神医学、药学、临床药学、中医学、中药学、护理学、生物医学工程、假肢矫形工程、康复治疗学、听力与言语康复学、公共事业管理（卫生管理）、法学（卫生法学）、医学实验技术、医学检验技术、医学影像技术、信息管理与信息系统（卫生信息管理）、眼视光医学、卫生检验与检疫、助产学24个；高职高专教育设有护理、医学检验技术、药学等14个专业。学校培养医学与医学相关学科的学术型和应用型人才，形成了全方位、多层次、创新型人才培养模式。\n\n学校目前有教育部、国家卫生健康委员会“卓越医生教育培养计划”试点项目6个；7个国家级和10个市级特色专业、2个北京市一流专业；7门国家级和18门市级精品课程、5门国家级精品资源共享课程、1门国家级精品视频公开课；3个国家级和6个市级实验教学示范中心、3个国家级人才培养模式创新实验区和1个市级人才培养模式创新试验区、1个国家级大学生校外实践教育基地、5个北京市级校外人才培养基地和1个素质教育基地；7个国家级优秀教学团队、1个黄大年式教师团队和11个市级优秀教学团队，1名国家级教学名师、24名市级教学名师、1名青年教学名师、3名“高创计划”教学名师。学校共获得教育教学成果奖国家级特等奖1项、二等奖7项、优秀奖1项，市级特等奖2项、一等奖22项、二等奖34项、优秀奖1项。学校2014年高水平通过了教育部临床医学专业认证，2018年在教育部本科教学工作审核评估中获得专家组充分肯定。\n\n学校有1个国家儿童医学中心、6个国家临床医学研究中心；1个省部共建国家重点实验室培育基地、5个教育部重点实验室、1个省部共建协同创新中心，1个北京实验室、54个北京市重点实验室；1个国家工程实验室、1个国家工程技术研究中心、4个教育部工程研究中心、10个北京市工程技术研究中心；有3个教育部创新团队，3个科技部重点领域优秀创新团队。学校“人脑保护高精尖创新中心”获批首批北京高精尖中心。在拥有一批国家级和市级重点学科和重点实验室的同时，学校建有高水平的国家级、市级研究和培训机构，如国家生命科学与技术人才培养基地、原卫生部全科医学培训中心、北京市全科医学培训中心、首都卫生管理与政策研究基地等。近5年来，学校承担了国家重点研发计划、国家科技重大专项、国家自然科学基金、国家社会科学基金、北京市科委以及北京市自然基金等科研项目2940项（其中国家级科研项目1545项），累计科研经费26.62亿元（其中国家级项目经费20.64亿元）。获得各类各级奖励85项，其中国家科技进步奖8项、北京市科技奖37项、教育部高等学校科学研究优秀成果奖5项、中华医学科技奖22项、吴阶平系列医学药学奖6项、茅以升北京青年科技奖2项、何梁何利科技奖1项、中源协和生命医学奖2项；北京市哲学社会科学优秀成果奖1项、北京市优秀调查成果奖1项。获得专利授权649项。\n\n学校建有图书馆、档案馆、校史馆、中心实验室、信息中心、实验动物部、学报编辑部、校医院等公共服务平台。\n\n学校先后与美国、加拿大、澳大利亚、英国、瑞典、挪威、香港、台湾等19个国家和地区的50多所高校和相关单位签订了友好交流合作协议。先后接待50多个国家和地区的专家学者和学生数百人来校进行学术交流访问。学校获批首批北京市“一带一路”国家人才培养基地。留学生涵盖了本科、硕士、博士研究生及进修生。同时，学校通过多种渠道，选派师生出国出境学习、进修，参加学术会议和交流访问。\n\n学校始终坚持社会主义办学方向，全面贯彻党的教育方针，坚持以立德树人为根本，秉承“扶伤济世、敬德修业”的校训，遵循医学教育规律，牢记社会责任，把为健康中国健康北京建设、京津冀协同发展战略实施、国际一流和谐宜居之都建设做贡献为己任，努力办人民满意的大学，培养人民满意的医生，做出能服务人民对健康美好需求的科研成果。站在新的历史起点上，全体首医人将进一步求真务实、凝心聚力、积极进取、追求卓越，全面提升学校核心竞争力，努力将学校建设成为高水平研究型医科大学。','CMU','华北地区',1,'北京第二医学院、北京联合大学中医药学院、北京医学高等专科学校、北京职工医学院','医科',1956,'北京市','是',0,0,0,0,10,7,0,1470,24,13,8,0,0,0,'http://www.ccmu.edu.cn/'),(14,115,'北京师范大学','北京',1,'北京师范大学是教育部直属重点大学，是一所以教师教育、教育科学和文理基础学科为主要特色的著名学府。学校的前身是1902年创立的京师大学堂师范馆，1908年改称京师优级师范学堂，独立设校，1912年改名为北京高等师范学校。1923年学校更名为北京师范大学，成为中国历史上第一所师范大学。1931年、1952年北平女子师范大学、辅仁大学先后并入北京师范大学。\n\n百余年来，北京师范大学始终同中华民族争取独立、自由、民主、富强的进步事业同呼吸、共命运，在“五·四”、“一二·九”等爱国运动中发挥了重要作用。以李大钊、鲁迅、梁启超、钱玄同、吴承仕、黎锦熙、陈垣、范文澜、侯外庐、白寿彝、钟敬文、启功、胡先骕、汪堃仁、周廷儒等为代表，一大批名师先贤在这里弘文励教。经过百余年的发展，学校秉承“爱国进步、诚信质朴、求真创新、为人师表”的优良传统和\"学为人师，行为世范\"的校训精神，形成了“治学修身，兼济天下”的育人理念。\n\n“七五”、“八五”期间，北京师范大学被确定为国家首批重点建设的十所大学之一。“九五”期间，又被首批列入“211工程”建设计划。2002年百年校庆之际，教育部和北京市决定重点共建北京师范大学，北京市第九次党代会将北京师范大学列入支持建设的世界一流大学的行列。“十五”期间，学校进入国家“985工程”建设计划。2017年，学校进入国家“世界一流大学”建设A类名单，11个学科进入国家“世界一流学科”建设名单。\n\n北京师范大学占地面积1191亩（海淀校园、西城校园、昌平校园G区），是学校教育教学活动的主要集中地。全日制在校生24000余人，其中：本科生10105人、研究生12756人、长期留学生1600余人。现设3个学部、23个学院、2个系、10个研究院（所）。馆藏图书470余万册，电子图书830余万册。\n\n北京师范大学学科综合实力位居全国高校前列。2002年，北京师范大学成为首批拥有自主设置本科专业审批权的6所高校之一。现有本科专业74个、硕士学位授权一级学科39个、博士学位授权一级学科28个、硕士学位授权二级学科点146个、博士学位授权二级学科点114个、博士后科研流动站25个。5个一级学科、11个二级学科获批国家重点学科，2个二级学科获批国家重点（培育）学科。现有5个一级学科北京市重点学科、9个二级学科北京市重点学科、1个交叉学科北京市重点学科。现有39个学科涵盖10个学科门类，形成了综合性学科布局。根据教育部学位与研究生教育发展中心发布的2016年一级学科评估（第四轮）结果，我校教育学、心理学、中国语言文学、中国史、戏剧与影视学、地理学6个一级学科获评A＋，2个一级学科获评A，7个一级学科获评A-，获评A+数量居全国高校第六位。在英国高等教育调查公司（QS）2019年公布的世界大学排行榜中，我校排名第277位，在中国内地高校中排名第10位。\n\n北京师范大学是国家人文社科科研和科技创新的一支重要力量。学校拥有国家重点实验室4个、国家工程实验室1个、国家野外科学观测研究站1个、国家级协同创新中心1个、国家教材建设重点研究基地2个、国家高端智库（培育）1个，教育部重点实验室9个、教育部工程研究中心5个、教育部人文社会科学重点研究基地7个、教育部区域和国别研究培育基地4个，北京市重点实验室12个、北京市工程技术研究中心3个、北京高等学校高精尖创新中心1个、北京市哲学社会科学重点研究基地2个、北京市新型智库1个。定期出版专业刊物25种。\n\n北京师范大学教育资源丰富，是国家高素质创新型人才培养的重要基地。现拥有国家文科基础学科人才培养和科学研究基地2个、国家理科基础科学研究和教学人才培养基地5个、国家试点学院1个，入选国家基础学科拔尖学生培养试验计划；是国家大学生文化素质教育基地、国家对外汉语教学人才培养基地、国家生命科学与技术人才培养基地、国家级中华优秀传统文化传承基地（中国话剧）；拥有国家级实验教学示范中心4个、国家级虚拟仿真实验教学中心2个、教育部人才培养模式创新实验区5个、国家卓越法律人才教育培养基地2个，承担教育部、文化部动漫高端人才联合培养实验计划。\n\n北京师范大学教师队伍结构合理、素质精良。现有校本部教职工3120人，其中专任教师2064人，92%的专任教师具有博士学位，16%的专任教师具有海外学历。现有两院院士8人、双聘院士15人，资深教授6人，长江学者特聘教授38人，国家杰出青年基金获得者49人，万人计划领军人才25人，国家级高等学校教学名师6人，国家级创新研究群体6个。万人计划青年拔尖人才、国家优秀青年科学基金资助获得者、“长江学者奖励计划”青年学者等共计82人次。\n\n北京师范大学国际合作交流广泛。学校与30多个国家和地区的近500所大学、研究机构签署了合作协议，与美国、加拿大、英国、德国、日本、韩国、瑞士、西班牙、港澳台等国家和地区的200余所大学有学生交流项目的合作。学校先后与英国曼彻斯特大学、美国旧金山州立大学、俄克拉荷马大学、加拿大道森学院、丹麦奥尔堡大学、意大利马切拉塔大学、美国威廉玛丽大学、美国塔夫茨大学合作建立了8所孔子学院。接收来自130个国家和地区的长短期留学生4100余人次，学历生占长期留学生的比例约为78.6%。\n\n北京师范大学第十三次党代会进一步明确了建设“综合性、研究型、教师教育领先的中国特色世界一流大学”的办学定位，提出了“三步走”的战略构想，明确到本世纪中叶进入世界一流大学前列。当前，学校正着力构建“高原支撑、高峰引领”的学科发展体系和以北京校区和珠海校区为两翼的一体化办学格局，不断深化综合改革，推进各项事业发展。北京师范大学正向着建设世界一流大学的目标稳步迈进。','BNU','华北地区',1,'京师大学堂师范馆、北京高等师范学校、国立北京师范大学校、京师大学校师范部','师范',1902,'教育部','是',985,211,1,11,22,8,40,2000,69,36,24,4,110,3700,'http://www.bnu.edu.cn/'),(70,116,'北京外国语大学','北京',1,'北京外国语大学坐落在北京市海淀区西三环北路，在三环路两侧分设东、西两个校区，是教育部直属、首批“211工程”高校、“985”优势学科创新平台高校、首批一流学科建设高校。\n\n学校是中国共产党创办的第一所外国语高等学校，前身是1941年成立于延安的中国抗日军政大学三分校俄文大队，后发展为延安外国语学校，建校始隶属于党中央领导。新中国成立后，学校归外交部领导，1954年更名为北京外国语学院，1959年与北京俄语学院合并组建新的北京外国语学院。1980年后直属教育部领导，1994年正式更名为北京外国语大学。\n\n学校已开设101种外国语言，欧洲语种群和亚非语种群是目前我国覆盖面最大的非通用语建设基地，是教育部第一批特色专业建设点。学校形成了以外国语言文学学科为主体，文、法、经、管等多学科协调发展的格局。按照时间先后，学校开设语种包括俄语、英语、法语、德语、西班牙语、波兰语、捷克语、罗马尼亚语、日语、阿拉伯语、柬埔寨语、老挝语、僧伽罗语、马来语、瑞典语、葡萄牙语、匈牙利语、阿尔巴尼亚语、保加利亚语、斯瓦希里语、缅甸语、印尼语、意大利语、克罗地亚语、塞尔维亚语、豪萨语、越南语、泰语、土耳其语、朝鲜语、斯洛伐克语、芬兰语、乌克兰语、荷兰语、挪威语、冰岛语、丹麦语、希腊语、菲律宾语、印地语、乌尔都语、希伯来语、波斯语、斯洛文尼亚语、爱沙尼亚语、拉脱维亚语、立陶宛语、爱尔兰语、马耳他语、孟加拉语、哈萨克语、乌兹别克语、拉丁语、祖鲁语、吉尔吉斯语、普什图语、梵语、巴利语、阿姆哈拉语、尼泊尔语、索马里语、泰米尔语、土库曼语、加泰罗尼亚语、约鲁巴语、蒙古语、亚美尼亚语、马达加斯加语、格鲁吉亚语、阿塞拜疆语、阿非利卡语、马其顿语、塔吉克语、茨瓦纳语、恩得贝莱语、科摩罗语、克里奥尔语、绍纳语、提格雷尼亚语、白俄罗斯语、毛利语、汤加语、萨摩亚语、库尔德语、比斯拉马语、达里语、德顿语、迪维希语、斐济语、库克群岛毛利语、隆迪语、卢森堡语、卢旺达语、纽埃语、皮金语、切瓦语、塞苏陀语、桑戈语、塔玛齐格特语、爪哇语、旁遮普语。学校秉承延安精神，坚持服务国家战略，目前已开齐与中国建交国家的官方用语。\n\n学校现有32个教学科研单位，近年来，学校与英国诺丁汉大学合作成立国际研究生院，陆续成立许国璋高等语言研究院、王佐良外国文学高等研究院、区域与全球治理高等研究院、比较文明与人文交流高等研究院等众多特色研究机构；大力推进人才培养模式改革，建立北外学院、国际组织学院、国际教育学院。学校拥有教育部人文社会科学重点研究基地：中国外语与教育研究中心，国家语委科研中心：国家语言能力发展研究中心，北京市哲学社会科学重点研究基地：北京中外文化交流研究基地等。4个教育部区域和国别研究培育基地（中东欧研究中心、日本研究中心、英国研究中心、加拿大研究中心）以及37个教育部备案国别和区域研究中心。3个教育部人文交流研究中心：中国-印尼人文交流研究中心、中法人文交流研究中心、中德人文交流研究中心。学校编辑出版《外语教学与研究》《外国文学》《国际论坛》《国际汉学》四种全国核心刊物，出版《中国俄语教学》《英语学习》《俄语学习》《德语人文研究》《法语国家与地区研究》《国际汉语教育》《区域与全球发展》《青少年法制教育》《中国应用语言学（英文）》《外语教育研究前沿》等刊物。学校有全国最大的外语类书籍、音像和电子产品出版基地：外语教学与研究出版社。\n\n学校开设本科专业121个，其中45个专业是全国唯一专业点。学校现有4个国家重点学科（含培育学科），7个北京市重点学科。拥有一级学科博士点2个（外国语言文学、管理科学与工程），一级学科学术型硕士点11个（法学、政治学、马克思主义理论、应用经济学、中国语言文学、外国语言文学、新闻传播学、管理科学与工程、工商管理、教育学、世界史），专业硕士点8个（金融、国际商务、汉语国际教育、翻译、新闻与传播、法律、会计、工商管理硕士），涵盖文学、经济学、管理学、法学、教育学、历史学六大学科门类。在2017年全国第四轮学科评估结果中，学校外国语言文学一级学科评级为A+，位居全国榜首。2018年“QS世界大学学科排名”发布，学校语言学、现代语言2门学科再次进入全球前100强，居国内同类院校之首。学校本科在校生5470人，研究生（硕士、博士）2800人，学历留学生1404人。\n\n学校创新人才管理机制，全面提升师资队伍水平，现有在职在编教职工1256人，来自55个国家和地区的外籍教师191人。学校拥有国家级突出贡献的中青年专家、国家“万人计划”哲学社会科学领军人才、国家“万人计划”教学名师、“长江学者”讲座教授、国家百万千万人才工程、国家“四个一批”人才、青年“长江学者”等高水平师资。教师中超过90%拥有海外学习经历。中国外语与教育研究中心教师团队入选首批“全国高校黄大年式教师团队”。\n\n学校坚持高端引领、整体推进的国际化办学思路，与世界上91个国家和地区的313所高校和学术机构开展交流，与英国诺丁汉大学、英国伦敦大学亚非学院、法国国立东方语言文化学院、俄罗斯莫斯科国立语言大学、德国哥廷根大学、意大利罗马大学、匈牙利罗兰大学、波兰雅盖隆大学等国外著名高校建立了实质性的合作关系。学校承办了23所海外孔子学院，位于亚、欧、美18个国家，居国内高校之首，包括德国纽伦堡-埃尔兰根孔子学院、比利时布鲁塞尔孔子学院、奥地利维也纳大学孔子学院、意大利罗马大学孔子学院、波兰克拉科夫孔子学院、比利时列日孔子学院、美国夏威夷大学孔子学院、德国杜塞尔多夫孔子学院、匈牙利罗兰大学孔子学院、保加利亚索非亚孔子学院、捷克帕拉茨基大学孔子学院、德国慕尼黑孔子学院、马来西亚马来亚大学孔子学院、韩国外国语大学孔子学院、西班牙巴塞罗那孔子学院、俄罗斯莫斯科国立语言大学孔子学院、阿联酋扎耶德大学孔子学院、英国伦敦孔子学院、阿尔巴尼亚地拉那大学孔子学院、德国哥廷根大学学术孔子学院、英国牛津布鲁克斯大学孔子学院、斯里兰卡科伦坡大学孔子学院、法国ESCP欧洲高等商学院商务孔子学院。\n\n学校图书馆馆藏纸质中外文图书近144万余册，中外文电子图书222万余册，中外文报刊1123种，中外文数据库97个，形成了以语言、文学、文化为主要资料的馆藏特色。近年来，随着学科建设的发展，政治、法律、外交、经济、新闻和管理等方面的文献逐渐形成藏书体系。学校不断加强信息化建设，形成“开放、互联、智能、创新、融合”的信息化构架，开发以多语种网站、数字北外、数据中心软件平台等为代表的多项标志性成果。其中，多语种网站项目于2015年启动，支持50种外国语言。2018年获批教育部人工智能助推教师队伍建设行动试点工作高校。\n\n在长期办学历程中，北京外国语大学紧密结合国家战略发展需要，形成了“外、特、精”的办学理念和“兼容并蓄、博学笃行”的校训精神，成为培养外交、翻译、经贸、新闻、法律、金融等涉外高素质人才的重要基地。据不完全统计，北京外国语大学毕业的校友中，先后出任驻外大使的就有400多人，出任参赞的1000多人，北京外国语大学因此赢得了“共和国外交官摇篮”的美誉。\n\n当前，学校全面贯彻落实习近平新时代中国特色社会主义思想和党的十九大精神，秉承传统，追求卓越，精益求精，致力于培养国家亟需，富有社会责任感、创新精神和实践能力，具有中国情怀、国际视野、思辨能力和跨文化能力的复合型、复语型、高层次国际化人才，加快中国特色、世界一流外国语大学的建设步伐。','BFSU','华北地区',1,'延安外国语学校、华北大学、北京外国语学院','语言',1941,'教育部','是',0,211,0,1,22,0,11,1255,97,42,13,0,321,0,'http://www.bfsu.edu.cn/'),(195,117,'北京第二外国语学院','北京',1,'北京第二外国语学院是一所以外国语言文学和旅游管理为优势特色学科，文学、管理学、经济学、法学、哲学等多学科门类协调发展的著名高校，是中国外语、翻译、旅游、经贸等人才培养与研究的重要基地，是一所具有鲜明国际化特色的大学。\n\n北京第二外国语学院是周恩来总理亲自提议、在原新华社外文干部学校基础上于1964年10月成立的，是全国第一批本科招生院校。先后隶属国家对外文化联络委员会、外交部、北京市人民政府、教育部和国家旅游局领导。2000年2月，划归北京市人民政府管理。\n\n建校以来，学校秉承“明德、勤学、求是、竞先”的校训精神，以“传承东西文明，促进中外交流”为己任，形成了“融中外、兼知行”的学风、“和易以思、人文化成”的教风，积淀了深厚的学术传统，有着独特的办学经验，产生了一批名家名师。新中国早期资深翻译家李越然、苏琦、周锡卿、董乐山、管震湖、李传松、王志佑、王文炯、舒雨等先生都曾在学校弘文励教。\n\n学校坐落于北京市朝阳区，西面毗邻中央商务区（CBD），东面紧靠北京城市副中心（通州）。现有各类在校生近万人，其中本科生6200余人、研究生1300余人、留学生700余人、贯培生1000余人。现设英语学院、日语学院、亚洲学院、欧洲学院、中东学院、高级翻译学院、旅游科学学院、商学院、经济学院、政党外交学院、文化与传播学院、汉语学院、马克思主义学院、体育部、基础科学部、贯培学院和夏斗湖学院17个教学单位。图书馆现有藏书117万余册，年订外文（包括港澳台）原版报刊约200种、中文报刊约850种；电子图书108万余册；数据库105个，其中中文数据库42个、外文数据库61个，自建数据库2个。\n\n学校现有本科专业44个（其中有26个语种专业）、硕士学位授权二级学科点28个、专业硕士学位授权点6个、硕士学位授权一级学科5个、联合培养博士点2个（与美国宾汉姆顿大学联合培养翻译博士，与美国南卡罗莱纳大学联合培养旅游博士）、博士后科研工作站2个（分别与社会科学文献出版社、首都经济贸易大学联合共建）。有北京高校高精尖学科2个（旅游管理学科、外国语言文学学科）、北京市重点建设学科4个（外国语言文学、旅游管理、企业管理、国际贸易）；国家级特色专业4个（日语、阿拉伯语、英语、旅游管理）、教育部专业综合改革试点专业1个（日语）、北京市一流专业1个（翻译专业）。\n\n学校坚持以服务国家战略和首都需求为导向，以培养“多语种复语、跨专业复合”的具有国际视野、家国情怀的高层次、应用型人才为根本任务，致力构建具有二外特色的高端复合人才培养体系。经过多年的办学实践，形成了“学用结合，注重实践”的办学特色。外语专业强调“技能领先，注重实训”，狠抓“听、说、读、写、译”基本技能训练；非外语专业依托优势外语教学资源和多元文化环境，坚持“应用导向，强化实践”，走产学研一体化道路。学生国际视野宽阔、创新精神和实践能力突出，综合素质高，就业竞争力强。从二外走出的数万名毕业生，遍及大江南北和世界各地，涌现出国务委员兼外交部部长王毅、原商务部部长高虎城，原中联部副部长刘洪才、徐绿平，原外交部副部长程国平、原中国政府中东问题特使吴思科，中信集团公司党委书记、董事长常振明，同济大学副校长江波、世界旅游联盟秘书长刘世军等一批政界、外交界、商界、文教界杰出人士。\n\n学校坚持以服务国家、首都重大战略为重点，以新型智库矩阵建设为支点，学术创新能力与社会服务能力持续增强，在“一带一路”、对外文化传播、旅游、国际文化交流等方面渐成品牌。现设有中国文化和旅游产业研究院、中国旅游人才发展研究院、中国文化和旅游大数据研究院、中国“一带一路”战略研究院、首都文化和旅游发展研究院、首都对外文化传播研究院、中阿改革发展研究院、首都国际交往中心研究院、中国公共政策翻译研究院、中国服务贸易研究院、人工智能与语言认知实验室、数字资源与知识服务实验室、二外期刊社等15个科研机构，拥有6个省部级科研基地，1个省部级协同创新中心，7个教育部国别和区域备案研究中心，形成了较为完备科研平台格局。编辑出版有《北京第二外国语学院学报》《旅游导刊》《跨文化研究》以及Journal of Chinese Economics等学术期刊，《北京第二外国语学院学报》被评定为语言学类“A刊核心期刊”。近年来，学校科研经费年均增长30%以上，高层次科研项目数量连续取得突破，高层次学术研讨会渐成品牌，高质量科研成果持续增长。\n\n学校拥有一支年龄、学历、专业、职称结构合理的师资队伍。现有在职在编教职工近千人，其中专任教师具有博士学位的占50%以上，具有副教授及以上职称的近60%，大多数教师具有在国外进修或工作的经历。学校还聘请兼职教授120余名，拥有外国专家60余名。学校有2个国家级优秀教学团队、4个北京市优秀教学团队、8个北京市学术创新团队。\n\n学校充分发挥多语种优势，大力开展国际交流与合作，具有鲜明国际化特色。学校与全球40多个国家、地区的170余所高校和教育机构建立了全方位、多层次、实质性的交流与合作，每年选派一定数量的教师出国进修、访学、合作科研，从事对外汉语教学等，年均赴外留学学生600余人，年均接收外国留学生1500余人次。学校与墨西哥奇瓦瓦自治大学、英国兰开夏中央大学、摩洛哥穆罕默德五世大学、法国克莱蒙费朗市、葡萄牙科英布拉大学、巴拿马大学合作共建了6所孔子学院。目前正在筹建波兰华沙大学等“一带一路”沿线国家孔子学院。\n\n学校第八次党代会确立的发展目标是：\n\n从现在到2022年，以“国际化、创新性、高水平、应用型”为发展定位，以培养“多语种复语、跨专业复合”的具有国际视野、家国情怀的高层次、应用型人才为根本任务，以外国语言文学和旅游管理为优势特色学科，加强内涵发展，将学校初步建成具有鲜明首都特色的中国一流、世界上有重要影响的新型外国语大学。\n\n到2035年前后，内涵式发展取得突出成效，与社会主义现代化国家相适应的国际化、应用型高端复合人才培养体系基本建成，以服务国家战略和首都功能定位为鲜明特色的科学研究体系已经形成，优势特色学科进入国家一流学科行列，全面建成中国一流、世界上有重要影响的新型外国语大学。','BISU','华北地区',1,NULL,'语言',1964,'北京市','否',0,0,0,0,17,0,0,0,44,5,2,0,0,0,'http://www.bisu.edu.cn/'),(78,119,'中国传媒大学','北京',1,'中国传媒大学是教育部直属的“一流学科建设高校”，“211工程”重点建设大学，“985优势学科创新平台”重点建设高校。学校始建于1954年，2004年8月由北京广播学院更名为中国传媒大学。\n\n学校坐落于北京古运河畔，地处首都功能核心区和北京城市副中心之间，交通便利，区位优势明显。校园环境优美，占地面积46.37万平方米，总建筑面积63.88万平方米。\n\n办学65年来，学校秉承“立德、敬业、博学、竞先”的校训，以培养“弘道崇德、经世致用”的传媒人为己任，培养了大量党和国家所需要的、能够应对未来媒体挑战、驰骋于国际舞台的优秀传媒人才,为党和国家的传媒事业以及经济社会发展作出了重要贡献，被誉为“中国广播电视及传媒人才摇篮”“信息传播领域知名学府”。\n\n进入新时代，学校党委励精图治，以系统化思维改革创新，以全新理念对学校进行战略性综合布局，统筹推进，学校面貌、师生面貌焕然一新。学校坚持立德树人、科学定位、内涵发展、特色办学，以管理质量、教育质量、工程质量“三质量”提升为统领，实施一流生源、一流师资、一流课程、一流教材、一流毕业生教育质量“五个一工程”，聚焦马克思主义铸魂、爱国情怀强基、人文素养修身、国际视野拓界、特色项目托举、未来媒体创新“六个维度”，传承弘扬“忠诚、自信、包容、竞先”中传文化基因，全面提升办学水平。\n\n学校坚持“结构合理、层次分明，重点突出、特色鲜明，优势互补、相互支撑”的学科建设思路，充分发挥传媒领域学科特色和综合优势，形成了以新闻传播学、戏剧与影视学、艺术学理论、信息与通信工程为龙头，设计学、音乐与舞蹈学、美术学、中国语言文学、外国语言文学、电子科学与技术、计算机科学与技术、法学、管理科学与工程等为支撑，互联网信息、文化产业、新媒体、艺术与科学等交叉学科为特色的多学科融合渗透、协调可持续发展的学科体系。\n\n学校新闻传播学、戏剧与影视学2个学科入选国家“双一流”建设学科名单，新闻学、广播电视艺术学2个国家重点学科，传播学1个国家重点培育学科，互联网信息、文化产业2个北京高校高精尖学科，新闻传播学、艺术学理论、戏剧与影视学3个一级学科北京市重点学科，语言学及应用语言学、通信与信息系统、电磁场与微波技术、动画学4个二级学科北京市重点学科，7个博士后科研流动站，8个博士学位授权一级学科点，19个硕士学位授权一级学科点，10个专业硕士类别，79个本科专业。 \n\n学校设有6个学部，21个学院（新闻学院、电视学院、戏剧影视学院、播音主持艺术学院、动画与数字艺术学院、音乐与录音艺术学院、信息与通信工程学院、计算机与网络空间安全学院、数据科学与智能媒体学院、人文学院、政法学院、外国语言文化学院、经济与管理学院、广告学院、文化产业管理学院、马克思主义学院、政府与公共事务学院、远程与继续教育学院、培训学院、高等职业技术学院、国际传媒教育学院）。现有全日制在校生近15000人，其中普通全日制本专科生9000余人，博士、硕士研究生5000余人。教职工1989人，其中专任教师1115人，正高级职称329人，副高级职称589人。拥有一批国内外享有盛誉的教授、学者，其中：3名双聘院士，2人入选“长江学者”，2人入选国务院学位委员会学科评议组成员，3人入选“新世纪百千万人才工程”国家级人选等。近年来，一批中青年学术骨干脱颖而出，分别入选教育部“新（跨）世纪优秀人才支持计划”，中宣部“四个一批”人才，“有突出贡献中青年专家”等，还有20余人次荣获国家级教学名师奖、全国优秀教师荣誉称号、北京市级教学名师奖等。学校还聘请了一批著名专家学者担任特聘教授、客座教授或兼职教授，教师队伍的整体水平日益增强。\n\n学校教育资源丰富，是信息传播研究领域的学术重镇。学校设有协同创新中心，建有传播研究院、艺术研究院、国家广告研究院、雄安新区发展研究院、新媒体研究院、互联网信息研究院、脑科学与智能媒体研究院，建有26个国家级、省部级科研平台，2个国家级实验教学示范中心，6个北京市实验教学示范中心。学校主持完成了国家数字中短波广播技术标准制订与系统研制、参与国家手机电视、移动多媒体技术标准制订，在数字广播技术领域处于国内领先、国际先进水平。\n\n学校拥有完善的教学、科研设备和公共服务体系。校园多媒体网络、数字有线综合业务网、图书文献信息资源网、现代远程教育网，公共服务体系日趋完善；多媒体教室、演播馆、实验室等装备精良，功能完善；图书馆形成了信息传播学科内容丰富，纸质、电子、网络形式多样的馆藏体系。 \n\n学校拥有《现代传播》《现代出版》《中国新闻传播研究》三本CSSCI来源刊物和《中国传媒大学学报》（自然科学版）、《音乐传播》、《媒介》、《大戏剧论坛》等学术刊物，以及记录中国广播电视行业发展的《中国广播电视年鉴》，为学校的双一流学科建设和科研进步提供强有力的平台支撑。\n\n学校与200多所国外知名大学、科研与传媒机构建立了交流合作关系。2009年学校发起成立了“传媒高等教育国际联盟”，27个国家和地区的67所知名传媒高校加入联盟。学校还是联合国教科文组织“媒介与女性”教席单位，建有亚洲传媒研究中心、欧洲传媒研究中心等国际学术研究机构。由我校主办的亚洲传媒论坛、世界大学女校长论坛、中国传播论坛等高层次国际学术会议，已成为国际传媒界、高教界交流的重要平台。\n\n面向未来，学校将牢牢把握时代发展大势、高等教育发展大势，守正创新，担当作为，以立德树人为根本，以中国特色、世界一流为目标，瞄准智能传媒和国际一流两大主攻方向，全面布局智能传媒教育，率先实现由传统传媒教育向智能传媒教育的转型跨越，赢得智能传媒教育主动权和主导权，引领推动新文科建设，早日实现世界知名高水平传媒大学的奋斗目标！','CUC','华北地区',1,'北京广播专科学校、北京广播学院','语言',1954,'教育部','否',0,211,0,2,27,3,3,1150,87,19,8,0,62,0,'http://www.cuc.edu.cn/'),(41,120,'对外经济贸易大学','北京',1,'对外经济贸易大学是教育部直属的全国重点大学，首批“211 工程”和首批“双一流”建设高校，坐落在首都北京朝阳区。学校校园规划精致，环境优雅，是中国社会主义经济建设事业人才培养和科学研究的重要基地之一。\n\n学校前身为高级商业干部学校，创建于1951 年，受中央贸易部和教育部双重领导。1952 年，中央贸易部撤销，学校划归对外贸易部，受对外贸易部和教育部双重领导。1953 年，学校更名为北京对外贸易专科学校，由教育部委托对外贸易部领导。1954 年，中国人民大学贸易系对外贸易专业并入北京对外贸易专科学校，以北京对外贸易专科学校为基础成立北京对外贸易学院。1984 年，学校更名为对外经济贸易大学。2000 年6 月，原中国金融学院与原对外经济贸易大学合并成立新的对外经济贸易大学，划归教育部直属。2010 年12 月，教育部与商务部正式签署协议，共建对外经济贸易大学。学校现任党委书记蒋庆哲，校长王稼琼。\n\n历经几代师生员工的艰苦创业和努力奋斗，对外经济贸易大学已经发展成为一所拥有经、管、法、文、理、工六大门类，以国际经济与贸易、法学（国际经济法）、金融学、工商管理、外语（商务外语）等优势专业为学科特色的多科性财经外语类大学。目前，学校下设研究生院及18 个学院，并设有体育部；拥有国家级重点学科2 个，国家级人文社会科学重点研究基地1 个，教育部战略研究（培育）基地1 个，教育战略与规划研究中心1 个，北京市重点学科7 个，北京市哲学社会科学研究基地1 个，博士后流动站4 个，一级学科博士点7 个，一级学科硕士点10 个，专业硕士学位授权点12 个，本科专业46 个。\n\n学校现有教职工1600 余人，其中专任教师1000 余人，学校还聘请一定数量的兼职教师及外国专家。这是一处群英荟萃之地，老教授中名家耀眼，中青年教师里新秀辈出。大批获得政府特殊津贴、受聘担任政府学术机构成员的专家学者工作在教学科研第一线；大部分中青年教师在国外留学或进修过，教学科研水平高，许多人被评为省部级学科带头人和骨干教师，入选国家优秀人才培养工程。\n\n学校现有在校学生1.6 万余人，其中本科生8300余人，研究生5200 余人，来华留学学历生2400余人。学校培养的学生一直以专业知识和技能扎实、外语娴熟、思维活跃、实践能力强而受到社会的普遍欢迎，毕业生遍布全国各地的外贸、金融、会计、教育科研等行业领域以及国家机关、中国驻外商务机构，为我国经济与社会发展，尤其是经贸事业的发展发挥着重要的作用。 \n\n学校的学术研究在我国对外经贸科研领域占有重要地位，设有130余个研究单位，其中中国WTO 研究院是全国唯一的研究世界贸易组织的国家级人文社科重点研究基地。学校图书馆文献总量190 余万册（件）。学校主办并公开发行多种学术刊物，其中《国际贸易问题》、《国际商务——对外经济贸易大学学报》、《日语学习与研究》、《Journal of WTO and China》（WTO与中国）等享有较高的学术地位。学校出版社每年出书百余种，在高校中享有较高声誉。学校也是国家培训高级经济管理干部的基地之一，设有政府委托或与外国合办的多个高级在职培训机构。\n\n学校于1989 年11 月在国内率先成立了校董会。李岚清为首任校董会主席，荣毅仁、霍英东等任名誉主席，吴仪为第二任校董会主席。2011 年9 月，校董会第三届会议选举施建军为第三任校董会主席，石广生、刘鸿儒、陈元为荣誉主席。校董会的建立，对于学校的建设发展发挥了重要作用。 \n\n学校在发展过程中受到海内外广泛关注和支持：政府机构、企业家、财团、大公司等纷纷向学校捐资设立基金或奖学金，许多国际著名的跨国企业成为学校的赞助团体。目前，社会力量在学校设立的奖学金、奖教金等20 余项，院系级基金则更多。学校与美、英、法、德、日、俄、意、澳等51 个国家和地区的280余所著名大学和研究机构建立合作交流关系，不断开展的对外交流与合作，使对外经济贸易大学面向世界办学的特色更加鲜明。\n\n面对新时期我国全面实现小康社会建设目标，加快社会主义现代化建设的新形势，学校倡导追求卓越、创造精品的理念，坚持办精品大学、控制办学规模，办特色大学，走内涵发展、特色发展和现代化发展之路，加强国际化建设，着力培养复合型人才，将建设特色鲜明世界一流大学作为长期愿景和历史使命。','UIBE','华北地区',1,'北京对外贸易学院','财经',1951,'教育部','是',0,211,0,1,18,0,5,1000,46,22,7,0,165,0,'http://www.uibe.edu.cn/'),(306,121,'北京物资学院','北京',1,'北京物资学院是一所以物流和流通为特色，以经济学科为基础，以管理学科为主干，经、管、理、工、文、法等多学科协调发展的公办普通高等院校。1980年建校，先后隶属于国家物资总局、物资部、国内贸易部，1998年10月划归北京市管理。2018年2月，经北京市人民政府批准，北京市商务科技学校并入北京物资学院。\n\n北京物资学院位于北京市朝阳北路东段，北京城市副中心核心区域，地处古老的京杭大运河源头，文化底蕴深厚，环境优美宜人，是北京市授予的“美丽校园”、“文明校园”、和“花园式单位”。校园占地670余亩，建筑面积20万余平方米，教学、科研和生活设施齐全。学校建有国家级特色专业—经济学专业、物流管理专业，国家级人才培养模式创新实验区—具有国际化视野的实战型物流人才培养实验区，国家级实验教学示范中心—物流系统与技术实验教学中心，北京市属高校一流专业—物流管理专业，北京市重点实验室—物流系统与技术实验室、智能物流系统实验室，北京市哲学社会科学研究基地—北京现代物流研究基地，北京高校工程研究中心—北京市高校物流工程中心，北京市协同创新中心—智能物流系统协同创新中心，是学习和科研的良好场所。\n\n1993年，学校开办国内高校第一个期货专业；1994年，开办国内高校第一个物流管理专业；2010年，开办国内高校第一个采购管理专业。目前，学校设有经济学院、物流学院、信息学院、商学院、法学院、外国语言与文化学院、国际学院、继续教育学院等8个学院，另设有马克思主义学院、体育部等教学机构。学校现有25个本科专业：经济学、国际经济与贸易、金融学、物流管理、物流工程、机械设计制造及其自动化、采购管理、质量管理工程、计算机科学与技术、信息工程、物联网工程、信息与计算科学、应用统计学、信息管理与信息系统、电子商务、会计学、财务管理、工商管理、市场营销、人力资源管理、劳动与社会保障、劳动关系、法学、英语、商务英语。\n\n学校1986年开始招收硕士研究生，目前拥有5个一级学科硕士学位授权（应用经济学、理论经济学、管理科学与工程、工商管理、计算机科学与技术），下设21个二级学科/专业；3个专业学位硕士授权类别（工程硕士、工商管理硕士、金融硕士）。学校拥有2个北京市重点建设学科。\n\n学校目前有本科生、硕士研究生、留学生等各类在校生近8000人，其中本科生6000余人，硕士研究生700余人。学校面向全国30个省、自治区、直辖市招生。几十年来，学校为国家培养了大批流通领域的高级专业人才，尤其在物流、证券期货等行业中，毕业生享有较高社会声誉。\n\n学校现有教职工694人，其中专任教师492人，教授75人，副教授195人；具有博士学位教师占专任教师比例为54%，具有硕士以上学位教师占专任教师比例为92%。享受政府特殊津贴专家10人，长城学者6人，全国五一劳动奖章获得者1人，首都劳动奖章获得者1人，北京市骨干教师和骨干人才51人，北京市青年英才和青年拔尖人才24人，北京市创新人才7人，北京市优秀人才资助8人，北京市科技新星3人，北京市高等学校教学名师10人，北京市高创名师3名，北京市高创计划青年拔尖人才2人，北京市师德先进个人3人，北京市优秀教师4人，北京市优秀教育工作者3人，北京高校优秀德育工作者12人，北京市优秀辅导员13人。\n\n学校建有国家级特色专业建设点2个，“本科教学工程”地方高校本科专业综合改革试点1个，国家级人才培养模式创新实验区1个，国家级实验教学示范中心1个，国家级大学生校外实践教育基地1个，获批北京地区高校示范性创业中心，北京市一流专业1个，北京市特色专业建设点3个，北京市高等学校实验教学示范中心2个，北京市高等学校校外人才培养基地5个，北京市高等学校示范性校内创新实践基地2个，北京市精品课程4门，北京市精品教材8部，北京高等教育精品教材建设立项6部，国家级规划教材9部，市级本科优秀教学团队5个，本科生科学研究与创业行动项目立项1698项。2008年5月，学校本科教学工作被教育部评为“优秀”。在近两届北京市教育教学成果奖评选中共获得一等奖2项，二等奖9项。\n\n学校重视学生综合素质特别是实践创新能力的培养。多年来，学校逐渐形成了培养科学精神与人文精神、发展共性与突出个性相结合，面向未来、与时俱进、丰富多彩、健康向上，既充满活力、又有深厚底蕴的校园文化。近三年，学生在全国高校商业精英挑战赛、“挑战杯”大学生课外学术科技作品竞赛、全国大学英语竞赛、全国大学生物流设计大赛、国际大学生数学建模竞赛、全国大学生数学建模与计算机应用竞赛、中国大学生iCAN物联网创新创业大赛、全国大学生创业综合模拟大赛、全国大学生物理竞赛、全国大学生艺术展演比赛、西班牙哈巴涅拉国际合唱比赛等大赛中取得骄人的成绩。\n\n学校坚持以科研促进教学，围绕建设高水平特色型大学的目标和国家物流业发展中长期规划，不断加大科研力度，在流通现代化研究等领域取得了显著成果。学校建有流通经济研究所、农业与食品物流研究所、区域经济与城市发展研究中心、物流统计研究所、电子商务研究所、期货研究所、中国盐业研究中心等研究机构。近三年来，学校承担国家自然科学基金、国家社会科学基金等国家级项目15项，省部级课题108项；出版专著、译著、教材170部；发表学术论文1239篇，其中SCI及国内权威期刊学术论文186篇；获国家授权专利157项。\n\n学校积极推进政产学研用深度合作，社会服务能力显著提升。以对外合作办公室为平台，整合校内科研力量，形成对外合作窗口。近年来，建立市级大学科技园、中关村智慧物流产业技术研究院、中关村开放实验室和中关村科技型企业创业孵化集聚区，为科技成果转化创造良好条件。积极探索校企合作新模式，成立京东电子商务学院，促进教学科研与产业需求紧密结合，提高学生实践能力与社会适应能力。积极推进与中关村国家自主创新示范区、中国物流与采购联合会为代表的行业协会、全国商务系统、期货行业系统以及地方政府在内的“五大合作”，与北京市贸促会、北京市对口支援和经济合作办公室、平谷区、中关村管委会、南通市、洛阳市、十堰市、厦门市、青岛市、赤峰市、拉萨市、三门峡市、山东省科技厅等地方政府建立战略合作关系，成立南方物流研究院、现代物流产业（华东）研究院、京津冀物流一体化研究中心、西藏现代物流研究中心、曹妃甸现代产业发展研究院、平谷现代产业发展研究院等科研合作机构，逐步形成服务地方的共赢合作模式。\n\n发展与国内外院校，尤其是与国内外著名大学及研究机构的交流合作关系是学校工作的一个重要组成部分。目前，北京物资学院已经与美国、德国、法国、英国、澳大利亚、新西兰、日本、韩国等20个国家和地区的56所大学或研究机构建立了良好的交流合作关系。在科研课题、合作办学、互派留学生、教师互访、师资培训等方面开展了一系列合作，促进了学校的学科建设以及教学和科研的发展，扩大了学校在国际上的影响力。\n\n古韵今风相辉映，文脉相承育新人。如今，北京物资学院秉承“厚德博学、笃行日新”的校训，求真务实，开拓创新，正努力建设首都乃至全国的高素质物流人才培养中心、物流理论研究中心、物流政策与决策咨询中心和物流技术创新中心，朝着建设高水平特色型大学的目标阔步前进。',NULL,'华北地区',1,NULL,'财经',1980,'北京市','否',0,0,0,0,8,0,0,646,26,5,0,0,882,0,'http://www.bwu.edu.cn/'),(117,122,'首都经济贸易大学','北京',1,'首都经济贸易大学创建于1956年，是由原北京经济学院和原北京财贸学院于1995年3月合并、组建的北京市属重点大学。建校60余年来，秉承“崇德尚能，经世济民”的校训精神，首经贸人将思想和行动凝聚为“博纳敏行，知行合一”的骆驼精神，坚持“立足北京、服务首都、面向全国、走向世界”，先后为国家和北京市培养了十八万优秀人才。首经贸校友遍布海内外，成为服务国家和北京市经济社会发展的重要力量。\n\n首经贸校本部位于丰台区花乡，以全日制本科和研究生教育为主，红庙校区位于朝阳区红庙，以留学生和成人教育为主。\n\n60余年来，学校已发展为一所以经济学、管理学为重要特色和突出优势，并拥有法学、文学、理学和工学等六大学科，各学科相互支撑、协调发展的现代化、多科性财经类大学。拥有4个博士学位授权一级学科及相应的博士后科研流动站，7个一级学科硕士学位授权点，18个专业硕士学位授权点。学校共设城市经济与公共管理学院、工商管理学院、经济学院、会计学院、劳动经济学院、文化与传播学院、信息学院、安全与环境工程学院、财政税务学院、法学院、金融学院、统计学院、外国语学院、华侨学院、马克思主义学院、国际经济管理学院、体育部、国际学院、继续教育学院等19个教学单位。\n\n学校劳动经济学获批国家级重点学科，并入选教育部“特色重点学科项目”。应用经济学、统计学获批一级学科北京市重点学科。企业管理、会计学获批二级学科北京市重点学科，管理科学与工程获批一级学科北京市重点建设学科，政治经济学等7个学科获批二级学科北京市重点建设学科。经济学、劳动与社会保障、统计学、人力资源管理等专业获批国家级特色专业，经济学、统计学、劳动与社会保障获批国家级专业综合改革试点，会计学、金融学获批市属高校首批一流专业，政治经济学、劳动经济学、社会保障学获批国家级精品课程，政治经济学、社会保障学获批国家级精品资源共享课，财务会计、国际经济学、国际商务获批国家级双语教学示范课程，人力资源管理课程群教学团队、经济学核心课程教学团队被评为国家级教学团队。经济与管理实验教学中心被评为国家高等学校实验教学示范中心，经济学国际化人才培养实验区被评为国家级人才培养模式创新实验区，会计学院德勤华永会计师事务所获批国家级大学生校外实践教育基地，《走进管理的世界》被评为国家级精品视频公开课，学校获评 “国家生态文明教育基地”。\n\n目前，学校在籍学生17576人，其中本科生10324人，硕士研究生3159人，博士研究生438人，留学生466人，普通预科生17人，成人教育学生3164人。本科生高考录取分数线、毕业生就业率、毕业生起始年薪、与专业相关度、职业发展空间等指标评分都远高于北京地区高校平均水平。\n\n学校现有教职工1446人，专职教师881人，其中，教授等正高职专业技术人员168人，副教授等副高职专业技术人员336人。博士生导师115人，硕士生导师455人。近年来，在职教师中287人次获得全国优秀教师、全国优秀科技工作者、国家级教学名师、国家百千万人才工程、国家高层次人才特殊支持计划（万人计划）、教育部新世纪人才支持计划、北京市“高创”教学名师、北京市教学名师等各类荣誉称号。学校从2006年起开展教师职业生涯规划工作，并在国内高校中率先成立了教师促进中心（OTA），是北京市最早开展教师职业生涯规划与教师职业促进的教师自治组织。\n\n近年来，学校承担国家级项目的能力大幅提升，标志性研究成果不断涌现，服务国家和首都北京的辐射力日益增强。学校特大城市经济社会发展研究院入选首批首都高端智库建设试点单位。学校拥有北京市哲学社会科学CBD发展研究基地、北京市经济社会发展政策研究基地、城市群系统演化与可持续发展的决策模拟北京市重点实验室、北京哲学社会科学国家税收法律研究基地等省部级研究机构，以及人口经济研究所、首都经济研究所等30个校级研究机构。学校主办的《经济与管理研究》是CSSCI来源期刊、全国中文核心期刊、中国人文社会科学核心期刊、RCCSE核心期刊；《人口与经济》是我国最早创刊的人口学类期刊之一，是CSSCI来源期刊、全国中文核心期刊、中国人文社会科学核心期刊、国家社会科学基金资助的200个重要期刊之一。\n\n学校与33个国家和地区的139所大学及研究机构签署校际合作协议。学校自1986年开始招收留学生，现已发展形成多层次、多科性的国际人才培养体系，学生类别包含博士研究生、硕士研究生、本科生、高级进修生、普通进修生、语言生和各类短期生等。学校于2007年开办全英文授课硕士班，2011年开办全英文授课博士班。目前有来自94个国家和地区的留学生在校就读，留学生规模达910人。\n\n学校将继续以培养适应当代经济和社会发展需要、德智体全面发展、理论基础扎实、知识面较宽、富有创新精神和实践能力的高素质应用型、创新型人才为目标，朝着建成“现代化、国际化、多科性、有特色的国内一流、国际知名财经大学”的目标开拓奋进。','CUEB','华北地区',1,'北京经济学院、北京财贸学院','财经',1956,'北京市','是',0,0,0,0,19,0,0,881,44,18,4,0,260,0,'http://www.cueb.edu.cn/'),(91,124,'中央民族大学','北京',1,'中央民族大学（Minzu University of China）坐落于北京学府林立的海淀区，南邻国家图书馆，北依中关村科技园，校园环境典雅，古朴幽美，人文氛围浓郁，具有鲜明的民族特色。\n\n中央民族大学是党和国家为解决中国民族问题、培养少数民族干部和高级专门人才而创建的一所具有鲜明特色的高等学校。学校前身是1941年成立的延安民族学院。新中国成立后，经中央政府批准，1951年在北京成立了中央民族学院，乌兰夫任首任院长；1993年11月，更名为中央民族大学；1999年、2004年学校先后进入国家“211工程”、“985工程”；2002年，国家民委、教育部、北京市人民政府签署了《关于重点共建中央民族大学的协议》，加快了学校改革和发展的步伐；2017年顺利进入“双一流”A类高校建设行列。现任校党委书记张京泽、校长黄泰岩。\n\n党和国家历届领导集体始终非常关心和重视中央民族大学的建设和发展。毛泽东、邓小平等老一辈党和国家领导人先后17次接见学校师生代表。1993年学校更名为中央民族大学时，江泽民同志为学校题写了新校名。2001年6月，朱镕基同志到校视察，并代表中央政府提出了“把中央民族大学建成世界一流的民族大学”的奋斗目标。同年，胡锦涛、李岚清等领导同志亲自审批了国家民委、教育部、国家计委、财政部四部委联合上报的《关于进一步办好中央民族大学的请示》。2009年，习近平同志在视察学校工作时指出：“中央民族大学在我国高等教育体系和民族团结进步事业中具有重要地位，为国家的民族团结进步事业，为民族地区经济社会文化建设做出了重要贡献。”经过70多年的发展建设和几代人的不懈努力，学校已经建设成为培养少数民族优秀人才的重要基地、民族理论研究和民族问题决策咨询的重要基地、传承和弘扬各民族优秀文化的重要基地。\n\n学校建校之初，一批学界名流，如潘光旦、吴文藻、费孝通、翁独健、吴泽霖、冯家昇、杨成志、林耀华、傅乐焕、于道泉、闻宥、马学良、王锺翰等到校任教，成为学校教学科研和学科建设的重要奠基人。现有教职工总数为1834人，其中专任教师1114人，专任教师中教授、副教授641人，占专任教师总数的57.5%。他们当中有戴庆厦、牟钟鉴、胡振华、施正一、刘秉江、马跃、糜若如、李魁正、杨圣敏、金炳镐、薛达元等知名专家学者，还有不断涌现的诸多中青年新秀，已渐成学界翘楚。另外，学校每年都聘有130多名外籍专家任教。学校目前有教育部“长江学者奖励计划”特聘教授3人、教育部“长江学者奖励计划”青年学者等高层次人才63人，国家民委突出贡献专家8人。国家级教学名师2人，北京市教学名师16人，享受国务院政府特殊津贴21人。学校与44个国家和地区的200余所大学和科研机构建立了交流合作关系，包括美国耶鲁大学、英国剑桥大学、牛津大学、德国柏林洪堡大学、荷兰莱顿大学等30所世界顶级和一流大学，每年都有一批在相关学科领域有重要影响的著名学者来校访问讲学、交流。\n\n学校现有25个学院，有覆盖10个学科门类的65个本科专业、5个一级学科博士学位授权点、5个博士后科研流动站，27个一级学科硕士学位授权点，国家级重点学科3个、省部级重点学科一级5个、二级17个，省部交叉重点学科1个，2个国家文科基础学科人才培养和科学研究基地， 1个教育部人文社会科学重点研究基地，1个教育部重点实验室和1个北京市工程技术研究中心等近40个省部级设立或委托建设的科研平台，1个国家级实验教学示范中心，5个北京市实验教学示范中心。2018年学校被北京市教委批准为北京高校大学生创业园。\n\n学校面向全国31个省、自治区、直辖市和台港澳地区招生，现有全日制在校生16456人，其中本科生11248人，硕士、博士研究生5026人，少数民族预科生182人（本校）；本科生（含预科生）中，少数民族学生比例为44.6%。学校是“中国政府奖学金”留学生接收学校，有来自50多个国家的近700名留学生在校学习。建校以来，学校为国家输送了10万余名各民族毕业生。其中有知名专家学者近千名，少数民族省部级领导干部近百名，地厅级领导干部近千名，县处级领导干部万余名，他们在不同的岗位上为国家的民族团结与发展事业做出了突出的贡献。中央民族大学已成为培养少数民族杰出人才和民族团结表率的摇篮。\n\n学校现占地面积为38万平方米，校园建筑面积59.2万平方米，教学行政用房20余万平方米，资产总值34亿元。新校区位于北京丰台区青龙湖畔，占地面积81万平方米，目前正在如火如荼建设中，计划2020年9月第一批学生正式入驻。学校拥有各类图书和电子图书480万余册，民族学、中国少数民族语言文学图书文献资料藏量居国内高校前列。学校出版社是全国民族学科教材和著作的出版基地，《中央民族大学学报》和《民族教育研究》是面向国内外发行的全国核心期刊。民族博物馆藏有3万余件珍贵的民族文物，已经成为学生爱国主义教育和教学实践基地。\n\n当前，全校师生员工高举习近平新时代中国特色社会主义思想伟大旗帜，深入贯彻落实党的十九大精神，不忘初心，牢记使命，服务于国家战略和民族团结进步事业，服务于少数民族和民族地区发展，全面推进实施质量立校、人才强校、特色兴校、管理强校和开放办校五大战略，以改革创新精神和攻坚克难的勇气，奋力推进“双一流”建设。','MUC','华北地区',1,'中央民族学院','民族',1941,'国家民委','否',985,211,1,1,24,0,66,1114,65,35,5,1,60,0,'http://www.muc.edu.cn/'),(55,125,'中国政法大学','北京',1,'中国政法大学是一所以法学学科为特色和优势，兼有政治学、经济学、管理学、文学、历史学、哲学、教育学、理学、工学等学科的“211工程”重点建设大学，“‘985工程’优势学科创新平台”、“2011计划”和“111计划”（高校学科创新引智计划）重点建设高校，国家“双一流”建设高校，直属于国家教育部，正致力于建设世界一流大学和一流学科。现有海淀区学院路和昌平区府学路两个校区。\n\n学校的前身是1952年由北京大学、清华大学、燕京大学、辅仁大学四校的法学、政治学、社会学等学科组合而成的北京政法学院，毛泽东同志亲笔题写了校名。学校最初在北京沙滩红楼办学，1954年迁址至学院路。1960年成为国家确定的全国重点高校。“文革”中学校停办，1978年复办。1983年，北京政法学院与中央政法干校合并，组建成立中国政法大学，邓小平同志亲笔题写了校名。学校形成一校及本科生院、研究生院、进修生院三院办学格局。1985年，学校开辟昌平校区。进修生院后更名为中央政法管理干部学院单独办学，2000年复并入中国政法大学。\n\n学校被誉为“中国法学教育的最高学府”“中国人文社会科学领域的学术重镇”。在60多年的办学历程中，学校为国家培养了各类优秀人才20多万人。学校是国家法学教育和法治人才培养的主力军，参与了自建校以来几乎国家的所有立法活动，引领国家法学教育的创新、法学理论的革新和法治思想的更新，代表国家对外进行法学学术和法治文化交流。同时，学校多学科和跨学科的人才培养模式也为社会输送了一大批人文社会科学高级专门人才，成为国家政治、经济、社会、文化等领域人才培养的生力军。2017年5月3日，中共中央总书记、国家主席、中央军委主席习近平莅临中国政法大学考察并就全面依法治国、法治人才培养和青年成长成才发表重要讲话，为中国政法大学的建设发展指明了前进的方向。\n\n学校现有在校生17233人，其中本科生9488人，研究生6905人，港澳台及外国留学生840人；教师912人，教学科研岗教师中教授319人，博士生导师197人、硕士生导师663人，博士学位和硕士学位获得者占比达90.90%。\n\n学校设有法学院、民商经济法学院、国际法学院、刑事司法学院、政治与公共管理学院、商学院、人文学院、外国语学院、马克思主义学院、社会学院、光明新闻传播学院、中欧法学院、法律硕士学院、国际儒学院、国际教育学院/港澳台教育中心、继续教育学院/网络教育学院、科学技术教学部/法治信息管理学院、体育教学部共18个教学单位。另设有11个在编科研机构、10个新型研究机构、3个协同创新中心。其中，2个教育部人文社会科学重点研究基地（诉讼法学研究院、法律史学研究院），1个教育部重点实验室（证据科学研究院），1个北京市哲学社会科学研究基地（法治政府研究院），1个国家高端智库建设培育单位（人权研究院），1个国家人权教育与培训基地，2个教育部“高等学校学科创新引智计划”创新引智基地，1个教育部青少年法制教育研究基地，1个教育部教师法治教育研究中心、1个教育部全国法学教师培训基地，1个教育部全国高等院校古籍整理研究工作委员会直接联系单位。由中国政法大学牵头组建的“司法文明协同创新中心”是首批经教育部、财政部认定的14个国家“2011计划”协同创新中心之一，学校参与组建的“国家领土主权与海洋权益协同创新中心”成为第二批获得认定的24个国家“2011计划”协同创新中心之一,学校牵头组建的“马克思主义与全面依法治国协同创新中心”被认定为北京高校中国特色社会主义理论研究协同创新中心。\n\n学校设有法学、侦查学、政治学与行政学、行政管理、国际政治、公共事业管理、工商管理、经济学、国际商务、金融工程、哲学、汉语言文学、汉语言、思想政治教育、社会学、社会工作、应用心理学、英语、德语、翻译、新闻学、网络与新媒体、数学与应用数学、信息管理与信息系统（法治信息管理）共24个本科专业，其中法学、政治学与行政学、社会学为国家级特色专业。学校拥有37个博士学位授权点、77个硕士学位授权点、8个专业硕士学位授权点和3个博士后科研流动站。法学、政治学、马克思主义理论、理论经济学为博士学位授权一级学科，哲学、应用经济学、社会学、心理学、外国语言文学、新闻传播学、中国史、工商管理、公共管理为硕士学位授权一级学科，其中，法学为一级学科国家重点学科，政治学为一级学科北京市重点学科。在第四轮学科评估中，学校9个一级学科参评并全部上榜，其中法学进入A+档，并列全国第一，政治学排名全国第八，社会学、哲学、马克思主义理论等学科取得较好成绩。\n\n学校先后与53个国家和地区的258所知名大学、科研机构和国际组织建立了合作交流关系，其中有实质合作的世界五百强高校占比60％。每年通过各类合作交流项目派出千余名师生赴境外交流学习，聘请数百名长短期外国专家来校讲学。2008年建立的中国政法大学中欧法学院是中国政府和欧盟在法学教育领域最大的合作项目。学校从2009年开始全面实施国际化发展战略，不断提升国际化办学水平，学校培养国际型人才的格局已经初步形成。2012年以来，学校先后在英国、罗马尼亚、巴巴多斯、挪威共建4所海外孔子学院。\n\n学校的校训是：厚德、明法、格物、致公。\n\n学校将遵循国家教育方针和高等教育规律，弘扬传统，与时俱进，努力办成开放式、国际化、多科性、创新型的世界一流法科强校。','CUPL','华北地区',1,'北京政法学院','政法',1952,'教育部','是',0,211,0,1,18,0,3,968,24,78,34,0,2127,0,'http://www.cupl.edu.cn/'),(19,126,'南开大学','天津',8,'南开大学（Nankai University，NKU），简称“南开”，由中华人民共和国教育部直属，中央直管副部级建制，位列国家首批211工程、985工程、世界一流大学和一流学科建设高校，入选首批2011计划、111计划、珠峰计划、卓越法律人才教育培养计划、国家建设高水平大学公派研究生项目、教育部来华留学示范基地，为中国政府奖学金来华留学生接收院校、全国深化创新创业教育改革示范高校，学科门类覆盖文、史、哲、经、管、法、理、工、农、医、教、艺等，是“学府北辰”之一。南开大学由严修、张伯苓秉承教育救国理念创办，肇始于1904年，正式成立于1919年，1937年校园遭侵华日军炸毁而南迁，与北京大学、清华大学在湖南长沙合组国立长沙临时大学，1938年迁往云南昆明，长沙临时大学更名为国立西南联合大学；1946年回天津复校并改为国立；新中国成立后，经历高等学校院系调整，成为文理并重的全国重点大学；改革开放以来，天津对外贸易学院、中国旅游管理干部学院相继并入。','NKU','华北地区',1,'','综合',1919,'教育部','是',985,211,1,5,26,0,42,2046,86,231,172,2,0,0,'http://www.nankai.edu.cn/'),(185,127,'天津科技大学','天津',8,'天津科技大学（Tianjin University of Science&Technology）位于渤海之滨、海河之畔，入选教育部“卓越工程师教育培养计划”高校、国家建设高水平大学公派研究生项目、国家“特色重点学科项目”建设高校、国家级新工科研究与实践项目（卓越工程师教育培养计划2.0）、国家级大学生创新创业训练计划、中国政府奖学金来华留学生接收院校，CDIO工程教育联盟成员单位，是中央和地方共建、天津市重点建设的以工为主的多科性大学。学校建于1958年，时名河北轻工业学院，隶属于国家轻工业部,是国家第一批建立的4所轻工类本科院校之一。1968年，学校改名为天津轻工业学院。1998年,学校隶属关系由国家轻工业部划转至天津市。2002年,经教育部批准，学校更名为天津科技大学。','TUST','华北地区',1,'天津轻工业学院','理工',1958,'天津市','是',0,0,0,0,18,0,3,2000,66,74,17,0,0,0,'http://www.tuli.edu.cn/'),(150,128,'天津工业大学','天津',8,'天津工业大学是中华人民共和国教育部与天津市共建高校，天津市属高校中唯一国家国防科技工业局和天津市人民政府共建高校，世界一流学科建设高校、天津市重点建设的全日制普通高等学校，入选国家卓越工程师教育培养计划、国家建设高水平大学公派研究生项目、国家“特色重点学科项目”、中国政府奖学金来华留学生接收院校、国家“111计划”、国家级大学生创新创业训练计划。学校最早的学科始建于1912年的北京工业专门学校机织科，先后更名为北平大学工学院机织工程系、西北联合大学西北工业学院纺织系、国立北洋大学纺织系、天津大学纺织系；1952年四川乐山技艺专科学校棉纺和棉织两专修科并入天津大学纺织系；1958年，天津大学纺织系、天津纺织工业学校合并成立河北纺织工学院；1968年，河北纺织工学院更名为天津纺织工学院；2000年，天津纺织工学院与天津经济管理干部学院合并组建天津工业大学；2004年，天津工业职业技术学院并入天津工业大学。','TGU','华北地区',1,'','理工',1912,'天津市','是',0,0,0,1,20,0,4,1600,64,74,14,1,0,0,'http://www.tjpu.edu.cn/'),(217,129,'中国民航大学','天津',8,'中国民航大学是中国民用航空局直属的一所以培养民航高级工程技术和管理人才为主的高等学府，是中国民用航空局、天津市人民政府、教育部共建高校，是教育部第二批“卓越工程师教育培养计划”高校、天津市高水平特色大学建设高校，入选“新工科”研究与实践项目，CDIO工程教育联盟成员单位。中国民航大学前身是1951年9月成立的军委民航局第二民用航空学校，1956年更名为中国人民解放军第十四航校，1958年更名为中国民用航空高级航空学校，1963年重组为中国民用航空机械专科学校，1977年更名为中国民用航空专科学校，1981年更名为中国民用航空学院，2006年5月30日，更名为中国民航大学。','CAUC','华北地区',1,'中国民用航空学院','理工',1951,'交通运输部','否',0,0,0,0,19,0,0,1900,31,62,0,0,0,0,'http://www.cauc.edu.cn/'),(176,130,'天津理工大学','天津',8,'天津理工大学是一所以工为主，工理结合，工、理、管、文、艺等多学科发展的天津市市属普通高等学校。学校是卓越工程师教育培养计划高校，入选高等学校学科创新引智计划（111计划）、中国政府奖学金来华留学生接收院校、首批“新工科”研究与实践项目。学校前身是天津理工学院，于1979年以天津大学理工分校名义开始招收本科生，1981年经国务院批准正式设立。1996年，原天津理工学院与原天津理工学院一分院（天津大学机电分校）、天津理工学院二分院（天津大学分校）、天津大学冶金分校合并，组建新的天津理工学院。2004年，经教育部批准，天津理工学院更名为天津理工大学。','TUT','华北地区',1,'天津理工学院','理工',1979,'天津市','是',0,0,0,0,17,0,0,1419,65,69,12,0,0,0,' http://www.tjut.edu.cn/'),(317,131,'天津农学院','天津',8,'天津农学院（Tianjin Agricultural University）始建于1976年，是天津市属唯一的一所高等农业类院校；是国家“973计划”、“863计划”承担高校；入选国家首批卓越农林人才教育培养计划、国防教育特色学校； [1]  是以农科为主体和优势，农学、工学、管理学、理学、经济学、文学协调发展的都市型现代农业院校。天津农学院始建于1976年。2002年经天津市政府批准，天津市城乡经济学校并入天津农学院。1982年获学士学位授予权，2006年获硕士学位授予权。1996年首批通过教育部本科教学合格评价，2007年在教育部进行的本科教学工作水平评估中获得“优秀”。','','华北地区',1,'','农林',1976,'天津市','是',0,0,0,0,16,0,0,960,45,15,0,0,0,0,'http://www.tjac.edu.cn/'),(62,132,'天津医科大学','天津',8,'天津医科大学（Tianjin Medical University），简称“TMU”，为天津市人民政府、国家卫生健康委员会和教育部共建高校，是国家“211工程”重点建设院校、“双一流”世界一流学科建设高校、入选“2011计划”牵头高校、国家建设高水平大学公派研究生项目高校、教育部来华留学示范基地建设高校、中国政府奖学金来华留学生接收院校、卓越医生教育培养计划试点高校、新工科研究与实践项目实施高校。天津医科大学前身天津医学院建于1951年。1984年6月，以天津医学专科学校为基础建立天津第二医学院。1993年，天津市市委、市政府决定天津医学院与天津第二医学院合并组建天津医科大学，同年12月得到国家教委批准。1996年，学校进入国家“211工程”重点建设高校行列。2017年9月，学校入围首批国家“双一流”世界一流学科建设高校名单。 ','TMU','华北地区',1,'','医科',1951,'天津市','是',0,211,0,1,19,0,6,8429,21,68,44,0,0,0,'http://www.tijmu.edu.cn/'),(179,133,'天津中医药大学','天津',8,'天津中医药大学（Tianjin University of Traditional Chinese Medicine）位于天津市，简称天中，是教育部高等学校中医学、医学人文素质教学指导委员会主任委员单位，世界中医药学会联合会教育指导委员会主任委员单位，世界一流学科建设高校，教育部、国家中医药管理局、天津市政府共建高校，为世界一流中医药大学建设联盟成员，入选“卓越医生教育培养计划”改革试点高校、中国政府奖学金来华留学生接收院校、国家建设高水平大学公派研究生项目、国家“特色重点学科项目”建设高校、国家级大学生创新创业训练计划、国家级新工科研究与实践项目。天津中医药大学创建于1958年，原名天津中医学院，是中国最早建立的中医高等院校之一，1992年，经国家教委批准，加挂中国传统医药国际学院校牌。2006年，正式更名为天津中医药大学。2017年，中药学科入选教育部“双一流学科建设学科”名单，并为天津市“双一流建设高校”。','TUTCM','华北地区',1,'天津中医学院','医科',1958,'天津市','否',0,0,0,1,12,0,1,3590,24,25,20,0,0,0,'http://www.tjutcm.edu.cn/'),(145,134,'天津师范大学','天津',8,'天津师范大学（Tianjin Normal University），简称“天师大”，坐落于中国历史文化名城、直辖市天津。入选教育部“卓越教师培养计划”、国家“特色重点学科项目”、”国家建设高水平大学公派研究生项目“、首批“新工科研究与实践项目”、“国家级大学生创新创业训练计划”、首批“教育部来华留学示范基地”、“中国政府奖学金来华留学生接收院校”，“中日人文交流大学联盟”创始成员 [1]  ，是天津市属重点大学。学校的办学目标是将天津师范大学建设成为国内一流的教师教育特色综合性大学。天津师范大学始建于1958年，原名天津师范学院，1961年天津工农师范专科学校并入天津师范学院。1982年学校更名为天津师范大学。1999年4月，原天津师范大学、天津师范高等专科学校、天津教育学院合并组建新天津师范大学。','TNU','华北地区',1,'','师范',1958,'天津市','是',0,0,0,0,22,0,0,2343,70,41,6,0,0,0,'http://www.tjnu.edu.cn/'),(244,135,'天津外国语大学','天津',8,'天津外国语大学（Tianjin Foreign Studies University，TFSU）坐落在天津，是天津市一所主要外语语种齐全，文学、经济学、管理学、法学、教育学、艺术学、工学等多学科协调发展的天津市属重点院校。天津外国语大学前身之一是始建于1921年的天津工商大学，主体前身是1964年成立的秦皇岛外语专科学校，后更名为河北外国语专科学校、天津外国语专科学校。1974年，学校恢复并改名为天津外国语学院，后经教育部批准更名为天津外国语大学','TFSU','华北地区',1,'天津外国语学院','语言',1921,'天津市','是',0,0,0,0,15,0,0,601,57,26,1,0,0,0,'http://www.tjfsu.edu.cn/'),(358,136,'天津商业大学','天津',8,'天津商业大学（Tianjin University of Commerce），简称TUC，是一所以商学为主干，管、经、工、法、文、理、艺多学科相互支撑、协调发展的高等学校，是天津市属重点大学，国家首批卓越农林人才教育培养计划改革试点高校，入选国家级大学生创新创业训练计划、教育部首批新工科研究与实践项目。天津商业大学的前身是天津商学院，由原国家商业部和天津市人民政府于1980年创建。1998年，实行中央与地方共建、以地方管理为主的管理体制。2007年，天津商学院更名为天津商业大学。','TUC','华北地区',1,'天津商学院','财经',1980,'天津市','否',0,0,0,0,14,0,0,956,54,66,0,0,0,0,'http://www.tjcu.edu.cn/'),(190,137,'天津财经大学','天津',8,'天津财经大学（Tianjin University of Finance and Economics，TUFE），位于天津市，是天津市属重点大学和中国北方培养高级经济管理人才的重要基地，是一所以经济学和管理学为主干，兼有法学、文学、理学、工学、教育学、艺术学等学科门类交叉渗透、协调发展的多科性大学。入选国家“特色重点学科项目”建设高校、国家建设高水平大学公派研究生项目、国家级大学生创新创业训练计划。学校始建于1958年，是以南开大学经济类专业为基础组建的，原名为河北财经学院，1969年10月1日划归天津市领导，更名为天津财经学院。2004年5月17日，经国家教育部批准，更名为天津财经大学。','TUFE','华北地区',1,'天津财经学院','财经',1958,'天津市','否',0,0,0,0,14,0,0,742,49,66,24,0,0,0,'http://www.tjufe.edu.cn/'),(276,139,'河北工程大学','河北',2,'河北工程大学是河北省重点骨干大学，河北省人民政府与水利部共建高校，河北省国家一流大学建设二层次高校，河北省文明单位，坐落在中国历史文化名城、“成语典故之都”邯郸市，具有学士、硕士、博士多层次人才培养体系，CDIO工程教育联盟成员单位。学校前身是2003年4月由原河北建筑科技学院（含2002年4月并入的华北水利水电学院邯郸分部）、邯郸医学高等专科学校、邯郸农业高等专科学校合并组建而成的河北工程学院。拥有河北省唯一的服务国家特殊需求博士人才培养项目——水资源水环境调控及综合管理和河北省唯一的水利工程博士后科研流动站。','HUE','华北地区',1,'河北工程学院','理工',1952,'河北省','是',0,0,0,0,11,3,1,2180,85,41,1,0,0,0,'http://www.hebeu.edu.cn/'),(378,140,'河北地质大学','河北',2,'河北地质大学位于河北省石家庄市，是原地质部直属五大院校之一，是自然资源部与河北省人民政府共建高校，河北省重点骨干大学。学校建于1953年，由李四光、何长工选址塞外古城宣化成立中央人民政府地质部宣化地质学校；1955年更名为地质部宣化地质学校；1970年更名为河北省宣化地质学校；1971年更名为河北地质学院；1985年6月，从宣化迁至河北省省会石家庄办学；1996年5月，更名为石家庄经济学院；2016年3月，定名为河北地质大学。 [2]  2016年，学校成为博士学位授权立项建设单位。','','华北地区',1,'河北地质学院','财经',1953,'河北省','否',0,0,0,0,15,0,0,912,64,9,0,0,0,0,'http://www.sjzue.edu.cn/'),(241,141,'华北水利水电大学','河南',10,'华北水利水电大学(North China University of Water Resources and Electric Power)占地面积2335亩,学校建有花园校区(河南省郑州市北环路36号)和龙子湖校区(河南省郑州市金水东路136号)，是水利部与河南省共建、以河南省管理为主的高校，是河南省重点支持建设的骨干高校，是“金砖国家网络大学”中方高校牵头单位。\n\n学校起源自1951年创建于北京的中央人民政府水利部水利学校，1954年更名为水利部北京水利学校。1958年北京水力发电学校、北京水力发电函授学院并入，成立北京水利水电学院。1969年迁至河北省磁县岳城水库办学，1971年更名为河北水利水电学院。1977年迁至河北省邯郸市办学，1978年更名为华北水利水电学院。1990年迁至河南省郑州市办学，2000年整建制由水利部划转河南省管理。2009年水利部与河南省政府签署共建华北水利水电学院战略协议。2013年更名为华北水利水电大学。\n\n华北水利水电大学缘水而生、因水而存、籍水而兴，伴随着新中国水利水电建设事业而发展壮大。学校曾经三次搬迁，经历了多年边建校、边搬迁、边办学的艰苦卓绝历程，从北京到河北，最后到河南，筚路蓝缕、矢志不移，主动服务区域经济社会发展和国家水利电力事业，形成了“育人为本、学以致用”的办学理念和“情系水利、自强不息”的办学精神。学校设有水利学院、电力学院等23个教学单位，黄河科学研究院、水文化研究中心等98个研究机构。水利部水务培训中心、水利部电大开放教育办公室设在学校。学校是国家首批硕士学位授予权单位，是博士学位授予权单位，是教育部第二批“卓越工程师教育培养计划”高校。2012年，学校入选中西部高校基础能力建设工程高校。2016年获批水利部“国家水情教育基地”。学校以国家“双一流”建设和河南省高校分类发展为契机，立足河南，面向全国，持续巩固提升本科教育基础地位，积极发展研究生教育，大力拓展国际合作办学，为区域经济社会发展和国家水利电力事业提供有力的人才资源和智力支撑。\n\n学科专业布局合理。经过68年的建设与发展，学校已成为以水利电力为特色,工科为主干，理、工、管、农、经、文、法、艺等多学科协调发展的大学。现有66个全日制本科专业，包括9个国家级、省级卓越计划建设专业，15个国家级、省级综合改革试点专业，17个国家级、省级特色专业建设点。拥有14个省级一级重点学科，3个博士学位授权一级学科,19个硕士学位授权一级学科，10个专业硕士学位类别，其中有8个工程硕士领域。\n\n人才培养成绩斐然。学校面向全国31个省、自治区、直辖市及港澳台地区招生。现有全日制在校本科生和硕士、博士研究生、外国留学生32000余人。建校以来，学校为国家培养了近30万名高级专业技术人才和管理人才，包括国家部委部长、武警水电部队将军和省部级领导近20名，全国道德模范获得者吴新芬，第五届全国道德模范提名奖、全国优秀大学生、全国优秀共青团员等十余项荣誉称号的孟瑞鹏，“雨果奖”获得者刘慈欣，组建“中国蓝天救援队”的安少华等各领域杰出校友，彰显了学校“下得去，吃得苦，留得住，用得上，干得好”的人才培养特色。\n\n师资队伍不断壮大。着力实施人才强校战略，加大师资队伍建设力度，特聘中国工程院院士王复明、王浩、周丰峻、王光谦、夏军、姚建铨等为我校双聘院士。学校现有教职工2300人，其中专职教师1600人，具有正高级职称教师230人，具有博士学位教师680人。国家百千万人才工程入选者3人，国家千人计划专家3人，全国模范教师3人，省优秀专家、省学术技术带头人、省级教学名师、模范教师和优秀教师42人。\n\n科学研究成果丰硕。近五年来，学校主持、参与各类研究项目2300余项，其中主持国家自然科学基金和国家社会科学基金123项。主持参与国家项目23项，省级项目538项，合同经费100万以上横向项目12项，累计到款科研经费2.7亿多元，发表核心期刊学术论文3100余篇，其中被SCI、SSCI、CSSCI、EI等索引收录近2000余篇；出版学术著作700余部。获各级各类科研奖励580余项，其中，国家科技进步奖1项，省部级科研奖励94余项，获奖层次和数量稳居河南省高校前列。\n\n社会服务成效显著。学校在社会服务方面取得了卓越成就，在国内同行业中享有较高的知名度和影响力。为水利电力行业和地方经济建设培训专业技术人才30万余人，培训监理人员6万余人。校属企业河南华北水利水电监理有限公司承担的东江-深圳供水工程获得广东省科技进步特等奖和国家优质工程奖；勘察设计研究公司负责设计的膜结构海南三亚会展中心，得到国内外同行的高度评价；承担100余项科研开发项目，其中“地坑院”项目成功入选国家非物质文化遗产名录。2008年汶川地震后，学校选派10名专家，高质量地指导完成了救灾一线中小型病险水库除险加固工作，成为江油市重建样板工程之一。\n\n国际合作前景广阔。学校国际合作历史悠久，1958年起开始接收留学生，对口援建越南水利水电大学。近年来，学校更加注重国际合作交流，实施开放活校战略，不断提升办学实力和国际化水平，相继与澳大利亚斯威本科技大学、法国尼斯大学、英国提赛德大学、韩国启明大学、俄罗斯乌拉尔联邦大学等世界高水平大学签署合作协议，开展中外合作办学。积极融入金砖国家高等教育交流合作体系，成为“金砖国家网络大学”水资源与污染治理、能源两个领域的牵头高校。与俄罗斯乌拉尔联邦大学设立了“金砖国家网络大学·金砖国家大学联盟水工程与能源研究中心”，与俄罗斯圣彼得堡国立交通大学联合设立了中俄高铁研究中心，与韩国仁荷大学共同设立了物流工程研究中心。金砖国家网络大学体系下第一个合作办学机构-华北水利水电大学乌拉尔学院获教育部批准，并实现正式招生；成为河南省教育对外开放的重大突破性成果。\n\n校园文化丰富多彩。学校十分重视大学生艺术教育，连续四届被评为“河南省艺术教育一类院校”，连续11届获“河南省大学生科技文化艺术节优秀组织单位”，2015年被授予“全国五四红旗团委”。2016年学校两个团支部荣获全国高校“活力团支部”，校团委荣获了团中央暑期社会实践“镜头中的三下乡”活动优秀组织单位等荣誉称号。2017年校团委荣获2017年全国大中专学生志愿者暑期“三下乡”社会实践活动优秀单位、“丝路新世界-青春中国梦”全国大学生暑期社会实践专项活动优秀组织单位、河南省“出彩中原·教育脱贫攻坚革命老区行”博士生社会实践活动先进集体、2017年十三届“挑战杯”河南省大学生课外学术科技作品竞赛优胜杯、2017年河南省“一带一路新道路，同筑青春中国梦”主题演讲比赛优秀组织单位、2017年度河南省铁路春运志愿服务先进集体等荣誉称号。组织申报的《凝聚榜样力量  汇集青春能量——颂学瑞鹏主题教育活动》荣获河南省首届校园文化建设优秀成果一等奖。2018年校团委荣获全国大学生社会实践活动优秀单位。\n\n社会声望持续提升。学校先后被授予“全国水利科技先进集体”、“全国高等院校本科教学工作水平评估优秀单位”、“全国教育系统先进集体”、“全国高校毕业生就业工作先进集体”、“全国教育系统纪检监察先进单位”等荣誉称号。连续三届被评为“省级文明单位”，并被评为“省级文明单位标兵”。近年来先后荣获“最具就业竞争力的10张河南教育名片”、“全国大学生就业最佳企业评选优秀组织高校奖”和“全国毕业生就业50所典型经验高校”等荣誉。在第三方权威性数据机构（麦可思）发布的2013年度《河南省高校毕业生就业、预警和重点产业人才供应》报告中，学校位居河南省高校就业竞争力第一名。2014年，根据国家统计局调查显示，用人单位对参与调查的71所本科院校毕业生满意度测评，学校排名第一。2015年，学校被确定为“河南省首批大学生创业示范基地”，获得“河南省创新创业示范基地”荣誉称号。2016年被评为河南省“最具就业竞争力的十佳典范高校”和“最具品牌影响力的典范高校”。入围“2017年全国高校毕业生就业竞争力100强”。新媒体中心荣获河南省十佳豫青新媒体工作室称号。\n\n面向未来，我校以习近平新时代中国特色社会主义思想为指导，认真学习宣传贯彻党的十九大精神。全面落实党的教育方针，以区域经济社会发展和国家水利电力事业需求为导向，以高等教育综合改革为动力，以立德树人为根本任务，加强社会主义核心价值观教育，着力实施“质量立校、人才强校、特色兴校、开放活校”战略，持续提高人才培养质量、科学研究水平、服务社会能力、文化传承创新能力和国际交流合作水平，努力建设特色鲜明的高水平水利水电大学，为区域经济社会发展和国家水利电力事业做出更大的贡献。','NCWU','华中地区',4,'北京水利学校、河北水利水电学院、华北水利水电学院','理工',1951,'河南省','是',0,0,0,0,23,0,0,2386,64,19,3,0,4055,0,'http://www.ncwu.edu.cn/'),(106,142,'河北工业大学','河北',2,'河北工业大学（Hebei University of Technology）坐落于中国直辖市天津，由中华人民共和国教育部与河北省人民政府、天津市人民政府共建，是隶属于河北省的一所国家世界一流学科建设高校、国家“211工程”重点建设高校，入选中西部高校基础能力建设工程”、卓越工程师教育培养计划、河北省国家一流大学建设工程、天津市高水平特色大学建设项目、国家建设高水平大学公派研究生项目、中国政府奖学金来华留学生接收院校、国家级新工科研究与实践项目、首批高等学校科技成果转化和技术转移基地，CDIO工程教育联盟成员单位。','HEBUT','华北地区',1,'河北工学院','理工',1903,'河北省','是',0,211,1,1,18,0,0,2700,73,128,42,0,0,0,'http://www.hebut.edu.cn/'),(211,143,'华北理工大学','河北',2,'华北理工大学（North China University of Science and Technology），简称华北理工，学校位于河北省唐山市，由始建于1958年的原河北理工大学和始建于1926年的原华北煤炭医学院合并组建而成。2010年5月，两校正式合并组建为河北联合大学，2015年2月，国家教育部批准河北联合大学更名为华北理工大学。学校前身为创办于1895年的北洋西学学堂矿务学学门，是一所以工、医为主，理、经、管、文、法、艺多学科协调发展，具有留学生教育、研究生教育、本科教育、继续教育等全方位教育层次的省属重点骨干大学。学校是国家安全生产监督管理总局、国家国防科技工业局与河北省人民政府三方共建高校，是第二批卓越工程师教育培养计划，第一批卓越医生教育培养计划项目试点高校，是首批国家“卓越工程师教育培养计划2.0”、“新工科研究与实践项目”入选高校。','NCUST','华北地区',1,'河北联合大学','综合',2010,'河北省','是',0,0,0,0,28,0,0,1000,94,27,3,0,0,0,'http://www.ncst.edu.cn'),(200,144,'河北科技大学','河北',2,'河北科技大学（Hebei University of Science & Technology）坐落于河北省会石家庄市，是一所成立于1996年5月的地方一流教学研究型大学，学校为教育部第二批卓越工程师教育培养计划高校，入选国家级大学生创新创业训练计划、全国高校实践育人创新创业基地、全国深化创新创业教育改革示范高校、教育部首批新工科研究与实践项目（卓越工程师教育培养计划2.0），国家国防科技工业局与河北省人民政府共建的省部共建大学。工程学学科位于ESI世界排名前1%。','HEBUST','华北地区',1,NULL,'理工',1956,'河北省','是',0,0,0,0,15,0,0,2300,79,17,0,0,0,0,'http://www.hebust.edu.cn/'),(111,145,'河北医科大学','河北',2,'河北医科大学（Hebei Medical University），简称“河北医大”，位于河北省省会石家庄市，由河北省人民政府、国家卫生和计划生育委员会和教育部三方共建， [1]  入选国家“中西部高校基础能力建设工程”、“卓越医生教育培养计划”、”国家建设高水平大学公派研究生项目“、国家“特色重点学科项目”建设高校，是河北省重点骨干大学；现已成为集教学、科研、医疗为一体的综合性医科大学。据2019年6月学校官网显示，学校占地面积22.39万平方米，建筑面积36.12万平方米；全校系统共有教职工15000余人（含直属附属医院）；学校有两个校区、五所直属附属医院，下设18个二级学院，24个本科专业；全日制在校生13795人，其中博士生、硕士生3418人，留学生413人。','HEBMU','华北地区',1,'河北医学院','医科',1894,'河北省','是',0,0,0,0,18,0,0,12443,24,68,42,0,0,0,'http://www.hebmu.edu.cn/'),(135,146,'河北师范大学','河北',2,'河北师范大学（Hebei Normal University）位于河北省会石家庄市，由中华人民共和国教育部与河北省人民政府共建，是一所具有百年历史和光荣传统的省属重点大学，河北省重点支持的国家一流大学建设一层次高校，是中国较早成立、规模较大的高等师范院校之一，入选卓越教师培养计划、中西部高校基础能力建设工程、国家建设高水平大学公派研究生项目、国家“特色重点学科项目”建设高校、新工科研究与实践项目、国家级大学生创新创业训练计划、全国深化创新创业教育改革示范高校、教育部来华留学示范基地建设高校。','HEBNU','华北地区',1,NULL,'师范',1902,'河北省','是',0,0,0,0,21,0,0,2606,95,26,8,0,0,0,'http://www.hebtu.edu.cn/'),(497,147,'河北北方学院','河北',2,'河北北方学院（Hebei North University）坐落在张家口市，是河北省西北部具有硕士学位授予权的综合性省属本科院校，入选教育部卓越农林人才教育培养计划，为国家农业部授予的农业部现代农业技术培训基地。学校于2003年9月经国家教育部批准由张家口医学院、张家口师范专科学校和张家口农业高等专科学校三所省属高校合并组建而成。张家口医学院的前身是1945年建立的晋察冀白求恩卫生学校，1982年12月经教育部批准升格为本科院校，定名为张家口医学院；张家口师范专科学校始建于1956年；张家口农业高等专科学校始建于1923年。','HBNU','华北地区',1,NULL,'综合',1923,'河北省','是',0,0,0,0,16,0,0,4257,75,47,0,0,0,0,'http://www.hebeinu.edu.cn/'),(430,148,'保定学院','河北',2,'保定学院（Baoding University）是经教育部批准建立的全日制普通本科院校。学校前身是始建于1904年的“保定初级师范学堂”。1910年改称“直隶第二初级师范学堂”，1928年随省易名为“河北省立第二师范学校”。新中国成立后定名“河北省立保定师范学校”，1978年改建“保定师范专科学校”，2007年升格为本科层次的普通高校，并更名为“保定学院”','','华北地区',1,'保定师范专科学校','师范',1904,'河北省','否',0,0,0,0,14,0,0,924,47,0,0,0,0,0,'http://www.bdu.edu.cn/'),(392,149,'唐山师范学院','河北',2,'唐山师范学院位于河北省唐山市高新技术开发区，是一所省属全日制普通本科师范院校。学校创办于1956年，最初名为唐山速成师范专科学校；1958年，升格为唐山师范学院；1962年，因国家经济困难而停办，一年后恢复改建为唐山专区教师进修学校，“文革”期间被解散；1979年，恢复建立唐山师范专科学校；1984年4月，唐山师范专科学校与河北唐山教育学院合并；2000年3月，经国家教育部批准，改建为本科院校，更名为唐山师范学院。','','华北地区',1,NULL,'师范',1956,'河北省','是',0,0,0,0,15,0,0,1032,60,0,0,0,0,0,'http://www.tstc.edu.cn/'),(427,150,'廊坊师范学院','河北',2,'廊坊师范学院（Langfang Normal University）坐落于河北省廊坊市，是河北省省属全日制普通本科院校。2000年经教育部批准，廊坊师范专科学校、廊坊市教育学院、廊坊师范学校合并，成立廊坊师范学院；2004年12月，经河北省政府批准与河北职业技术学院合并，组建新的廊坊师范学院。廊坊师范专科学校起源于1946年建立的安次县立简易师范学校，后历经调整与发展，于1964年更名为廊坊师范专科学校。廊坊市师范学校起源于1950年建立的蓟县初级师范学校，后历经调整与发展，于1989年更名为廊坊市师范学校。廊坊市教育学院建立于1978年。河北职业技术学院起源于1969年建立的轻工业部五七干校，后历经调整与发展，于1999年更名为河北职业技术学院，2003年农业部乡镇企业管理干部学院并入河北职业技术学院。','','华北地区',1,'河北职业技术学院','师范',1946,'河北省','是',0,0,0,0,16,0,0,1400,62,0,0,0,0,0,'http://www.lfsfxy.org.cn/'),(452,151,'邯郸学院','河北',2,'邯郸学院（Handan University）位于河北省邯郸市，是一所公办全日制普通本科院校。学校办学历史始于1905年成立的邯郸县初级师范学堂，1982年升格为邯郸师范专科学校，2004年升格为本科院校并定名为邯郸学院，2012年、2017年先后顺利通过教育部本科教学合格评估、审核评估。','HDC','华北地区',1,'邯郸师范专科学校','师范',1905,'河北省','否',0,0,0,0,17,0,0,1045,48,0,0,0,0,0,' http://www.hdc.edu.cn/'),(475,152,'沧州师范学院','河北',2,'沧州师范学院坐落于河北省沧州市，是一所以教育类学科为主，涵盖文、理、工、管、艺术等多学科的地方性、应用型普通本科高等院校学校始建于1958年，1984年升建为沧州师范专科学校，2010年3月升建为本科层次的普通高校，更名“沧州师范学院”。2014年获得学士学位授予权。截至2017年6月30日，学校规划用地1028亩，完成713亩的规划建设，附属中学占地62.8亩，建筑面积36.82万平方米，有各类实验（实训）室239个，校外实习基地130个，固定资产44376万元，其中教学设备仪器总值8747万元；图书馆馆藏纸质图书128万册，纸质期刊1439种，电子图书44万册；有教职工871名，其中专任教师607人，副高级以上职称233人，具有硕士、博士学位者405人；有18个二级学院，招生专业中本科38个，专科26个；有全日制在校生12603人，其中本科生8578人，专科生4025人。','CAZTC','华北地区',1,NULL,'师范',1858,'河北省','否',0,0,0,0,18,0,0,871,38,0,0,0,0,0,'http://www.caztc.edu.cn/'),(168,153,'石家庄铁道大学','河北',2,'石家庄铁道大学（Shijiazhuang Tiedao University）是全国重点大学，河北省人民政府与教育部、国家国防科工局、国家铁路局共建高校，入选“2011计划”、国家“中西部高校基础能力建设工程”、国家级大学生创新创业训练计划、国家级新工科研究与实践项目，入选教育部“卓越工程师教育培养计划”高校，2016年被河北省列为重点支持的一流大学和一流学科建设高校。','STU','华北地区',1,'石家庄铁道学院','理工',1950,'河北省','否',0,0,0,0,19,0,0,1000,51,55,2,0,0,0,'http://www.stdu.edu.cn/'),(116,154,'山西大学','山西',21,'山西大学坐落在具有2500多年历史的文化古都——山西省省会太原市，学校前身为创建于1902年的山西大学堂，其悠远的文脉可以上溯至明代三立书院及清代晋阳书院和令德书院。早期的山西大学堂中西合璧、文理并重，办学思路开阔，育人理念先进，是我国近代高等教育重要的发祥地之一，也是三晋大地百年文化科教的重镇。历经117年办学历程，山西大学始终根植于华夏文明的沃土，以“教授高深学术，养成硕学闳材，应国家之需要”为己任，秉承“中西会通、求真至善、登崇俊良、自强报国”的光荣传统，弘扬“勤奋、严谨、信实、创新”的优良校风，培育了三十多万名优秀人才，为我国高等教育事业的发展作出了重要贡献。\n\n百年育英才，盛世续华章。近年来，承载着科教兴国、人才强国的时代使命，山西大学实现了跨越式发展，成为一所文理工并重的高水平综合性大学。1998年，成为山西省重点建设大学。2005年，成为山西省人民政府与教育部共同建设的省部共建大学。2012年，成为全国14所“中西部高校提升综合实力工程”入选高校之一，迈上了国家建设“有特色、高水平”大学的新平台。2014年，具有六十年办学历史的太原电力高等专科学校并入山西大学，学科结构进一步完善。2016年，成为国家中西部“一省一校”重点建设大学。2017年，山西省出台意见支持山西大学深入实施“1331工程”，对标“双一流”实现率先发展。2018年，在省部共建和“一省一校”建设基础上，我校正式成为教育部和山西省政府合建的部省合建高校。\n\n学科门类齐全，科研实力雄厚。学校拥有一批以国家重点学科、国家重点实验室、教育部重点实验室、教育部人文社会科学重点研究基地、教育部工程研究中心、国家地方联合实验室为代表的重要学科平台。设有涵盖文、史、哲、理、工、农、经、管、法、教、艺、医等12大学科门类86个本科专业以及9个本科双学位招生专业。现有博士学位一级学科授权点17个、二级学科授权点3个；硕士学位一级学科授权点37个、二级学科硕士点9个；硕士专业学位种类18个；自主设置交叉学科博、硕士点各2个；目录外二级学科博、硕士点各4个。化学和工程2个学科入选ESI全球排名前1%。近年来，学校主持的国家“973”计划项目、重点研发计划项目和国家自然科学基金、社会科学基金等各类项目稳步增加，先后荣获国家自然科学二等奖、技术发明二等奖、科技进步二等奖、教育部高校人文社科研究优秀成果一等奖等16项国家科研大奖。\n\n名师名人荟萃，教学成果显著。现有专任教师2138人，高级职称教师1084人。其中包括中科院院士、国际杰出专业技术人才、长江学者特聘教授和讲座教授、千人计划海外高层次引进人才、万人计划高层次领军人才、国家杰出青年科学基金获得者、国家优秀青年科学基金获得者、全国模范教师和享受政府特殊津贴的专家近百人。拥有1个国家自然科学基金创新研究群体、3个国家级教学团队、2个教育部创新团队。5门课程入选国家级精品课程，3门课程入选国家级精品视频公开课、1门课程入选国家级精品资源共享课。9部教材成为国家级规划教材，共有10人获得全国优秀博士论文奖或提名奖。\n\n实践活动丰富，育人成绩斐然。学校现有全日制在校生30702人，其中本科生24122人，研究生6580人。开设“初民试验班”，引入研究性教学理念，强化实践教学环节，显著提升了学生学术素养和创新能力。学生一次性就业率始终保持在85%以上。在近几年的全国大学生数学建模竞赛、“挑战杯”课外科技作品竞赛、电子设计大赛、广告艺术大赛和英语竞赛中，我校学子共荣获全国特等奖、一等奖40余项；在奥运会、亚运会、全运会等赛事中，先后获得金银铜牌十余枚。\n\n国际交流广泛，留学渠道通畅。学校不断拓展对外交流，同美、日、韩、英、法、德、加等国家和地区的60余所高校及科研院所建立合作关系，与美国北卡罗莱纳大学夏洛特分校合作建立孔子学院。学校积极拓展本科生国际交流渠道，与多所国外大学建立了合作培养机制，为学生出国深造创造了良好条件。\n\n校园环境优雅，办学条件优良。目前拥有坞城校区、大东关校区、东山校区（在建）三个校区，总占地面积2985亩，建筑面积110万平方米。学校林荫遮道、花草围楼，处处散发着宜学宜居的人文气息，被省政府命名为“园林化单位”和“绿色学校”。图书馆馆藏文献367万册，电子图书34万余册，教学科研仪器设备总值超11亿元。在建的东山新校区更将为学校人才培养和科研创新提供广阔的发展空间。\n\n百年传承，薪火相继。走过117年光辉历程的山西大学，面临着全面提升综合实力的难得机遇。站在新的历史起点上，学校将紧紧抓住国家“双一流”建设、部省合建、山西省“1331工程”建设和率先发展的历史机遇，坚持以提升办学质量为核心，走内涵式发展道路，争取早日建成区域特色鲜明的高水平研究型大学，努力建设社会更为满意的人才高地、贡献更为卓越的创新基地、辐射更为广泛的教育要地，为国家和地方经济社会发展提供更加有力的人才支撑和智力支持。 ','SXU','华北地区',1,'山西大学堂、国立山西大学','综合',1902,'山西省','是',0,0,0,0,31,19,6,2095,86,38,17,1,6180,0,'http://www.sxu.edu.cn/'),(329,155,'太原科技大学','山西',21,'太原科技大学是新中国第一所重型机械本科院校，是一所办学特色鲜明的多科性大学，也是我国重大技术装备领域重要的人才培养和科技研发基地。学校是教育部本科教学工作水平评估优秀高校（2006年）、入选教育部中西部高校基础能力建设工程支持高校（2016年）。\n\n1952年，学校前身山西省机械制造工业学校创建，近代中国机械工业奠基人、中国内燃机研制先驱支秉渊先生出任校长。1953年学校划归中央第一机械工业部。1955年长春汽校、汉口机器制造学校锻冲专业并入。1960年更名为太原重型机械学院。1965年大连工学院、沈阳机电学院起重输送机械专业并入。1998年改为省部共建、省管为主。2004年更名为太原科技大学。\n\n学校秉承”负重奋进，笃行求实”的精神，经过六十五年不懈努力，现已发展成为一所以工为主，文理科为两翼，装备制造主流学科特色鲜明，理学、工学、哲学、法学、文学、经济学、管理学、艺术学、教育学等九大学科门类相互支撑，学士、硕士、博士多层次教育合理衔接的教学研究型大学。\n\n学校现有本科专业58个，其中，国家级综合改革试点专业1个，国家级特色专业建设点5个，省级特色专业7个，省级优势特色专业6个，省级品牌专业11个，中外合作办学专业1个，通过工程教育专业认证的专业2个。\n\n学校现有省级“1331工程”优势特色学科1个，服务产业创新学科群2个，省级重点学科8个；拥有一级学科博士学位授权点3个，一级学科硕士学位授权点16个；拥有工商管理（MBA）、法律、社会工作、工程等4个专业学位类别硕士学位授权点，其中工程硕士在13个领域拥有授权资格；设有机械工程博士后科研流动站。\n\n学校现有专任教师1099人，其中，高级职称教师486人，博士生导师38人，硕士生导师382人，集聚了一批长江学者、国家杰青、百千万人才、教育部新世纪优秀人才和山西省高校中青年拔尖人才、青年学术带头人、学术技术带头人、“333人才”、“百人计划”、“青年三晋学者”等为代表的高层次人才；有省级重点创新团队6个。2017年，我校重型机械教育部工程中心主任黄庆学教授成功当选中国工程院院士。\n\n学校现有全日制在校学生22000余人，拥有国家级和省部级大学生创新创业示范基地、大学生校外实践基地、实验教学示范中心、精品课程、精品视频公开课程等优质教学资源。学校设有教育部数据中国“百校工程”大数据应用创新中心、教育部ICT产教融合创新基地。学校的机械设计制造及其自动化专业在山西省第一个通过工程教育专业认证，毕业生就业率连续十多年居全省高校前列。\n\n“十一五”以来，学校先后获得国家科技进步奖4项、国家技术发明奖1项、何梁何利基金科学与技术创新奖1项及省部级科技奖励数十项，多次参与国家重大科研项目和国家重点工程科技攻关任务，2015年授权国家发明专利数进入全国大学20强。2016年以来，学校在山西省“1331工程”建设中获财政支持总额位居全省高校第四。\n\n学校现有省部共建国家重点实验室培育基地1个、教育部工程研究中心1个、国家地方联合工程研究中心1个、省级重点实验室4个、省级工程技术研究中心9个，省级协同创新中心3个（含省级重点协同创新中心1个）、省人文社科重点研究基地3个。\n\n学校是产学研合作的积极倡导者和先行者，2001年学校联合太重集团、太钢集团、柳工集团、徐工集团、山推集团等全国40多家机械制造骨干企业成立产学研董事会，先后与三一重工、中信重工、中联重工、徐工、柳工、洛阳轴承等全国两百多家企业建立了产学研战略合作（联盟）关系，并与江苏海安县合作建设海安锻压装备产业研究院，与山西省晋城市合作建设晋城产业技术创新研究院。\n\n建校以来，学校先后培养出12万多名优秀学生，校友遍布国民经济各个领域，许多校友成长为优秀的科学家、一流的工程师和杰出的企业家。徐州工程机械集团有限公司董事长王民，太原重型机械集团有限公司董事长岳普煜、王创民，总工程师顾翠云、闫少泉，山推工程机械股份有限公司董事长张秀文，洛阳轴承集团公司董事长赵东海等是其中的佼佼者。\n\n学校坚持国际化办学路线，先后与美国奥本大学、北卡罗来纳大学、北佛罗里达大学、旧金山州立大学、澳大利亚卧龙岗大学、日本京都女子大学和日本丰桥创造大学等数十所国外高校建立了友好合作关系，联合开展大学生“2+2”、“3+1”、等国际合作培养项目及教师互访交流。',NULL,'华北地区',1,'山西省机械制造工业学校、太原重型机械学院','理工',1952,'山西省','是',0,0,0,0,16,3,0,1000,58,59,9,0,5800,0,'http://www.tyust.edu.cn/'),(112,156,'太原理工大学','山西',21,'太原理工大学是国家“双一流”重点建设高校，由原太原工业大学和原山西矿业学院合并组建而成。历经117年传承发展，学校业已建设成为以工为主、理工结合、多学科协调发展的高等学府，是国家“211工程”重点建设大学、教育部首批“卓越工程师教育培养计划”试点高校和国家“大学生创新创业训练计划”实施学校；先后被评为“普通高等学校本科教学工作优秀单位”“全国文明单位”“全国文明校园”，入选“全国首批深化创新创业教育改革示范高校”“国家创新人才培养示范基地”“全国高校实践育人创新创业基地”“教育部‘三全育人’综合改革试点高校”，荣膺“全国五一劳动奖状”“全国先进基层党组织”。\n\n学校植根三晋大地，平台广阔人才济济。目前，学校占地3200余亩，校舍建筑面积149万平方米；现有全日制在校本科生30800名，硕士研究生6220名，博士研究生763名，留学生485名；现有84个本科专业，34个一级学科硕士点，15个一级学科博士点，13个博士后流动站，七类专业学位授权点；拥有3个国家重点学科，4个国家级实验教学示范中心，5个国家级特色专业建设点，7个国家级“工程实践教育中心”, 1个省部共建国家重点实验室培育基地，4个教育部重点实验室，1个科技部重点领域创新团队，2个教育部长江学者创新团队，工程学、材料学、化学3个学科进入了ESI全球排名前1%；现有教职工3480名，其中专任教师2157名，工程院院士3名，博士生导师238名，具有高级专业技术职称教师1236名。\n\n学校挺立创新潮头，科技研发成效卓著。太原理工大学曾连续两次作为首席科学家单位承担国家重点基础研究发展计划（973计划）项目，累计承担“863”计划、“国家重点研发计划”等国家级各类项目1326项；获得国家和省部级科技成果奖516项，其中包括国家自然科学奖、技术发明奖、科技进步奖等42项；国家杰出青年基金获得者4人、国家优秀青年基金获得者5人，“万人计划”科技创新领军人才4人，中青年科技创新领军人才3名, 教育部创新团队2个， 科技部重点领域创新团队 1个。2018年，我校首次在《Science》发表研究论文，实现了重大历史性突破。多年来，科研优势和创新能力源源不断地转化为服务行业和区域发展的现实生产力，成果转化、技术转移累计为企业创造经济效益逾百亿元，为国家经济建设和社会发展做出了突出贡献。\n\n学校恪守育人使命，立德树人硕果丰盈。多年来，学校始终坚持以立德树人为根本任务，以学生为中心，育人渠道和途径更趋完善、更加优化。国际工程教育专业认证取得突破进展，18个专业通过认证，数量位居全国高校前十位；获批国家虚拟仿真实验教学项目3项，位居全省首位；“生涯导航”教育计划产生重要影响，获得山西省教学成果特等奖；数学建模、超算、机器人、晓明研究室、晋豹车队等创新团队在国内外竞赛中屡创佳绩，我校大学生学科竞赛成绩稳居全国高校50强；“清泽心雨”思政平台获批教育部高校思想政治工作精品项目；“螺丝钉之家”入选全国学雷锋活动示范点，“爱心家园”荣膺“第四届山西道德模范”等殊荣；校男子篮球队两次夺得CUBA全国总冠军、校男子足球队成功夺得CUFL全国总冠军，我校成为唯一一所在足球、篮球两个项目上均获得过全国总冠军的大学。\n\n学校树立全球视野，积极开展对外交流。近年来，学校大力实施国际化战略工程，不断加大国际交流与合作的力度，加速国际化进程、提升国际化办学水平，先后与美国、日本、英国、澳大利亚、加拿大、德国、俄罗斯、法国、意大利等国家（地区）的132所高校、科研院所等开展多层次、双向人才培养合作办学和科技合作，与国际间的学术高层往来更加密切，在海内外的知名度与日提升。目前，学校还承办了阿富汗喀布尔大学孔子学院和牙买加西印度群岛大学莫纳分校孔子学院。\n\n回望过去，理工大人奋力拼搏，铸就了辉煌成绩；翘首明天，世纪学府砥砺前行，承载着新的希望。在奋力推进“双一流”建设的征程上，太原理工大学全体师生将以习近平新时代中国特色社会主义思想为指导，认真学习贯彻落实党的十九大精神，坚持立德树人，坚持质量第一，坚持以学科建设为龙头、以学生为中心，走内涵式发展道路，以一流人才、一流成果、一流文化和一流精神，为创建高水平国际化创新型大学不懈奋斗！','TYUT','华北地区',1,'中西大学堂、山西大学堂、山西大学、太原工学院、太原工业大学','理工',1902,'山西省','否',0,211,1,1,25,18,7,2014,81,123,51,0,7980,0,'http://www.tyut.edu.cn/'),(291,157,'山西农业大学','山西',21,'山西农业大学是我国著名的高等农业学府，建国前山西省两所本科高校之一，首批获得硕士学位授权、第七批获得博士学位授权的高校之一，教育部本科教学评估优秀高校，省政府与农业部共建高校，全国首批深化创新创业教育改革示范高校，国家中西部基础能力建设入选高校，全国农业农村信息化示范基地，山西省高等教育综合改革试点高校，山西省第二批省级大众创业万众创新示范基地。\n\n百年学府，历史厚重。学校始建于1907年孔祥熙创办的私立铭贤学堂，后发展为私立铭贤农工专科学校、私立铭贤学院，与山西大学堂一起，开创了山西近代高等教育的先河；1951年改私立为公办，成立山西农学院；1979年更名为山西农业大学，成为改革开放初全国99所重点大学之一。在111年的办学实践中，学校立足山西，面向全国，服务“三农”，不断提高人才培养质量、科技创新水平和社会服务能力，推进文化传承与创新，为山西乃至全国的经济建设和社会发展作出了重要贡献，铸就了“崇学事农、艰苦兴校”的办学精神，积淀了“甘于奉献、敬业乐教”的教风，培育了“勤奋学习、注重实践”的学风, 锤炼了“追梦、实干、吃苦、钻研、坚韧”的大学生创业精神。学校先后培养了13万余名各类人才，遍布大江南北，各行各业。学校培养了一大批革命家、政治家和党政干部，如太谷地区最早的共产党员张维琛，原中国革命军事博物馆副馆长赵振鑫、国务院外事办公室副主任郝德青、中央党校副校长侯维煜、外交部纪委副书记朱霖、八路军129师新编第10旅30团政委马定夫、吉林省委书记王大任、河南省委书记张树德、北京农业大学校长安民、政治局委员（天津市委书记）谭绍文、山东省委书记高克亭、交通部部长李清、国家海洋局局长罗钰如、山西省政协主席郑社奎、山西省政协主席金银焕、山西省纪委书记冯芝茂、山西省政协副主席汤祊德、山西省政协副主席聂向庭、山西省人大副主任张铭等；培养了一大批科学家，如2012年国家最高科技奖获得者郑哲敏，中科院院士王志均，中科院院士席承藩，中科院院士郭承基，工程院院士朱尊权，中科院院士庄文颖，中科院院士高福，全国著名大豆育种专家王绶，养猪专家张龙志，养羊专家吕效吾，养牛专家冀一伦，联合国粮农组织助理总干事王韧等。\n\n农科为基，特色鲜明。在长期的历史积淀中，学校逐步形成了以农科为特色、多学科协调发展的学科专业体系。近年来，学校用现代生物技术和信息技术提升改造传统学科，初步构建了以农科为核心，向上游生物学科和下游食品学科双向延伸,其他相关学科为支撑的学科体系。现有农学院、动物科技学院、林学院、园艺学院、资源环境学院、工学院、经济管理学院、食品科学与工程学院、文理学院、生命科学学院、公共管理学院、信息科学与工程学院、软件学院、城乡建设学院、体育学院、马克思主义学院（思想政治理论教学研究部）等16个教学单位。学科专业涵盖了农学、理学、工学、经济学、管理学、文学、法学、艺术学等8个门类，本科专业63个。有博士后科研流动站8个，一级学科博士学位授权点9个，二级学科博士学位授权点34个,专业博士学位授权点1个；一级学科硕士学位授权点14个，二级学科硕士学位授权点60个，专业硕士学位授权点7个；国家重点（培育）学科1个，省级重点学科5个，省级重点建设学科5个，省级重点扶持学科3个，省级优势学科1个，省级特色学科4个，“1331”工程优势学科1个，省优势学科攀升计划学科1个，省服务产业创新学科群2个；成人函授本科专业15个，成人函授专科专业5个；主考的自学考试本科专业4个，主考的自学考试专科专业2个。\n\n人才荟萃，育人为本。学校始终贯彻党的教育方针，坚持人才培养的中心地位，着力培养造就具有“高尚品德、自信气质、务实精神、专业本领”的农大风格、农大气派的高素质专门人才。学校现有全日制在校生24518人，教职员工1700余人，专任教师1086人，其中有国务院学位委员会学科评议组成员1人，教育部高等学校教学指导委员会委员12人，百千万人才工程国家级人选1人，国家有突出贡献专家2人，教育部“新世纪优秀人才支持计划”入选者4人，享受政府特殊津贴4人，“青年三晋学者”6人，省“百人计划”入选者34人，省学术技术带头人15人，省“131”人才项目第一层次柔性引进院士12人，第二层次知名学者和学术带头人67人。学校大力实施本科教学质量工程，有国家级教学名师1人，全国高校黄大年式教师团队1个，全国优秀教师1人，全国师德标兵1人，国家级教学团队1个，国家级特色专业建设点5个，国家级精品课程2门，国家级精品资源共享课程1门，国家级教学实验示范中心3个，国家级大学生校外实践教育基地1个，国家农科教人才合作培养基地1个；省级教学名师29人，省级教学团队3个，省级品牌、特色专业建设点17个，省级精品课程16门，省级精品资源共享课程14门，省级实验教学示范中心11个，省级虚拟仿真实验教学中心1个，省级人才培养模式创新实验区2个；先后获省部级以上教学成果奖100余项。学校成立了山西省首家创业学院，建成了“互联网+农业”创新创业园、大学生创业园两个“省级众创空间”，打造了“双创”教育的农大品牌。有博士生导师69人，硕士生导师400余人，省级研究生教育优秀导师团队3个，省级研究生教育优秀导师8人，省级研究生培养基地1个，省级研究生教育创新中心8个。学校积极推进对外合作交流，与美国欧柏林大学有长达百余年的合作历史，与德国安哈尔特应用科技大学合作培养本科生，累计派出1100余名学生。与新西兰梅西大学、奥克兰理工大学、美国博伊西州立大学等10余所大学开展交换生项目，先后有来自伊朗、印度及巴基斯坦等国家的学生来校求学。\n\n需求导向，创新驱动。现有国家功能杂粮技术创新中心、农业部华北黄土高原地区作物栽培与耕地保育科学观测实验站，国家中药材产业技术体系长治综合试验站、农业部现代种业提升工程“梨原种保存及扩繁基地”；省“1331工程”高校思想政治工作实践育人协同中心1个、协同创新中心4个、重点实验室1个、工程（技术）研究中心2个、产业技术创新研究院（战略联盟）2个、重点创新团队1个，普通高等学校重点实验室1个，省教育厅创新基地1个，省高等学校人文社科重点研究基地1个；省重点实验室3个，省工程技术研究中心9个，省工程研究中心1个，省产业技术创新战略联盟4个；农业部转基因生物产品成分监督检验测试中心、农业部农药登记试验资质单位、国家电子计算机质量监督检验中心山西分中心等资质6个；国家首批农科教合作人才培养基地1个，国家级科技特派员创业链1个，国家级科技特派员创业培训基地1个，山西省众创空间1个，挂靠学校省级以上学（协）会7个；国家现代农业产业技术体系岗位科学家4人，省现代农业产业体系首席专家5人、岗位专家22人，省青年拔尖人才2人，学科技术带头人4人，新兴产业领军人才3人，省高校优秀青年学术带头人18人，农村科技特派员137人，省科技创新团队8个。学校在小麦、大豆、杂粮、猪、羊、肉牛、羊驼、草地生态、环境兽医、兽药、食用菌、设施蔬菜、欧李、现代农业装备、食醋等十多个研究领域具有一定优势和特色。“十二五”以来，学校汇聚校内外创新资源，大力推进协同创新，扎实推动“政产学研用”深度融合，取得各类科技成果900余项，晋汾白猪、晋农1号达乌里胡枝子、晋岚绒山羊3个动植物新品种相继通过国家审定，连续五年获得省部级科学技术一等奖。\n\n立足“三农”，服务山西。学校贯彻落实乡村振兴战略，在山西农谷建设中，瞄准“立足山西、面向全国的功能农业（食品）研发高地、农业科技创新高地、技术集成示范推广平台”和“山西农谷综合性、专业性科创中心”的目标，建设以“一城五区多平台”为内容的山西农谷科创城。秉承“论文写在田间地头，把科技播撒三晋大地”的理念，面向11个市、90余个县区开展技术服务，有近百支科技服务团队活跃在全省农业生产第一线，形成了十大优势和特色科技服务体系，涌现出十三支品牌服务队伍，有35个项目列入了国务院农村综合改革试点“新型农业社会化服务体系”建设项目，128个项目列入山西省农业技术推广示范行动项目，24个项目列入国家和山西省农业综合开发科技推广项目，在全省和周边10余个省份推广晋汾白猪、红枣防裂果、食用菌栽培、谷子生产机械化、钙果、大豆、羊驼、设施蔬菜等80余个新品种，161项先进实用技术，创造了显著的经济社会效益。学校科技助力精准扶贫，在全省开展脱贫成效第三方评估，与吕梁市签订战略合作协议，开展“助力攻坚深度贫困吕梁行动”，组织六个团队，分赴六个贫困县，开展产业技术帮扶脱贫,我校对口帮扶临县项目被选为全国省属院校精准扶贫精准脱贫典型项目。学校发挥农科优势，“十三五”以来累计培训涉农管理干部、技术人员、企业家等30万人次，成为山西省新型农民教育和农村干部培训的重要基地。\n\n古今交融，读书圣地。校园建设古今交融，中西合璧，园中有园，文明和谐，是读书治学的圣地，修身养德的圣地。校园占地面积2243亩，毗邻同蒲铁路、大西高铁、108国道、太太公路、太长高速、龙城高速，到太原市区、武宿机场均在1小时车程内。“山西铭贤学校旧址”是全国重点文物保护单位，完整保存了清代至民国时期的建筑群，是山西省近代教育史、农业发展史、中西文化教育交流史的鲜活史料。学校是全国绿化模范单位，校园绿化面积达60万平方米，主校区绿化覆盖率达85%以上，百年以上古稀树木有100余株，名贵、稀有树种50余种。学校教育资源雄厚，有纸质藏书180余万册，纸质期刊800余种，电子图书将近300万册，为师生员工的学习、生活及开展学术文化交流活动提供了良好保障。\n\n新时代，新使命，新作为。在习近平新时代中国特色社会主义思想指引下，学校坚持党对教育工作的全面领导，全面贯彻党的教育方针，以培养德智体美劳全面发展的社会主义建设者和接班人为出发点和落脚点，主动书写新时代教育改革发展的奋进之笔，深化内涵发展，推动融合发展，正在扎根三晋大地、争创一流本科学校的道路上阔步前进！ ','SXAU','华北地区',1,'铭贤学堂、铭贤学院','农林',1907,'山西省','是',0,0,0,0,20,0,0,1700,65,60,34,0,0,0,'http://www.sxau.edu.cn/'),(217,158,'山西医科大学','山西',21,'山西医科大学的前身是山西医学传习所,1919年创建于古城并州。1932年改为私立山西川至医学专科学校。1940年更名为山西大学医学专修科。1946年升格为国立山西大学医学院。新中国成立后，学校步入崭新的发展时期。1953年独立建校，更名为山西医学院。1996年更名为山西医科大学。学校是教育部批准成立的全国第一个法医学本科专业院校、全国首批硕士学位授权单位、全国首批学士学位授权单位、全国第三批和山西省首批博士学位授权单位、全国首批护理学博士学位授权单位、全国开办七年制临床医学专业的25所医学院校之一、全国首批恢复儿科学本科专业的8所医学院校之一、国家中西部高校基础能力建设工程支持高校，教育部首批“卓越医生教育培养计划”试点单位。\n\n经过一百年发展，学校成为一所以医学为主，医文结合、医理结合、医工结合、医管结合，协调发展的医科大学，拥有以本科教育为主、本硕博多层次的完整人才培养体系，形成“医理博精、德能高邃”校训、“献身、务实、团结、进取”校风和“求实精进、坚韧弘毅”的山医精神。\n\n学校现有川至、迎泽、中都三个校区，占地总面积1422亩，建筑面积71万平方米；固定资产总值25.6亿元，其中教学科研仪器设备值4.96亿元。现有第一临床医学院、第二临床医学院、汾阳学院、基础医学院、公共卫生学院、药学院、法医学院、护理学院、研究生学院、人文社会科学学院、继续教育学院（职业技术学院）、管理学院、口腔医学院、马克思主义学院、国际教育学院、儿科医学系、麻醉学系、医学影像学系、外语系、体育教学部、计算机教学部、临床技能教学模拟医院、第六临床医学院、第七临床医学院、第八临床医学院、第九临床医学院、第十临床医学院、第十一临床医学院、第十五临床医学院、精神卫生学院、晋祠学院31个教学机构，第一医院、第二医院、口腔医院、第六医院、汾阳医院、临汾医院、运城医院、太原中心医院、忻州医院、吕梁医院、晋中医院、精神卫生医院12所直属附属医院，有12所非直属附属医院，36个教学医院，122个其它教学基地，15个研究生培养基地，2个科学研究基地。\n\n学校现有教师1900余人，研究生导师 1600 余人。拥有国家杰出青年科学基金获得者2名，优秀青年基金获得者1名，国家“万人计划”领军专家1名，国家重点研发计划首席科学家1名，享受政府特殊津贴人员134名，国家级有突出贡献专家2名；中央联系的高级专家5名，“新世纪百千万人才工程”国家级人选4名，教育部新世纪优秀人才5名，国家级教学名师 1 名，国家优秀教学团队1个，全国优秀思想政治辅导员2名。各类省级优秀人才189名，省级团队23个，省委联系的高级专家 48名。聘有以3名院士为代表的一批客座教授。一批教师荣获全国或全省模范教师、五一劳动奖章等荣誉称号；一批教师在全国和全省教师教学基本功竞赛及各类科技奖励中获奖。\n\n学校现有1个国家重点学科——生理学；1个教育部重点实验室——细胞生理学实验室；3个国家级示范中心——临床技能实训中心、基础医学实验教学中心、临床教学培训示范中心；13个国家临床重点专科——重症医学、泌尿外科学、中医护理学、骨科学、肾病学、普通外科学、老年病学、急诊医学、风湿免疫学、护理学、病理学、眼科学、心血管内科；各类省级优势、特色、重点学科15个；各类省级重点教学科研平台31个。现有3个一级学科设有博士后科研流动站——生物学、公共卫生与预防医学、临床医学。6个一级学科博士学位授权点——生物学、临床医学、公共卫生与预防医学、护理学、基础医学、特种医学；1个博士专业学位授权点——临床医学；9个一级学科硕士学位授权点——生物学、基础医学、临床医学、公共卫生与预防医学、药学、中药学、护理学、口腔医学、特种医学；8个硕士专业学位授权点——临床医学、口腔医学、公共卫生、护理、药学、应用心理、社会工作、图书情报。\n\n学校现有5个国家级特色专业建设点——临床医学、预防医学、法医学、药学、护理学。其中，临床医学（五年制）、临床医学（农村定向免费生）、护理专业为教育部首批国家“本科教学工程”地方高校本科专业综合改革试点专业。现设有2 9个本科专业，即：临床医学（五年制、“5+3”一体化）、预防医学、法医学、口腔医学、儿科学、医学影像学、麻醉学、护理学、精神医学、基础医学、眼视光医学、药学（含药剂学方向）、中药学、药物制剂、临床药学、医学检验技术、医学实验技术、医学影像技术、生物制药、眼视光学、卫生检验与检疫、康复治疗学、运动康复、社会工作、信息管理与信息系统（医药方向）、公共事业管理（卫生事业管理方向、健康产业管理方向）、劳动与社会保障、英语（医学方向）、助产学。\n\n学校生源来自全国各省、市、自治区，现有全日制在校生19800人，其中普通本科生15403人，硕士研究生3889人，博士研究生323人，国际学生185人。建校以来，学校培养了以全国首枚“白求恩奖章”获得者、“人民的好医生”赵雪芳，中国工程院院士徐建国、田志刚，南丁格尔奖章获得者杨辉，加拿大生命科学院院士、加拿大再生医学首席科学家李仁科等为代表的医药卫生专业人才12万余名。近年来，本科毕业生升学率在30%以上，就业率达90%以上。调查显示，用人单位对学校人才培养的满意度在90%以上。\n\n学校科研发展势头良好。2018年，科技总经费达1.3亿元；承担省级以上科技计划项目362项，其中国家自然科学基金项目50项；发表科研论文2900余篇，其中SCI等收录论文233篇；获专利授权49项，出版著作39部；获省部级科技奖励24项。\n\n学校积极开展国际交流合作。与美国、加拿大、英国、爱尔兰、日本、俄罗斯、澳大利亚等国家的大学和机构建立了合作关系；实施了“青年骨干教师海外培养计划”“学科管理业务骨干海外培养计划”“博士研究生海外培养计划”，开展了“推荐优秀本科生直博”“硕士研究生1+1+1联合培养”本科生学分互换等项目；广泛开展师生交流和国际科研合作。\n\n学校历经百年薪火相传，几代人艰辛拼搏，取得了辉煌成就。学校将紧紧抓住国家“双一流”建设和山西“1331”工程实施、健康中国和健康山西建设、医学教育综合改革等发展机遇，继承优良传统，加快建设步伐，为早日建成特色鲜明的高水平研究教学型医科大学而努力奋斗。','','华北地区',1,'山西医学传习所、私立山西川至医学专科学校、国立山西大学医学院、山西医学院','医科',1919,'山西省','否',0,0,0,0,31,3,0,1600,26,9,6,0,5521,0,'http://www.sxmu.edu.cn/'),(9999,159,'山西医科大学汾阳学院','山西',21,'山西医科大学汾阳学院，是山西医科大学二级学院，前身系山西省汾阳高级护理学校，由美国基督教华北公理会于1923年创办，1985年恢复重建，1996年改为现名，学校坐落于素有“秦晋旱码头”之称的千年古城汾阳市，青银高速公路、307国道、太中银铁路倚门而过，交通便利。学校占地面积500余亩，建筑面积14.68万平方米。校园花木扶疏，绿树成荫，环境优雅，风景如画。我院是一所多专业、多学制、多层次的高等医学院校。设置有临床医学、护理学、口腔医学、医学影像学、医学检验技术、眼视光学、康复治疗学、医学实验技术8个本科专业和临床医学、护理、助产、卫生信息管理、药品经营与管理、健康管理等6个专科专业及护理（老年护理方向）1个专科专业分流方向；有硕士、本科、专科三个办学层次；有普通高等教育、高职高专教育和成人高等教育等多种办学形式。目前，学院有护理学、人体解剖学与组织胚胎学、生物化学与分子生物学、生理学、病理学与病理生理学、临床检验诊断学、药理学、内科学、外科学、神经病学、耳鼻咽喉科学和公共卫生等12个学科招收硕士研究生。学院现有教学仪器设备总价值7000余万元，教学手段先进，实现了校园网络全覆盖，搭建了网络教育教学平台。教室、实验室及住宿等条件均符合国家有关规定。建有96所校外实践教学基地，包括直属附属医院2所、非直属附属医院1所、教学医院24所、实习医院48所、临床实习实训基地16所、社区实践教学基地5所。学院创办和（共同）主办有《护理研究》，共同主办有《山西省高等学校社会科学学报》，协办有《中华医学教育》等刊物，为《中国高等医学教育》理事单位。学院现有专任教师309人，外聘教师132人，直属附属医院具有卫生专业技术职称临床教师1383人，折合教师数582人。专任教师中具有高级专业技术职务者95人，获博士、硕士学位者219人，山西省教学名师6人、中青年骨干教师6人，硕士生导师29人。学院有230余人次教师参编了190余部全国规划教材的编写。学院组织编写的46万余字的《中国护理发展史》填补了国内空白，在中华护理学会成立90周年庆典大会上受到1200位中外护理专家的好评。目前，学院面向全国26省、市招生，现有在校生9000余人。建校以来，学校培养了以“南丁格尔奖”获得者王桂英和杨辉等为代表的一大批优秀护理工作者，许多人已成为省内外二、三级医疗卫生机构的业务骨干。特别是恢复重建以来，已向社会输送各级各类人才30000余人。学生就业形势良好，年平均就业率居全省同类院校前列，学生质量受到用人单位普遍好评，学校的知名度日益提高。为了让贫困学生顺利完成学业，学校建立了助学贷款，构建由国家奖学金、国家励志奖学金、国家助学金、学校优秀学生奖学金、勤工助学岗位等助学保障体系。多年来，学校始终坚持“立足山西、面向市场、质量优先、办出特色”的办学思路，形成了以护理教育为龙头的办学特色。本科护理学专业为国家级特色专业建设点；本科医学检验技术专业为山西省优势专业；本科护理学、医学检验技术、眼视光学专业为山西省高等学校特色专业；临床护理实验教学中心、医学检验实验教学中心和基础医学实验教学中心为山西省普通高等学校实验教学示范中心；专科临床医学专业是教育部、卫生部第一批卓越医生教育培养计划项目“‘3+2’三年制专科临床医学教育人才培养模式改革”试点专业；专科护理专业为山西省高等职业教育特色专业。从1997年至今连续21年获得新加坡护理专科文凭全额奖学金留学生项目，已输送留学生596名，是全国唯一一所连续21年获得该项目的学校。学院在全国及山西省举办的护理、助产技能大赛中多次荣获一、二等奖和优秀指导教师及组织奖项等荣誉。医学检验技术专业在“人卫杯”全国高等学校医学检验技术中青年教师讲课竞赛中荣获一等奖、三等奖及优秀奖。学院不断拓展对外交流与合作的范围，与中国社会福利与养老服务协会签订合作框架协议，与协会合作建设“山西医科大学汾阳学院长者福祉学院”；与天津医科大学医学检验学院、深圳华大基因研究院、山西高科技医学检测中心、山西锦波生物医药股份有限公司、山西欣宏科技有限公司等校企合作办学。与新加坡义安理工学院连续多年开展了浸入式文化交流学习；与英国苏格兰科特布里奇学院、阿伯丁大学、爱尔兰科克大学成功签署合作备忘录，与日本龟田医疗大学、美国海格思大学达成合作意向，有效地实现了资源共享、优势互补。全院上下将以“立德树人”为己任，主动适应高等教育改革和医疗卫生事业发展的需要，进一步明确了学院的办学定位、办学思路和奋斗目标，紧紧围绕学院“新理念、新规划、新发展”的总体要求和“一二四五”(“一”全面达到本科办学条件的高等医学院校；“二”提升教育教学质量和服务社会能力；“四”“强化”管理、特色、素质、服务；“五”“建设”基础设施、内涵建设、人才队伍建设、党建和校园文化建设)的发展思路，秉承“仁术爱仁、精益求精”的校训，弘扬“团结、勤奋、求实、创新”的优良作风，以政治建设为统领，以立德树人为根本，以稳中求进为总基调，以全面从严治党为主线，以改革创新为动力，以狠抓落实为基点，以提升质量为要求，深化内涵发展，强化特色创新，努力把学院建设成为全面达本科办学要求的高等医学院校，为社会培养大批应用型医学高级人才，为人类的健康事业做出更大的贡献。','','华北地区',1,'山西省立汾阳护士学校，山西省汾阳高级护理学校，山西医学院汾阳专科部','医科',1923,'陕西省教育厅','是',0,0,0,0,8,0,0,309,6,0,0,0,0,0,'http://www.sxmufyc.edu.cn/'),(668,160,'长治医学院','山西',21,'长治医学院是山西省属全日制普通高等医学院校，座落在素有红色之都、魅力之城美誉的山西省长治市，位于太行、太岳腹地和晋冀豫三省交界，是本地区开展人才培养、医疗服务、医学研究的重要基地和进行对外交流与合作的主要窗口。\n\n学校创建于1946年，其前身是“晋冀鲁豫军区白求恩国际和平医院总院”开办的护士学校，是在刘伯承、邓小平等老一辈革命家的关怀下，由延安中央医院院长何穆博士亲手创建，是中国共产党最早创建的医学院校之一。1948年改建为“和平医专”，1958年更名为“晋东南医学专科学校”，1986年升格为本科院校，更名为长治医学院。1996年通过本科教学工作合格评估；2004年通过本科教学工作水平评估。2011年被国务院学位委员会批准为临床医学硕士专业学位研究生培养试点单位。2012年在全国同类院校中率先通过教育部临床医学专业认证。2016年列入国家中西部高校基础能力建设工程。在长达72年的办学历程中，学校传承红色基因、担当历史使命，秉承“面向基层、德育为先”的优良传统，坚持“重质量、重实践、重特色”的办学理念，形成了以“厚德精业、济世报国”为核心的长医精神，为社会培养输送了近5万名优秀医学人才。\n\n发展至今，学校形成了医、工、文、理、教育、管理、艺术多学科相互渗透、协调发展的办学格局，构建了集研究生教育、本科教育、继续教育于一体的多层次人才培养体系。设有19个教学院系（部），开设本科专业18个。在校生11486人，其中硕士研究生173人、本科生9556人、成教学生1757人。现有校本部和南校区两个校区，占地（含两所直属附属医院）965.20亩，建筑面积32.71万平方米，固定资产总值达到12.9亿元(校本部3.42亿元)，其中教学科研仪器设备总值6.43亿元(校本部0.97亿元)。\n\n学校现有在编职工2671人（校本部872人），专任教师703人（校本部490人），具有硕士学位教师499人（校本部347人），具有博士学位教师83人（校本部38人），高级职称教师362人（校本部189人）。本校临床医学硕士专业学位研究生导师127名，外聘兼职硕士研究生导师87名。内科学教学团队为国家级教学团队，药理学教学团队为省级优秀教学团队。\n\n学校拥有国家级特色专业建设点和山西省品牌专业2个，山西省特色专业4个，山西省优势特色专业2个；国家级精品资源共享课程1门，省级精品课程5门，省级精品资源共享课程4门；山西省高校首批重点实验室1个，山西省示范实验室7个，省级虚拟仿真实验中心1个。拥有直属三级甲等附属医院2所、非直属附属医院9所，实践教学基地100余个。临床医学专业为教育部“本科教学工程”综合改革试点专业。2011年学校被教育部、卫生部批准成为全国第一批卓越医生教育培养计划项目试点高校。\n\n临床医学为山西省“1331工程”优势特色学科；内科学为山西省重点建设学科；“消化道肿瘤的基础与临床研究”团队为山西省“1331工程”重点创新团队；“消化道肿瘤综合防治学科群”为山西省服务产业创新学科群。近五年来，学校共承担各类科研项目302项，其中国家自然科学基金项目4项，省部级科研项目114项。发表论文2870篇，被SCI收录259篇；教师出版学术专著130部。\n\n学校坚持开放办学，先后选派25名教师赴国外进行访问研修、学术交流。邀请美国圣·路易斯大学医学院、加州大学、伊萨卡大学等院校的著名专家学者来校讲学。与美国罗克福德学院、英国阿伯丁大学、黑山大学等建立了友好合作关系。开展中美大学生文化交流活动6次，举办中美心血管疾病国际研讨会、中日消化道肿瘤高峰论坛等有重要影响的学术会议20余场。    \n\n面向未来，学校以习近平新时代中国特色社会主义思想为指引，继承发扬优良传统，牢牢把握历史机遇，全面持续深化改革，朝着建设“特色鲜明、区域领先”教学应用型医学院校的发展目标努力奋进，致力于办好人民满意的医学教育。',NULL,'华北地区',1,'太行白求恩国际和平医院附属护士学校、长治和平医专、山西省立长治医科专门学校、晋东南医学专科学校','医科',1946,'山西省','否',0,0,0,0,20,0,0,2706,18,1,0,0,0,0,'http://www.czmc.com/'),(256,161,'山西师范大学','山西',21,'山西师范大学坐落于华夏文明的重要发祥地古尧都临汾市。学校创建于1958年，前身是晋南师范专科学校，1964年升格为本科院校，定名为山西师范学院，1984年更名为山西师范大学，1999年山西师范大学体育学院（独立建制）和山西省职业师范专科学校并入。建校60年来，学校秉承师范教育的优良传统，不断深化教师教育改革，现已发展成为一所培养体系完备、办学特色鲜明的省属师范大学，是培养山西省基础教育师资的重要基地。\n\n学校现有全日制本科生16172人，研究生3810人；设有21个学院，62个本科专业，20个一级硕士学位授权学科，7个硕士专业学位类别，2个一级博士学位授权学科，2个博士后流动站，1个教育部重点实验室，1个教育部创新团队，1个山西省服务产业创新学科群，9个省级重点学科，3个省级协同创新中心，4个山西省高校人文社科重点研究基地，承担教育部“卓越教师培养计划改革项目”，承担国家级人才培养模式创新实验区项目和国家级大学生校外实践教育基地项目，有6个国家级特色专业、1个国家级专业综合改革试点、6个省级优势专业、10个省级品牌专业，6个省级特色专业，11门省级精品课程，9个省级实验教学示范中心。\n\n学校现有专任教师1139人。其中，国家杰青2人，中青年科技创新领军人才1人，“万人计划”领军人才1人，国家百千万人才工程人选3人，教育部新世纪优秀人才支持计划人选5人，山西省高等学校优秀青年学术带头人32人，省级教学名师24人，山西省青年拔尖创新人才6人，山西省学术技术带头人17人。 \n\n学校高度重视本科教学工作，始终把教学质量作为立校之本，不断深化教育教学改革，全面提高人才培养质量。2014年开始，实施“以学生为中心”的课堂教学改革。2016年以来，又实施以课堂教学改革为突破口的人才培养模式改革，着力改造现有大学课堂，全面更新教育教学理念，努力创新教育教学方法，大力实施教学改革创新计划，全面深化实践教学改革，全面加强大学生创新创业教育，积极推进信息技术与教育教学深度融合，积极探索精英人才培养模式。学生的学习能力、创新能力和实践能力不断提高，教育教学质量稳步提升，就业率稳居全省高校前列，毕业生受到社会各界广泛欢迎和认可。\n\n学校坚持以学科建设为龙头，不断提升学科和科研水平。“十三五”以来，学校大力推进重点学科建设，按照“一体两翼、多点协同”的学科整体布局，实施以引进高端人才和高水平团队为突破口的重点学科攀升计划。“一体”即以教师教育为主体，“两翼”即以“戏剧与影视学”和“化学”两个一级博士点学科为牵头学科，辐射和引领大文、大理各相关学科，形成两大优势学科群。“多点协同”包括重点学科和重点学科方向两类。目前，已经确立了教师教育、文化创意产业、分子科学等3个学科群，中国语言文学、中国史、生物学、数学等4个重点学科，以及马克思主义理论、体育学、产业经济学、物理学、农产品加工与贮藏工程、自然地理学、美术学等7个重点学科方向。\n\n学校高度重视发挥高层次人才的支撑引领作用，“十三五”以来，大力培养和引进一批活跃在学术前沿、满足国家和山西省战略需求的领军人才，努力建设一批高水平创新团队。目前已柔性引进特聘教授37名，其中中国科学院院士特聘教授1人，中国工程院院士特聘教授1人，“千人计划”人选特聘教授3人，“长江学者”特聘教授14人，杰出青年特聘教授1人，山西省“百人计划”17人。\n\n学校主办有一批国内外影响广泛的报纸期刊。《中华戏曲》是海内外戏剧戏曲学界有广泛影响的辑刊之一；《山西师大学报》（社科版）是全国高校百强社科期刊、北方优秀期刊、山西省一级期刊；《语文报》和《英语周报》享誉海内外，是家喻户晓的“中国驰名商标”。\n\n学校实施开放办学，开展多层次、宽领域的教育交流与合作。先后与国外40余所大学和学术团体建立了友好合作交流关系，与国内兄弟院校的校际交往和交流合作日益频繁，与社会各界的互动合作日益广泛。学校积极拓展来华留学教育，现有来自俄罗斯、白俄罗斯、乌克兰、吉尔吉斯斯坦、越南、泰国等10个国家的在校留学生64名。\n\n学校重视精神文明建设，连续多年被评为山西省文明和谐单位，先后荣获全国师德师风建设先进单位、山西省“五一”劳动奖状和文明和谐单位标兵等荣誉称号，大学生社会实践活动连续多年受到中宣部、教育部和团中央的表彰。\n\n潮平两岸阔，风正一帆悬。当前，全校教职员工正以更加广阔的视野，更加开放的姿态，更加执著的努力，更加坚实的步伐，为把山西师范大学建设成为特色鲜明的高水平师范大学而努力奋斗！','SXNU','华北地区',1,'晋南师范专科学校、山西师范学院','师范',1958,'山西省','否',0,0,0,0,22,2,0,1160,60,17,2,0,0,0,'http://www.sxnu.edu.cn/'),(419,162,'太原师范学院','山西',21,'太原师范学院是一所以本科师范教育为主、非师范教育和硕士研究生教育协调发展的全日制高等师范院校，1999年3月经国家教育部批准，由山西大学师范学院、太原师范专科学校和山西省教育学院合并组建而成。山西大学师范学院创立于改革开放年代，1988年开办本科高等师范教育；太原师范专科学校成立于建国初期，1958年在山西大学堂原址办学；山西省教育学院诞生于民国年间，1929年在国民师范旧址创建。在各自长期的办学历程中，它们弦歌不辍、薪火传承，誉满三晋、声名远播，为国家建设、山西基础教育和经济社会发展培养了大批合格人才。\n\n学校现有4个校区，总占地面积约1884亩，其中办学主体在位于晋中市榆次区高校园区的新校区，新校区占地面积约1569亩，建筑面积约55万平方米。现有全日制在校本科生24000余名，硕士研究生近400名，生源来自全国27个省、市、自治区。学校组建近二十年来，锐意进取，开拓奋进，育人质量和办学水平逐年提升，呈现出欣欣向荣的发展态势。\n\n教学质量不断提高。学校现有教学院（系）21个，本科专业55个，分布在经济学、法学、教育学、文学、历史学、理学、工学、管理学、艺术学等九大学科门类。拥有2个国家级特色专业，7个省级优势和特色专业，6门省级精品课程，7个省级实验教学示范中心。此外开设了科学教育、人文教育等通才教育专业，在创新人才培养模式方面进行了成功探索。\n\n实践教学成果丰硕。师范专业学生参加“华文杯”全国师范院校师范生各学科教学技能大赛多次荣获特等奖，行知舞蹈团登上了人民大会堂“百花迎春”中国文学艺术界联欢会的舞台，行知合唱团多次与国内外著名艺术家及团体合作演出，一大批学子在国家级比赛中摘金夺银、屡获佳绩，彰显了学校优良的人才培养质量。\n\n学科科研水平明显提升。学校现有1个山西省“1331工程”重点学科建设计划优势特色学科，8个山西省重点建设学科，2个山西省重点扶持学科；1个山西省高校思想政治工作协同育人中心（培育），2个山西省协同创新中心，1个山西省工程技术研究中心，1个山西省“1331工程”重点创新团队，一个山西省“1331工程”工程研究中心，2个山西省高校人文社会科学重点研究基地，1个山西省高校重点实验室，2个山西省研究生教育创新中心。近三年，学校共承担国家自然科学基金和国家社会科学基金项目33项，国家艺术基金项目5项，国家外专局项目1项，承担省部级项目339项；获中国电视剧飞天奖（理论类）一等奖1项，山西省社会科学研究优秀成果一等奖2项，二等奖9项，三等奖15项；获国内授权专利81项。主办有《太原师范学院学报》（社会科学版和自然科学版）、《教学与管理》（中文核心期刊、中国期刊方阵“双效期刊”）等学术期刊。\n\n研究生教育稳步发展。现有中国语言文学、中国史、数学、地理学、生物学、艺术学理论6个一级学科硕士点。电子信息硕士、教育硕士、旅游管理硕士、艺术硕士4个专业学位授权点。\n\n师资实力明显增强。现有专任教师1016人，包括教授113人，副教授327人，具有博士学位教师215名，硕士生导师331名。教师中享受国务院政府特殊津贴专家2名，国家“万人计划”青年拔尖人才1名，省委联系的高级专家1名，山西省高校中青年拔尖创新人才1名，山西省学术技术带头人8名，山西省宣传文化系统“四个一批”人才4名，山西省教学名师14名，山西省高等学校优秀青年学术带头人7名。\n\n对外合作交流取得进步。与我校签署合作协议的国外高校23所，涵盖11个国家。学校聘请国内外知名专家、学者上百人次来校讲学，并选派优秀教师60余人次到国内外知名学府、科研院所访问学习或开展学术交流。近三年，赴美国纽约州立大学奥尔巴尼分校、北密歇根大学和意大利佛罗伦萨大学等留学深造的本科生已达56人。\n\n附属中学秉承“德育为先、能力为本、因材施教、全面发展”的办学理念和“敦品励学”的校训，课程改革不断深入，学科建设稳步推进，素质教育落地生根，中高考成绩名列太原市前茅，锻造出了“艰苦奋斗、团结协作、思变思新、追求卓越”的附中精神。当前正为强化品牌效应，建成全市领先、全省一流、全国知名的基础教育领域优质学校而努力。\n\n学校组建以来，始终秉承“崇德、博学、团结、创新”的校训精神，凝炼了“行知统一、造就良师、服务社会”的办学理念，大力弘扬以“德育为本、实践第一、创新教育”为主要内涵的行知精神，坚定不移地走教师教育特色鲜明的发展道路。学校行知文化育人特色鲜明，被全国陶研会授予“全国优秀陶研学校”称号；党建、思想政治教育、精神文明建设成效显著，连续两次荣获了“山西省高校文明单位标兵”称号，2018年获评“山西省文明校园”，获批山西省首批“三全育人”综合改革试点高校，营造了良好的育人氛围。\n\n诞生于世纪之交的太原师范学院如同一艘扬帆启航的大船，在世纪首航的波翻浪涌中高歌猛进，执着前行。2008年学校顺利通过教育部本科教学工作水平评估；2009年获得硕士学位授予单位立项建设资格；2010年开始建设新校区；2013年顺利通过硕士学位授予单位验收，同年入驻新校区；2014年正式招收全日制硕士研究生；2019年被山西省人民政府学位委员会、山西省教育厅正式列为博士学位立项建设单位。学校发展站在了新的起点上。\n\n2016年9月，学校第二次党代会科学谋划制定了“三步走”发展战略，为长远发展指明了方向。展望未来，学校坚持内涵发展，质量提升，着力以教师教育为本，实现多学科协调发展，推动办学质量和水平不断提高，为建成特色鲜明、省内前列、国内有一定影响的师范大学而努力奋斗。',NULL,'华北地区',1,'山西省教育学院、太原师范专科学校、山西大学师范学院','师范',1999,'山西省','是',0,0,0,0,21,0,0,810,54,3,0,0,0,0,'http://www.tynu.edu.cn/'),(532,163,'运城学院','山西',21,'运城学院坐落于中华文明重要发祥地---山西省运城市，前身为始建于1978年的运城师范专科学校。1989年12月，经国家教委批准，山西省人民政府决定，由运城师范专科学校、运城教育学院、河东大学三校合并组建运城高等专科学校。2002年2月经国家教育部正式批准成立运城学院。春风化雨、润物无声，建校以来始终秉承“立德有为”校训，大力弘扬自强不息、艰苦奋斗、精益求精、追求卓越的精神，为地方经济社会发展做出了重要贡献。\n\n学校现设17个教学科研单位，51个本科专业，涉及经济学、法学、教育学、文学、历史学、理学、工学、农学、管理学、艺术学等10大学科门类，拥有2个国家级特色专业建设点，1个教育部地方高校本科专业综合改革试点专业，5个省级特色专业建设点，2个省级优势专业建设点，1个省级人文社科重点研究基地，1个山西省“1331工程”重点学科建设计划优势特色学科，2个省级重点扶持学科，1个山西省高校重点实验室。学校占地面积3089亩，建筑面积60.34万平米，固定资产总值15.1亿元，教学仪器设备总值1.82亿余元，馆藏纸质图书177万册，电子图书62万册。现有全日制普通本科在校生20900余人。\n\n学校现有教职工1198人，其中专任教师917人，具有博士学位教师135人，具有硕士学位教师671人；有正高级职称45人，副高级职称277人，博士、硕士研究生导师69人。有全国优秀教师1人，享受国务院特殊津贴专家1人，山西省教学名师8人，山西省学术技术带头人3人，山西省高等学校优秀青年学术带头人4人，山西省高等学校“131”领军人才优秀中青年拔尖创新人才9人，山西省优秀教师6人，山西省模范教师4人。先后聘请山西省高等学校“131”领军人才特聘教授10人，其中中国工程院院士1名、具有国内领先水平的知名学者、学术带头人8人、优秀中青年拔尖创新人才1人；外聘学科专业带头人22人。工商管理教学研究创新团队入选了“1331工程”重点创新团队建设计划。物理与电子技术实验实践教研室荣获“山西省普通高校优秀教学团队”称号。\n\n学校坚持以教学为中心，不断深化教学改革，人才培养质量稳步提升。近年来，学生在全国大学生数学建模竞赛、计算机设计大赛、广告艺术设计大赛、机械创新设计大赛、机器人大赛等赛事中获得国家级奖励280余项。教师承担纵向省级及以上科研项目455项，其中国家自然科学基金23项，国家社科基金5项，教育部人文社科项目9项。\n\n学校不断拓宽开放办学之路，先后与美国、德国、英国、俄罗斯、澳大利亚、韩国的24所高校签订合作协议，与波兰、韩国、马来西亚、俄罗斯的6所大学开展交换生项目，在泰国建立汉语实习基地，与韩国高水平大学联合培养硕士。与中国电子电路行业协会共建“电子电路技术学院”，与山西亚宝药业合作举办“特殊医用食品班”，与运城制版集团等121家行业企业积极开展产学研合作。\n\n近年来，校党委和行政带领广大师生，抢抓机遇，深化改革，开拓前进，先后荣获“暑期社会实践活动全国先进单位”、“山西省党建和思想政治工作先进高校”、 “山西省大学生思想政治工作先进集体”、“山西省模范单位”、“山西省文明学校”、“山西省创新创业教育试点校”、“省级园林单位”等荣誉称号，赢得了良好的社会声誉。\n\n展望未来，运城学院将以党的十九大精神和习近平新时代中国特色社会主义思想为指引，进一步贯彻落实省委省政府、省高校工委、省教育厅安排部署和我校二次党代会精神，以服务地方经济社会发展为基本导向，以应用型人才培养为根本任务，坚持“一个方向，两手抓”办学治校总方略，坚持“三步跨越、四方针”改革发展新理念，坚持“五个布局、六计划”贯彻落实新举措，突出彰显河东文化特色，重点发展理工产教融合和特色师范教育，锐意进取、苦干实干，为建设特色鲜明的高水平应用型地方本科院校而努力奋斗！\n\n运城学院正以崭新的姿态，昂首迈向更加辉煌的明天！',NULL,'华北地区',1,'运城师范专科学校、运城教育学院','师范',1978,'山西省','是',0,0,0,0,17,1,0,1201,51,0,0,0,4685,0,'http://www.ycu.edu.cn/'),(521,164,'忻州师范学院','山西',21,'忻州师范学院是一所省属全日制普通本科院校，坐落在山西省忻州市。学院前身可追溯到1958年创建的忻县师范专科学校，1984年更名为忻州师范专科学校，1998年与忻州地区教育学院、忻州职工大学、山西广播电视大学忻州分校合并组建忻州师范高等专科学校。2000年经教育部批准，忻州师范高等专科学校与忻州师范学校合并建成忻州师范学院。学院为教育部、财政部确定的首批国家级“人才培养模式创新实验区”；被国务院授予“全国‘两基’工作先进单位”荣誉称号。\n\n60年来，学院始终秉承“厚学启智，修德树人”的校训，坚持面向基础教育、研究基础教育、服务基础教育和区域经济社会发展，现已发展成为以本科教育为主，兼顾专科教育和继续教育的普通高等师范院校。现有26个教学单位，41个本科专业（师范类专业19个、非师范类专业22个），涵盖法、教、文、史、理、工、管、艺等8个学科门类。学院现有山西省“1331工程”优势特色学科1个，山西省重点扶持学科2个；山西省“服务产业创新学科群建设计划”培育项目1项，山西省协同创新培育项目1项；教育部、财政部确定的特色专业建设点3个，国家级综合改革试点专业1个，省级特色专业、优势特色专业5个；省级精品资源共享课程7门，省级精品课程4门；省级普通高校实验教学示范中心8个。\n\n学院现有全日制在校学生19216人；在编教职工1231人，其中，专任教师822人，副高及以上职称教师309人，硕士及以上学位教师658人，山西省“百人计划”海外高层次人才2人，山西省学术技术带头人1人，山西省教学名师9人，山西省高校“131”领军人才、学术带头人和优秀中青年拔尖创新人才8人，兼职博士生、硕士生导师32人。\n\n学院现有五个校区，总占地面积约1574.33亩，校舍总面积526753.85平方米。学院拥有较为先进的教师教育综合训练中心、计算机校园网络平台、多媒体教室、体育馆及各类专业实验室、琴房、画室、练功房等教学设施。截至2017年底，学院共有馆藏图书325万册，其中，纸质图书154万册、电子图书171万册；有电子资源数据库20个。\n\n学院坚持社会主义办学方向，牢固树立人才培养中心地位，全面落实立德树人根本任务，立足忻州等经济欠发达地区基础教育发展现状，持续深入推进教育教学改革，坚持开展扶贫顶岗实习支教工作，构建了高师院校、地方政府、中小学校“三位一体”协同育人机制，人才培养质量稳步提高。特别是1997年至今，累计派出43000余名大学生，到忻州市各县（市、区）和运城、阳泉、大同、雄安新区及海南等地共700余所农村中小学进行扶贫顶岗实习支教，培养了一批批有理想信念、有道德情操、有扎实学识、有仁爱之心，愿意为基础教育辛勤耕耘、默默奉献的优秀教师，缓解了经济欠发达地区农村基础教育师资短缺问题，有效地促进了城乡义务教育一体化，推动了乡村振兴战略的实施。时任国务委员陈至立批示：“忻州师范学院做了我们近年来一直想做但尚未做成的事。他们的做法，为深化师范院校的教学改革，加强学生思想政治工作，解决贫困地区教师队伍建设问题，提供了实践经验，很值得进一步总结推广”。时任教育部部长周济批示：“要认真总结山西忻州师院的经验，全面推进师范生顶岗实习半年的工作，这是师范生教育中带有战略意义的重要举措”。原全国人大常委会副委员长严隽琪、原全国政协副主席罗富和、原教育部部长周济，山西省副省长张复明等各级领导均到校视察指导并给予肯定，要求我院拓宽服务山老贫困地区的路子，为提升农村教育和地方经济社会发展，做出新的更大的贡献。\n\n多年来，学院为社会培养合格师资和各级各类人才7.5万余名，毕业生就业率、考研率、考取村官和特岗教师的比例，一直位居全省同类本科院校前列。其中师范类毕业生多数成为扎根山老贫困地区基础教育事业的骨干，为国家、山西和忻州的教育、文化、经济和社会全面进步提供了良好的人才支持，做出了积极的贡献。\n\n党的十九大以来，学院积极适应高等教育发展新形势，坚持党的教育方针，坚持“以本为本”，推进“四个回归”，按照“十三五”规划“注重内涵、突出特色、补齐短板、提升能力”的发展思路和第四次党代会“三步走”发展战略，牢固树立党建统领、立德树人、依法治校、人才强校、特色凸显等“五个理念”，着力实施修德树人、师资队伍改善、教学质量提升、学科与科研引领、创新驱动、办学空间拓展、现代大学治理、惠及民生等“八大工程”，不断提升综合办学实力，为建设特色鲜明、国内知名的高水平应用型师范大学而努力奋斗。 ',NULL,'华北地区',1,'忻县区师范专科学校、忻县地区五七学校、忻县地区师范专科学校、山西师范学院附属、忻县师范专科学校、忻州师范专科学校、忻州师范高等专科学校','师范',1958,'山西省','是',0,0,0,0,26,0,0,1291,38,0,0,0,4037,0,'http://www.xztc.edu.cn/'),(123,165,'内蒙古大学','内蒙古',22,'【历史沿革】内蒙古大学位于内蒙古自治区首府、历史文化名城呼和浩特市，距北京400余公里，是新中国成立后党和国家在少数民族地区创办的第一所综合大学。学校于1957年高起点创办，时任国务院副总理、自治区主席乌兰夫任首任校长（1956—1968），原高教部综合大学司副司长于北辰任副校长，中科院学部委员、著名生物学家、北京大学一级教授、耶鲁大学博士李继侗先生来校执教并任学术副校长。党和政府从北京大学等国内十几所著名高校，选派一批知名学科带头人和学术骨干组成了建校初期高起点的师资队伍，带来名校积淀悠久的治学传统和崇高大学精神，为学校后续发展奠定了坚实办学基础和优良学风校风。学校于1962年招收研究生，1978年被确定为全国重点大学，1984年获博士学位授予权，1997年被批准为国家“211工程”重点建设院校，2004年成为内蒙古自治区人民政府和国家教育部共建大学，2012年入选国家“中西部高校提升综合实力计划”高校，2016年被国务院确定为中西部“一省一校”高水平大学建设支持高校、被内蒙古自治区人民政府确定为“双一流”建设首选支持高校。\n\n【机构、学科与专业设置】学校由校本部、交通学院、满洲里学院以及艺术学院、鄂尔多斯学院校区构成，另设有1所新机制新模式独立二级学院——创业学院。校本部设有20个学院和1个体育教学部。\n\n学校学科涵盖哲学、经济学、法学、文学、历史学、理学、工学、农学、管理学、艺术学10大学科门类，拥有中国少数民族语言文学、动物学2个国家重点学科，生态学1个国家重点培育学科；有18个自治区重点学科、8个自治区重点培育学科。有7个博士学位授权一级学科、另有3个博士学位授权二级学科，26个硕士学位授权一级学科、另有6个硕士学位授权二级学科，10个硕士专业学位授权类别（其中工程硕士含7个工程领域），7个博士后科研流动站。\n\n学校是国家教育部批准的国家级大学生创新性实验计划学校、创新创业训练计划学校和全国首批深化创新创业教育改革示范高校。现设有87个本科专业，有100个本科教学实验室、58个校内外教学实习实训基地。现有4个国家级基础科学研究和教学人才培养基地、1个国家大学生文化素质教育基地、1个国家西部卓越基层法律人才培养基地，1个国家专业综合改革试点，2个国家级人才培养模式创新实验区，8个国家级特色专业，2个国家级实验教学示范中心，2个国家大学生校外实践教育基地，6门国家级精品课、3门国家级精品资源共享课、7门国家级精品视频公开课、2门国家级双语教学示范课、1门来华留学英语授课品牌课程，4个国家级教学团队。\n\n【在校生人数】全校有各类在校生30305人。其中，本科生19257人、研究生6580人，校本部本科生12287人、研究生6392人、来华留学生年接收量800余人。在校生中，蒙古族、达斡尔族、鄂温克族、鄂伦春族等少数民族学生占35%左右。\n\n【人才队伍】全校有在编教职工2836人。其中，教学科研人员1861人，教授275人、副教授581人；校本部有教学科研人员1047人，具有博士学位比例占64.5%，教授218人、副教授337人。入选教育部“长江学者奖励计划”特聘教授2人、教育部“长江学者和创新团队发展计划”研究团队2个、科技部创新人才推进计划重点领域创新团队1个；国家“百千万人才工程”5人、国家级教学名师奖获得者3人、全国首批青年拔尖人才支持计划1人、教育部“新世纪优秀人才支持计划”12人；“万人计划”哲学社会科学领军人才2人、科技创新领军人才1人；国家优秀青年科学基金项目1人；自治区“草原英才”工程人选99人、创新团队16个，自治区科技创新团队14个、教学团队23个。有国务院学科评议组成员3人，教育部科学技术委员会学部委员1人，教育部学科发展与专业设置专家委员会委员1人，教育部高等学校教学指导委员会委员18人。获“国家有突出贡献中青年专家”称号4人，享受国务院政府特殊津贴专家59人，全国“杰出专业技术人才”称号1人；“内蒙古自治区杰出人才奖”9人，“自治区有突出贡献中青年专家”称号33人；1个团队获“全国专业技术人才先进集体”荣誉称号。\n\n【科学研究】学校在蒙古学、生物学、生态学、化学等领域设有教育部人文社会科学重点研究基地、科技部省部共建国家重点实验室培育基地、农业部重点实验室、教育部重点实验室、教育部工程研究中心等部级重点研究平台6个；有自治区重点实验室、工程技术研究中心、人文社会科学重点研究基地35个。“十二五”以来，共完成国家和部委科研项目488项、新获批立项759项，完成自治区科研项目624项、新获批899项，到账科研经费6.2亿元。主持或承担国家科技重大专项、国家科技支撑计划等国家级自然科学重大重点项目45项，国家社科基金重大项目5项、重点项目1项，教育部人文社会科学重大攻关项目2项、重大委托项目1项。获国家科学技术奖和国家科学技术进步奖二等奖、国家技术发明奖各1项，教育部高等学校科学研究优秀成果二等奖1项；自治区哲学科学成果奖187项，自然科学奖、科技进步奖26项。基于Web of Science统计结果，2016年学校学术成果国际影响力增长到2010年的5.2倍。\n\n【社会服务】学校服务地方发展能力明显提升，在现代农牧业、生态环境建设、能源与新材料、矿物资源利用、生物医药、食品安全、新一代信息技术、民族文化大区强区建设、战略决策与咨询等领域的研究与应用取得重要进展，面向地方发展的贡献度与日俱增。实现了牛羊繁殖生物技术体系的完善与产业化应用，建立了牛、羊和骆驼生态养殖新模式；集成草地适应性放牧利用技术、退化草地综合改良技术和刈割草场持续利用技术，进行了现代家庭牧场生态畜牧业综合示范区建设；依托国家科技支撑计划项目，研究解决马铃薯行业产业中存在的共性关键技术；在传统蒙药基础上，利用现代药学技术开发创新蒙药；针对原矿杂质矿物质提纯分离关键技术问题，开发了系列膨润土深加工专利技术；为自治区发展战略性新兴光伏产业、建设光伏小镇与现代农牧业融合示范基地提供了重要技术支撑；开发出以蒙古文为主的多语种手机电子词典软件和蒙古文与基里尔蒙古文相互转换系统、国内首款蒙古语语音识别和语音合成系统等。\n\n【对外交流合作】学校与国（境）外的95所高校和科研机构建立了合作与交流关系，师生国际合作交流日益扩大和深入。与美国亚利桑那州立大学合作设有中美生态、能源及可持续性科学内蒙古研究中心，与日本爱知大学合作设有中日共同GIS应用与地域经济产业研究中心，与英国华威大学合作设有中英企业数字实验室。学校设有蒙古国研究中心、俄罗斯语言文化中心、加拿大研究中心、日本学研究中心、澳大利亚研究中心、美国研究中心，与俄罗斯卡尔梅克国立大学合作建有孔子学院。学校是国家教育部批准的首批来华留学示范基地高校，是接受中国政府奖学金来华留学生高校，是孔子学院奖学金来华留学生高校。学校设有国际教育学院，年接收来自亚、欧、美、非四大洲的蒙古、俄罗斯、日本、韩国、印度、泰国、巴基斯坦、老挝、越南、乌兹别克斯坦、也门、吉尔吉斯斯坦、波兰、美国、加拿大、加纳、卢旺达、多哥、几内亚、赞比亚等20个国家的来华留学生800余人。\n\n【办学条件】全校占地面积4772亩，建筑面积97.9万平方米，教学科研仪器设备总值8.67亿元。其中，校本部占地面积1845亩，建筑面积61.18万平方米，教学科研仪器设备总值6.99亿元。图书馆是CALIS（中国高等教育文献保障系统联合目录项目）B级成员馆、省级中心馆，全校印本文献372.35万册、中外文数据库95个、电子图书410余万种、全文电子期刊4.6万种。校园网覆盖全校教学科研单位和办公与生活区，校园网络中心是中国教育科研计算机网内蒙古地区主节点。\n\n【办学目标】学校遵循首任校长乌兰夫提出的“双重任务”办学基本方针：既要注重质量与水平、在全国达到一定高度、成为自治区最高学府，又要承担起繁荣发展内蒙古文化、培养少数民族知识分子的任务；坚持“立足北疆、面向全国、育人为本、特色强校”办学理念，秉承“求真务实”校训，弘扬“崇尚真知、追求卓越”的内大精神，日益成为祖国北部边疆高素质人才培养、高水平科技研究、高质量成果转化、高层次决策咨询和文化传承创新的重要基地。面向未来，在党和国家的特别关怀支持下，学校坚持走质量、内涵、特色和国际化发展道路，不断提升办学实力与水平，加快建设有特色高水平大学，为国家和地方经济社会发展做出新的更大贡献！','IMU','华北地区',1,'','综合',1957,'自治区','否',0,211,0,1,25,1,2,2727,82,26,7,0,0,0,'http://www.imu.edu.cn/'),(314,166,'内蒙古科技大学','内蒙古',22,'内蒙古科技大学坐落在首批全国文明城市、美丽的草原钢城——包头，前身为1956年建校的包头钢铁工业学校和包头建筑工程学校，隶属原冶金工业部，1958年两校合并组建包头工学院，1960年更名为包头钢铁学院，1998年划归内蒙古自治区管理，实行中央与地方共建，2000年内蒙古煤炭工业学校并入，2003年更名为内蒙古科技大学。1997年通过教育部本科教学合格评价，2008年以优秀等次通过教育部本科教学工作水平评估。1986年联合培养硕士研究生，1991年成为硕士学位授权单位，2013年成为博士学位授权单位。\n\n学校现已发展成为以冶金、矿业、煤炭、稀土为特色，以工为主，工、理、文、管、经、法、艺术、教育等学科协调发展，集研究生教育、本科教育、高职教育、继续教育、留学生教育为一体的多科性大学。\n\n学校设有学院、部、中心等教学单位20个。现有一级学科博士学位授权点2个，一级学科硕士学位授权点12个，二级学科硕士学位点10个，专业硕士学位授权领域11个。本科专业66个，其中国家特色专业建设点4个，教育部卓越工程师教育培养计划专业3个，工程教育认证专业2个，教育部专业综合改革试点1个，自治区专业综合改革试点1个，自治区级重点建设专业4个，自治区级品牌专业22个，现有自治区级精品课程53门。\n\n学校现有专任教师1335名，其中高级职称629人，具有博士、硕士学位1123人，博士生导师19人，硕士生导师446人（校外兼职导师148名）。教师队伍中拥有全国杰出专业技术人才、全国首批“新世纪百千万人才工程”入选者、内蒙古首次“科教兴区”特别奖获得者、内蒙古首位中组部“千人计划”入选者、内蒙古首批国家杰出青年基金获得者，还有“长江学者特聘教授”、国家特支计划国家重点领域创新团队、教育部创新团队，自治区教学名师、教学团队等。\n\n学校现有各类全日制在校生24281名。60多年来，为国家输送了10万余名合格人才，毕业生就业率连续多年位居内蒙古高校领先水平，2000年、2009年学校2次被教育部评为“全国普通高校毕业生就业工作先进集体”，2010年成为50家“全国毕业生就业工作典型经验高校”之一，2012年，学校被中华人民共和国国务院授予“全国就业先进工作单位”光荣称号（全国共15家高校）。\n\n学校拥有省部级重点学科9个，国家地方联合工程研究中心1个，省部共建国家重点实验室培育基地1个，内蒙古协同创新中心1个，内蒙古协同创新培育中心1个，省部共建教育部重点实验室1个，内蒙古新型科技研究开发机构1个，内蒙古工程研究中心1个，内蒙古重点实验室15个，内蒙古工程技术研究中心6个，院士专家工作站3个，内蒙古哲学社会科学研究基地1个，内蒙古高校人文社会科学研究基地1个，内蒙古高校创新方法推广应用基地1个，自治区生产力促进中心2个，国家级“本科教学工程”大学生校外实践教育基地2个，省部级实验教学示范中心9个。\n\n学校积极面向经济社会发展开展科学研究，先后获得国家级科技进步二等奖1项、三等奖1项，教育部科技进步一等奖1项，内蒙古自然科学一等奖4项、科技进步一等奖11项，科研获奖位居内蒙古高校前列。近五年来，学校承担国家973计划项目、国家863计划项目、国家重点研发计划、国家科技支撑计划、国际合作项目、军工项目、国家自然基金、国家社科基金、教育部人文社科项目等国家级科研项目264项，被三大检索收录论文1778篇，获授权专利574项，其中发明专利244项。白云鄂博矿多金属资源综合利用创新团队研制出的矿渣微晶玻璃管材，被确定为生产该类产品的国家行业标准，并已实现产业化。学校2008年被内蒙古自治区政府评为“科技创新先导型大学”，2010年被国家知识产权局评为全国知识产权试点单位。\n\n学校学生课外科技创新成果显著，近三年获得省部级以上各类学生课外科技作品竞赛奖励586项，其中国家级奖励188项，公开发表的学术论文60篇，获批专利56项。大学生创业孵化基地被共青团中央授予“全国大学生创业示范园”称号（内蒙古高校唯一一家），被自治区人社厅、财政厅、科技厅认定为全区首批“自治区大学生创业孵化基地”、“自治区示范性创业园创业孵化基地”、“内蒙古自治区众创空间”等荣誉称号。\n\n学生竞技体育走在全国高校前列：截止2016年，我校运动员在国内外体育竞赛中获省部级及以上奖牌396枚，其中国际体育竞赛金牌9枚，国家级体育竞赛金牌26枚，省部级体育竞赛金牌168枚。在全国大学生田径最高级别比赛上，我校连续七年获得全国高校前十名。我校学生运动员连续五届代表国家参加世界大学生运动会田径项目并获得一金四银一铜，先后代表国家参加三届世界大学生越野锦标赛，三名学生运动员曾参加2004、2008、2016年的奥运会。2011年学校当选全国大体协副主席单位，2014年在自治区率先成立内蒙古西部校园足球联盟和内蒙古校园足球联合会并被自治区命名为内蒙古自治区青少年足球夏令营活动基地，为内蒙古校园足球事业做出了应有的贡献。\n\n学校坚持开放办学，积极开展对外交流与合作办学，先后与30多个国家和地区的50多所高等院校和科研机构建立了交流合作关系，在教学、科研和人才培养方面开展了全面合作。学校引进智力，促进发展，先后聘请彭苏萍、薛其坤院士等168名专家任兼职或客座教授。\n\n学校作为内蒙古自治区高校管理体制改革试点单位，大力进行内部管理体制改革，在坚持党委领导下的校长负责制的前提下，不断强化校党委的领导核心地位，积极探索以学术工作委员会为主体的学术权力决策机制，以校长办公会为基本形式的行政执行机制，以学生工作委员会为主要形式的中国特色大学生理想信念、道德规范的教育管理机制，以巡视督导工作委员会为主要抓手的监督机制和以功能相对集中的专项工作委员会为主导的政策制定与组织协调机制。\n\n展望未来，内蒙古科技大学将紧密团结在以习近平同志为核心的党中央周围，以立德树人为根本、以“双一流”建设为目标、以党建工作为保障，大力推行“党委领导、校长负责、民主管理、教授治学”的管理体制改革，积极践行“学术为本、学业为上、学生为体、学者为魂”的办学理念，秉承“自强不息、敢为人先”的校园精神和“百炼成钢”的校训，争取早日建成人民满意的好大学。','IMUST','华北地区',1,'冶金工业部包头工业学校、包头工学院、包头钢铁工业学院、包头钢铁学院、包头钢铁学校','综合',1956,'自治区','是',0,0,0,0,15,0,1,1400,64,12,2,1,5650,0,'http://www.imust.cn/'),(260,167,'内蒙古工业大学','内蒙古',22,'内蒙古工业大学坐落在内蒙古自治区呼和浩特市，前身是始建于1951年的绥远省高级工业学校。1958年在清华大学等支援下成立内蒙古工学院，曾隶属机械工业部、农业机械部，1983年划归内蒙古自治区，1993年更名为内蒙古工业大学。学校目前在新城校区、金川校区、准格尔校区办学，占地面积3227亩。\n\n经过66年的发展，学校已建设成为一所以工为主，工、理、文、经、管、法、教育、艺术相结合，具有博士、硕士、本科完整人才培养体系的特色鲜明的多科性大学。现设有25个院（部、中心），拥有62个研究院(所)；有博士后流动站1个，博士学位授权一级学科3个，博士学位授权二级学科（不含一级学科覆盖点）1个；硕士学位授权一级学科19个，硕士学位二级学科（不含一级学科覆盖点）10个，具有工程硕士（14个授权领域）、工商管理硕士（MBA）、工程管理硕士（MEM）、建筑学硕士、社会工作硕士、翻译硕士等6个专业学位授予类别；有本科专业69个，具有推荐优秀本科生免试攻读硕士研究生资格。学校现有全日制本专科学生22000余人，博士、硕士研究生4400余人。\n\n“十二五”以来，先后承担国家自然科学基金、国家社会科学基金、国家“863”计划、国家科技支撑计划等各级各类科研项目1600余项，获省部级以上科学技术奖励50项，其中国家科技进步二等奖2项，内蒙古科技进步奖、自然科学奖一、二等奖19项，内蒙古哲学社会科学优秀成果政府奖一、二、三等奖29项，获得国家授权专利200余项，被SCI、EI、ISTP、CSSCI等检索系统收录论文2000余篇。\n\n学校现有教职工2026人，专任教师1312人，其中教授178人，副教授421人，博士生导师54人，硕士生导师557人，专任教师中有89.71％获得博士、硕士学位。教师中，72人次享受国务院政府特殊津贴或被授予国家和自治区“有突出贡献中青年专家”称号，1人入选国家“百千万人才工程”一、二层次，9位教师获得“内蒙古杰出人才奖”，12人入选教育部“新世纪优秀人才工程”，48人入选内蒙古“草原英才”工程，7个团队入选“草原英才”创新团队，42人入选自治区“新世纪321人才工程”一、二层次，11人入选内蒙古自治区高等教育人才培养“111”工程一、二层次，14人入选自治区“高等学校科技英才支持计划”。学校还聘请了一批国内外知名学者担任兼职教授。\n\n学校现有5个教育部特色专业建设点，4个教育部卓越工程师培养计划试点专业，1个国家级校外大学生工程实践基地，1个国家级实验教学示范中心，1个国家级专业综合改革试点专业，4个自治区级重点建设专业， 25个自治区级品牌专业，8个自治区级实验教学示范中心，18个自治区级教学团队，16名自治区教学名师。\n\n学校在2006年教育部本科教学工作水平评估中获得“优秀”，2012年被评为“全国毕业生就业50所典型经验高校”，2016年进入国家“中西部高校基础能力建设工程（二期）”支持院校行列，是教育部批准设立的首批32个国家级大学生文化素质教育基地之一。学校是全国“党的建设和思想政治工作先进高等学校”“全国普通高等学校就业工作先进集体”。先后被自治区授予“教育先进集体”“思想政治工作优秀单位”“精神文明先进单位”“高校学生工作先进院校”“人才工作先进单位”“民族团结进步先进集体”等荣誉称号。\n\n学校坚持开放办学理念，积极开展对外交流与合作。与清华大学、天津大学等多所国内著名院校和蒙古、俄罗斯、美国、德国、法国、日本、加拿大等19个国家（地区）的56所大学、2个科研院所建立并开展实质性合作与交流。2005年、2007年先后成为“内蒙古自治区政府留学生奖学金项目”和教育部留学生项目“中国政府奖学金项目”招生单位。与呼和浩特、包头、乌海、乌兰察布市等地方政府和40余家大中型企业签订了合作协议，共建创新中心和研发平台。\n\n六十六年来，学校培养了近15万名优秀人才，他们奋斗在大江南北、工作在各行各业，许多已成为大中型企业的领军人物和技术骨干、高校或科研院所的专家学者、党和政府的重要领导干部。站在新的历史起点上，学校将秉承“博学躬行，尚志明德”的校训，弘扬“唯实尚行”的校风，坚持创新、特色、内涵、区域、开放的发展理念，努力建设国内知名、民族地区一流的教学研究型大学。','IMUT','华北地区',1,'绥远省高级工业学校、绥远省归绥工业学校、华北第三工业学校、内蒙古第一工业学校、呼和浩特工业学校、呼和浩特机械制造工业学校、内蒙古工学院','理工',1951,'自治区','否',0,0,0,0,22,0,0,2026,70,18,3,0,5781,0,'http://www.imut.edu.cn/'),(238,168,'内蒙古农业大学','内蒙古',22,'内蒙古农业大学是自治区重点大学，是国家首批32个大学生文化素质教育基地之一。2001年学校成为国家西部大开发“一省一校”重点支持建设院校，2012年成为自治区人民政府和国家林业局合作共建高校，2013年进入国家“中西部高等教育振兴计划”支持院校行列。学校位于中国历史文化名城——呼和浩特市，是“全国绿化模范单位”，被誉为塞外“花园式高等学府”。 \n\n内蒙古农业大学是内蒙古自治区成立最早的本科高等院校，由原内蒙古农牧学院和内蒙古林学院于1999年合并组建而成。追根溯源，1952年，原河北农学院、平原农学院的畜牧兽医系和山西农学院的兽医专业迁至呼和浩特市，组建内蒙古自治区第一所本科高等学校——内蒙古畜牧兽医学院。1960年，内蒙古畜牧兽医学院更名为内蒙古农牧学院。1958年，内蒙古林学院成立，是新中国在少数民族地区建立的第一所高等林业院校。 \n\n学校是一所以农为主，以草原畜牧业为优势特色，具有农、工、理、经、管、文、法、艺等8个学科门类的多科性大学。学校现设有动物科学学院等22个院（部），职业技术学院是全国高等职业教育示范院校建设单位。学校现有全日制在校学生34382人，其中，博士、硕士研究生2657人。建校60多年来，共培养各类人才12万余人，遍布祖国各地，现大多已成为自治区农、牧、林、水、草、乳、沙等行业的领军人物和技术骨干。 \n\n2002年和2008年，学校两次接受教育部本科教学工作水平评估，均获得“优秀”。学校现有84个本科专业，拥有国家卓越农林人才教育培养计划试点项目2个（含8个专业），有7个国家级特色专业、1个国家级专业综合改革试点、1个国家级教学团队。有5门国家级精品课程、2门国家级精品资源共享课。有1个国家人才培养模式创新实验区、2个国家级实验教学示范中心、1个国家级大学生校外实践教育基地、3个首批教育部和农业部批准农科教合作人才培养基地。有40个自治区级品牌专业、6个自治区重点建设专业，1个自治区级本科专业综合改革试点，有84门自治区级精品课程、4门自治区级优质精品课程，有19个自治区级教学团队，8个自治区级实验教学示范中心。获国家级教学成果奖8项、自治区级教学成果奖59项。“十一五”以来，3部教材获国家级教材奖、95部教材被列为国家“十一五”规划教材，其中7部为国家级重点教材、42部为教育部面向21世纪课程教材，1部入选国家百门精品课程教材，2部获中华科教基金奖。 \n\n学校现有1个国家重点学科、3个国家重点（培育）学科、1个农业部重点学科、2个国家林业总局重点学科、5个自治区重点一级学科、22个自治区重点学科、4个自治区重点（培育）学科。有10个博士后科研流动站。有11个一级学科博士学位授权点，21个一级学科硕士学位授权点，7种专业学位授权点。学位授权点涉及经济学、法学、理学、工学、农学、管理学、艺术学等学科门类。 \n\n“十二五”以来，学校新上国家973、863、科技支撑、国际科技合作以及公益性行业科研专项、自治区重大科技专项等科技项目和课题1324项，其中，国家自然科学基金390项，包括国家杰出青年科学基金1项、优秀科学青年基金1项、重点项目4项、重点国际合作研究项目1项；科研项目进账经费6.8亿元。先后有973个项目通过了鉴定评审和结题验收。学校在特色动物基因组及乳酸菌等研究领域的3项成果发表在影响因子超10.00的国际知名期刊《Nature Communications》上，研制开发的“益生菌Lactobacillus casei Zhang”“口蹄疫疫苗中完整病毒含量的破乳检测技术”等成果成功地应用于伊利集团、金宇集团、内蒙古金河生物制品有限公司等知名企业。学校承担并组织实施的草原绿色肉业、牧区生产生态生活优化、传统乳制品现代化生产技术、内蒙古春玉米小麦持续丰产高效技术、河套灌区粮油作物节水技术等国家和行业科技集成与示范类项目，取得了重要进展和成果，在科技引领和支撑区域经济发展方面发挥了重要作用。两位教师获得自治区科学技术特别贡献奖，一位专家获得何梁何利科学与技术创新奖。学校作为第一完成单位获得省部级科技和社科类成果奖51项，作为主要完成单位获得国家科技进步二等奖2项、科普二等奖1项。\n\n学校立项资助30个校级科技创新（培育）团队，其中有3个团队入选科技部重点领域创新团队支持计划、3个团队入选教育部科技创新团队支持计划、8个团队入选自治区科技创新团队支持计划。“乳酸菌筛选与乳品发酵技术国家地方联合工程实验室”获得国家发改委立项建设，学校已建成1个国家级野外科学研究观测站，38个省部级重点实验室、工程研究中心、野外台站和人文社科基地。2010年，学校被科技部批准为“国际科技合作基地”。2013年，内蒙古农业大学新农村研究院被列入第二批国家高等学校新农村发展研究院建设计划。2015年学校“绿色畜产品加工协同创新培育中心”和“现代马业健康发展协同创新培育中心”分别获得自治区教育厅批准立项建设。 \n\n学校现有教职工2650人，其中专任教师1551人，专任教师中教授306人、副教授467人。现有博士生导师133人，硕士生导师454人；有特聘院士9人，“长江学者奖励计划”特聘教授和“国家杰出青年科学基金”资助1人，享受政府特殊津贴专家88人，国家、自治区有突出贡献的中青年专家46人，国家“百千万人才工程”一、二层次人选7人，国家、自治区优秀留学回国人员15人，全国劳动模范、先进工作者3人，自治区深入工农牧业生产第一线做出突出贡献的科技人员18人，中国青年女科学家1人，自治区“333人才工程”首席专家3人、“新世纪321人才工程”一、二层次人选18人，入选自治区“草原英才”60人、草原英才团队21个，有内蒙古自治区杰出人才奖8人。有国家级教学名师1名、自治区级教学名师24人、自治区级教坛新秀14人。有教育部新世纪优秀人才支持计划6人。 \n\n学校由呼和浩特市校区、职业技术学院校区和海流图科技园区3部分组成，形成了一校五区的办学格局。学校总占地面积1000公顷，其中，教学科研用地600余公顷。校舍建筑总面积108.1万平方米，标准田径场（馆）4个，教学科研仪器设备总值8.26亿元。 \n\n学校图书馆是内蒙古自治区农、林、牧等行业重要的文献信息资源，馆藏纸质文献186.61（包含职院28.05）余万册，中外文期刊1185种，电子图书180.9万种，创建个人数字图书馆500个。拥有91个中外文数据库，其中包括SCI、EI、Willy等世界顶尖外文数据库及3个自建特色数据库。 \n\n学校不断加强对外交流，与加拿大、美国、日本和蒙古国等十几个国家的60余所大学和科研机构建立了校际合作及学术交流关系。学校成立了“中加可持续农业研究与发展中心”、“马利克管理中心内蒙古农业大学分中心”，“教育部出国留学培训与研究中心”。近五年来，外国专家教授700余人次来校开展交流合作、访问讲学等活动，部分担任学校特聘教授和顾问。学校还选派了380余名教师到国外学习。目前，有来自蒙古国、俄罗斯、加拿大、波兰、柬埔寨等国家的130多名外国留学生在校学习。 \n\n学校校园文化活动丰富多彩，以“十佳毕业生评选”活动为代表的校园十大品牌活动和教职工“红烛杯”文艺演出深受师生喜爱，一、二课堂协同联动推动学生在全国数学建模竞赛、机器人大赛等赛事中屡创佳绩。我校男子篮球队连续多年获得自治区高校冠军，并代表内蒙古参加中国CUBA大学生篮球联赛；校田径队连续多年荣获自治区大学生田径运动会团体总分第1名，并在全国赛事中屡获佳绩。在2014年10月韩国仁川亚运会上，我校2011级学生施小霖作为国家队主力队员兼教练，喜获男子软式网球男团比赛铜牌。在2012年9月全国第九届大学生运动会女子甲组标枪、铁饼比赛中，2009级学生谭丽娜、李爱飞分别以48.65米、47.00米的成绩夺得金牌和银牌。2015年，我校在自治区“主席杯”校园足球总决赛中，获得男子校园组冠军，女子校园组冠军、女子超级组亚军。 \n\n学校党委荣获“全国先进基层党组织”荣誉称号。职业技术学院获得“全国教育系统先进集体”荣誉称号，学校被授予“全区五一劳动奖章”，动物科学与医学学院被授予“全国五一劳动奖章”，教务处获得“全国普通高等学校优秀教务处”等称号，校团委多次被评为“全区五?四红旗团委”，学校连续多年获得“全国大中专学生志愿者暑期“三下乡”优秀单位”“全区高校学生工作和毕业生就业工作先进集体”等。校史展览馆被确定为“内蒙古大学生德育基地”。 \n\n全校师生正凝心聚力、振奋精神、鼓足干劲，以新理念引领新发展,继续推进“质量立校、人才强校、科技兴校”工程，大力提升创新创业教育能力，不断调结构、抓内涵、固基础、提质量，为努力实现学校“十三五”发展奋斗目标，办人民满意的高等教育而努力奋斗。','IMAU','华北地区',1,'内蒙古畜牧兽医学院、内蒙古农牧学院','农林',1952,'自治区','是',0,0,0,0,22,9,1,2639,0,24,13,1,7245,0,'http://www.imau.edu.cn/'),(280,169,'内蒙古师范大学','内蒙古',22,'内蒙古师范大学创建于1952年，是新中国成立后党和国家在边疆少数民族地区最早建立的高等学校，自治区重点大学，是具有鲜明教师教育和民族教育特色的综合性师范大学。经过几代人的艰苦创业、实践探索，现已成为自治区培养基础教育、民族教育师资和蒙汉兼通少数民族复合型人才的重要基地，是自治区中学教师培养中心、中小学教师继续教育中心、基础教育与民族教育改革发展研究中心，被社会誉为“民族教育的摇篮”。\n\n学校拥有盛乐校区和赛罕校区两个校区，占地总面积3800余亩（约253公顷），校舍建筑面积80余万平方米，固定资产总值近20亿元，其中教学仪器设备总值3.8亿余元。馆藏图书230余万册，电子图书110余万种，配备磁盘存储100TB。学校下设21个职能处室，33个二级学院（盛乐校区20个、赛罕校区13个），4个合作办学学院，1个独立学院，2个教研部，4个教辅部门，5个服务机构及校办企业，3个附属中学。现有教职工2053人，其中专任教师1274人，具有硕士、博士学位教师929人，占专任教师总数的73％，具有副高级以上职称教师618人，占专任教师总数的48.5％；有博士生、硕士生导师909人，有自治区级教学名师18人、教坛新秀10人，国家和自治区有突出贡献中青年专家23人，入选国家“百千万人才工程”、自治区“新世纪321人才工程”113人（次）、自治区“草原英才”26人，享受国务院特殊津贴专家25人。\n\n学校有在校全日制本专科生24524人（赛罕校区7925人，盛乐校区16599人），其中蒙古语授课本专科生5379人，有各类研究生4168人，各类成人教育学生5756人，各类留学生264人，形成了多层次的人才培养体系。学校开设79个本科专业（其中蒙古语授课专业30个），涵盖10大学科门类。有国家级精品课程1门，国家级精品视频公开课1门，国家级教学团队2个，教育部特色专业建设点6个；有自治区级品牌专业32个，自治区重点建设专业4个，国家级“专业综合改革试点”1个，自治区级“专业综合改革试点”1个，自治区精品课程73门，自治区级教学团队15个；有中央与地方高校共建基础实验室和特色实验室12个，自治区级实验教学示范中心9个，自治区重点学科和重点培育学科18个，校级重点学科37个。\n\n学校拥有博士学位授予权一级学科2个、二级学科4个，博士后科研流动站1个，硕士学位授予权一级学科24个、二级学科131个，硕士专业学位10种；具有硕士研究生免试推荐入学资格。学校有科研机构91个，自治区协同创新中心1个、校级协同创新中心6个，自治区级重点实验室6个、高校重点实验室2个、高等学校重点实验室培育基地1个、高等学校人文社会科学重点研究基地4个、哲学社会科学研究基地2个、工程技术研究中心2个、工程实验室1个、工程研究中心1个、院士专家工作站1个，同时拥有全国“人文社科普及基地”1个。\n\n建校60余年来，学校形成了“求真务实，育人至上，学术创新，服务社会”的办学理念，强化了“双学位教师教育和蒙汉兼通人才培养”的办学特色，秉承“献身、求实、团结、奋进”的校训，已培养输送各类毕业生20万余人。学校坚持开放办学，与蒙古、俄罗斯、日本、韩国、马来西亚、美国、英国、德国、法国、荷兰、加拿大、澳大利亚等国家及国内一些知名院校和科研机构建立了良好的合作关系；2009年被批准为汉语国际教育硕士研究生培养单位。学校先后聘请包括五位两院院士在内的近四百位国内外著名专家学者任学校学术顾问、客座教授和兼职教授。\n\n进入新世纪，学校坚持以邓小平理论、“三个代表”重要思想、科学发展观为指导，认真执行党的教育方针和民族政策，坚持全面建设和又好又快的发展方针，全面加强党的建设；以学科建设为龙头，教学科研并重，突出内涵发展；以本科和研究生教育为主体，以人才培养为根本，以提高教育教学质量为核心，以体制创新为保障和动力，努力促进质量、效益、规模、结构的协调发展；以办人民满意的教育为根本要求，求真务实、艰苦奋斗、开拓创新，不断提高服务地区经济社会发展的能力。\n\n目前，全校上下正以党的十八大精神为指导，按照“抓机遇、谋发展、强特色、上水平、建名校”的发展思路，坚持“全面发展、突出重点、重点突破、带动全面”的发展策略，艰苦奋斗，开拓创新，为实现“把学校建设成特色鲜明的教学研究型综合性师范大学”的宏伟目标而努力奋斗!','IMNU','华北地区',1,'内蒙古师范学院、','师范',1952,'自治区','否',0,0,0,0,31,0,0,2053,92,131,4,0,8028,0,'http://www.imnu.edu.cn/'),(318,170,'内蒙古民族大学','内蒙古',22,'内蒙古民族大学始建于1958年，2000年由原内蒙古民族师范学院、内蒙古蒙医学院、哲里木畜牧学院合并组建而成，是内蒙古自治区重点建设高校，内蒙古自治区人民政府和国家民族事务委员会共建高校，也是国家“十三五”中西部高校基础能力建设工程（二期）支持高校。经过59年的建设与发展，学校现已发展成为一所学科门类齐全、师资力量较为雄厚、教学设施相对完善、办学形式多样、教学质量优良、办学特色鲜明的综合性民族大学，累计培养各类人才16万余名，为国家和民族地区经济社会发展和稳定做出了重要贡献。\n\n学校位于内蒙古自治区东部，坐落在被誉为“科尔沁草原明珠”的通辽市，拥有霍林河、西拉木伦2个校区，占地面积104.34万平方米，建筑面积53.24万平方米，固定资产总值9.06亿元，教学科研仪器设备总值2.34亿元，馆藏纸质图书164万余册。\n\n学校设有27个教学单位、6个教辅单位、1个附属单位，开设本科专业74个，涵盖了经济学、法学、教育学、文学、历史学、理学、工学、农学、医学、管理学、艺术学等11个学科门类。有服务国家特殊需求蒙药学博士人才项目1个，硕士学位授权一级学科11个，涵盖了73个硕士学位授权二级学科，有专业硕士学位授权点4个，具有教授自评权。\n\n现有全日制在校学生22591人，其中普通本科学生 20819人、普通专科学生381人、少数民族预科学生119人、硕士研究生753人、博士研究生9人、留学生510人，另有成人教育学生1870人。有教职工1739人，其中专任教师1119人，具有高级职称教师570人，具有博士学位教师243人。另有附属医院职工1475人，其中医生475人，具有正高级职称103人。在教师队伍中，享受国务院政府特殊津贴专家19人，全国先进工作者1人、全国“五一”劳动奖章获得者2人、全国优秀教师2人、全国高校优秀思想政治理论课教师1人、全国高等学校教学指导委员会委员2人。学校实施了“柔性引进人才计划”，引进高层次人才 123人次，其中院士3名、社科院学部委员2名、“长江学者”3名、国家杰青1名;实施“科尔沁学者”计划，评选十届“科尔沁学者”共319人次；实施了“示范课教师计划”，已评选四届38人。\n\n现有国家级教学团队1个，有国家级特色专业3个、综合改革试点专业1个、“卓越医生（中医）教育培养计划”项目专业1个、产学合作专业综合改革试点项目1个，有国家级实验教学示范中心1个、大学生校外实践教育基地1个、大学生创新创业训练基地1个，有自治区级综合改革试点专业1个、重点建设专业4个、品牌专业17个、实验教学示范中心4个、精品课程39门、教学团队11个。获国家级教学成果二等奖1项，自治区级教学成果一、二、三等奖共29项。\n\n学校有省部级重点学科（含重点培育学科）13个；有省部级科研平台17个，其中国家民委—教育部重点实验室1个，自治区级重点实验室（工程中心）8个，国家民委人文社会科学研究基地 1个，全国社会科学普及基地 1个，国家体育总局研究基地1个，自治区研究基地5个。有省部级协同创新中心1个，省部级科技创新团队4个。“十二五”以来，获国家社会科学基金项目73项，国家自然科学基金项目141项，被SCI、EI、CPCI检索论文760篇，获国家科技进步二等奖1项，自治区级各类科研奖励47项。有6个版本的学报在国内外公开发行，其中社会科学版（汉文）为中国人文社会科学核心期刊（扩展版），《民族高等教育研究》是全国首个研究民族高等教育的专业期刊。\n\n学校是中国政府奖学金来华留学生培养单位，与俄罗斯布里亚特国立农业大学、蒙古国国立医科大学、美国杰克逊威尔州立大学、澳大利亚纽卡斯尔大学、日本上越教育大学、韩国江原大学等28所国外高校和机构建立了友好合作关系。其中与蒙古国国立医科大学的合作交流成效显著，在蒙古国共建了蒙药研发工程重点实验室、蒙药制药厂、合作国际蒙医医院和蒙药联合研究中心。学校是国家留学基金委“国际本科学术互认课程（ISEC）”项目培养单位，开设“ISEC”项目专业3个。与美国托马斯大学开展了护理学本科专业双学士学位联合培养项目。\n\n蒙医药是我校特色学科和优势学科，2012年蒙药学获批服务国家特殊需求人才博士点培养项目，2015年蒙医学专业获批教育部“卓越医生培养计划”，获国家级科研项目300余项、经费达到8000余万元，在学科建设、人才培养和产学研结合等方面处于国内外领先地位，成为我国蒙医学领域教学、科研、临床人员较为集中，实验室仪器设备和临床教学条件优越，研究方向明确、民族特色鲜明的重要人才培养基地和学术研究基地。目前，学校贯彻落实李纪恒书记视察我校时重要指示精神，全力推进蒙医药国家级重点实验室和一流学科建设。\n\n现有直属附属医院1所，是国家三级甲等民族医综合医院，是全国唯一一家国家级重点蒙西医结合医院，也是国家执业医师考试考点、国家首批蒙医住院医师规范化培训基地和临床住院医师规范化培训基地，有医护人员1094人，开放床位1300张。有非直属附属医院4所，第二附属医院（内蒙古林业总医院）、第三附属医院（赤峰市第二医院）、呼伦贝尔临床医学院（呼伦贝尔市人民医院）。附属蒙医医院（通辽蒙医中心医院 ）在建。\n\n近年来，学校获得多项荣誉。2009年学校被评为全国“民族团结进步模范集体”，2012年被评为“全区对外文化交流工作先进单位”和“自治区一级信誉等级涉外院校”，2014年附属医院和蒙医药学院分别被评为“全国民族团结进步模范集体”、“全国教育系统先进集体”。\n\n面向未来，学校秉承“博学明理、崇德至善”的校训，坚持“务实办学、创新办学、开放办学、特色办学”的理念，不断深化综合改革，促进科学发展、内涵发展和转型发展，努力把学校建设成为具有鲜明地方特点、民族特色和学科优势的高水平综合性民族大学。','IMUN','华北地区',1,'哲里木畜牧学院、内蒙古民族师范学院、内蒙古蒙医学院','民族',1958,'自治区','是',0,0,0,0,27,5,3,1961,74,73,1,0,0,0,'http://www.imun.edu.cn/'),(119,171,'辽宁大学','辽宁',11,'辽宁大学是一所具备文、史、哲、经、法、理、工、管、医、艺等学科门类的省属综合性大学。学校现有三个校区，即沈阳崇山校区、沈阳蒲河校区和辽阳武圣校区。学校占地面积2190亩，校舍建筑面积65.4万平方米，是国家“211工程”重点建设院校和世界一流学科建设高校。\n\n辽宁大学源起于1948年11月东北人民政府在沈阳建立的商业专门学校，是中国共产党创建的第一所专门商科高校。1953年，东北商业专科学校合入东北财经学院。公元1958年，东北财经学院、沈阳师范学院的部分科系与沈阳俄文专科学校合并，组建成辽宁大学，朱德同志亲笔题写了校名。\n\n学校设有26个学院，即文学院、历史学院、哲学与公共管理学院、经济学院、商学院、法学院、外国语学院、广播影视学院、新闻与传播学院、国际关系学院、马克思主义学院、亚澳商学院、新华国际商学院、艺术学院、数学院、物理学院、化学院、药学院、生命科学院、环境学院、信息学院、轻型产业学院、国际教育学院、人文科技学院、公共基础学院、创新创业学院。\n\n学校现有专任教师1385人，其中教授302人、副教授526人，博士生导师199人，享受国务院政府特殊津贴专家72人，长江学者特聘教授3人，双聘院士1人，国家高层次人才特殊支持计划（万人计划）4人，中国工程院院士有效候选人1人，辽宁省攀登学者8人，辽宁省特聘教授19人，辽宁省“兴辽英才计划”入选者21人。\n\n学校现有本科专业72个；一级学科硕士学位授权点27个，设有MBA（工商管理硕士）、MPA（公共管理硕士）、JM（法律硕士）、MTCSOL（汉语国际教育硕士）、MFA（艺术硕士）等专业硕士学位授权点25个；现有理论经济学、应用经济学、工商管理、中国语言文学、哲学、法学、化学、统计学、中国史、物理学、环境科学与工程和马克思主义理论等12个一级学科博士学位授权点，8个博士后流动站；现有世界经济、国民经济学和金融学3个国家重点学科，应用经济学入选世界一流学科建设名单，理论经济学、应用经济学、法学、工商管理、马克思主义理论、统计学6个一级学科入选辽宁省一流学科建设A类行列；设有国家经济学基础人才培养基地、高校辅导员培训与研修基地、教育部人文社会科学重点研究基地——转型国家经济政治研究中心、4个教育部国别和区域研究中心；学校是国家大学生文化素质教育基地、国家级深化创新创业教育改革示范高校，有2个国家级实验教学示范中心、6个省级人文社科重点研究基地、5个省级智库、10个省级重点实验室、5个省级工程技术研究中心、4个省级工程实验室、1个辽宁省高等学校对接产业集群协同创新基地、3个辽宁省教育厅辽宁省协同创新中心、11个省级实验教学示范中心、1个省级虚拟仿真实验教学中心、6个中央与地方共建高校特色优势学科实验室、14个中央财政支持地方高校教学实验平台、11个省级大学生校外实践教育基地、1个省级大学生创新创业实践教育基地、1个省级大学生创业项目孵化示范基地、1个大学生创业项目选育基地、7个辽宁省首批一流本科教育示范本科专业、24个省高等学校创新团队。\n\n截至2019年6月，学校有全日制在校学生近2.6万人，其中本科生1.7万人，研究生7300余人；有各类留学生1800余人。\n\n学校现有各类实验室（中心）39个，实习基地208个，教学科研仪器设备总值27413.97万元。公共服务体系建设较为完善，图书馆总面积达4.2万平方米，馆藏文献476万册（件），其中古籍善本书300余种，是国务院批准的“全国古籍重点保护单位”，是联合国出版署指定的联合国文件托存图书馆。学校设有历史博物馆、自然博物馆，珍藏2000余件文物和1.6万余号生物标本。校园网是中国教育和科研网的组成部分，已成为沈阳北部大学区域节点。\n\n自建校以来，辽宁大学已为国家培养各类学生近30万人，为美国、日本、俄罗斯、韩国、意大利、英国、法国等128个国家培养长期留学生11000余人，短期留学生3700余人。学校分别与俄罗斯伊尔库茨克国立大学、立陶宛维尔纽斯大学、塞内加尔达喀尔大学共建孔子学院，精心打造的孔子学院品牌跨越亚、欧、非三大洲，国际化办学步入全新的发展阶段。\n\n面对新形势的要求，辽宁大学秉承“明德精学、笃行致强”的校训精神，力争经过全体辽大人的扎实工作，早日把辽宁大学建设成为应用特色鲜明、国际知名的综合性高水平大学。',NULL,'东北地区',2,'沈阳师范学院、东北财经学院、沈阳俄文专科学校','综合',1948,'辽宁省','是',0,211,0,1,26,1,3,1385,72,27,12,0,0,0,'http://www.lnu.edu.cn/'),(27,172,'大连理工大学','辽宁',11,'大连理工大学是中国共产党在新中国成立前夕，面向新中国工业体系建设亲手创办的第一所新型正规大学。\n\n大连理工大学是教育部直属全国重点大学，是国家“211工程”和“985工程”重点建设高校，也是世界一流大学A类建设高校。学校以培养精英人才、促进科技进步、传承优秀文化、引领社会风尚为宗旨，秉承“海纳百川、自强不息、厚德笃学、知行合一”为基本特质的大工精神，致力于创造、发现、传授、保存和应用知识，勇于担当社会责任，服务国家，造福人类。\n\n大连理工大学1949年4月建校，时为大连大学工学院；1950年7月大连大学建制撤销，大连大学工学院独立为大连工学院；1960年10月被确定为教育部直属全国重点大学；1986年4月设立研究生院；1988年3月更名为大连理工大学；1996年启动实施“211工程”建设，教育部、辽宁省、大连市共建大连理工大学；2001年启动实施“985工程”建设，教育部、辽宁省、大连市重点共建大连理工大学；2003年被中央确定为中管干部学校；2012年正式启动领军型大学建设工程。2012年12月教育部正式批准大连理工大学建设盘锦校区，该校区依照“统筹规划、错位发展、坚持标准、创新模式”的指导思想建设，与主校区同标准、同档次、同水平办学。2017年9月，经国家批准，入选世界一流大学A类建设高校。\n\n学校现有教职工4167人，其中专任教师2520人；学校有中国科学院和中国工程院院士12人、瑞典皇家工程院院士1人，兼职教师中的两院院士33人，国务院学位委员会学科评议组成员11人，“长江学者”奖励计划特聘教授32人、讲座教授6人、青年学者12人，国家杰出青年基金获得者37人，国家“万人计划”入选者31人（其中科技创新领军人才21人，哲学社会科学领军人才2人，教学名师4人，青年拔尖人才4人），“973计划”项目首席科学家10人，“973计划”青年科学家专题项目首席科学家2人，百千万人才工程国家级人选16人，科技部创新人才推进计划中青年科技创新领军人才入选者16人，国家“千人计划”青年项目入选者19人，教育部跨世纪优秀人才基金获得者17人，教育部“新世纪优秀人才支持计划”入选者116人，“国家级教学名师奖”获得者4人，辽宁省普通高等学校本科教学名师35人；博士生导师846人，专任教师中正高级职称859人，副高级职称1132人。学校现有博士生4616人，硕士生11113人，本科生25512人。\n\n学校坚持以立德树人为根本，本科生教育与研究生教育并重，已形成以理工为主，理、工、经、管、文、法、哲、艺术等协调发展的多学科育人体系。学校设有研究生院，6个学部，11个独立建制的学院、教学部，3个专门学院和1所独立学院；开发区校区设有3个学院；盘锦校区设有8个学院和1个基础教学部。有29个一级学科博士点，136个二级学科博士点，42个一级学科硕士点，222个二级学科硕士点，25个博士后科研流动站，有工商管理（MBA，含EMBA）、公共管理（MPA）、建筑学、金融、应用统计、艺术、翻译、工程管理、新闻与传播、城市规划、汉语国际教育、体育、电子信息、机械、材料与化工、资源与环境、能源动力、土木水利、生物与医药等19个类别硕士专业学位授予权，还有电子信息、机械、材料与化工、土木水利等4个博士专业学位授予权以及高校教师在职攻读硕士学位授予权。学校设有88个本科专业，5个第二学士学位专业，78个本科专业进行招生，其中国家级特色专业23个。4个国家级教育、教学基地（国家工科化学教学基地、国家大学生文化素质教育基地、国家集成电路人才培养基地、国家理科基础科学研究和教学人才培养基地），8个国家级实验教学示范中心（基础化学实验教学中心、工程训练中心、电工电子实验教学中心、基础物理实验教学中心、土木水利实验教学中心、文科综合实验教学中心、化工综合实验教学中心、机械工程实验教学中心），3个国家级虚拟仿真实验教学中心（化学虚拟仿真实验教学中心、电工电子虚拟仿真实验教学中心、车辆工程虚拟仿真实验教学中心），7个国家级教学团队，是教育部“三全育人”综合改革首批十所试点高校，国家实施工程教育改革十所试点高校，国家首批大学生创新性实验计划试点高校，首批全国高校实践育人创新创业基地，首批全国高校创新创业典型经验高校，首批全国深化创新创业教育改革示范高校，教育部首批“卓越工程师教育培养计划”的试点高校，教育部首批公共外语教学改革试点高校。\n\n学校的工程学和化学学科领域入选国家世界一流学科建设名单，优势学科资源丰富、研究实力雄厚。有一级学科国家重点学科4个，二级学科国家重点学科6个。在世界一流大学建设中，学校重点建设7个学科群，涵盖17个一级学科，辽宁省重点建设20个一级学科。目前有9个学科领域进入ESI国际学科排名前1%，其中工程学、化学、材料科学进入前1‰；10个学科领域进入QS世界学科排名前500名；在全国第四轮学科评估结果中，共有32个一级学科参评，7个学科进入A类，19个学科进入B类（其中B+，8个）；上海软科最好学科排名中，13个学科进入全国前10%。在2016年首次专业学位水平评估中，获得2个A-。\n\n学校拥有一批高水平的科研平台，建有1个国家级“2011协同创新中心”（辽宁重大装备制造协同创新中心），3个国家重点实验室（海岸和近海工程国家重点实验室、精细化工国家重点实验室、工业装备结构分析国家重点实验室），1个国家工程研究中心（船舶制造国家工程研究中心），1个国家工程实验室（消防与应急救援国家工程实验室[共建]），6个国家地方联合工程研究中心[工程实验室]（电子政务模拟仿真国家地方联合工程研究中心、工业装备节能控制技术国家地方联合工程实验室、先进装备设计与CAE软件开发国家地方联合工程研究中心、桥梁与隧道技术国家地方联合工程实验室、制造管理信息化技术国家地方联合工程实验室、热能综合利用技术国家地方联合工程研究中心），1个国家大学科技园（大连理工大学国家大学科技园），1个国家级技术转移中心（大连理工大学国家技术转移中心），1个国家级技术中心（大连理工大学国家振动与强度测试中心），1个国家知识产权战略实施研究基地，1个国家知识产权（辽宁）培训基地。学校有6个教育部重点实验室（精密与特种加工教育部重点实验室、工业生态与环境工程教育部重点实验室、海洋能源利用与节能教育部重点实验室、三束材料改性教育部重点实验室、工业装备智能控制与优化教育部重点实验室、精密/特种加工及微制造技术教育部重点实验室[B类]），4个教育部工程研究中心（模塑建材制品教育部工程研究中心、制造管理技术教育部工程研究中心、动物性食品安全保障技术教育部工程研究中心、工业装备检测与控制教育部工程研究中心），27个辽宁省重点实验室，25个辽宁省工程（技术）研究中心（工程实验室），4个国家级国际科技合作基地，1个教育部国际合作联合实验室，7个高等学校学科创新引智基地，2个公共技术创新平台，16个校外研究院。7个国家创新研究群体，4个科技部创新人才推进计划重点领域创新团队，10个教育部创新团队。2001年以来，学校共获国家科技成果奖励54项，其中以第一完成单位获得26项，包括国家技术发明一等奖2项；省部级科技成果奖励621项。2012年以来，以第一完成单位获得中国专利奖金奖1项、银奖1项、优秀奖8项。\n\n学校依山傍海，办学条件优良。已形成一校、两地（大连市、盘锦市）、三区（大连凌水主校区、开发区校区、盘锦校区）的办学格局，总占地面积351.2万平方米，建筑面积约172.7万平方米。学校图书馆现有藏书347万余册，中外文电子期刊6.4万余种。学校现有运动场馆面积19.6万平方米。同时，学校拥有国内一流的校园网络体系。\n\n学校与30个国家和地区的217所海外知名高校、科研机构建立了长期、稳定的校际校所合作关系；2015-2018年，获批教育部外国文教专家引智项目60余项，聘请长短期外国专家3163人，资助金额共计3870万元。教师赴海外交流近两年派出近三千人次出国境开展短期交流，同时通过国家公派高级研究学者及访问学者博士后项目、青年骨干教师出国研修项目、博士生导师短期出国交流项目等每年派出一百余名教师赴国境外进行长期交流，大力促进师资队伍国际化。为提高学生的国际化视野与专业水平，学校积极拓展包括国家公派、联合培养、交换学习、短期交流访问、海外实习等150余项海外交流项目。近两年，每年派出长短期交流学生约两千人次。\n\n面向未来，学校将传承大工红色基因，发扬大工精神，以建设特色鲜明的世界一流大学为目标，践行“四个服务”历史使命，深化综合改革，强化内涵特色，努力推动学校发展实现新的历史跨越。。','DUT','东北地区',2,'大连大学工学院、','理工',1949,'教育部','否',985,211,1,2,11,12,32,4167,88,42,29,3,6260,0,'http://www.dlut.edu.cn/'),(9999,173,'大连理工大学盘锦校区','辽宁',11,'大连理工大学盘锦校区位于辽宁省盘锦市辽东湾新区，是大连理工大学“一校、两地（大连市、盘锦市）、三区（大连凌水主校区、开发区校区、盘锦校区）”办学格局的重要组成部分，由大连理工大学与辽宁省盘锦市合作共建，于2012年12月30日获得教育部批复，并于2013年9月5日正式启用。学校依照“统筹规划、错位发展、坚持标准、创新模式”的指导思想建设盘锦校区，坚持三个校区同标准、同档次、同水平办学。盘锦校区已建校园占地面积56.3万平方米，建筑面积38.3万平方米；现有各类编制教职工334人，其中教学科研专任教师201人，包括教育部长江学者奖励计划特聘教授1人，国家杰出青年科学基金获得者1人，国家“万人计划”（科技创新领军人才）2人，博士生导师24人，硕士生导师146人，正高级职称34人，副高级职称68人；现有在籍学生5800人，包括全日制本科生5299人，研究生501人；已毕业五届研究生、三届本科生。校区规划全日制本、硕、博在校生10000人，教师800人。为落实校区建设指导思想，学校对盘锦校区实施“条块结合，以条为主”的管理模式，以学院为办学主体。目前驻校区教学科研单位包括海洋科学与技术学院、生命科学与药学学院、大连理工大学莱斯特国际学院3个由学校直接管理的独立建制学院，化工学院盘锦分院、经济管理学院盘锦分院、外国语学院盘锦分院、体育与健康学院盘锦分院4个由大连凌水主校区相应学院直接管理的分院，以及工业装备结构分析国家重点实验室分室、精细化工国家重点实验室分室、工业生态和环境工程教育部重点实验室分室、分子识别与成像辽宁省重点实验室、国家知识产权培训（辽宁）基地等重要平台。学校部分职能部门在盘锦校区设立派出机构，并通过校区行政服务办事大厅实施行政事务一站式服务；设立盘锦校区党工委、纪工委、管委会，分别负责校区日常党建工作的统筹协调，纪检监察以及日常运行的管理、统筹、协调、服务。学校启用盘锦校区以来，以建设一流校区为目标，积极探索高水平大学异地办学、更好履行“四个服务”历史使命的实践之路，多措并举实现全员、全过程、全方位育人。校区教职工荣获包括国家科技进步二等奖在内的国家及省部级、国际奖项多项；校区学生已有2600余人次获得国际、国家、省市级以上各类学科竞赛奖励；努力提升国际化办学水平，不断探索与国际知名高校合作新模式，学校第一个中英合作办学机构—大连理工大学莱斯特国际学院2017年获得教育部批复并在校区设立，目前已招收三届本科生。学校依照高起点、新模式、国际化、合作共建的办学方针统筹推进盘锦校区发展；驻校区各单位秉承“海纳百川、自强不息、厚德笃学、知行合一”的大工精神，传承学校“红色基因”，发扬优良办学传统，坚持立德树人，不忘创业初心、牢记发展使命，以敢为人先的奋斗姿态和争做事业新增长极的责任担当，努力为大连理工大学“双一流”建设作出新的更大贡献！',NULL,'东北地区',2,NULL,'理工',2012,'大连理工大学','否',985,211,1,2,8,0,1,419,21,2,0,2,6260,0,'http://pjzsjy.dlut.edu.cn/zsjh.htm'),(235,174,'沈阳工业大学','辽宁',11,'沈阳工业大学是一所以工为主的多科性研究应用型大学,始建于1949年，1985年由沈阳机电学院更名为沈阳工业大学，原为国家机械工业部所属院校，1998年起由中央和地方共建，以辽宁省管理为主，在“双一流”建设中被列为全省冲击国内一流大学重点建设高校。\n\n学校由位于辽宁省沈阳市的中央校区、兴顺校区、国家大学科技园和位于辽阳市的辽阳分校组成，总占地面积158.99万平方米，校舍建筑面积76.92万平方米。办学主体位置坐落在有“东方鲁尔”美誉的沈阳市铁西区，中央校区（主校区）位于沈阳市装备制造业聚集的国家级经济技术开发区，毗邻中国制造2025与德国工业4.0战略对接的重要载体中德高端装备制造产业园，占地面积95.8万平方米，建筑面积50.81万平方米。\n\n学校设有19个学院、2个教学部和2个工程实训中心，共设59个本科专业、11个专科专业。现有普通本科生17860人，硕士研究生3532人，博士研究生399人，专科生1485人，成人教育学生9854人，外国留学生186人。近三年，毕业生年均初次就业率93.18%，年终就业率94.63%。\n\n学校现有师资队伍1310人，其中教授244人、副教授431人、讲师610人，博士生导师200人、硕士生导师657人。具有博士学位的教师占师资队伍总数的36.4%，具有硕士和博士学位的教师占师资队伍总数的87.1%,具有高级职称教师占专任教师的比例为51.5%。在领军人才方面，拥有中国工程院院士2人，国家级百千万人才工程人选2人，国家“万人计划”科技创业领军人才2人，中科院“百人计划”入选者1人，国务院政府特殊津贴获得者18人，辽宁省高等学校攀登学者4人，辽宁省百千万人才工程百人、千人层次人选70人，辽宁特聘教授10人，辽宁省优秀专家8人，辽宁省教学名师16人，辽宁省“高层次人才培养支持计划”科技创新领军人才1人，辽宁省“兴辽英才计划”青年拔尖人才3人。\n\n学校具有学士、硕士、博士三级学位授予权。现有博士学位授权一级学科5个，硕士学位授权一级学科20个，其中国家重点二级学科1个（电机与电器），辽宁省一流大学重点建设一流学科4个（电气工程、材料科学与工程、机械工程、仪器科学与技术），博士后科研流动站4个（电气工程、材料科学与工程、机械工程、仪器科学与技术）。设有金融、电子信息、机械、材料与化工、能源动力、土木水利、工商管理、会计和工程管理9个专业学位类别。2010年获批教育部专业学位研究生教育综合改革试点单位，机械工程领域获“全国工程硕士研究生教育特色工程领域”。\n\n学校牢固树立教学工作中心地位，以人才培养供给侧改革为抓手，着力创建一流本科教育。精心打造品牌特色专业，4个专业入选全球工程教育第一方阵，5个专业入选辽宁省一流本科建设专业，现有国家第一类特色专业建设点5个，教育部本科专业综合改革试点专业2个，教育部“卓越工程师培养计划”试点专业7个，辽宁省示范性本科专业8个，辽宁省本科综合改革试点专业4个，辽宁省本科工程人才培养模式改革试点专业3个，辽宁省重点支持建设专业2个，辽宁省本科优势特色专业4个，辽宁省本科课程体系国际化试点专业1个。积极探索课程和教材改革，现有省级精品课24门，省级视频公开课3门，省级资源共享课13门；“十二五”普通高等教育本科国家级规划教材5部，“十二五”普通高等教育本科省级规划教材20部。持续推进教学改革，去年以第一、第二完成单位荣获两项国家教学成果二等奖，获批国家教育改革创新典型案例50强和国家新工科建设项目，完成的基于数学模型的人才供需对接和人才供需预测数据平台获得省政府高度评价，一流课堂建设工程在全省推广。切实加强教学团队培育，现有辽宁省高校黄大年式教师团队1个,省级教学团队10个。不断加强实践实训基地建设，现有与企业共建国家级工程实践教育中心3个，辽宁省实验教学示范中心18个，辽宁省虚拟仿真实验教学中心5个，辽宁省大学生校外实践教育基地8个，辽宁省高等学校实训培训基地1个，辽宁省青少年法治教育实践基地1个，辽宁省装备制造业紧缺人才培养基地1个。大力构建创新创业教育体系，先后荣获全国首届高校创新创业宣传总结工作典型学校（50强）、全国首批创新创业教育改革示范校（99所）等荣誉称号，获批全国高校实践育人创新创业基地、全国高校教师创业实践基地、全国高校学生科技创业实习基地，获批教育部大学生校外实践教育基地建设项目1项，辽宁省创新创业试点专业2个，辽宁省向应用型转型发展试点专业12个，其中有7个专业被评为向应用型转变示范专业。\n\n科技工作发展迅速，现已形成国家、省、市、校四级科研平台体系，拥有国家级工程技术研究中心、国家级大学科技园、国家级“2011”协同创新中心、国家级技术转移中心、国家级工业设计中心、教育部创新团队，先后获得国家科技进步二等奖4项、三等奖1项。在这里，诞生了4项中国首创具有完全自主知识产权的科技成果，分别是1979年的中国第一台稀土永磁电机，1997年的中国第一台异型螺杆加工数控铣床，2004年的中国第一套长输管道智能检测系统，2005年的中国第一台兆瓦级风力发电机组。近三年来，学校科技进款额每年均超过1亿元，荣获省部级科技奖励一等奖8项，申报国家科技进步奖4项。学校立足和服务辽宁老工业基地，形成了以工为主的办学优势与特色。特别是“十三五”以来，坚持以服务辽宁新一轮全面振兴为导向，牵头组建了辽宁装备制造产业校企联盟等联盟5个，创办了辽宁智能制造技术与装备研究院。推进校企协同创新，从科技成果转化源头做起，打通科研立项、产品开发、产品中试、成果转化、企业孵化、技术辐射的全流程，先后组建校企共建研究院近20个；推进校地深度融合，以服务辽宁“三市两区”为重点，创建铁岭研究院、辽阳研究院，完善营口研究院，与沈阳经济技术开发区和沈抚新区开展紧密合作；推进科技成果转化，与德国韦格纳吉、中国太库科技三方合作共建“中德国际技术转移服务中心”，引导校内外、省内外、国内外先进成果在辽宁落地转化，近年来学校科技成果在辽转化数量和转化率位于省属高校前列。\n\n学生工作以立德树人为根本，大力营造良好育人生态，不断推进全员育人、全过程育人、全方位育人。坚持思想教育是灵魂，把思想政治教育贯穿于学生管理、学生服务、校园活动的全过程，形成了有影响力的“大学讲堂”等系列品牌活动，荣获辽宁省思想政治教育创新奖；坚持安全稳定是底线，扎实推进心理健康教育和全方位的安全意识教育，被评为辽宁省心理健康教育示范校；坚持制度建设是防线，落实《普通高校学生管理规定》，形成体系完备的学生管理服务制度体系；坚持队伍建设是引线，深入开展辅导员系统培训和职业能力大赛，1人荣获全国高校辅导员年度人物，在辽宁省辅导员职业能力大赛中连续获得优秀组织奖并先后获得一等奖两次。坚持学风建设是主线，持续推进优良学风班级、优良学风年级和优良学风学院创建活动，形成了“学在工大”的良好氛围。先后荣获全国毕业生就业工作先进单位，全国大学生社会实践优秀组织单位，辽宁省学生思想政治工作先进单位，辽宁省学生军训工作先进单位等荣誉称号。\n\n学校坚持面向世界，开放办学。与美国、俄罗斯、日本、德国、英国、澳大利亚、韩国、芬兰等国家的62所院校建立了长期稳定的校际合作关系。从1995年开始招收外国留学生，2001年开始与澳大利亚格里菲斯大学、美国班尼迪克大学在本科和研究生层次开展国际合作办学，积累了丰富的办学经验，已经形成1700多名学生的培养规模，与美国班尼迪克大学联合培养的MBA研究生已毕业九届。\n\n目前，学校正在“家文化”理念的引领下，按照教学、科研、学生工作“三个轮子”一起转的总体布局，一流之“士”、一流之师、一流之“吏”、一流之校“四个一流”一起抓的具体举措，勠力同心创建国内一流大学，以“奋进之笔”谱写新时代各项事业发展的新篇章。',NULL,'东北地区',2,'东北机械管理局技工学校、机械工业管理局机械职业学校、机器工业学校、长春机器制造学校','理工',1949,'辽宁省','是',0,0,0,0,20,2,0,1308,59,20,5,0,3076,0,'http://www.sut.edu.cn'),(278,175,'沈阳理工大学','辽宁',11,'沈阳理工大学前身是东北军区军工部工业专门学校，创建于1948年。1960年组建成立沈阳工业学院，2004年经教育部批准更名为沈阳理工大学。2010年辽宁省人民政府与中国兵器装备集团、中国兵器工业集团签署了共建沈阳理工大学协议。2016年获批“国家国防科技工业局与辽宁省人民政府共建高校”，成为省局共建的国防特色院校。学校是国家卓越工程师教育培养计划试点高校，是中国政府奖学金来华留学生招收院校，是教育部批准的招收高水平足球运动队的高等院校。\n\n经过七十年的建设和发展，沈阳理工大学已由一所学科单一的军工院校发展成为以工为主，理、经、管、文、法、艺相结合，服务辽宁、面向全国，具有鲜明国防特色的多科性大学。\n\n学校占地面积113万平方米，校舍建筑面积44.4万平方米。校园内建有局域网，图书馆面积4万平方米，纸质藏书137.52万册，图书总量242.52万册。学生文化体育设施齐全，建有标准运动场和大学生文体中心，为学生全面发展提供了良好条件。学校建有东北地区唯一的兵器博物馆，是国家和辽宁省科普教育基地、辽宁省国防教育基地及沈阳市爱国主义教育基地。\n\n学校设有19个学院、系、部等，设有55个本科专业，分布在工、理、经、管、文、法、艺术等7个学科门类，具备完整的本、硕、博人才培养体系。拥有国家级特色专业建设点2个，国家级专业综合改革试点专业1个，省级特色（示范）专业、改革试点专业及重点支持专业19个。有6个省级教学团队，教育部精品视频公开课1门，省级精品课程13门，省级视频公开课和精品资源共享课14门。2007年，学校以“优秀”成绩通过教育部高等学校本科教学工作水平评估。2011年，学校被教育部批准为国家卓越工程师教育培养计划试点高校。有国家级大学生校外实践教育基地建设项目1个，省级大学生实践教育基地、实验教学示范中心、工程实践教育中心等17个。\n\n现有教职工1685人，其中双聘院士1人，博士、硕士研究生导师424人，高级职称专任教师546人。国家“百千万人才工程”百人层次、国家级教学名师、享受国务院政府特殊津贴、教育部“新世纪优秀人才”13人。省领军人才、省优秀专家、省特聘教授、省教学名师、省高校优秀科技人才支持计划、省高校杰出青年学者成长计划、省“百千万人才工程”百人层次70人，辽宁省创新团队4个。\n\n现有各类全日制在校生17003人，其中本科生15385人，博士、硕士研究生1398人。有博士人才培养项目1个，博士后科研流动站1个，博士后科研工作站1个；有13个硕士学位授权一级学科，涵盖45个二级学科（学科方向）；有8个工程硕士授权领域和4个专业学位授权点。拥有国家级沈阳中俄科技合作基地、国家863高技术发展计划重点实验室和1个省级高校重大科技平台。有5个国家级国防特色学科，1个辽宁省一流学科，5个辽宁省重点学科，4个学科列入辽宁省“提升高校核心竞争力特色学科建设工程”, 26个省、部级重点实验室和工程技术研究中心。\n\n“十二五”以来，学校共承担科研项目2800余项，科研到款逾3亿元。获批国家自然科学基金项目、科技部重点研发计划、中央军委装备发展部、国家国防科工局等国家级项目130项。获批省级奖项40项。获得授权专利1056项，发表三大检索论文1434篇。\n\n学校与美国、俄罗斯、英国、加拿大、德国、法国、芬兰等18个国家的80所高校及科研院所建立了友好合作关系，开展了不同程度的交流与合作。与波兰西里西亚技术大学开展化学工程与工艺专业的中外合作办学项目获批教育部本科合作办学项目。与俄罗斯和白俄罗斯国家科学院所属的9个研究所共建了4个设备技术先进的合作实验室。与英国、美国、加拿大、俄罗斯、法国等国家的大学开展2+1+1，2+2以及3+1等不同模式的本科生及硕士生联合办学项目。学校是中国政府奖学金来华留学生招收院校，2015年以来，招收留学生1685人，政府奖学金本科生58人，硕士生274人。学校与俄罗斯托木斯克国立大学合作建立了孔子学院，并于2015年度获得“全球先进孔子学院”荣誉。\n\n学校始终坚持“育人为本、改革创新、强化特色、提高质量”的工作方针，全面实施“特色立校、人才强校、开放兴校”三大战略和“教学质量工程、科技创新工程、学科提升工程、师资队伍建设工程、管理体制改革工程”五大工程。弘扬“爱国奉献、自强不息、求真务实、追求卓越”的兵工精神，秉承“弘志励学，德才并蓄”的校训，以人才培养为根本任务，走内涵式发展道路，不断深化教育教学改革，努力把学校建设成为具有鲜明国防特色的研究应用型大学。','SYLU','东北地区',2,'沈阳第一化学工业学校、沈阳机械制造工业学校、阳第一工业学校','理工',1948,'辽宁省','是',0,0,0,0,19,1,0,1685,55,13,0,0,2650,0,'http://www.syit.edu.cn'),(270,176,'辽宁工程技术大学','辽宁',11,'辽宁工程技术大学创建于1949年，是一所具有六十多年悠久历史的国家重点大学。新中国成立前夕，为恢复生产、发展经济、支援解放全中国，经东北人民政府批准，分别成立了阜新工科高级职业学校、抚顺工科高级职业学校、鹤岗工科高级职业学校、鸡西煤矿工业学校以及东北煤矿工人速成学校。1958年，在这些学校的基础上，分别成立了阜新煤矿学院、抚顺煤矿学院、鸡西矿业学院和辽宁煤矿师范学院。六十年代初期院校调整，四校合并，校址选于辽宁省阜新市，定名为阜新煤矿学院，成为东北地区唯一一所煤炭高等院校。1978年学校更名为阜新矿业学院。1996年学校更名为辽宁工程技术大学。1998年学校由原煤炭工业部划归辽宁省人民政府管理，实行中央与地方共建、以地方管理为主的体制。1999、2000年，原阜新工业学校、辽宁电子计算机学校和辽宁省财政学校先后并入学校。现设有25个学院、67个本科专业。在册学生3.2万余人，其中全日制在校博士生、硕士生、本科生、留学生2.6万余人。\n\n六十多年来，一代又一代辽宁工大人秉承“诚朴求是、博学笃行”的校训，弘扬“朴实无华、坚韧顽强、无私奉献”的太阳石精神，改革创新，苦干实干，创造了辉煌业绩，取得了令人瞩目的伟大成就。1978年被国务院确定为全国首批88所重点院校之一；1981年被批准为首批硕士学位授予单位；1993年获准为博士学位授予单位、有条件接收留学生单位；1999年被批准设立博士后科研流动站，同年获得工程硕士专业学位授予权；2009年获得工商管理硕士（MBA）专业学位授予权；2010年获工程管理硕士专业学位授予权；2011年获批为教育部第二批卓越工程师教育培养计划高校；2012获批为接受中国政府奖学金学生院校；2013年获批为国家“中西部高校基础能力建设工程”重点建设高校；2017年被评为全国创新创业典型经验高校，同年成为辽宁省国内高水平大学重点建设高校，矿业工程、安全科学与工程、测绘科学与技术3个学科入选为辽宁省双一流建设学科。现已成为以工为主，工、管、理等多学科协调发展，办学特色突出，办学设施完善，师资力量雄厚的著名高等学府。\n\n辽宁工大教学科研设施完善、学习生活环境幽雅。学校建有阜新校区中华路校园、玉龙校园和葫芦岛校区龙湾校园，占地4240亩，其中阜新校区占地1900亩、葫芦岛校区占地2340亩，校舍建筑面积73万平方米。建筑面积4.5万平方米的3座现代化图书馆分布在三个校园，馆藏图书（电子图书）近290万册，各类数据库60余个，中外文期刊1600多种。学校编辑出版《辽宁工程技术大学学报（自然科学版）》和《辽宁工程技术大学学报（社会科学版）》，其中《辽宁工程技术大学学报（自然科学版）》是中文核心期刊、中国科技核心期刊、RCCSE中国核心学术期刊、美国（CA）来源期刊、荷兰(Scopus) 来源期刊、美国EBSCO来源期刊、美国（Ulrichweb)来源期刊，俄罗斯（AJ，VINITI）来源期刊。《辽宁工程技术大学学报（社会科学版）》是全国高校优秀社科期刊。\n\n辽宁工大坚持走特色发展之路，拥有一批特色鲜明、实力较强的传统学科。建有矿业工程、力学、测绘科学与技术、安全科学与工程4个博士后科研流动站；拥有7个一级学科博士学位授权点、27个二级学科博士学位授权点、16个一级学科硕士学位授权点、60个硕士学位授权点；拥有工商管理硕士、工程硕士（含17个授权领域）、会计、公共管理4个专业学位授权点；拥有1个国家重点（培育）学科、3个辽宁省一流建设学科、9个省级重点学科；拥有8个国家级特色专业、7个教育部卓越工程师教育培养计划试点专业、2个本科重点支持专业；拥有1个国家级教学团队、1个国家级实验教学示范中心、6个国家级工程实践教育中心。学校大力推进“专业+人工智能”新工科建设，产生了一批生机勃勃的新兴学科。2018年，学校与腾讯云计算（北京）有限责任公司、上海墨桐花开教育科技有限公司联合成立辽宁工程技术大学—腾讯云人工智能学院，进一步推进“专业+人工智能、云计算、大数据、互联网+等”新工科建设，培养新工科人才。\n\n辽宁工大大力实施人才强校战略，不断加大人才队伍建设力度，建设了一支数量适当、结构合理、发展趋势好、专业水平和教学水平高，具有创新精神和实践能力，充满生机活力的师资队伍。现有专任教师1484人，高级职称教师874人，中组部千人计划1人，长江学者1人，辽宁省攀登学者5人，辽宁特聘教授9人，二级教授26人。1名教师被确定为国家千人计划人选，3名教师被确定为新世纪百千万人才工程国家级人选，66名教师被确定为辽宁省百千万人才工程百千层次人选。一批教师分别被确定为省部级学科学术带头人、教育部教学指导委员会委员、国家自然科学基金专家评审组成员、国家安全生产专家组成员、国家煤炭工业技术委员会委员、省学位委员会学科评议组成员及各类学会与学术期刊编委会委员。\n\n辽宁工大作为国家卓越工程师教育培养计划高校，始终把人才培养质量视为学校的生命，以培养应用创新型人才为目标，着力推进应用创新型人才培养体系建设和实践，不断深化人才培养模式改革，培养学生“太阳石精神”和应用创新能力，让每一名学生成为最好的自己。2006年，学校以优秀成绩通过教育部本科教学工作水平评估。近年来，学校先后有80余项教学成果获国家和辽宁省优秀教学成果奖。毕业生就业率连年达95%以上，居辽宁省高校前列。广大毕业生以脚踏实地、吃苦耐劳、勇于实践、团结协作、甘于奉献的特点，赢得了国家机关和企事业单位的欢迎，受到了社会的好评。教务处被教育部评为全国普通高校优秀教务处，学校先后两次被教育部评为全国普通高校毕业生就业工作先进集体，2010年被评为全国首批毕业生就业典型经验高校，2016年获批为全国首批深化创新创业教育改革示范高校，2017年被评为全国创新创业典型经验高校。\n\n辽宁工大积极转变科技发展方式，创新发展模式，立足辽宁，面向全国，积极服务区域经济社会与地矿行业发展，着力为地方经济社会发展提供科技支撑，着力打造国家能源领域重要的科技创新基地。建有国家大学科技园、国家级科技企业孵化器、国家技术转移示范机构；建有1个教育部重点实验室、1个国家地方联合工程研究中心、1个国家安全生产技术基础研究中心、1个煤炭行业协同创新中心；建有2个辽宁省高等学校重大科技平台、18个省部级重点建设实验室、13个省部级工程技术研究中心。近年来，先后承担国家科技攻关、国家自然科学基金、国家“973”计划、“863 ”国家高新技术等各类科研课题3500余项，240余项成果获得国家和省部级奖励，先后有4项成果获国家科技进步二等奖，拥有了一批具有自主知识产权的关键技术，在矿山灾害治理、安全技术与工程、测绘科学与技术、管理科学与工程、机械制造等研究领域形成了明显优势和突出特色，在冲击地压预测与防治、煤炭自燃火灾防治、煤矿瓦斯防治理论与技术、安全结构分析与预测、全球卫星导航精密单点定位理论与算法、遥感卫星对地观测系统图像处理理论与方法等许多研究领域已达到国内国际先进水平。学校连续组织参加了中国国际高新技术成果交易会，多次获得“中国国际高新技术成果交易会优秀组织奖”；先后举办国际测绘与地球空间信息研讨会、智能装备及机器人技术国际研讨会、煤矿冲击地压防治工程前沿技术高端论坛、第35届国际采矿岩层控制会议、2017国际智慧矿山创新论坛、管理科学与工程学会2017年年会暨第十五届中国管理科学与工程论坛等近百次国际学术会议和全国学术会议，具有较高的学术声誉和国际知名度。\n\n辽宁工大始终坚持开放办学，对外交流与合作日益扩大。与阜新、朝阳、营口、铁岭等市签订了市校共建合作协议，与神华集团、中煤能源集团、中平能化集团、大同煤业集团、山西焦煤集团等20多家国内大型企业签订了实质性校企合作协议。学校拥有国家安全培训一级资质、全国煤炭行业职业经理人资格认证一级培训资质和全国煤炭行业经营管理干部工商管理培训资质。在省内高校中率先成立了教育发展基金会，拥有董事会单位120家。学校建立了长期、稳固的校际交流与合作国际合作平台，已与美国、加拿大、哥伦比亚、英国、德国、法国、波兰、意大利、爱尔兰、瑞典、俄罗斯、乌克兰、乌兹别克斯坦、澳大利亚、新西兰、日本、韩国、新加坡、越南、蒙古、巴基斯坦、柬埔寨、马来西亚、泰国、尼泊尔、南非、中国台湾、中国香港等30多个国家和地区的近200所高等院校和科研机构建立了校际合作关系，在学者交流、人才培养、科学研究等领域开展实质性合作。学校还与美国劳伦斯科技大学开展“3+2”、与澳大利亚詹姆斯库克大学开展“2+2”等学生联合培养项目。2008年我校成为接收辽宁省政府奖学金生院校，2012年获批教育部 “中国政府奖学金自主招生资格院校”，2013年成为国家汉办汉语水平考试(HSK)辽宁地区考点，2017年获批“丝绸之路”来华留学生中国政府奖学金项目。留学生生源国达35个，建立了涵盖研修生、本科生、硕士生、博士生的留学培养体系。学校坚持“扩大规模，提高层次，保证质量，规范管理”的来华留学工作方针，在稳步提升留学生招生规模和层次的同时，不断提高留学生教育教学质量，注重内涵式发展，打造 “留学工大”的教育品牌。\n\n辽宁工大校园文化生活丰富多彩。作为首批全国大学生素质拓展计划试点高校，创新构建实施双嵌入素质拓展学分模式，学生课外素质拓展活动内容不断丰富，数量和质量不断提高，形成“一节、两月、三类讲座、四项工程、十五项赛事”为框架的校园文化活动体系。作为全国第二批大学生创新性实验计划项目高校，先后有1000余个学生项目获得国家和省级资助，学校被评为全国大学生创新创业训练计划工作实施先进单位。通过“五位一体”本科生课外科研训练体系，实现专业教师的全参与和对本科生创新创业能力培养的全覆盖。3名本科生先后被授予第七届、第八届、第十届“中国青少年科技创新奖”，1个学生科研团队获全国大学生“小平科技创新团队”称号。学校获批团中央KAB创业俱乐部，学生在国家级和省级创新创业竞赛中获奖近2000项，1个学生社团被授予全国百佳大学生创业社团，2名学生被授予全国百名大学生创业英雄称号。持续开展以低年级侧重社会观察，高年级侧重专业实践为主要形式的社会实践活动，多项成果、多个集体和个人受到国家级或省级表彰。学生广泛参与志愿服务活动，积极投身志愿服务西部计划、志愿服务辽西北计划和研究生支教团工作，学校3次荣获“全国大学生志愿服务西部计划优秀等次项目办”。学校被确定为首批全国共青团基层组织建设和基层工作试点单位，3次被共青团中央授予“全国五四红旗团委”，多个团支部被授予“全国五四红旗团支部”“全国高校践行社会主义核心价值观示范团支部”和“全国活力团支部”。大学生习近平新时代中国特色社会主义思想宣讲团荣获“全国高校优秀学生社团标兵”等多项荣誉。\n\n栉风沐雨，春华秋实。辽宁工大以其优良的学风和校风，多次被辽宁省政府、省教育厅授予“文明单位”、 “精神文明创建工作先进单位”、“安全文明校园”、“优良学风校”等荣誉称号。建校69年来，学校为国家培养了13万余名优秀毕业生，其中有10余名校友先后当选为中央委员、中央候补委员和中纪委委员，有数十名校友当选为国家部委领导、省委书记、省长，有3名校友当选为中国工程院院士，有一批优秀校友担任大中型企业负责人。学校位列2017-2018中国重点大学综合竞争力排行榜理工类第49位、2015年中国政要排行榜全国第55位、1977年恢复高考以来培养两院院士最多大学排行榜第51位，素有煤炭行业“黄埔军校”的美誉。\n\n伟大的时代赋予崇高的使命，辽宁工大人正以昂扬的斗志、豪迈的情怀、创新的精神，高举中国特色社会主义伟大旗帜，以本科教育为立校之本，以研究生教育为强校之路，以培养应用创新型人才为中心，以服务辽宁区域和地矿行业发展为价值追求，以内涵发展和质量提升为工作主线，深化改革创新，大力实施特色发展、质量提升、创新驱动、人才强校、文化引领五大发展战略，扎实推进“双一流”建设，全面建设特色鲜明的国内高水平研究应用型大学，为全面建成小康社会做出新的、更大贡献。','LNTU','东北地区',2,'阜新煤矿学院、阜新矿业学院、','理工',1949,'辽宁省','否',0,0,0,0,23,0,1,1484,67,16,7,1,5205,0,'http://www.lntu.edu.cn/'),(243,177,'沈阳化工大学','辽宁',11,'1952年，承载着振兴新中国化学工业的光荣使命，沈阳化工大学应运而生。1960年，为顺应国家工业繁荣发展和新兴学科建设崛起的需要，学校更名为“辽宁科学技术大学”，隶属中国科学院；1962年，为适应国家化学工业的大发展，学校隶属关系变更为化学工业部；1998年，国家高校管理体制调整，学校实行中央与地方共建、以地方管理为主。\n\n经过六十余载的建设与发展，沈阳化工大学已经成为一所以工为主，以化工为特色，工、理、管、经、文、法、医等7大学科门类相结合的高等学府。学校为辽宁省“双一流”（一流大学、一流学科）重点建设高校，国家“中西部高校基础能力建设工程”（小“211”）重点建设高校。\n\n学校是国家首批学士学位授予权单位，国务院学位委员会“八五”初期批准的硕士学位授予权单位，教育部卓越工程师教育培养计划高校，教育部高等学校特色专业建设单位，国家级“大学生创新创业训练计划”入选高校。学校是辽宁省石油化工技术紧缺本科人才培养基地，辽宁省工程教育中心，辽宁省专业技术人员继续教育基地，辽宁省科技创新与新技术转移推广基地。\n\n学校占地近1500亩，建筑面积50余万平方米，校园融学术、园林、艺术于一体，被赞誉为全省最美的校园之一。现设有25个二级院（系）及直属机构，在校全日制本科生、研究生、留学生14000余人。教职工1100余人，其中：长江学者特聘教授、国家“杰出青年基金”获得者、“973”首席科学家等5人，国家教学名师、国家督学1人，全国模范教师、优秀教师2人，宝钢优秀教师2人，享受国务院政府特殊津贴专家15人；辽宁省领军人才、学科带头人1人，辽宁省优秀专家6人，辽宁省教学名师17人。\n\n学校现有57个本科专业，13个一级学科硕士点，64个二级学科硕士点，4个专业硕士类别（含7个工程硕士领域），具有推荐优秀应届本科毕业生免试攻读研究生资格，多个学科联合培养博士研究生。化学工程与技术学科入选辽宁省一流学科，8个学科入选辽宁省重点学科。学校拥有国家地方联合工程实验室、国家实验教学示范中心、省重点实验室、工程技术中心和新技术转移推广中心等37个。学校是“辽宁精细化工协同创新中心”、“辽宁省精细化工产业技术联盟”和“辽宁省石油化工产业校企联盟 ” 的牵头单位。\n\n学校坚持育人为本，以先进的成果导向教育（OBE）为核心，大力推进人才培养供给侧改革，建立“113”人才培养体系（即：1个理念—OBE理念，1种模式—CDIO模式，3化举措—协同化育人、家庭化培养、个性化指导），培养高素质应用型人才，打造全国最有特色的应用型本科教育。近年来，连续保持毕业生一次就业率年均在95%以上；在全国“挑战杯”、化工设计、数学建模、机械设计、计算机设计等大赛中获国家奖50余项；连续多年被教育部、团中央授予全国“挑战杯”高校优秀组织奖，被中宣部、中央文明办、教育部、共青团中央、全国学生联合会评为“全国大中专学生志愿者暑期‘三下乡’社会实践活动先进单位”。\n\n学校坚持创新驱动，大力开展科学研究，为服务化工行业发展和东北老工业基地振兴提供强大的科技和智力支撑。近年来，学校承担国家科技攻关计划项目、“863”项目、“973”项目、科技部重大科技专项、国家自然科学基金重点项目、国家社会科学基金重点项目等国家级科研项目数百项，省部级和企业科研项目数千项。学校获得国家科技进步奖、国家发明奖、国防科学技术奖一等奖等17项，获其它省部级科技奖近百项，授权专利成果1100余项，大批科技成果在辽宁及省外大型企业推广应用，创造了显著的经济效益和社会效益。学校在化工过程强化技术领域研究居国际领先水平，具有该技术领域成功研发系列技术和满足该技术领域高层次人才培养的不可替代优势。以全职引进的“腾飞学者”为带头人，学校成功打造5大高峰学科，使学校在装备可靠性与振动控制、绿色农药创制与应用、化工降能与污染控制、碳资源分级转化与利用等领域具有国际或国内领先水平，将成为科技强国和科技强省建设的重要引擎。\n\n学校坚持开放办学，现已与国外30多所高校和科研单位建立了长期友好合作关系，在人才培养和科学研究等方面开展务实合作，留学生来自世界30余个国家。\n\n学校坚持社会主义办学方向，不断加强和改进党的领导。认真贯彻落实“四个全面”战略布局和习近平总书记系列重要讲话精神，把从严治党、立德树人的要求贯穿高等教育改革发展全过程，努力办好中国特色社会主义大学。学校党委先后被授予“辽宁省教育系统先进党委”、 “沈阳市先进基层党组织”“沈阳市教科系统先进党委”、“辽宁省思想政治工作先进单位”、“中国石油和化学工业文化建设先进单位”等荣誉称号。\n\n学校发展建设得到了党和政府的高度重视，上世纪80年代，胡锦涛、李克强、贺国强等党和国家领导同志先后亲临我校视察指导工作，极大地激励了广大师生员工的进取精神和工作热情。\n\n“十三五”开局以来，学校审时度势、谋篇布局，成功制定出台了 “423”改革发展战略（即：“顶层设计23条”、“综合改革23条”、“‘十三五’规划23条”和“党建规划23条”），“2个5”供给侧改革、“四位一体”服务辽宁振兴发展战略等一系列重要的战略举措。以这些战略举措为指引，学校成功入选辽宁省“双一流”重点建设高校，无论人才引进、人才培养，还是科技创新、服务社会，每一个领域，化大人都敢为人先、勇立潮头！\n\n“潮平两岸阔，风正一帆悬”,沈阳化工大学正处于一个历史上最好的发展时期。肩负着国家化学工业腾飞以及辽宁老工业基地新一轮振兴和科技强省建设的历史使命，秉承“强学力行、喻理求真”的校训，弘扬“修德、自强、求是、创新”的化大精神，学校正朝着“建成省属一流、全国著名的高水平教学研究型大学”的宏伟蓝图劈波斩浪、扬帆远航!','SYUCT','东北地区',2,'沈阳化学工业技术学校、重工业部沈阳化学工业学校、沈阳化工学院','理工',1952,'辽宁省','是',0,0,0,0,25,0,5,1100,57,13,0,0,0,0,'http://www.syuct.edu.cn/'),(218,178,'大连交通大学','辽宁',11,'1956年，学校应我国铁道机车车辆工业的发展需要而创建, 时为大连机车车辆制造学校。1958年升格为大连铁道学院，隶属原铁道部管理。2000年2月，划转为辽宁省政府管理。2004年5月，更名为大连交通大学。 \n\n学校有沙河口校区、旅顺口校区两个校区，占地总面积82.1万平方米，校舍建筑总面积54.1万平方米。教学科研仪器设备总值2.94亿元，馆藏文献资源总量为125.03万册，电子图书143.40万册，中外文电子期刊22700余种。 \n\n学校具有学士、硕士、博士学位授予权，具有推荐优秀应届本科毕业生免试攻读研究生资格。学校是辽宁省一流大学重点学科建设高校，拥有机械工程、材料科学与工程2个博士后科研流动站，2个博士学位授权一级学科，13个硕士学位授权一级学科，11个工程硕士专业学位授权领域，涵盖了工、管、文、理、经、法、艺等7个学科门类。机械工程、材料科学与工程2个学科是辽宁省高等学校一流重点建设学科；机械工程、材料科学与工程、交通运输工程3个学科是省一级重点学科。建有国家级大学科技园、国家地方联合工程研究中心、教育部工程中心、国家技术转移示范机构、辽宁省高等学校重大科技平台等国家级、省部级科技平台29个。 \n\n学校是教育部第二批实施卓越工程师教育培养计划高校、国家软件人才国际培训（大连）基地、全国建设高水平运动队院校和体育文化研究基地、辽宁省车辆工程紧缺本科人才培养基地、辽宁省对日服务外包人才培养基地和大连东北亚国际航运人才培训基地。设有18个二级教学单位，47个本科专业，10个五年制双专业，工商管理、软件工程、计算机科学与技术具有第二学士学位授予权。2006年以“优秀”的成绩通过了教育部本科教学工作水平评估。建有国家级人才培养模式创新实验区1个、国家级特色专业建设点4个、国家级综合改革试点专业2个、教育部卓越工程师教育培养计划试点专业5个、中国工程教育认证专业3个，省一流本科教育示范专业2个、省级特色专业6个、省级示范专业3个、省级重点支持专业2个、省级综合改革试点专业4个、省级工程人才培养模式改革试点专业5个、省级创新创业教育改革试点专业3个、省级国际化试点改革专业1个、应用型转型专业5个、应用型转型示范专业2个；国家级虚拟仿真实验教学中心1个、国家级工程实践教育中心4个、省级实验教学示范中心17个、省级大学生实践教育基地11个；省级以上本科精品类课程35门。机械工程、车辆工程、焊接技术与工程3个专业在辽宁省专业综合评价中排名第一。目前，全日制在校生16356人，其中，本科生14655人，硕士、博士研究生1701人。 \n\n学校现有专任教师922人，其中教授157人、副教授322人，具有硕士、博士学位的教师约占教师总数的90%。拥有“长江学者奖励计划”特聘教授、国家杰出青年科学基金获得者1人，百千万人才工程国家级人选2人、国务院政府特殊津贴获得者11人、全国优秀教师1人、科技部“创新人才推进计划”入选者1人，省部级有突出贡献的专家4人、省高等学校“攀登学者”2人、省特聘教授7人、省优秀专家2人、省“十百千高端人才引进工程”人选者1人、省百千万层次人选82人、省“兴辽英才计划”青年拔尖人才3人，省高校创新人才4人、省部级优秀教师8人、省级教学名师13人、省优秀青年骨干教师26人，省青年学科带头人2人、省级专业带头人5人，省级教学团队6个，省级科研创新团队3个。 \n\n2015年以来，学校获批国家973计划、国家863计划、国家科技支撑计划、国家自然科学基金等国家级科研项目74项、省部级项目464项；获省部级科技成果奖23项；国家授权专利904件，其中发明专利205件；发表学术论文2294篇，其中被三大检索收录869篇；出版学术专著106部。签订横向合同681项，合同额超1.8亿元。 \n\n学校是全国大学生暑期社会实践先进单位。在全国大学生艺术展演活动中两次获得优秀组织奖，“青年马克思主义者百千万培养工程”等两项成果被评为教育部高校校园文化建设优秀成果。近三年，学生获得国家级、省级科技竞赛奖励554项。 \n\n学校是国家产学研合作先进单位，是辽宁省产学研合作创新基地。与中国铁路总公司、中国中车股份有限公司、广西玉柴机器集团有限公司等企业成立“中国轨道交通装备制造创新联盟”，牵头成立了“辽宁省轨道交通产业校企联盟”。 \n\n学校是中国政府奖学金来华留学生招收院校、《中美人才培养计划》121项目创新人才培养实验基地。先后与澳大利亚、美国、日本、俄罗斯等国家的80多所院校和教育科研机构建立了校际合作关系。建有中日友好大连人才培训中心，现有中外合作办学本科教育项目3个。 \n\n建校以来，学校培养了十余万名毕业生，有大多数毕业生在轨道交通行业就业，部分已成为了行业领军人物和核心技术骨干，在业内享有“中国轨道交通装备制造业工程师的摇篮”的美誉。新时代，学校以习近平新时代中国特色社会主义思想为指引，紧紧围绕辽宁老工业基地全面振兴和现代轨道交通装备制造业发展，抓牢国家“一带一路”建设、“高铁走出去”战略实施等发展机遇，全面深化改革，深入推进“双一流”建设，大力推进学校党代会确定的“五大战略”，凝聚发展力量，坚定不移地朝着特色鲜明的高水平研究应用型大学的建设目标迈进。 ',NULL,'东北地区',2,'大连机车车辆制造学校、大连机器制造学校、大连铁道学院、','理工',1956,'辽宁省','否',0,0,0,0,14,0,0,913,43,13,2,0,1590,0,'http://www.djtu.edu.cn/'),(186,179,'沈阳建筑大学','辽宁',11,'沈阳建筑大学是以建筑、土木等学科为特色，以工为主，多学科门类协调发展的省部共建高等学校，原隶属于国家建设部，2000 年在国家办学管理体制调整中划归辽宁省管理，2010年成为国家住房和城乡建设部与辽宁省人民政府共建高校。\n\n学校现有建筑学、城乡规划学、风景园林学、土木工程、机械工程5个博士学位授权一级学科，土木工程、机械工程2个博士后科研流动站，16个硕士学位授权一级学科，15个硕士专业学位授权点。学校为推荐优秀应届本科毕业生免试攻读硕士学位研究生工作单位。在全国第四轮一级学科评估中，学校学科总体排名位列辽宁省属理工类高校第一名。5个学科入选省一流学科重点建设学科，7个专业获批辽宁省普通高等学校首批一流本科教育示范专业，学校入选省一流大学建设高校。\n\n学校设有16个学院（教学部），15个直属科研机构、4个直属科研平台，现有一支德才兼备、业务精湛、奋发有为、充满活力的师资队伍。有教职工1700余人，其中博士硕士生导师571人，教授180余人，副教授410余人。有教育部“长江学者奖励计划”特聘教授2人，国家“万人计划”百千万工程领军人才1人，“新世纪百千万人才工程”国家级人选4人，国务院学位委员会学科评议组成员、教育部专业指导委员会、高等学校教学指导委员会委员12人，教育部（住建部）土建学科专业指导委员会委员9人，享受国务院政府特殊津贴专家、全国优秀教师、教育部“新世纪优秀人才支持计划”入选者23人，辽宁省攀登学者、特聘教授、辽宁省教学名师、辽宁省百千万人才工程“百人层次”人选66人，教育部长江学者创新团队及辽宁省创新团队10个。学校现有本科生11021人，博士、硕士研究生3026人，外国留学生858人。\n\n学校本科教育包括7个门类下24个专业类，共47种专业，于2006年9月以“优秀”的成绩通过了国家教育部本科教学工作水平评估。土木工程、机械设计制造及其自动化专业已通过工程教育认证。建筑学、城乡规划、给排水科学与工程、建筑环境与能源应用工程和工程管理专业以最长有效期通过了住建部专业教育评估（认证）。6个专业被评为国家级特色专业建设点，7个专业获批辽宁省首批普通高等教育本科优势特色专业。学校在参评辽宁省普通高等学校本科专业综合评价的30个专业中，有7个专业排名第一，23个专业排名进入前五名。3个教学团队被评为国家级教学团队，2门课程被评为国家级精品课程。我校是教育部确定的国家卓越工程师教育培养计划实施高校，国家大学生创新创业训练计划实施高校。学校有国家技术转移示范机构、国家地方联合实验室、教育部国际合作联合实验室3个国家级科技平台，3个国家级工程实践教育中心，1个国家级专业学位研究生联合培养基地，1个国家级大学生校外实践教育基地，1个国家级人才培养模式创新试验区，11个省部级重点学科，37个省部级重点实验室（工程技术研究中心），24个省级实验中心及实践基地。我校学生十二五以来在国家级学科竞赛中获得各类奖项千余项，先后荣获2018年CTBUH国际学生高层建筑设计竞赛国际级一等奖、全国大学生机械创新设计大赛一等奖、中国研究生电子设计大赛一等奖、2018年“创青春”全国大学生创业大赛银奖等。有三名学生获得2014、2015年度中国自立自强之星提名奖。学校是国家教育部确定的培养高水平运动员试点单位之一。学校2002～2018年的本科和研究生毕业生初次就业率在辽宁省高校名列前茅。\n\n“十二五”以来，学校获得科技总经费12.65亿元，累计科研立项3800余项，其中获得“十三五”国家重点研发计划“ 北方地区高大空间公共建筑绿色设计新方法与技术协同化” “ 预制混凝土构件高效配筋及性能化设计理论” “ 复杂预制构件混凝土数字化智能精确布料技术与设备研发”“可再生能源和蓄能技术耦合应用关键技术研究”以及国家基金重点项目“装配式耗能结构抗多灾害作用的全寿命设计理论研究”“ 寒地大空间公共建筑绿色设计理论与方法”等在内的国家级课题和省部级课题1300余项。90余项科研成果获得国家和省部级奖励，其中，“大连理工大学辽东湾校区项目”获得全国优秀工程勘察设计行业奖一等奖，“用于石材加工的单驱动可分度复合切削部件”获国家专利金奖。发表论文被“三大检索”收录2200余篇，其中多篇学术论文在《Nature Geoscience》等国际顶级期刊上发表。按全国技术市场统计，学校分别在2014、2015、2016年中国高校产学研实力排行榜中排名第13、12、13名。学校现已承担国家和省部级教改立项320余项，教改成果获国家和省部级奖励130余项，“一般工科院校本科人才培养研究与实践”获国家高等教育教学成果奖二等奖，学校2次被评为全国“优秀高等教育研究机构”，连续6次被评为辽宁省教育科研先进集体。《沈阳建筑大学学报（自然科学版）》被评为中国高校优秀科技期刊和中国科技论文在线优秀期刊一等奖，入选中国科学引文数据库（CSCD）和北京大学《中文核心期刊要目总览》，《沈阳建筑大学学报（社会科学版）》被评为中国高校优秀社科期刊和中国科技论文在线优秀期刊二等奖。学校图书馆馆藏图书150余万册。\n\n学校与美国迈阿密大学、澳大利亚悉尼科技大学、俄罗斯顿河国立技术大学及英国谢菲尔德大学等40多个国家和地区的60多所大学建立了校际交流或科研、合作办学关系，是国家教育部选定的招收国内访问学者单位，是国务院确定的全国首批可以招收外国留学生的学校，是中国政府奖学金来华留学生招收院校，是辽宁省政府外国留学生博士奖学金生招收院校,是辽宁省高层次人才培养基地和辽宁省干部教育培训基地，学校与美国班尼迪克大学合作举办的信息管理硕士学位教育项目是基地的示范项目。“中芬（北欧）木结构节能环保别墅建筑示范项目”和“中德节能示范中心”搭建了高端国际科技合作平台。学校与罗马尼亚特来西瓦尼亚大学共同建立了孔子学院。\n\n学校位于沈阳浑南区的主校区占地面积1500亩，建筑面积50万平方米。主校区规划设计体现了以人为本、与自然和谐共生的理念，建筑形式现代、质朴、简练，功能设施齐全。教学区为网格式、具有东方文化底蕴的庭院组合，有利于资源共享和学科交流。一座长达 756 米的亚洲第一文化长廊将教学区、图书馆、实验区、办公区、生活区等巧妙地连接在一起，形成校园一道独特的风景。学校拥有开放式图书馆、国内首座建筑博物馆、中国古建筑保护示范区、中德节能示范中心、多功能体育中心、网球训练中心、校园数字信息化平台。学校校园获得国家“2008年中国人居环境范例奖”。\n\n学校全面贯彻党的教育方针，高举中国特色社会主义伟大旗帜，坚持以马克思列宁主义、毛泽东思想、邓小平理论、“三个代表”重要思想、科学发展观、习近平新时代中国特色社会主义思想为指导，深入贯彻落实习近平新时代中国特色社会主义思想，坚持高质量发展，秉承“严谨治学、追求卓越”的办学理念和“博学善建、厚德大成”的校训，坚持以立德树人为根本，不断深化各项教育教学改革，求真务实，开拓进取，为建设国际知名、国内一流的建筑大学而努力奋斗！','SJZU','东北地区',2,'东北兵工专门学校、东北建筑工程学校、中央人民政府建筑工程部沈阳建筑工程学校、辽宁省建筑工业学校、沈阳市技术干部学校、沈阳市工业技术学校、沈阳市工业学校','理工',1948,'辽宁省','是',0,0,0,0,16,0,4,1700,47,16,5,0,1481,0,'http://www.sjzu.edu.cn/'),(183,180,'沈阳农业大学','辽宁',11,'沈阳农业大学是一所地方与中央共建的全国重点大学。现任党委书记刘广林，校长陈启军。\n\n学校坐落在沈抚同城连接带中心区域天柱山南麓，东与世界文化遗产清福陵毗邻，南与沈水浑河相望，占地面积10246亩。校园环境幽雅，风景秀丽，人文气息浓厚，教学、科研条件优越。\n\n沈阳农业大学的办学历史可追溯到我国农业教育的始兴时期—1906年清政府设立的省立奉天农业学堂，以后经历了奉天农业大学、东北大学农学院和沈阳农学院几个历史时期，1952年全国高等院校调整，与复旦大学农学院合并组建成了新的沈阳农学院。1979年10月经国务院批准为全国重点高等院校，1981年被批准为首批具有博士、硕士学位授予权单位；1985年10月，更名为沈阳农业大学，邓小平同志亲自为学校题写了校名。2000年，学校由农业部所属划转为以辽宁省管理为主、辽宁省与中央共建的全国重点大学。\n\n在党的教育方针指引下，几代沈农人努力拼搏，开拓创新，学校的各项事业不断发展壮大，成为中西部高校基础能力建设工程重点建设高校，国家首批建设新农村发展研究院试点高校，教育部、农业部、国家林业局开展的国家首批卓越农林人才教育培养计划改革试点高校。今天的沈阳农业大学已成为教学和科研并举，以农业与生命科学为特色，农、理、工、经、管、法多学科协调发展的研究应用型大学。\n\n学校形成了比较完整的农业教育科研体系。现有6个博士后科研流动站，72个博士学位授予权专业，114个硕士学位授予权专业，58个本科专业；学校有3个国家重点学科，3个农业部重点学科，7个辽宁省高等学校一流学科，27个辽宁省重点学科。根据科睿唯安发布的最新ESI数据显示，我校植物学与动物学首次进入全球前1%学科，农业科学持续进入全球ESI前1%学科。学校设有16个学院、4个教学部（中心、所）；有包括国家工程实验室、国家地方联合工程实验室、科技部和教育部批准设立的新农村发展研究院在内的省级以上（含沈阳市）科学研究机构93个、基础实验室及实验教学中心19个。\n\n建校以来，一大批海内外知名学者先后来校任教。自1990年以来，已有257人享受国务院政府特殊津贴。学校现有教职工1691人，其中专任教师1217人，教授189人，副教授334人，博士研究生导师127人和硕士研究生导师573人。专业教师中有院士2人，特聘院士5人，国家杰出青年科学基金获得者2人，万人计划科技创新领军人才1人，国家优秀青年基金获得者等国家级青年人才3人，国家“百千万人才工程”第一、二层次人选3人，新世纪百千万人才工程国家级人选2人，国家百千万人才工程人选1人，国家级教学名师1人，国务院学科评议组成员3人，教育部优秀人才支持计划人选4人，科技部中青年科技创新领军人才1人，教育部创新团队1个，农业部农业科研杰出人才及其创新团队1个，农业部“神农计划”人选2人，辽宁省“院士培养工程”人选1人，辽宁省首批高端人才引进工程1人，辽宁省攀登学者7人，特聘教授20人，辽宁省“兴辽英才计划”科技创新领军人才3人，青年拔尖人才12人，高水平科技创新团队1个，辽宁省学科评议组成员4人，辽宁省教学名师21人。一支以博士和硕士研究生导师为骨干、以具有博士学位的中青年教师为主体的高水平的师资队伍已经形成，学校具备了发展新兴学科、边缘学科和进行多学科联合科技攻关的基础和优势。\n\n学校现有国家级优秀教学团队1个，国家级特色专业和综合改革试点专业5个，国家卓越农林人才教育培养计划改革试点项目2个，涵盖8个专业，国家级精品资源共享课1门，国家级优秀网络课程2门，国家级农科教合作人才培养基地4个，国家级大学生校外实践教育基地2个，国家级实验教学示范中心1个。辽宁省优秀教学团队9个，省级特色、重点支持和转型发展、创新创业教育试点专业41个，辽宁省首批一流本科教育示范专业7个，辽宁省精品课程21门，辽宁省精品资源共享课16门，辽宁省视频公开课2门，省级实验教学示范中心14个，省级大学生实践教育基地14个，省级虚拟仿真实验项目2个。校内外教学实习基地176个。2000年以来，获得国家级教育教学成果二等奖2项，省级本科教育教学成果奖51项，省级以上各类教学研究项目170项。\n\n学校始终把提高人才培养质量放在各项工作的首位，重视教学研究和改革，着力于学生的综合素质和创新精神培养。建校以来，学校为祖国社会主义建设事业培养本、专科生和博士、硕士研究生13万人。我校毕业生基础扎实，爱岗敬业，在各自岗位上勤奋工作，为国家经济发展和社会进步做出了重要贡献，受到社会的广泛好评。学校现有学生20752人，其中全日制本科生13183人，全日制博士研究生746人，全日制硕士研究生2755人，非全日制专业学位研究生459人，留学生432人，成人继续教育生3191人。“团结、勤奋、求实、创新”的优良校风发扬光大，学生综合素质不断提高。\n\n2000年以来，学校共承担国家、省、市的各类科研项目8000余项，有298项成果获国家、省（部）、市奖励，78%的科研成果在生产中得到大面积推广应用，创造了巨大的经济效益。学校先后在辽宁省14个市26个县（区）建立了科教基点，选派58名科技人员到当地担任科技副县（市）、乡（镇）长或科技顾问，有力地推动了“科教兴农”工作的深入开展。\n\n学校积极构建国际合作平台，不断加强与国际知名大学、科研院所的交流与合作，目前已与40多个国家和地区的84所院校结为友好学校。\n\n进入“十二五”以来，全校师生始终保持奋发有为的精神状态，抢抓机遇，锐意改革，励精图治，攻坚克难，各项事业快速发展。学校成功入选国家中西部高校基础能力建设工程，为今后发展注入了新的动力。包括建筑面积20万平方米的17个建筑单体的新教学科研区和占地500亩、设施先进的校内新教学科研基地建成投入使用，极大地改善了办学条件；位于海城、辽中、彰武等地占地4300亩的校外长期教学科研基地建设任务已完成并投入使用，新的基地建成后将为高水平开展教学科研活动提供保障，极大地拓展学校的发展空间。校园文化建设成果丰硕，形成了坚韧不拔、坚持发展、坚定信心、坚持不懈、坚持事在人为的沈农精神，创造了沈农速度，营造了尊重知识、尊重人才、崇尚科学的良好氛围。学校先后荣获全国文明单位、全国文明校园、全国毕业生就业50所典型经验高校、全国模范职工之家、全国学位与研究生教育信息工作先进单位、辽宁省先进基层党组织、辽宁省文明单位、辽宁省五一劳动奖状、辽宁省高校党建工作创新奖、辽宁省校园文化建设品牌学校、辽宁省高校宣传思想工作先进集体、辽宁省高校平安校园、沈阳市先进党委、沈阳市先进单位、服务沈阳突出贡献集体等荣誉称号。\n\n在新形势下，学校正在围绕人才队伍、学科建设两大工作重点，努力开创内涵提升的新局面。沈阳农业大学正以崭新的姿态迈向更加辉煌的明天！ ','SYAU','东北地区',2,'奉天省立第一农科高级中学、奉天国立农业大学、沈阳农学院、辽宁农学院、','农林',1952,'辽宁省','否',0,0,0,0,16,7,0,1711,57,21,10,0,0,0,'http://www.syau.edu.cn/'),(285,181,'大连海洋大学','辽宁',11,'大连海洋大学是我国北方地区唯一的一所以海洋和水产学科为特色，农、工、理、管、文、法、经、艺等学科协调发展的多科性高等院校。学校创建于1952年，前身为东北水产技术学校，1958年升格为大连水产专科学校，1978年升格为大连水产学院。2000年由农业部划转辽宁省管理。2010年经教育部批准更名为大连海洋大学。\n\n学校坐落于美丽的海滨城市大连。有黄海校区、渤海校区和瓦房店校区3个校区，占地面积74万平方米，教学、科研使用海域面积67万平方米，总建筑面积40万平方米。学校现设有15个学院、1个教学部。有教职工近1200人，全日制在校生13600余人。有一级学科硕士学位授权点13个、二级学科硕士学位授权点47个，有硕士专业学位授权领域12个，分布在农业、法律、翻译、土木水利、机械、电子信息等6个类别，有46个本科专业。\n\n学校学科体系完善，优势特色学科明显。水产一级学科为辽宁省“双一流”建设学科，该学科在教育部第四轮学科评估中并列第四。拥有国家级多学科协同创新平台——大连海洋大学新农村发展研究院。\n\n学校不断深化教育教学改革，积极承担质量工程项目和教研教改项目。“充分发挥多学科优势，建设有自己特色的淡水渔业教学体系”曾获国家级优秀教学成果一等奖，“水产养殖学专业（本科）教学内容、课程体系及人才培养模式改革的研究与实践”曾获国家级优秀教学成果二等奖。在近两届教学成果评选中，获得省级优秀教学成果奖25项，其中一等奖4项，二等奖14项，三等奖7项。有1个国家级人才培养模式创新实验区；1个国家级实验教学示范中心，9个省级实验教学示范中心建设项目，4个省级虚拟仿真实验教学中心建设项目；1个国家级大学生实践教育基地建设项目，11个省级大学生实践教育基地建设项目，1个省级大学生创新创业教育基地建设项目，1个省级实训培训基地。获批1个国家级卓越农林人才教育培养计划改革试点项目，1个国家级“新工科”研究与实践项目，1个国家级地方高校本科专业综合改革试点专业，3个国家级特色专业，4个省级一流本科教育示范专业，4个省级本科特色（示范）专业，3个省级本科综合改革试点专业，3个省级本科工程人才培养模式改革试点专业，1个省级本科重点支持专业；9个省级向应用型转变试点专业，3个省级向应用型转变示范专业；1个省级本科课程体系国际化试点专业；1个省级创新创业教育改革试点专业；1个辽宁省紧缺本科人才培养基地。已建成国家级精品课程1门、省级精品课程22门，省级虚拟仿真实验教学项目2项，省级精品资源共享课4门，省级精品视频公开课3门。\n\n学校有较为完备的教学科研平台。有农业农村部刺参遗传育种中心1个，国家海藻加工技术研发分中心1个，农业农村部重点开放实验室1个，教育部重点实验室1个，教育部国别与区域研究中心1个，辽宁省协同创新中心1个，省高校重大科技平台2个，省级重点实验室9个，省级工程技术研究中心4个，省级工程研究中心2个，省级科技服务中心1个，省高校重点实验室4个。\n\n学校科技创新能力不断增强。“十二五”以来，学校承担了国家重点研发计划、国家863计划、国家科技支撑、国家自然科学基金、国家海洋公益专项等各级各类科研项目1800余项，其中国家级183项，省部级657项。有54项科研成果获市级及以上奖励，其中国家级2项，省部级27项。“海水池塘高效清洁养殖技术研究与应用”、“刺参健康养殖综合技术研究及产业化应用”分别于2012年、2015年获得国家科技进步二等奖。学校主持完成的“刺参疾病的发生机制及无公害防控技术研究与示范”于2013年获得国家海洋局海洋科学技术二等奖；学校主持完成的“北方典型河口滩涂生物资源恢复与生境修复关键技术研究与示范”获2014年获得辽宁省科技进步二等奖；学校主持完成的“沙蚕生物滤池研制及其在海珍品循环养殖中应用”获2015年获得辽宁省技术发明二等奖。学校主持完成的“基于生态系统的北方海域全产业链现代海洋牧场生产模式构建与示范”获2017年辽宁省科技进步二等奖；学校主持完成的“基于多生物功能群资源恢复的北方河口湿地生态修复工程技术应用与示范”获2017年辽宁省技术发明三等奖；另外，学校获批4个水产新品种：中间球海胆“大金”、菲律宾蛤仔“斑马蛤”和菲律宾蛤仔“白斑马蛤”和虾夷扇贝“明月贝”。\n\n学校拥有一支结构相对合理，学科领域覆盖较全的师资队伍，有专任教师831人，具有高级职称的384人。目前，有“双聘院士”5人，国家杰出青年基金获得者1人，国家“万人计划”科技创新领军人才1人，国务院水产学科评议组成员1人，863项目首席科学家1人，国家百千万人才工程人选2人，教育部新世纪优秀人才支持计划1人，教育部教学指导委员会副主任委员2人、委员2人，农业部农业科研杰出人才3人，农业部产业技术体系岗位专家5人，辽宁攀登学者1人，辽宁特聘教授5人，辽宁杰出科技工作者2人，享受各级政府特殊津贴51人，“兴辽英才计划”青年拔尖人才4人，“兴辽英才计划”白千万人才工程领军人才1人，入选省百千万人才工程90人，其中百人层次16人、千人层次28人，省高等学校优秀人才支持计划49人，辽宁青年科技奖十大英才1人，省农业领域青年科技创新人才培养计划9人，省、市优秀专家26人，省高校教学名师16人，省高校专业带头人7人，大连市领军人才3人及领军后备人才7人，大连市杰出青年科技人才2人，大连市青年科技之星培育计划28人。有国家级教学团队1个，省级教学团队9个，有科技部重点领域创新团队1个，农业农村部农业科研创新团队3个，辽宁省农业科技创新团队6个，辽宁省高等学校创新团队4个，辽宁省“兴辽英才计划”高水平创新创业团队1个。\n\n学校拥有浓厚的蓝色育人氛围，形成了以“学贯江海、德润方厚”的校训精神和“兼容并蓄、追求卓越”的大海大精神为核心的蓝色大学文化特色。学校积极倡导”厚德博学、为人师表”的教风，不断培育“明德尚学、志存高远”的学风，精心打造文化品牌项目“蓝色讲坛”，拥有“海洋之声合唱团”等各类文体、科技学生社团，定期举办校园文化节，不断丰富第二课堂科技文化活动，学生的综合素质不断提高。\n\n学校先后与世界13个国家（地区）的72所院校（机构）签订了友好协议，涉及亚洲、北美洲、欧洲、大洋洲，学校的对外影响和国际声誉不断提升。学校大力开展国际教育，拥有新西兰奥塔哥理工学院“3+1”合作举办机械设计制造及其自动化专业本科教育项目，与英国、美国、俄罗斯、韩国、日本、澳大利亚、台湾等国家和地区的多所高校举办校际交流和联合培养项目，并且被国家留学基金委批准确定为“优秀本科生国际项目”实施院校。学校积极响应“一带一路”倡议，招收培养多种模式和层次的来华留学生，学生生源涉及日本、韩国、俄罗斯、孟加拉、赤道几内亚等国家。\n\n站在新的历史起点，学校将全力贯彻落实党的十九大提出的“实现高等教育内涵式发展”、“加快建设海洋强国”的战略部署，以建设“蓝色大学”为发展理念，朝着“特色鲜明、国家一流研究应用型海洋大学”的奋斗目标加速迈进！','DLOU','东北地区',2,'大连水产学院、辽宁省农业工程学校','农林',1952,'辽宁省','是',0,0,0,0,19,5,0,831,40,12,0,0,3434,0,'http://www.dlou.edu.cn/'),(59,182,'中国医科大学','辽宁',11,'中国医科大学是中国共产党最早创建的院校，是唯一以学校名义走完红军两万五千里长征全程并在长征中继续办学的院校，是我国最早进行西医学学院式教育的医学高校之一。其前身为1931年11月创建于江西瑞金的中国工农红军军医学校。1932年学校更名为中国工农红军卫生学校。1940年9月在延安，经毛泽东同志提议，中共中央批准，学校更名为中国医科大学。1948年11月，在沈阳接收原国立沈阳医学院（前身为满州医科大学，1911年由日本国南满铁道株式会社建立），合并了辽宁医科大学（前身为盛京医科大学，1883年由英国苏格兰教会建立）。中国医科大学是原卫生部部属高等医学院校，2000年改为省部共建学校。2015年获批为辽宁省人民政府、国家卫生健康委、教育部共建高校。\n\n中国医科大学建校至今，共培养了10万多名高级医学专门人才，毕业生遍布全国各地及世界许多国家和地区。多年来，培养和造就了一大批国家卫生管理的著名领导干部和医学界的著名专家学者。据不完全统计，担任副部级以上职务百余位，卫生行业主管部门正、副部长10位，将军40多位，中国科学院、中国工程院院士14位。\n\n学校现有沈北、和平2个校区，设有15个学院（学部）、4个研究院。在医学、教育学、理学、工学、哲学和管理学等6学科门类拥有学位授予权；在基础医学、临床医学、口腔医学、公共卫生与预防医学、药学、护理学、生物学等7个学科具有一级学科博士学位授予权，设有博士学位授权学科（专业）69个，硕士学位授权学科（专业）74个；国家重点学科5个，国家重点（培育）学科1个；国家临床重点专科建设项目40个；省级临床重点专科59个，省级临床重点专科建设项目7个，辽宁省高等学校一流学科A类7个；博士后流动站7个；本科专业21个。国家级教学项目有五年制临床医学人才培养模式改革试点项目1项，拔尖创新医学人才培养模式改革试点项目1项，人才培养模式创新实验区1个，特色专业建设点5个，综合改革试点专业1个，精品课程11门，精品视频公开课1门，精品资源共享课10门，教学团队2个，双语教学示范课程1门，实验教学示范中心2个，大学生校外实践教育基地1个，虚拟仿真实验教学项目3个，精品教材2本，“十二五”规划教材16本；省级教学项目有一流本科教育示范专业7个，示范性专业8个，品牌专业1个，本科综合改革试点专业5个，重点支持专业1个，创新创业教育改革试点专业4个，本科工程人才培养模式改革试点专业1个，应用型转型示范专业2个，本科课程体系国际化试点专业1个，学术学位研究生课程建设试点项目1个，精品课程41门，精品视频公开课5门，精品资源共享课24门，教学团队12个，双语教学示范课程2门，实验教学示范中心11个，跨专业实训平台1个，虚拟仿真实验教学中心5个，大学生实践教学基地8个，创新创业实践教育基地1个，辽宁省高等学校实训培训基地1个，紧缺人才培养基地1个，虚拟仿真实验教学项目1个，精品教材8本，“十二五”规划教材4本。有国家临床医学研究中心1个，国家地方联合工程研究中心1个，国家药品临床研究基地2个，省部共建国家重点实验室培育基地1个，教育部重点实验室3个，国家卫生健康委重点实验室6个，辽宁省工程研究中心（实验室）6个，辽宁省重点实验室31个，辽宁省工程技术研究中心5个，辽宁省临床医学研究中心17个，辽宁省临床医学协同创新联盟2个，辽宁省高校重大科技平台2个，辽宁省2011协同创新中心1个，辽宁省高校重点实验室15个；辽宁省精准医疗重点实验室1个，辽宁省医疗中心50个。学校现有3所附属综合性医院和2所专科性医院，开放床位11332张，牙科综合治疗椅273张。\n\n学校在编教职工8948人。全校各类专业技术人员占职工总数的89.11％，全校教授级775人，副教授级1055人，研究生指导教师1754人，其中博士生指导教师496人。学校全日制在校生16366人，其中博士生1217人，硕士生（含七年制）4675人，普通本科生9265人，预科生23人，外国留学生1186人。\n\n学校是国家创新人才培养示范基地。教师中有中国工程院院士1人；全国杰出专业技术人才1人；高等学校国家级教学名师1人；国家高层次人才特殊支持计划（“万人计划”）领军人才4人（其中科技创新领军人才3人、教学名师1人）、青年拔尖人才人选1人；国家海外高层次人才引进计划（“千人计划”）长期项目专家2人、青年项目专家2人；教育部“长江学者奖励计划”特聘教授3人、讲座教授1人；国家自然科学基金委杰出青年科学基金项目获得者1人；教育部“长江学者和创新团队发展计划”团队3个；全国高校黄大年式教师团队1个；创新人才推进计划中青年科技创新领军人才人选1人、重点领域创新团队2个；“百千万人才工程”国家级人选7人；国家有突出贡献的中青年专家2人，国家卫生计生突出贡献中青年专家14人；国务院特殊津贴获得者50人；辽宁省院士后备人选培养工程人选4人；“兴辽英才计划”杰出人才1人、高水平创新创业团队培养引进计划创新团队1个，辽宁省领军人才4人次，辽宁省优秀专家34人次，辽宁省高等学校攀登学者23人次，辽宁特聘教授45人次；“兴辽英才计划”高层次人才培养支持计划科技创新领军人才3人、百千万人才工程领军人才1人、科技创新领军人才（辽宁特聘教授）6人、青年拔尖人才13人；辽宁省普通高校专业带头人5人，高等学校省级教学名师15人，辽宁省优秀教师3人，辽宁省黄大年教师团队1个，辽宁省“百千万人才工程”百人层次114人、千人层次124人。在中华医学会担任主任委员6人（含候任主任委员和名誉主任委员），担任副主任委员6人；有国务院学位委员会学科评议组成员2人，教育部教学指导委员会主任委员1人，副主任委员3人，秘书长1人，委员10人。\n\n学校总占地面积152.02万平方米，建筑面积为76.38万平方米。图书馆是国家卫生健康委全国医学文献资源共享网络东北地区中心馆，国家卫生健康委医药卫生科技项目查新咨询单位；馆藏纸质书刊133万册，电子资源计160万册；订购中外文全文书刊数据库92种，日文医学文献收藏尤其丰富，为全国日文医学文献中心馆。实验动物部是辽宁省实验动物质量检测中心和辽宁省转基因动物研究重点实验室的依托单位。学校主办学术期刊15种。\n\n学校积极开展国际交流与合作，与英国贝尔法斯特女王大学合作成立了中英联合学院，与美国、英国、法国、日本、俄罗斯、韩国、加拿大、澳大利亚、意大利、丹麦、西班牙、朝鲜、芬兰以及澳门和台湾等国家、地区的94所大学、科研机构建立了良好的交流关系，与世界银行（WBG）、美国中华医学基金会（CMB）、日本国际协力事业团（JICA）等机构开展了卓有成效的合作。学校是国家高等学校学科创新引智计划基地（简称“111计划”）。有408名国际知名专家学者受聘为学校荣誉职称，近年来开展了82项重要国际合作项目，38个CMB项目。','CMU','东北地区',2,'中国工农红军军医学校、中国工农红军卫生学校、','医科',1931,'辽宁省','否',0,0,0,0,33,1,3,1462,0,73,6,1,1993,0,'http://www.cmu.edu.cn/'),(127,183,'大连医科大学','辽宁',11,'大连医科大学创建于1947年，前身为关东医学院。1949年并入大连大学，称大连大学医学院。1950年撤销大连大学建制，大连医学院独立。1969年大连医学院举校南迁贵州省遵义市，建立遵义医学院。1978年在大连市原址复办，仍称大连医学院。1994年更名为大连医科大学。学校现已发展成为以医学为主，理学、工学、管理学、艺术学、法学和哲学等多学科发展的医科大学，是辽宁省一流大学重点建设高校。\n\n学校位于辽宁省大连市旅顺南路西段9号，占地面积151万平方米，建筑面积53.2万平方米。学校固定资产总值21.85亿元，其中教学科研仪器设备总值4.05亿元。学校藏书254.1万册，数据库资源60种。现有23个教学、科研单位，21所附属医院；本科专业23个。\n\n学校现有国家重点学科1个，辽宁省一流建设学科4个，辽宁省一流特色学科4个。2个学科进入ESI全球机构前1%。有博士后科研流动站4个，一级学科博士学位授权点4个，专业博士学位授权点1个；一级学科硕士学位授权点11个，专业硕士学位授权点6个。获教育部创新团队1个，2人入选科技部创新人才推进计划中青年科技创新领军人才。\n\n学校现有教职工及医护人员近9000余人。其中正高级职称近900人、副高级职称1000余人，博士研究生导师190余人，硕士研究生导师1500余人。国家“万人计划”教学名师1人，国家“万人计划”科技创新领军人才1人，教育部“长江学者奖励计划”特聘教授5人，国家“百千万人才工程”人选7人，国家“杰出青年科学基金”获得者4人，享受国务院政府特殊津贴专家31人，国家中青年科技创新领军人才2人，国家卫生计生突出贡献中青年专家5人，国家“优秀青年科学基金”获得者4人，教育部新世纪优秀人才支持计划入选者2人。辽宁省教学名师25人，辽宁省高等学校攀登学者22人次，辽宁特聘教授34人次。\n\n学校全日制在校生13200人，其中研究生4500人，本科生7500人，外国留学生950余人。学校是国家首批“卓越医生教育培养计划”项目试点单位，辽宁省第二批转型发展试点高校。现有国家级专业4个，省级专业17个；有国家级教学平台5个，省级教学平台27个；有国家级课程4门，省级课程42门。2个专业在省内专业排名中位列第一。学校连续7年荣获“全国高等医学院校大学生临床技能竞赛”东北赛区第一名；近年来获全国总决赛特等奖4次、一等奖2次。近年来获“‘挑战杯’全国大学生课外学术科技作品竞赛”一等奖1项、二等奖1项、三等奖10项；“全国大学生基础医学创新论坛暨实验设计大赛”二等奖3项、三等奖3项、中俄赛三等奖1项。2018年获国家大学生创新创业项目16项。造血干细胞志愿捐献工作走在全国前列，是东北地区成功捐献人数最多的高校，共16名学生完成捐献。\n\n学校现有国家地方联合工程实验室1个、国家地方联合工程研究中心1个、国家级国际联合研究中心1个、国家级国际科技合作基地2个。有部级实验室、省重点实验室、省工程技术研究中心、省高校重大科技平台、省工程实验室、省工程研究中心、省高校重点实验室、省临床医学研究中心等省部级科研平台45个。“十三五”以来，学校获批国家重点研发计划课题、国家自然科学基金、国家社会科学基金等国家级项目226项，以大连医科大学为第一完成单位发表SCI论文1799篇，进入ESI高被引论文25篇，5位教授连续入选2016-2018年“中国高被引学者榜单”。获国家科技进步二等奖1项，省部级科技奖励30项。\n\n学校现有3所直属附属医院。附属第一医院、附属第二医院均入选“中国顶级医院100强”。 附属第一医院获全国首批心衰中心、房颤中心认证，国内首家神经电生理诊疗及神经调控中心，经皮主动脉瓣置换术总例数东北首位。组建国内唯一儿童重大疾病干细胞临床研究团队；东北唯一国家区域医学中心；获国际医院“品管圈”铜奖；科技部重点研发计划专项；建立紧密型医联体，实现“一院五院区”布局。人才数量位居东北医科院校附属医院首位。获中国医院管理奖价值案例、“2018年度群众满意的医疗机构”、再获全国“巾帼文明岗”。附属第二医院牵头建成辽南地区首个区域医联体；获批全国肿瘤疑难病症诊治能力提升工程项目；建成大连医科大学器官移植中心，是大连市唯一肝脏移植准入单位，拥有东北最先进的骨髓移植病房；全国首批建立现代医院管理制度试点单位；获批全国卫生系统最高奖项“先进集体”、全国改善医疗服务行动示范医院、全国特色医院文化单位等。附属第三医院正在建设中。\n\n学校大力推进教育国际化，留学生教育水平位居全国医科院校前列，被评为首批“全国来华留学示范基地”，获批中国政府奖学金生自主招生资格和教育部“丝绸之路”奖学金项目，通过全国来华留学质量认证，医学学历教育获得了世界卫生组织的认可。生源累计覆盖五大洲115个国家和地区，先后与美国、英国、德国、法国、澳大利亚、俄罗斯、日本等30余个国家和地区的100余所大学及科研院所建立合作关系；与美国班尼迪克大学合作举办了“中美公共卫生硕士中外合作办学项目”。学校已加入全球丝绸之路大学联盟健康子联盟。\n\n传承历史，开创未来。全校师生秉承“甚解、敏行、明仁、济世”的校训和“团结、严谨、求实、创新”的校风，坚持走以提高质量为核心的内涵式发展之路，为建设国内先进、国际知名的医科大学而努力奋斗。','DLM','东北地区',2,'关东医学院、大连医学院 ','医科',1947,'辽宁省','否',0,0,0,0,23,0,5,9000,23,11,4,0,0,0,'http://www.dlmedu.edu.cn/'),(190,184,'沈阳药科大学','辽宁',11,'沈阳药科大学1931年诞生于江西瑞金，其前身为中国工农红军卫生学校，是一所具有光荣革命传统的高等药学学府。学校总占地面积143.45万平方米，总建筑面积约74.9万平方米。其中校本部（文化路）校区占地面积21.5万平方米，建筑面积约17.9万平方米；铁西校区占地面积1.6万平方米，建筑面积约1.7万平方米；南校区（本溪高新区）占地面积120.35万平方米，建筑面积约55.3万平方米。\n\n学校目前已发展成为多学科、多层次、多形式教育的高等药学学府，设有药学院、制药工程学院、中药学院、生命科学与生物制药学院、工商管理学院、医疗器械学院、功能食品与葡萄酒学院、无涯学院、社科与文体学院和继续教育学院10个学院。现有在校研究生2625名（博士463、硕士2162）、本科生7932名、成人函授生4902名。学校荟萃了众多的专家学者。在1133名教职工中，专任教师592名，其中正高级职称138人（教授134人），副高级职称247人（副教授222人）。现有国家工程院院士1人，国家特聘专家2人，“万人计划”科技创新领军人才1人，“长江学者”特聘教授1人，“百千万人才工程”国家级人选3人，教育部新世纪优秀人才支持计划入选者1人，国务院药学学科评议组成员1人（召集人），中科院“百人”1人，国务院特殊津贴获得者13人，教育部有突出贡献的中青年专家1人，教育部首届青年教师奖1人，全国优秀科技工作者2人；辽宁省领军人才1人，辽宁省优秀专家12人，辽宁省攀登学者6人，辽宁省特聘教授21人，辽宁省中青年学科带头人2人，辽宁省“院士后备人选培养工程”第二批人选1人，辽宁省优秀教师4人，辽宁省百千万人才工程百层次人选40人，“兴辽英才计划”领军人才5人、青年拔尖人才4人、高水平创新创业团队2个。\n\n学校始终以医药及相关行业发展需求为导向，围绕学校办学定位、突出药学学科优势、坚持药学教育主线，适度拓展本科专业布局，持续优化本科专业结构，逐步形成了以药为主，跨医、工、理、管、经5个学科门类10个专业类的本科专业结构布局。共设置20个本科专业，5个成人本专科专业。有国家理科基础科学研究与教学人才培养基地、国家生命科学与技术人才培养基地。有5个国家级特色专业，1个国家级人才培养模式创新实验区；19门次国家级精品课程、精品开放课程、双语教学示范课；3个国家级实验教学示范中心、校外实践教育基地；3个国家级教学团队，1名国家级教学名师。有省级各类示范专业和改革试点专业项目34项，省级各类精品和示范课程48门次，省级各类实践教学项目28项，省级教学团队、教学名师奖、精品教材36项。获得国家级教学成果奖4项、省级32项。\n\n学校是国家批准有权授予博士学位、硕士学位和招收港、澳、台地区学员及外国留学生、国内高中保送生的院校。现有博士后流动站2个（药学、中药学），一级学科博士学位授权点2个,二级学科博士学位授权点19个，一级学科硕士授权点8个，二级学科硕士授权点53个，硕士专业学位授权点4个。药剂学科为国家级重点学科，获批辽宁省“双一流”首批重点建设高校，药学、中药学两个学科入选“一流学科”。学校学科布局合理，特色鲜明，国内外学术影响力不断提升。根据2019年1月ESI最新发布的数据，学校在全球高校和研究机构中排名1745位，进入世界前0.29%,全国排名第107位。3个学科稳定进入ESI全球前1%，其中，药理学与毒理学科进入全球前千分之一，世界排名71位、国内第6位，是辽宁省属高校中唯一进入全球前千分之一的学科。我校6名教授再次入选2018 Elsevier中国高被引学者榜单，连续五年在“药理学、毒理学和药剂学”领域排名全国第一。USNEWS2019年全球大学专业排名中，我校药理学与毒理学科排名世界第99位、国内第9位。我校主办的《亚洲药物制剂科学》（Asian Journal of Pharmaceutical Sciences，简称AJPS）学术期刊影响因子为4.56，是国内第一本被SCIE收录的药剂学学术期刊。《沈阳药科大学学报》和《中国药物化学杂志》也已成为国家药学类核心期刊。\n\n学校始终坚持“以药学为特色，注重应用基础与技术研究，鼓励学科交叉与颠覆性技术创新，兼顾基础研究”的科技发展战略，以服务国家战略和区域发展为科研方向，构建了技术研发、成果孵化、成果推广三大科技创新体系，在技术创新和成果转化方面形成了独特的优势。建有涵盖临床前创新药物研发完整技术链的重点实验室、技术平台、工程技术中心58个，其中，辽宁省国家重大新药创制综合平台1个，基于靶点的药物设计与研究国家教育部重点实验室1个，中药质量控制技术国家发改委地方联合工程实验室1个。\n\n学校立足前沿科学和产业技术需求，深入开展协同协作、联合攻关，在多相脂质体、粉体学、中药质量化学模式识别、脑科学研究、“配体包埋式”主动靶向纳米粒等领域居国内外领先水平，其中脑科学研究领域的相关成果在《自然》杂志子刊上发表。先后获得国家科技进步一等奖1项，国家科技进步二等奖2项，国家发明三等奖1项；向省内外医药企业转让新药证书137项，其中一类新药10个，为国家及区域医药行业、经济建设发挥了重要作用。学校联合地方政府、科研院所、制药企业，共同搭建产学研合作平台，实现科研成果与科研人才的有效供给。牵头成立国家创新药物产学研战略联盟、辽宁省现代制药产业校企联盟、辽宁省生物医药产业共性技术创新战略联盟、辽宁省生物医药产业协同创新战略联盟，省内成员单位近100家。本溪药物研究院先后接待美国明尼苏达大学等多所国际知名学府和科研机构专家和留学生的交流访问活动，营造了良好的学术交流氛围。\n\n学校积极开展国内外学术交流与合作，先后与国内一些知名大学签订了合作办学协议，实现资源共享；与美国、日本、英国、俄罗斯等19个国家和地区的69所高等院校及科研机构建立了合作关系。\n\n新时期，沈阳药科大学以习近平新时代中国特色社会主义思想为指导,制定了面向大健康领域，加快建设特色鲜明、多学科交叉、国际知名、国内一流的药科大学发展目标，明确了以教育教学为中心，以人才培养、科学研究、服务社会为职能，以“团结、勤奋、求实、创新”的校训及红色基因、红色传统为文化传承，开创学校事业发展的新局面，为东北地区经济和社会发展、为我国高等教育事业发展、为实现中华民族伟大复兴的中国梦做出更大贡献。\n\n新时代的沈阳药科大学洋溢着更加自信的新气象、奋力实现新作为！','SPU','东北地区',2,'中国工农红军卫生学校、中国医科大学、延安药科学校、东北药学院、沈阳药学院','医科',1931,'辽宁省','否',0,0,0,0,10,1,1,1133,20,8,2,0,1914,0,'http://www.syphu.edu.cn/'),(424,185,'鞍山师范学院','辽宁',11,'鞍山师范学院是一所全日制普通本科师范院校，位于拥有世界第一玉佛、亚洲著名温泉、国家名胜千山、中华宝玉之都和祖国钢铁之都的优秀旅游城市、国家卫生城市——辽宁省鞍山市。\n\n学校始建于1958年，1978年恢复建立鞍山师范专科学校，1993年升格为鞍山师范学院（本科），2004年通过了国家教育部本科教学评估，2011年获批教育硕士专业学位研究生培养单位，2013年获批中国政府奖学金来华留学生接收院校，2018年通过国务院学位委员会首期特需项目评估验收。\n\n建校60余年来，学校秉承“博雅兼上  知行合一”的校训，坚持“育人为本、质量立校、服务社会”的办学理念，坚持“师范性、地方性、应用型”的办学定位，坚定不移地走以提高质量为核心的内涵式发展道路，不断建立健全现代大学制度，形成了优良的办学传统，为国家基础教育、经济建设和社会发展培养了大批优秀毕业生，遍布了祖国的各条战线。\n\n学校现有3个校区，占地面积75万平方米，教学科研行政用房面积20万平方米。设有16个二级学院、3个公共教研部，还有1个附属中学。固定资产3.67亿元，教学科研仪器设备值11409万元，图书馆藏书111.7万册。\n\n学校现有教职员工1063人，其中专任教师712人，具有副高级以上职称人数467人。入选辽宁省百千万人才工程26人，有省级教学团队4个，省级专业带头人3人，省级教学名师7人，市突出贡献专家2人，市高层次人才2人，本地培养领军人才1人，硕士生导师52人。\n\n学校现有教育硕士招生领域7个，分别是学前教育、学科教学（语文、数学、思政、化学、英语、物理）。有51个全日制本科专业（涵盖文、理、教育、历史、管理、法、工、艺术8个学科门类），高职专业34个，本专科生招生省份达28个。各类在校生总数近1.7万人，其中教育硕士研究生130人，全日制本专科生12250人，已形成了从全日制本专科生教育到研究生教育、成人教育、留学生教育全方位、多层次的办学格局。\n\n学校着力构建以教师教育为“主体”、以人文社科和理科新工科为“两翼”的学科专业体系。现有国家级示范专业1个，省级一流专业1个，省级示范专业1个，省级转型发展试点专业4个，省级向应用型转变示范专业2个；省级教学成果奖33项；教育部教职委重点建设课程、省级精品课程17门；国家级规划教材3部；省级大学生校外实践教育基地4个。\n\n“十二五”以来，学校先后承担省级以上科研项目355项，其中国家自然科学基金、国家社科基金、国家艺术基金等国家级项目13项；发表核心期刊以上论文数985篇，其中SCI、EI、CPCI等检索收录460篇，主编、参编著作、教材311部。学校主办了《鞍山师范学院学报》，并在全国发行。\n\n在人才培养上，学校不断深化人才培养模式改革和通识教育改革，强化教师教育优势特色，大力推进应用型人才培养。同时，牢固树立“全员育人、全程育人、全方位育人”理念，全面实行综合导师制和学业导师制，实施大学生“润德”志愿服务行动，大力促进学生知识、能力、素质的协调发展。至今，学校的班级、社团、个人先后获得了“全国先进班集体”、“省先进班集体”、“省大学生示范社团”、“全国优秀共青团员”等荣誉称号。学生参与科研立项、公开发表论文及各类科技大赛获奖数量逐年增加，学生创新创业大赛的获奖数量和级别不断攀高，考研率逐年提升，毕业生年终就业率始终保持在95%左右。\n\n学校充分挥人文艺术学科优势，助力区域城市文化建设。依托刘兰芳艺术研究中心、国学中心及14个研究平台，开展千山文化、岫岩玉雕、北方陶瓷、鞍山评书、工业遗产、传统音乐等区域特色文化的研究、整理、传播工作。学校积极推进校企合作产教融合，获批教育部ICT产教融合创新基地等20余个，对接企业100余家，社会服务能力明显增强。\n\n学校广泛开展国际交流与合作，积极推进国际化办学。截止目前，学校先后与美国、英国、法国、西班牙、波兰、俄罗斯、澳大利亚、新西兰、尼日利亚、秘鲁、日本、韩国等20多个国家及地区百余所大学和教育科研机构签订友好合作协议，在学术交流、师生交流、文化交流等诸多方面开展了实质性的交流与合作。作为“中国政府奖学金来华留学生接受院校”，学校先后培养来自20多个国家的长短期留学生3500多人。\n\n近年来，学校社会影响不断扩大，是全国地方高校教师教育联盟副主席单位、“三北”地区高师院校协作单位、省基础教育师资培训基地、省幼儿园园长培训基地、省教师职业技能训练研究基地、省文化艺术交流基地、省大学生实践教学基地、省大学生创新创业实践基地。同时，学校还先后荣获“全国外国专家聘请管理工作先进单位”、“全国首批社会主义核心价值观共建共育基地”、“全国暑期社会实践工作优秀单位”等殊荣。\n\n面向未来，学校将高举习近平新时代中国特色社会主义思想伟大旗帜，全面贯彻党的十九大精神，全面落实全国教育大会精神，以立德树人为根本，以内涵发展为核心，为建成教师教育特色鲜明的地方高水平应用型大学而努力奋斗！',NULL,'东北地区',2,'鞍山师范学院','师范',1958,'辽宁省','否',0,0,0,0,11,0,0,1063,51,1,0,0,2336,0,'http://www.asnc.edu.cn/'),(260,186,'大连外国语大学','辽宁',11,'大连外国语大学地处辽宁省大连市，是辽宁省省属高校，是东北地区唯一一所公立外国语大学。学校于1964年在周恩来总理的亲切关怀下成立，时名大连日语专科学校，1970年更名为辽宁外语专科学校，1974年开办本科教育，1978年更名为大连外国语学院，2013年更名为大连外国语大学，目前已发展成为以外语为主，以国际化办学为特色，拥有文学、经济学、管理学、工学、法学、艺术学等学科的多科性外国语大学。2016年入选“辽宁省一流大学重点建设高校”，外国语言文学学科入选辽宁省重点建设的“一流学科”。\n\n学校拥有旅顺校区和中山校区两个校区，总占地面积126.15万平方米，建筑面积47.31万平方米，教学科研及辅助用房15.85万平方米，图书馆馆藏纸质图书196.5万册、纸质期刊794种、电子图书101万册，教学科研仪器设备总值9613.2万元，信息化设备资产总值6958.99万元。\n\n学校下设21个教学单位，设有服务国家特殊需求的“东北亚外交外事高端人才”博士培养项目1个，拥有外国语言文学和中国语言文学2个一级学科硕士点、17个二级学科硕士点和5个专业学位硕士点。其中，外国语言文学为辽宁省一流学科，俄语语言文学、英语语言文学、日语语言文学、西班牙语言文学为辽宁省重点学科。学校开设38个本科专业，覆盖14个外语语种。其中，国家级特色专业4个，辽宁省本科优势特色专业3个，辽宁省本科示范性专业5个，辽宁省本科综合改革试点专业4个，辽宁省普通高等学校本科课程体系国际化试点专业2个，辽宁省普通本科高等学校向应用型转变示范专业2个，辽宁省创新创业教育改革试点专业1个。\n\n目前，学校全日制在校生共14282人。全校在职教职工1017人，其中专任教师649人，聘请65名外籍教师。拥有国家级教学名师1人，国家“万人计划”教学名师1人，省级教学名师10人，国务院特殊津贴获得者7人，教育部高等学校外国语言文学类专业教学指导委员会委员7人，省级优秀专家3人，辽宁省特聘教授2人，辽宁省攀登学者1人，省级优秀教师7人，省级中青年学科带头人2人，省级专业带头人4人，省级优秀骨干教师20人，省级百千万人才工程43人。学校拥有博士生导师8人，硕士研究生导师185人，教授88人、副教授217人，具有硕士及以上学位教师596人。\n\n近年来，学校教育教学改革取得显著成绩。获得国家级教学成果奖2项，省级教学成果奖45项，省级精品课程14门，国家级精品资源共享课2门，省级精品资源共享课5门，省级精品视频公开课1门。获评国家级教学团队1个，全国高校黄大年式教师团队1个，省级教学团队5个。\n\n学校科研成果丰硕。比较文化研究基地为辽宁省教育厅人文社会科学重点研究基地，同声传译实验室为辽宁省重点实验室。学校现有15个科研机构，其中在教育部备案的研究中心7个。2012年以来获批省级以上科研项目近400项，其中国家社科基金重大项目1项。发表核心期刊以上学术论文400余篇，32部教材获批“十二五”普通高等教育本科国家级、省级规划教材，近300项学术成果获市级以上科研奖励。学校主办的《外语与外语教学》入选全国文科百强期刊，同时入选CSSCI和北大核心期刊。\n\n近年来，学校学生共有500余人次在国家、省市及地区各类专业比赛中获奖。奥运会、残奥会、夏季达沃斯、亚欧经济部长会议等重要国际大型活动上都活跃着学校志愿者的身影。近年来，毕业生年度就业率均在95％以上，建校以来学校共有百余名毕业生先后进入国家外交部工作。\n\n学校国际化办学特色鲜明。与34个国家和地区的223所高校和机构建立了友好合作交流关系。学校现有孔子学院、中俄大学生交流基地、俄语中心以及上合大学中方校长委员会主席单位、辽宁省国际型外语人才培养模式创新实验区等众多国际化平台。以交换留学、本科双学位、本硕连读、硕博连读、多语种交流、国际实训等培养模式，与国外知名院校联合培养本科生、硕士生、博士生，每年派出近1000名学生赴国外友好院校学习和参加实践项目。学校依托近10个国家公派项目每年公派学生150余人赴海外留学。截至目前学校共建立10所孔子学院。自2015年至今，共接收来自俄罗斯、日本、韩国、澳大利亚、新西兰等近60个国家的5125人次国际学生来校学习。\n\n学校遵循“育人为本、质量至上、突出特色、科学发展”的办学理念，坚持立德树人，坚持复合型、国际型、应用型人才的培养目标，坚持国际化办学特色，着力提高人才培养质量和办学实力。近年来，学校响应国家和辽宁省关于引导地方本科高校转型发展的号召和要求，积极向高水平应用型外国语大学转型，取得了较好的成效，正朝着建设质量优良、特色鲜明的高水平应用型外国语大学的目标不断迈进。','DUFL','东北地区',2,'大连日语专科学、辽宁外语专科学校、大连外国语学院','语言',1964,'辽宁省','否',0,0,0,0,21,0,0,900,68,2,0,0,3028,0,'http://www.dlufl.edu.cn'),(70,187,'东北财经大学','辽宁',11,'东北财经大学以“培育卓越财经人才，服务经济社会发展”为使命，是一所突出经济学、管理学优势和特色，经济学、管理学、法学、文学、理学等多学科协调发展的财经大学。学校坐落于美丽的海滨城市大连，总占地650204.9平方米,有全日制在校生1.6余万人，其中全日制本科生9482人，研究生6793人。学校坚持“引育用相结合、老中青全覆盖、各岗位人才全面发展”的人才工作方针，现有全职教职工1800余人，其中专任教师824人，含“长江学者奖励计划”5人、国家“万人计划”领军人才人选4人、国家杰出青年科学基金获得者1人、第七届国务院学位委员会学科评议组成员3人、“百千万人才工程”国家级人选4人、全国文化名家暨“四个一批”人才3人、国家级教学名师3人，并通过海外院长（中心主任）、特聘教授（研究员）、客座教授等形式，柔性引进高水平人才17人。多年来，学校着力推进精致东财建设，学科专业追求精品，教学科研和管理服务追求精益，师资队伍追求精锐，人才培养追求卓越，将环境育人理念贯穿校园建设始终，多次被辽宁省人民政府和大连市人民政府评为“绿化先进单位”“优秀花园式单位”“绿色大学”等称号。\n\n东北财经大学坚持学科引领战略，是辽宁省一流大学重点建设高校。在现有各学科中，财政学、产业经济学、会计学是国家重点学科及教育部、财政部批准的“国家级特色重点学科”，数量经济学为国家重点（培育）学科，应用经济学、统计学、工商管理、公共管理、管理科学与工程、理论经济学6个一级学科入选为辽宁省高等学校一流学科A类。在教育部组织的全国第四轮学科评估中，我校应用经济学评估结果为A，进入全国2%-5%行列；工商管理评估结果为A-，进入全国5%-10%行列；统计学评估结果为A-，进入全国5%-10%行列；管理科学与工程评估结果为B+，进入全国10%-20%行列。\n\n学校现有理论经济学、应用经济学、统计学、工商管理和公共管理5个一级学科博士后流动站；有理论经济学、应用经济学、统计学、管理科学与工程、工商管理和公共管理6个一级学科博士学位授权点，下设42个二级学科博士学位授权点（含自主设置16个）；有理论经济学、应用经济学、统计学、法学、马克思主义理论、外国语言文学、管理科学与工程、工商管理、公共管理和新闻传播学10个一级学科学术型硕士学位授权点，下设73个二级学科学术型硕士学位授权点（含自主设置18个）；有金融、应用统计、税务、国际商务、保险、资产评估、审计、法律、翻译、新闻与传播、汉语国际教育、工商管理、公共管理、会计、旅游管理、工程管理16个类别的全日制硕士专业学位授权点，在20个领域招收专业学位硕士研究生。工商管理（MBA）项目曾在国务院学位办组织的全国MBA教学评估中排名第二，学校是全国财经院校第一所正式开办公共管理专业学位（MPA）的高校，是全国19个试点开办会计专业学位（MPAcc）的单位之一。在教育部首次专业学位水平评估中，我校工商管理、会计专业学位进入全国A类行列。\n\n学校现有40个本科专业，其中会计学、金融学、工商管理、财政学、统计学、税务、保险学、工程管理、旅游管理等9个专业为国家级特色专业，工商管理、金融学为国家级综合改革试点专业，财政学、金融学、会计学、工商管理、国际经济与贸易、经济学、统计学、工程管理、旅游管理、物流管理等10个专业为辽宁省示范（特色）专业。会计学、金融学、工商管理、经济统计学、旅游管理、财务管理、市场营销、电子商务、保险学、劳动与社会保障等10个本科专业入选辽宁省首批一流本科教育示范专业建设项目，入选专业数量居辽宁省属高校首位。\n\n此外，学校还有成人教育、网络教育两种非全日制普通本科教育形式，学校是教育部首批继续教育示范基地，全国网络教育68个试点单位之一、10个首批评估单位之一。\n\n东北财经大学以人才培养为中心，以立德树人为根本，坚持树立以提高质量为核心的教育发展观，培养具有优良品德、人文情怀、科学素养、国际视野，富有社会责任感、法治意识、创新精神和实践能力的卓越财经人才。学校入选“全国百篇优秀博士学位论文”及获得提名论文数量在全国财经高校中居于前列，在本科教学改革与质量工程建设中获批国家级教学名师3人、国家级教学团队5个、国家级教学成果奖二等奖5项、国家级特色专业9个、国家级综合改革试点专业2个、国家级精品课程11门、国家级双语示范课程3门、国家级精品资源共享课程10门、国家级精品视频公开课1门、教育部来华英语授课品牌课程3门、国家级实验教学示范中心2个，成果数量在全国财经高校居于前列。本科专业在全国各省份均为一批次招生，其中有24个专业按9个专业类招生，多年来录取分数连年居于辽宁省前3名、东北三省前5名。\n\n东北财经大学坚持基础研究要前沿、应用研究要管用的“顶天立地”科研与社会服务工作方针。现有教育部人文社会科学重点研究基地1个、财政部共建基地1个、教育部国别和区域研究中心1个，与地方政府共建的自贸区研究院1个，其他省级以上重点研究机构及科研平台17个（包括辽宁省协同创新中心1个、辽宁省人文社会科学重点研究基地9个，辽宁省高校重点实验室2个，省级其他类别重点研究机构5个），校级科研机构40个，有辽宁省省级创新团队16个，初步建立了以人文社科重点研究基地、辽宁省协同创新中心以及部省共建试点基地为代表的智库新模式。学校出版社是全国百佳图书出版单位，国家一级出版社。《财经问题研究》是国家社科基金资助期刊、CSSCI（2019-2020）来源期刊，《东北财经大学学报》是中国人文社会科学扩展期刊。\n\n多年来，学校以高水平科研成果为导向，密切结合国家与地方经济社会发展实际，及时追踪经济改革的前沿和动态，积极服务地方、区域、国家经济社会发展。科研成果质量显著提升，科研精品不断涌现。自2009以来学校连续10年获批国家社科基金重大项目，立项数量名列辽宁省高校前列。学校是省内较早推动高校智库建设的高校，承担了一大批重大、重点科研项目、决策咨询项目，部分决策咨询研究报告所提出的可操作性对策建议得到国家和省有关部门的批示，相关资政建议较好地转化为管理者的决策或方案。\n\n东北财经大学早在20世纪80年代即开始实质性国际交流，于1994年启动中外合作办学，并逐步形成“高标准、国际化”的办学思路。经过三十余年的探索与实践，学校系统引进国外先进的大学教学管理模式，充分利用校内外和国内外的教学资源，逐步实现教学语言国际化、教材选用国际化、课程体系国际化、学生来源国际化、教师资源国际化、教学管理方式国际化，并不断发展实施交换学生项目、师资交换项目，开发国际合作科研平台，提升国际化办学质量与水平，在国际交流合作的广度、宽度、深度上逐步提高。\n\n学校有1个中外合作办学机构——萨里国际学院开展“留学不出国”的全日制本科与研究生教育。有6个分别与加拿大西安大略大学、澳大利亚科廷大学、英国厄尔斯特大学合作的中外合作办学项目。与美国霍夫斯特拉大学合作开展的境外办学项目是我国首批在美国境内向美国大学生授予中国大学文凭的境外办学项目。建立了包括本科、硕士和博士研究生学历教育，长短期汉语、商务汉语进修非学历教育在内的来华留学生教育管理体系。与美国南缅因州立大学、荷兰南方应用科技大学、斯洛伐克马杰伊贝尔大学分别建有孔子学院。截至目前，学校与世界22个国家和地区的114所高等院校、8个国际机构建立了不同形式的友好合作关系，与其中50余所大学开展着长期稳定的交往合作。\n\n学校是国内首家参与并获得“中外合作办学质量认证”的高校，牵头构建了中英合作办学联盟，是中国政府奖学金来华留学生、孔子学院奖学金的接收院校，是美国特许金融分析师协会（CFA）大学项目合作伙伴，会计学专业通过全球三大会计师公会之一的澳洲会计师公会（CPA Australia）的全面认证，萨里国际学院工商管理、旅游管理两个双学位专业获得国际商学院协会AACSB认证。\n\n学校有国内首个中外合作办学机构研究基地，建有孔子学院绩效评价与财务管理研修基地，相关研究成果得到国家有关部门和领导的重要批示。\n\n遵循“博学济世”校训，坚持人才强校、学科引领、质量提升、国际化、特色发展五大发展战略，东北财经大学正向着建成“国内一流、国际知名、特色突出的高水平财经大学”的发展目标迈进。 ','DUFE','东北地区',2,'东北财经学院、辽宁大学、辽宁财经学院','财经',1952,'辽宁省','是',0,0,0,0,26,0,5,1800,39,7,5,0,0,0,'http://www.dufe.edu.cn/'),(24,188,'吉林大学','吉林',23,'吉林大学（Jilin University）简称“吉大”，位于吉林省省会长春，是教育部直属、中央直接管理的综合性研究型全国重点大学，国家“双一流”、“211工程”、“985工程”重点建设，入选珠峰计划、2011计划、111计划、卓越法律人才教育培养计划、卓越工程师教育培养计划、卓越医生教育培养计划、卓越农林人才教育培养计划、国家建设高水平大学公派研究生项目、国家大学生创新性实验计划、新工科研究与实践项目、国家级大学生创新创业训练计划、国家创新人才培养示范基地、全国深化创新创业教育改革示范高校、中国政府奖学金来华留学生接收院校，首批建立研究生院的22所大学之一，是亚太国际教育协会、21世纪学术联盟、中俄交通大学联盟、粤港澳大湾区物流与供应链创新联盟重要成员。','JLU','东北地区',2,'','综合',1946,'教育部','是',985,211,1,5,46,0,59,6624,139,299,237,5,0,0,'http://www.jlu.edu.cn/'),(176,189,'长春理工大学','吉林',23,'长春理工大学（Changchun University of Science and Technology），位于吉林长春，由吉林省、国家国防科技工业局、长春市共建，是一所以光电技术为特色，光、机、电、算、材相结合为优势，工、理、文、经、管、法、艺协调发展的多科性教学研究型大学，吉林省省属重点大学；入选中西部高校基础能力建设工程、卓越工程师教育培养计划、高等学校学科创新引智计划、国家级大学生创新创业训练计划、特色重点学科项目、国家建设高水平大学公派研究生项目、新工科研究与实践项目、国家大学生文化素质教育基地、国家国际科技合作基地、中国政府奖学金来华留学生接收院校，为上海合作组织大学、中俄工科大学联盟、中俄综合性大学联盟、中国兵器协同创新联盟成员，被誉为“中国光学英才的摇篮”。','CUST','东北地区',2,'长春光学精密机械学院','理工',1958,'吉林省','否',0,0,0,0,14,0,2,2080,61,17,7,1,0,0,'http://www.cust.edu.cn/'),(338,190,'吉林建筑大学','吉林',23,'吉林建筑大学（Jilin Jianzhu University）简称吉建，坐落于吉林省长春净月国家高新技术产业开发区，由吉林省人民政府与住房和城乡建设部共建， [1]  是一所以工为主，以土木建筑为特色，理、工、文、管、法、艺等多学科相互支撑、协调发展的吉林省重点建设的普通高等学校，CDIO工程教育联盟成员单位。学校前身是1956年国家城市建设部创建的长春城市建设工程学校，是吉林省第一所土建类中等专业学校，也是新中国首批建立的十所建筑类专门学校之一。1960年升格为本科院校，更名为吉林建筑工程学院，成为吉林省第一所土建类本科高等学校。2013年，学校正式更名为吉林建筑大学。','','东北地区',2,'吉林建筑工程学院','理工',1956,'吉林省','否',0,0,0,0,16,0,0,1300,52,35,0,0,0,0,'http://www.jliae.edu.cn/'),(56,191,'东北师范大学','吉林',23,'东北师范大学（Northeast Normal University），简称东北师大，是中华人民共和国教育部直属、吉林省人民政府共建的大学，是首批国家“世界一流学科建设高校”、国家“211工程”重点建设大学，入选国家“985工程优势学科创新平台”、“111计划”、“国培计划”、国家建设高水平大学公派研究生项目、国务院侨办华文教育基地、国家大学生文化素质教育基地、国家级大学生创新创业训练计划，是国家师范生免费教育和卓越教师培养计划试点高校、教育部“援疆学科建设计划”高校之一、中国政府奖学金来华留学生接收院校，为社会艺术水平考级机构。','NENU','东北地区',2,'吉林师范大学','师范',1946,'教育部','否',0,211,1,6,23,0,9,1557,78,185,133,0,0,0,'http://www.nenu.edu.cn/'),(327,192,'北华大学','吉林',23,'北华大学（Beihua University），简称北华，是吉林省人民政府设立的全日制普通高等学校，坐落在吉林省吉林市，是吉林省省属综合性重点大学，学校为全国首批“卓越医生教育培养计划”和“卓越农林人才教育培养计划”改革试点高校、 国家级大学生创新创业训练计划实施院校、国家大学生文化素质教育基地、首批全国高校实践育人创新创业基地、教育部来华留学示范基地建设高校、中国政府奖学金来华留学生接收院校、中俄医科大学联盟首批成员高校，CDIO工程教育联盟成员单位、吉林省人民政府和国家林业局合作共建高校。北华大学是吉林省唯一举办林学、林业工程等涉林一级学科的高校，吉林省60%以上的林业技术与管理骨干毕业于北华大学。','BHU','东北地区',2,'','综合',1906,'吉林省','是',0,0,0,0,24,4,0,2510,84,109,0,0,0,0,'http://www.beihua.edu.cn/'),(130,193,'黑龙江大学','黑龙江',7,'志存高远，爱国奉献，艰苦奋斗，自强不息。\n\n黑龙江大学是教育部与黑龙江省人民政府共建的有特色、高水平、现代化地方综合性大学，是国家国防科技工业局和黑龙江省共建高校，坐落于北国冰城哈尔滨，其前身是1941年成立的中国人民抗日军政大学第三分校俄文队。办学70多年以来，黑龙江大学在国家全球战略构架中，推进与服务中俄战略协作伙伴关系提升，强化俄语学科专业的优势地位和引领作用，实现人才培养方向、科学研究领域、对外交流合作和社会服务等多方位与俄罗斯对接，形成全国高校独树一帜的对俄办学特色。作为黑龙江省学科门类最全、专业覆盖最广、后发优势最强的综合性大学，坚持立足龙江、面向全国，全力为地方和国家经济社会发展提供高端的人才支持、智力支撑、科技服务和文化引领。“十一五”以来，黑龙江大学坚持以稳定规模、优化结构、提升质量、强化管理为主的发展道路，着力构建教学、科研和学科三位一体的内涵发展模式，整体办学水平、综合实力、核心竞争力和社会声誉显著提升。化学学科、材料学科、工程学科3个学科进入ESI全球排名前1%。在全国第四轮学科评估中，我校外国语言文学学科评为A类，哲学学科评为B+类，法学、中国语言文学、马克思主义理论、图书情报与档案管理4个学科评为B类，化学学科评为B-类，电子科学与技术、公共管理2个学科评为C+类，理论经济学、政治学、生物学、信息与通信工程4个学科评为C类，数学、生态学、控制科学与工程、计算机科学与技术、软件工程、化学工程与技术6个学科评为C-类，学科发展速度和整体实力居于省属高校前列。2018年获批黑龙江省“双一流”建设国内一流大学A类高校，外国语言文学获批国际一流建设学科，哲学、法学、马克思主义理论、中国语言文学、图书情报与档案管理等5个学科获批国内一流建设学科。此外，学校连续多年进入全国高校百强行列，在《中国大学及学科专业评价报告》中跨入重点大学行列。\n\n黑龙江大学学科门类齐全、综合优势明显，共设有31个教学院部，拥有涵盖哲、经、法、教、文、史、理、工、农、管、艺11个学科门类的本科专业84个，拥有博士学位授权一级学科10个，硕士学位授权一级学科35个，硕士专业学位类别15个；2个国家重点学科（含培育），1个学科群、12个一级学科为省级重点学科；拥有7个博士后科研流动站，3个博士后科研工作站。现拥有国家级特色专业10个、省级重点专业27个，国家级精品课程10门、国家级精品视频公开课3门、省级精品课程50门，国家级教学团队6个、省级教学团队8个，国家级人才培养模式创新实验区2个，国家级实验教学示范中心1个、省级教学示范中心6个，入选国家规划教材40部。被教育部确定为全国首批9所创业教育试点院校之一，与俄罗斯新西伯利亚国立大学联合设立的中俄学院被教育部列入全国17所国家教育体制改革试点学院之一，入选国家首批“中西部高校基础能力建设工程”高校，入选国家首批卓越法律人才教育培养基地。\n\n黑龙江大学坚持教学立校，以优质的教育资源为依托，以提高人才培养质量和社会适应能力为重点，围绕经济社会发展需求培养高素质、复合型、创新性高级专门人才，教育质量优异，教学成果斐然。在2001年和2008年的教育部普通高等学校本科教学工作水平评估中，均获得优秀结论。共获国家级优秀教学成果奖7项，获省级优秀教学成果奖189项；拥有“全国百篇优秀博士论文”1篇、提名7篇（次），黑龙江省优秀硕士论文41篇。现有全日制在校学生32000余名，各类研究生5000余名。建校以来已为龙江和祖国培养了近24万优秀毕业生，为国家和地方经济社会发展做出了重要贡献，其中有以阎明复、张左己、姜大明等为代表的诸多政界精英；有以叶正大、刘晓江等为代表的多位高级将领，有以李凤林、潘占林等为代表的数十名共和国优秀外交官，有以李锡胤、吕冀平等为代表的一大批著名专家学者。新时期，还陆续涌现出获“全国道德模范提名奖”何建章、“网络感动中国人物”陈顺达等先进人物。\n\n黑龙江大学教学设施完备，教学条件优良，校区占地面积174万平方米，建筑面积108万平方米，教学科研仪器设备总值5.48亿元，馆藏图书400余万册，拥有可容纳3万余名学生住宿生活的大学城，以及设施先进的实验楼、教学楼、图书馆、体育馆、游泳馆、餐饮中心等办学基础设施，以及宽带校园网、多媒体等现代化教学资源。\n\n黑龙江大学师资力量雄厚，名师荟萃、人才云集。现有教职员工近2900名，其中专任教师近1700人，研究生导师900余人，具有高级职称的教师近1000人。其中，获得“国家级教学名师奖”5人，位居全国高校第19位，列地方高校第一位；“全国优秀教师”、“全国模范教师”等各类国家级、省部级荣誉称号获得者200余人次；学校拥有“长江学者”特聘教授2人、“长江学者”青年学者1人，国家杰出青年科学基金获得者1人，“万人计划”高层次人才5人，“新世纪百千万人才工程”国家级人选5人，教育部“新世纪优秀人才支持计划”入选者17人，国家级“有突出贡献的中青年专家”6人，“普京奖”获得者1人，“普希金奖章”获得者5人，“享受国务院政府特殊津贴人员”59人。\n\n黑龙江大学坚持以科学研究为支撑，以提升科技创新水平为重点，以增强服务经济社会发展能力为落脚点，走科研兴校之路。现拥有教育部人文社会科学百所重点研究基地俄罗斯语言文学与文化研究中心、功能无机材料化学教育部重点实验室、教育部中俄催化技术国际合作联合实验室、农业微生物技术教育部工程研究中心、国家国际科技合作基地“催化技术国际联合研究中心”、光纤传感技术国家地方联合工程研究中心、国家糖料改良中心、农业部甜菜品质监督检验测试中心等众多国家级高端科研创新平台；拥有省级哲学社会科学重点研究基地4个、省级重点实验室7个、省级工程中心6个、省级技术创新服务平台1个、省高校人文社科重点研究基地13个、省高校重点实验室7个、省高校校企共建工程技术研发中心3个，以及俄罗斯研究研究中心、教育科学研究院等30余个校级科研机构，现已基本构成多学科、立体化、完整均衡的科研创新发展体系。“十五”以来，共获国家社会科学基金290项，国家自然科学基金341项，连续多年被SCI、EI、CSSCI收录的论文均达到700余篇，共取得各类科研成果27000余项，其中有1100余项成果获国家、部委、省级的奖励，获国家科学技术进步奖1项，中国高校人文社科研究优秀成果奖22项。李锡胤、潘国民教授主持编撰的《俄汉详解大词典》获国家图书奖，并作为国礼赠送给俄罗斯国家领导人。黑龙江大学编辑出版的《求是学刊》《外语学刊》《自然科学学报》《中国糖料》《满语研究》《黑龙江大学工程学报》《黑龙江教育》《北方法学》《俄罗斯学刊》《远东经贸导报》（俄文版）等高水平学术刊物在国内外公开发行。\n\n黑龙江大学牢固树立“立足龙江、面向全国、服务社会”的观念，通过整合、凝练和优化校内外优势资源及校企合作等方式，形成了以人文社科基地、重点实验室、工程中心和大学科技园为主的综合性科技文化创新服务体系，努力为高教强省和科教兴国做出积极贡献。在人文社会科学方面，黑龙江大学充分发挥“智囊团”和“思想库”的作用，围绕龙江经济建设、社会发展和文化繁荣，通过广泛参与决策咨询，承担高层次项目和应用性研究课题，参与重大理论和现实问题研究，担任实际部门顾问等多种形式，服务地方经济建设、对俄经贸文化交往和边疆文化大省建设。在自然科学领域，黑龙江大学强化理工科优势特色，不断加强科技成果转化与产业化的力度，特别是在生物技术、化学化工、电子信息等领域的中取得了大批科技创新成果。我国第一个机群并行数据库服务器系统、无模型控制器、IC卡电脑加油机、L—乳酸菌系列保健产品等科研项目成功实现成果转化，为龙江产生经济效益均数亿元。黑龙江大学积极整合科技资源、搭建成果转化平台，辟建了黑龙江省第一个省级大学科技园，形成了以计算机软件和电子信息产业为龙头的科技产业群体，年均产值上亿元，取得了良好的社会效益和经济效益。黑龙江大学校办产业学府书城是全国大学最大的书城，年营业额上亿元，曾获全国文化产业先进集体称号；黑龙江大学出版社作为全国地方性大学出版社的杰出代表，曾多次荣获中华优秀出版物奖、中国出版政府奖等，并荣获“出版业网站百强”荣誉称号。\n\n黑龙江大学注重通过树立和涵养优良的校风、学风和教风，以丰富多样的校园精神文化、制度文化、物质文化、文化活动为载体，努力构建全方位育人的和谐校园。黑龙江大学曾圆满承接第24届世界大学生冬季运动会主运动员村任务，开中国大学承办国际大型综合体育赛事之先河；曾成功承办亚洲男篮锦标赛、全国女篮甲A联赛、中国模拟联合国大会以及教育部主办的“五月的鲜花”全国大学生大型校园文艺演出活动等众多在国际、国内具有广泛影响的大型文化活动。黑龙江大学通过全方位服务体系打造关爱平台，开辟了学生工作一站式服务区、校长信箱、学业导师网络平台、学生工作热线、后勤服务热线、心理咨询热线和“校园110”热线等。黑龙江大学是被教育部确定的依法治校示范校，并获“全国文明单位”、“省级文明单位标兵”、“全省十佳和谐校园”等荣誉称号。\n\n黑龙江大学突出国际化开放式办学特色，高度重视开展广泛的国际合作与交流，已建构起全方位、多层次的国际交流合作和汉语推广工作平台。截至目前，黑龙江大学已与世界19个国家73所大学保持实质的交流与合作，先后向美国、俄罗斯、法国、英国、西班牙、日本、韩国、马来西亚、埃及等国家派出联合培养留学生近4000人，培养外国留学生14619人。现每年派出各类出国留学学生300余人，每年培养留学生1700余人次。学校拥有中俄人文合作和上海合作组织教育合作俄语翻译中心，经教育部批准、与俄罗斯新西伯利亚国立大学共办的中俄联合研究生学院，俄罗斯莫斯科国立大学共建的高翻学院，与俄罗斯圣彼得堡国立大学共建的俄语测试中心，俄罗斯境内的第一所孔子学院、与多米尼加圣多明各理工大学共建孔子学院及与吉林大学、辽宁大学、延边大学共建的汉语国际推广东北基地等对外交流合作平台。\n\n风雨砥砺开创优良传统，继往开来谱就精彩华章！站在新的历史起点，黑龙江大学将坚持“质量立校、特色兴校、人才强校、依法治校”的办学理念，遵循“教学为中心、科研为基础、学科为龙头、管理为保障”的四位一体协调发展模式，牢牢把握省部共建的战略机遇，以更加坚定的信念、更加澎湃的活力和更加豪迈的情怀奔向无限美好的未来，为努力实现建设有特色、高水平、现代化省部共建大学的宏伟目标而开拓奋进、再铸辉煌！','HLJU','东北地区',2,'中国人民抗日军政大学第三分校、哈尔滨外国语专科学校、哈尔滨外国语学院','综合',1941,'黑龙江省','是',0,0,0,0,31,0,3,2066,81,35,10,0,4083,0,'http://www.hlju.edu.cn/'),(92,195,'燕山大学','河北',2,'燕山大学（Yanshan University）简称“燕大”，坐落于河北省秦皇岛市，是河北省人民政府、教育部、工业和信息化部、国家国防科技工业局共建的全国重点大学，北京高科大学联盟 [1]  、CDIO工程教育联盟成员，河北省重点支持的国家一流大学和世界一流学科建设高校，入选国家建设高水平大学公派研究生项目、国家“特色重点学科项目”建设高校、卓越工程师教育培养计划、国家大学生创新性实验计划、国家级大学生创新创业训练计划、全国深化创新创业教育改革示范高校、中国政府奖学金来华留学生接收院校。','YSU','华北地区',1,'东北重型机械学院','理工',1920,'河北省','是',0,0,0,7,18,0,0,3200,64,36,11,0,0,0,'http://www.ysu.edu.cn/'),(51,196,'哈尔滨工程大学','黑龙江',7,'哈尔滨工程大学坐落于美丽的松花江畔——北国冰城哈尔滨市。学校前身是创建于1953年的中国人民解放军军事工程学院（“哈军工”）。1970年，以海军工程系全建制及其他系（部）部分干部教师为基础，在“哈军工”原址组建哈尔滨船舶工程学院。1994年，更名为哈尔滨工程大学。学校先后隶属于第六机械工业部、中国船舶工业总公司、国防科工委，现隶属于工业和信息化部。 \n\n学校1978年被国务院确定为全国重点大学；是首批具有博士、硕士学位授予权单位，首批“211工程”重点建设高校；2002年，获批建立研究生院；2007年，由原国防科工委、教育部、黑龙江省、海军四方共建；2011年，成为国家“985工程”优势学科创新平台项目建设高校；2017年，入选“双一流”建设高校；是国家“三海一核”（船舶工业、海军装备、海洋开发、核能应用）领域重要的人才培养和科学研究基地。 \n\n学校占地面积125.61万平方米，建筑面积103.22万平方米。校园建筑中西合璧，飞檐碧瓦，气势恢宏。设有16个学院（系、部）；设有40多个科研机构以及150多个科研和教学实验室，其中国家级重点实验室2个，国家工程实验室1个，国家地方联合工程研究中心1个，国家级国际科技合作基地1个，国际联合研究中心2个，国家级学科创新引智基地4个，工业和信息化部重点实验室7个，国防重点学科实验室2个，教育部重点实验室及工程研究中心5个，国家级电工电子教学基地1个，国家级实验教学示范中心7个，国家级虚拟仿真实验教学中心3个，国家大学生文化素质教育基地1个。固定资产总值41.16亿元。图书馆共有藏书474.92万册。\n\n学校以船舶与海洋装备、海洋信息、船舶动力、先进核能与核安全4个学科群为牵引，构建主体学科优势突出、通用和基础学科支撑配套、文管学科独具特色、专业结构布局合理的“三海一核”特色学科专业体系。现有一级学科博士点14个，一级学科硕士点32个，博士后科研流动站12个，博士后科研工作站2个。国家一级、二级重点学科各1个，国防特色学科10个、黑龙江省重点学科群1个、一级重点学科11个。现有本科招生专业37个，教育部特色专业7个，卓越工程师教育培养计划专业7个，国防特色紧缺专业和重点专业7个，省级重点专业29个，4个专业入选国家级专业综合改革试点项目。学校材料科学、工程学、化学进入ESI全球前1%；“现代舰船与深海工程”入选“985优势学科创新平台”建设；在国家第四轮学科评估中船舶与海洋工程、控制科学与工程学科分别获得A+和A-等级，现有一级学科博士点排名全部进入全国前30%；学校进入国家“双一流”建设行列。\n\n学校现有教职工2937人，其中专任教师1779人，教授、副教授1078人。教师中有“两院”院士8人（含双聘5人），博士生导师536人，享受国务院政府特殊津贴专家92人，“千人计划”13人，“万人计划”科技创新领军人才4人，教学名师1人，青年拔尖人才5人，“长江学者奖励计划”特聘教授4人、讲座教授3人、青年学者1人，国家杰出青年科学基金获得者1人，国家优秀青年基金获得者2人，国家百千万人才工程7人，教育部创新团队2个，国防科技创新团队6个，黑龙江省领军人才梯队6个。学校各类在校生28000余人，是首批入选“卓越工程师教育培养计划”高校，并被教育部、总政治部列入“21世纪人才强军计划”，是海军在东北地区唯一选拔培养后备军官的依托学校。 \n\n学校紧紧围绕提高人才培养质量这一主题，全面落实立德树人根本任务，深化创新创业教育改革、推动信息技术与教育教学深度融合、推进国际化进程，坚持“视野宽、基础厚、能力强、素质优、可靠顶用”的人才培养目标，致力于培养信念坚定、人格健全、乐于探索、务实笃行的一流工程师、行业领军人才和科学家。近年来，学生在中国“互联网+”大学生创新创业大赛、“挑战杯”全国大学生课外学术科技作品竞赛、全国大学生创新创业年会、“创青春”全国大学生创业大赛、国际无人水下机器人大赛等国际国内各类赛事中获得国际和国家级奖逾千项，“E唯”机器人创新团队、“创翼”创新团队获大学生“小平科技创新团队”，大学生创业联盟连续五年获“全国十佳KAB创业俱乐部”。2011年成立创业教育学院。学校被授予全国首批深化创新创业教育改革示范高校、全国创新创业典型经验高校、全国高校实践育人创新创业基地、中国青年科技创新行动示范基地、全国五四红旗团委标兵、全国科普教育基地等称号。本科生、研究生一次就业率保持在94%以上，连续多年位居全国高校前列，毕业生以“可靠顶用”而受到用人单位的广泛赞誉。成为全国毕业生就业50所典型经验高校之一。全国百篇优秀博士学位论文及提名获奖占全国船舶与海洋工程学科获奖总数的40%，省优秀硕士学位论文数量连续两年名列黑龙江省第一名。2009年，学校被评为全国普通高校毕业生就业工作先进集体。继2006年获得教育部本科评估优秀评价之后，2009年和2010年，学校分别通过工业和信息化部研究生教育优秀工程评估和办学水平提升工程评估，均获“优秀”评价。建校以来，学校为国家培养了12万多名各类高级专门人才，其中包括200多名共和国的将军、部长、省长、院士，2000多名高等院校、科研院所、大中型企业的技术领军和高级管理人才。他们为国防现代化建设和国家经济社会发展做出了重要贡献。\n\n科研工作一直是学校发展的先行力量，不仅以国内第一艘实验潜艇、第一艘水翼艇、第一台舰载计算机、第一套条带测深仪等数十项填补国内空白的重大科研成果著称，而且还以双工型潜器、气垫船、梯度声速仪等成果摘取过世界第一的桂冠。学校在船海核领域保持着很强的技术储备，水下机器人、减振降噪、船舶减摇、船舶动力、组合导航、水声定位、水下探测、核动力仿真、大型船舶仿真验证评估、三体船设计、水面无人艇等技术居国内领先或国际先进地位，现已成为我国舰船科学技术基础和应用研究的主力军之一、海军先进技术装备研制的重点单位、我国发展海洋高技术的重要依托力量。当前，学校是我国首座数值水池虚拟实验系统的联合牵头研制单位，是我国第七代超深水半潜式钻井平台、两万箱超大型集装箱船、智能船舶核心设计研发单位，是“蛟龙号”7000米载人、深海勇士潜水器水下导航定位系统国产化的装备提供方，为我国首条三体搜救艇、万吨集装箱船、钻井平台等新船型开发及深海工程装备研发提供了全面的基础技术支撑。“十一五”以来，承担国家重大科技攻关、国家和国防“973”计划及“863”计划、国防预研和科研型号等一大批科研项目，获得省部级以上科技奖励300余项。学校获评“高技术武器装备发展建设工程突出贡献奖”和“航母建设突出贡献奖”。科研产品质量管理通过ISO9000质量体系认证，是国内高校首家通过“双认证”的大学。牵头成立“深海工程与高技术船舶”“核安全与仿真技术”协同创新中心，获工业和信息化部认定，“舰船动力”“先进海洋材料”协同创新中心获黑龙江省认定。学校设有国家大学科技园，获评“国家级科技企业孵化器”。学校人均科研经费位居全国高校前列。\n\n学校坚持国际化、开放式办学。牵头成立国际船舶与海洋工程创新与合作联盟（简称ICNAME）。与包括美国加州大学伯克利分校、美国密西根大学、英国南安普顿大学、澳大利亚悉尼大学、俄罗斯鲍曼国立技术大学以及国际原子能机构、世界五大船级社等世界一流大学、研究机构、国际组织和企业在内的22个国家100余个单位建立了良好稳定的合作关系。学校是“中国政府奖学金”“国家建设高水平大学公派研究生”“创新型人才国际合作培养项目”“优秀本科生国际交流”项目实施高校，2017年成为首批唯一“中国政府原子能奖学金”项目委托培养单位，获高教学会“全国来华留学工作先进集体”称号。2012年，学校与乌克兰南方师范大学联合成立“孔子学院”，荣获2016年度全球“先进孔子学院”称号。学校每年资助400多名教师到国外进修、讲学和科研合作，每年选派500多名学生到国际知名大学交流学习和访问。\n\n60多年来，学校坚持和发扬老一辈革命家倡导培育的“哈军工精神”，形成了“以祖国需要为第一需要，以国防需求为第一使命，以人民满意为第一标准”的精神追求，凝炼了“大工至善，大学至真”的校训，形成了“忠诚、坚韧、团结、创新”的校风、“治学严谨、组织严密、要求严格”的教风和“严谨、求实、勤奋、创新”的学风。当前，学校以服务国家工业化、信息化和国防现代化为使命，以“双一流”建设为统领，坚定不移走内涵式发展道路，不断提升办学质量和水平，紧紧抓住“三海一核”领域的国家战略机遇，强化特色，继承创新，以人为本，科学发展，正在向着特色鲜明的高水平研究型大学目标阔步迈进。','HEU','东北地区',2,'中国人民解放军军事工程学院、哈尔滨工程学院','理工',1953,'国防科工委','否',0,211,0,1,19,8,4,2937,56,32,14,4,0,0,'http://www.hrbeu.edu.cn/'),(326,197,'黑龙江科技大学','黑龙江',7,'黑龙江科技大学是黑龙江、吉林乃至内蒙地区唯一一所以矿业为特色的高校，是黑龙江省人民政府与国家应急管理部（国家安全生产监督管理总局）共建高校，是国家教育部教学工作水平评估优秀高校，是服务国家特殊需求“煤矿事故应急救援与影响控制”博士层次人才培养项目的唯一一所高校，是安全科学与工程一级学科博士后科研流动站设立高校，是黑龙江“十二五”期间重点建设的特色应用型本科高校，是“矿产资源开发利用”省级特色学科项目和博士学位授权立项建设高校。在72年的办学历程中，学校始终践行“厚德博学、强吾兴邦”的校训，铸就了“自强不息、创业创新”的学校精神，培养出了10万余名优秀人才，为区域经济和我国煤炭工业的发展做出了突出的贡献，被誉为“煤炭工业的脊梁”。\n\n一、历史沿革\n\n学校前身是中国共产党1947年在鸡西建立的东北第一所煤矿工人干部学校。1954年与鹤岗煤矿学校合并成立鸡西煤矿学校。1958年在此基础上成立本科教育鸡西矿业学院。1964年因院校调整又改为鸡西煤矿学校。1978年复建本科教育鸡西矿业学院，1981年改称黑龙江矿业学院，隶属于原国家煤炭工业部。1998年划归地方，实行中央与地方共建、以黑龙江省管理为主的体制。2000年更名为黑龙江科技学院。2013年更名为黑龙江科技大学。\n\n二、发展历程\n\n学校坐落于冰城夏都哈尔滨市的松花江畔，办学历史悠久，文化底蕴深厚。71年的发展历程谱写了一部艰辛的创业史，可概括为三部曲：\n\n第一部曲——依煤而生，曲折发展。学校因煤而生、依煤而长。边陲煤城办学56载，几代科院人凭借北大荒精神、铁人精神、煤矿工人特别能战斗的精神，始终以服务煤炭为已任，甘守清贫，艰苦创业，献身煤炭，为国家煤炭工业和区域经济社会发展奉献了全部的青春和热血。1997年学校顺利通过了国家教学工作合格评价，办学水平、办学特色得到专家组的一致好评。\n\n第二部曲——二次创业，跨越发展。为更好地为国家煤炭工业和区域经济社会发展做贡献，学校抢抓高等教育迈向大众化的战略机遇，于2001至2003年白手起家在哈尔滨建设新校区。历尽千辛万苦，攻坚克难，用“泪水、汗水和血水”在一片废墟上建起一座现代化的新校园。2003年8月，学校主体由鸡西市迁入哈尔滨市，实现了办学空间的跨越。以新校区建设为契机，实施学科专业建设工程和人才强校战略，2003年获得硕士学位授予权，实现了办学层次的跨越。\n\n第三部曲——办学转型，科学发展。2004年以来，实施内涵发展和特色发展，2008年在国家教学工作水平评估中获得“优秀”，2010年被确定为黑龙江“十二五”期间重点建设的特色应用型本科高校，2012年获得服务国家特殊需求的“煤矿事故应急救援与影响控制”博士人才培养项目，2014年获批安全科学与工程一级学科博士后科研流动站，2017年获批“硕士研究生推免权”、“矿产资源开发利用”省级特色学科项目和博士学位授权立项建设单位，为学校的发展注入了新的生机与活力。\n\n“十三五”开局之际，学校科学确立了应用型的办学定位、“立足应用，精准育人”的办学理念，绘就出创建特色鲜明的高水平应用型科技大学的长远发展蓝图和“百年名校”的宏伟目标，汇聚全校合力，聚力转型发展，激发办学活力。\n\n三、学校现状\n\n学校不断积淀和创新，已经发展成为一所工学、管理学、经济学、理学、法学、文学多学科协调发展的普通高等学校，形成了“一校两地三校区”的办学格局。\n\n学校以立德树人为根本任务。坚持“育人为本、德育为先，以学为要、固基强实”的思想和“以生为本、为生尽责”的服务理念，以立足应用的教学理念为引导，以学生成长成才需求、地方经济与行业发展需求为导向，遵循教育教学规律与人才成长规律，努力培养“思想道德素质高、工程实践能力强”的高素质应用型人才，人才培养质量不断提高。现有全日制在校生22657人，其中研究生、博士生883人。“十五”以来，本科生一次就业率在90%以上，连续保持省内高校前三名，被教育部授予“全国毕业生就业50强高校”、“全国青年就业创业教育先进集体”。在全国、全省大学生标志性竞赛中，获奖数量和层次居于省内高校领先水平。\n\n学校坚持以教育教学为中心。坚持应用型的办学定位，不断深化教育教学改革，科学构建符合经济社会发展需求的应用型人才培养体系，实施产教融合、校企协同育人的人才培养模式，教育教学水平不断提升。现有57个本科专业，其中国家特色专业建设点4个、省重点专业10个。建成1门国家级精品课、14门省级精品课。建成2个国家级大学生校外实践教育基地、1个省级人才培养模式创新示范区、1个省级虚拟仿真实验教学中心、1个省级示范性虚拟仿真实验教学项目、4个省级实验教学示范中心、2个振兴东北老工业基地人才培训中心、1个国家煤矿安全培训基地。\n\n学校坚持以学科建设为龙头。围绕区域、行业和国家发展需求，按照学科链与产业链相协调，产学研有机结合的建设思想，构建以工科为主体，矿业为特色的多学科协调发展的学科体系。“双一流”建设期间，我校为黑龙江省特色学科建设高校，建有“矿产资源开发利用”特色学科建设项目，是黑龙江省拟新增博士学位授权立项建设单位，拥有“煤矿事故应急救援与影响控制”博士特需人才培养项目，具有安全科学与工程博士一级学科学位授予权，拥有1个博士后科研流动站。学校拥有矿业工程、安全科学与工程、机械工程、电气工程、材料科学与工程、化学工程与技术、土木工程、马克思主义理论、工商管理、公共管理10个硕士一级学科学位授权点，计算机应用技术1个硕士二级学科学位授权点，拥有资源与环境、机械、能源动力、电子信息、土木水利、工商管理（MBA）、会计、金融、社会工作9个硕士专业学位授权类别。拥有采矿工程、矿物加工工程、电力电子与电力传动、机械设计及理论、材料加工工程、岩土工程、工商管理、思想政治教育、安全技术与工程9个省级领军人才梯队。建有国家级中心实验室1个、省部级重点实验室5个，省部级工程技术研究中心10个，省级重点培育智库1个、省高校人文社科重点研究基地1个。具有国内一流的工程训练与基础实验中心等一批高端服务平台。\n\n学校高度重视人才队伍建设。坚持“以师为尊、为师服务”的理念，构建开放、多元、有序、效能的人才资源管理体系，实施“五大工程23项计划”的人才强校战略，加强高层次人才队伍建设。全国优秀教师、省模范教师、省优秀教师24人，省教学名师、教学新秀9人。省级领军人才梯队8个。国家级教学团队1个，省级教学团队4个。聘请教育界、企业界、政界知名人士等150余人为兼职教授。长期聘用企业工程技术骨干145人为“双师型”教师，直接服务特色人才培养。\n\n学校高度重视科学研究工作。坚持应用为主，把科研解决企业生产实际问题作为重点，不断推进科技工作向“质量性、战略性、组织性、导向性”转型，科技水平和服务能力不断增强。“十二五”以来，承担国家科技支撑计划、国家重点研发计划项目（课题）、国家自然科学基金项目等国家级项目80项、省部级项目266项，科技经费总额4.5亿余元。获省部级科技奖励90项，其中一等奖5项，二等奖27项，三等奖58项。\n\n学校建有5个省、部级重点实验室、10个省、部级工程技术研究中心和1个省级重点培育智库。学校围绕区域和国家战略发展需求，强化学科交叉融合，搭建了系列服务平台。其中，服务新材料战略性新兴产业的石墨技术创新服务平台跻身国家战略，总体负责“十二五”国家科技支撑计划项目“高纯石墨材料开发及其典型应用”，成为黑龙江和国家石墨新材料产业发展人才与技术主要支撑；服务高端装备制造战略性新兴产业和国防战略的现代装备制造研发平台，是军委总装备部颁发军品研发资质的高校之一，直接为国防与军工企业服务；服务国家安全生产技术支撑体系的“瓦斯等烃气输运管网安全基础研究”国家级中心实验室是国内外较早进行水合物技术预防瓦斯灾害和瓦斯固化储运的研究平台；牵头成立黑龙江省3D打印产业技术创新战略联盟，致力于3D打印技术创新研发和技术攻关；牵头申报了“矿产资源开发利用”省级协同创新中心，服务我省煤炭、石墨等矿产资源产业发展。\n\n学校坚持加强国际交流合作。积极推进跨文化学习与互动，不断拓宽国际合作领域，先后与俄罗斯、白俄罗斯、加拿大、奥地利、美国、德国、法国等开展合作办学和科技交流，引进、吸收国外优质教育资源，取得了国际合作办学的新经验，成为黑龙江省中外合作办学的典范。\n\n全面提高党建的科学化水平。以习近平新时代中国特色社会主义思想为统领，坚持党要管党、从严治党，全面加强和改进党的各项建设，为大学建设提供了强大动力和坚强保证。先后荣获“全国文明单位”、“全国五一劳动奖状”、“全国教育系统关心下一代工作先进集体”、“全国五四红旗团委”等20余项国家级荣誉和省“先进基层党组织”、“文明单位标兵”、“师德建设十佳单位”等80余项省级荣誉。\n\n当前，学校正在高举习近平新时代中国特色社会主义思想伟大旗帜，学习贯彻党的十九大精神，奋进新时代、勇担新使命，紧紧围绕立德树人根本任务，坚持“四个服务”的目标方向，聚焦内涵建设，推进高质量发展，奋力谱写新时代学校改革发展新篇章，开启了创建矿业特色鲜明的高水平应用型科技大学和“百年名校”梦的新征程，为国家经济发展、现代化新龙江建设和行业发展做出新的更大的贡献！','USTH','东北地区',2,'鹤岗矿务局职工技术学校、鹤岗煤矿工业学校、鸡西煤矿学校、黑龙江科技学院','理工',1947,'黑龙江省','是',0,0,0,0,21,0,0,1519,54,11,1,1,0,0,'http://www.usth.edu.cn/'),(236,198,'东北石油大学','黑龙江',7,'东北石油大学（原名东北石油学院、大庆石油学院）位于黑龙江省大庆市，创建于1960年，是一所以工学为主，工、理、管、文、经、法、教育、艺术多学科协调发展的高校。1978年被确定为88所全国重点大学之一。学校是黑龙江省与中国石油天然气集团公司、中国石油化工集团公司、中国海洋石油总公司共建高校，是黑龙江省重点建设的高水平大学。 \n\n学校从1961年开始招收本科生，1981年获硕士学位授予权，1993年获博士学位授予权。学校有65个本科专业，23个高职专业。8个国家级特色专业建设点，1个国家级综合改革试点专业，6个国家级“卓越工程师教育培训计划”试点专业，19个省级重点专业。1个国家重点一级学科，1个省级重点学科群，6个省级重点一级学科；4个博士后科研流动站，1个博士后科研工作站；4个博士学位授权一级学科，21个硕士学位授权一级学科；具有工程、工商管理（MBA）、社会工作、会计4个类别硕士专业学位授予权，其中工程类别拥有14个硕士专业学位授权点。工程学2016年进入世界ESI前1%。  \n\n学校占地169.13万平方米，其中秦皇岛校区占地33.28万平方米。教学科研仪器设备总值超过5.8亿元。建有万兆级校园网，图书馆藏图书255.05万册，有数据资源16种。建有国家级大学科技园。有科研平台44个，其中，省部共建国家重点实验室培育基地1个、省部共建教育部重点实验室2个、国家能源研发中心1个、国家工程实验室（研究室）1个、省重点实验室11个、省工程技术研究中心1个、与中海油研究总院有限责任公司共建联合研究院1个、省哲学社会科学重点研究基地1个。有国家级实验教学示范中心1个，国家级虚拟仿真实验教学中心1个，省级实验教学示范中心6个，省级虚拟仿真实验教学中心1个。有校内外实习基地158个，其中，国家级工程实践教育中心3个，国家级大学生校外实践教育基地2个、省级1个。 \n\n学校有教职工2118人，其中专任教师1307人。专任教师中，正高级专业技术职务286人、副高级专业技术职务530人，博士学位483人、硕士学位666人；博士生导师141人；双聘院士8人，其中，中国科学院院士1人，中国工程院院士7人；国务院学位委员会学科评议组成员1人；教育部高等学校教学指导委员会委员5人；享受国务院政府特殊津贴专家26人（现在职12人）；国家万人计划青年拔尖人才1人；科技部中青年科技创新领军人才、教育部长江学者奖励计划青年学者、国家自然科学基金优秀青年基金资助获得者1人；国家百千万人才工程人选2人；教育部新世纪优秀人才支持计划入选者5人；省杰出青年科学基金获得者14人；龙江学者特聘教授16人、青年学者4人、讲座教授5人；省领军人才梯队带头人15人、后备带头人29人。全国优秀教师3人，省级教学名师11人、模范教师4人、优秀教师7人、教学新秀1人、青年教学能手2人。国家级教学团队1个、省级教学团队4个。省自然科学基金研究团队1个、省高校科技创新团队6个、哲学社会科学创新团队培育计划1个。省领军人才梯队15个、优秀研究生导师团队3个。学校还在石油石化等企事业单位聘请了一批兼职教授、导师。  \n\n学校面向31个省（市、自治区）招生。现有全日制在校生23848人，其中本科生15478人、专科生5600人、硕士研究生2404人、博士研究生366人。  \n\n学校是本科教学工作水平评估优秀高校，获国家级教学成果奖2项，学生全国外语四级统考和黑龙江省高校非计算机专业计算机等级考试成绩始终名列省内高校前列。全国大学英语教学研讨会、俄语教学研讨会曾先后在我校召开。“十二五”以来，建成国家级精品视频公开课2门、国家级精品资源共享课2门。获省级及以上教学成果奖、全国普通高校优秀教材奖、全国多媒体教育软件大奖赛等多种奖项。 \n\n学校完成的科研成果获国家科技奖励13项，其中，国家科技进步特等奖1项，一等奖1项，二等奖10项；国家技术发明二等奖1项；国家自然科学二等奖1项。“十二五”以来，获批国家973计划课题4项，863计划课题4项，国家科技支撑项目4项，国家重大专项任务57项，国家自然科学基金项目165项（其中，主持重点项目3项，参与重大项目1项）、社会科学基金项目11项，教育部科技项目6项、教育部人文社科项目17项；科研总经费14亿元。获省部级科技成果奖励107项，其中自然科学一等奖2项，技术发明一等奖2项，科技进步一等奖3项；获省社会科学优秀科研成果奖26项，其中一等奖5项；获专利授权2791件；出版科研著作及教材370部，发表论文9131篇，被三大检索系统收录3360篇。 \n\n学校重视国际交流与合作，通过实施国际化战略，国际交流与合作领域和范围不断拓宽，国际影响不断扩大。现已与欧洲、美洲、非洲10余个国家的30余所国（境）外高水平大学和科研机构建立了长期稳定的合作关系，积极开展包括国家公派、联合培养、交换学习、短期交流等学生海外交流项目。鼓励教师开展国际学术交流及合作科学研究等实质性合作，与美国、德国、罗马尼亚等国的大学共建石油工程、非常规油气研究等联合实验室，共同参与欧盟ERASMUS+项目，合作开展“能源与水纽带关系及高效绿色利用关键技术”等政府间科研合作项目。学校积极开展来华留学生教育，培养来自10余个国家的外国留学生。  \n\n学校出版有《东北石油大学学报》（中国科技核心期刊和全国中文核心期刊）、《东北石油大学报》和《高等教育研究》等报刊，与全国300多所高校进行交流。  \n\n办学近六十年来，学校秉承“艰苦创业，严谨治学”的校训和“严谨、朴实、勤奋、创新”的校风，形成了“坚持用大庆精神办学育人”和“全方位多层次产学研合作办学”两个鲜明特色。时任国务院总理李鹏为学校题词“用大庆精神育人，培养跨世纪人才”。办学育人成就得到了社会各界的充分认可和媒体的广泛关注，赢得了良好的社会声誉。  \n\n办学近六十年来，培养各类人才近16万人，毕业生就业率始终保持在省内高校前列。涌现出了以傅成玉、王玉普、胡文瑞、沈殿成、何树山、刘合、段慧玲、高金森等为代表的许多优秀企业家、院士专家、党政领导杰出校友和一大批严谨务实、无私奉献、立足岗位、建功立业的优秀人才。  \n\n今天，东北石油大学正以培养担当民族复兴大任的时代新人为己任，坚持“质量立校、人才强校、改革创新、特色创优”新办学理念，按照“强陆——拓海——壮新”的发展思路，全力实施“陆上石油学科一流的石油大学——陆海并举的石油大学——高水平的能源大学”“三步走”战略，为建设高水平百年大学而努力奋斗！  ','NEPU','东北地区',2,'东北石油学院、大庆石油学院','理工',1960,'黑龙江省','否',0,0,0,0,20,8,1,2118,65,21,4,0,4567,0,'http://www.dqpi.edu.cn/'),(314,199,'黑龙江八一农垦大学','黑龙江',7,'黑龙江八一农垦大学是黑龙江省属全日制普通高等学校，创建于1958年7月，首任校长由原国家副主席、时任农垦部长的王震将军担任。学校原隶属农垦部，1973年划归黑龙江省人民政府。经过61年的发展建设，现已成为一所具有鲜明现代化大农业特色，以农为主、多学科协调发展的农业大学。\n\n学校诞生于十万复转官兵开发建设北大荒的历史大潮中，原校址位于密山市裴德镇北的山野乡间。边疆农村办学45年，学校师生发扬解放军优良传统和“抗大校风”，传承北大荒精神，走出一条自强不息、砥砺奋进的发展之路。2003年学校整体迁入大庆市，进入了提速升级、跨越发展的新时期。在长期的办学实践中，学校凝练形成了“艰苦奋斗、无私奉献、务实求真、负重致远”的办学精神，并因优良校风和办学实绩赢得了社会的广泛赞誉。\n\n学校现址位于大庆市高新技术产业开发区，总占地120.04万平方米，建筑面积40万平方米，固定资产总值12.41亿元，教学科研仪器设备3.05亿元，图书213.22万册。学校现有教职工1344人，其中专任教师833人，教授165人、副教授276人，其中，具有博士学位教师317人，占比38.1%；海外经历累计1年以上教师122人，占比14.6%。学校拥有“国家特殊支持计划”（万人计划）领军人才1人、“长江学者奖励计划”青年学者1人、科技部“中青年科技创新领军人才”1人、“龙江学者”特聘教授1人、龙江学者青年学者3 人、“王震讲座教授”22人、全国优秀教师1人、全国模范教师1人、省级教学名师4人，享受国务院和省政府特殊津贴38人，建有3个省级教学团队、6个省级领军人才梯队、21个农垦总局领军人才梯队、4个省高校科技创新团队。\n\n学校具有三级学位授予权，现建有11个本科学院以及马克思主义学院、体育教研部、继续教育学院等教学机构，学科专业涉及农学、工学、管理学、理学、法学、文学、经济学7个学科门类，拥有4个博士学位授权一级学科，9个硕士学位授权一级学科，5个硕士专业学位授权类别；建有49个本科专业，其中国家级特色专业3个、省级重点专业9个。学校面向23个省（直辖市、自治区）招生，全日制在校本科生近15000人，各类在校研究生1600余人。建校以来，学校累计培养和输送毕业生13万人，培训各类人员50余万人次。\n\n学校着力推进教学改革，人才培养质量不断提升。“十二五”以来，获批1个国家级专业综合改革试点项目，2个国家级卓越农林人才教育培养计划改革试点项目（涵盖7个专业），1个国家新工科研究与实践项目，3个省级专业综合改革试点项目，5个省级卓越农业人才教育培养计划改革试点专业项目，2个省级新工科研究与实践项目，4个省级示范性虚拟仿真实验项目；获批国家级实验教学示范中心2个、国家级校外实践教育基地1个，省级实验教学示范中心5个、省级虚拟仿真实验教学示范中心1个、省级校外实践教育基地3个；建有省级精品视频公开课4门、省级精品在线开放课程3门、省级线上线下精品课程1门；主编农业部“十二五”规划教材15部。毕业生就业率始终保持在省属高校前列，2015年度被评为“全国高校毕业生就业工作50强”。\n\n学校科研工作以应用研究和技术开发为主要方向，现建有国家杂粮工程技术研究中心、新农村发展研究院，教育部粮食副产物加工与利用工程技术研究中心、农业部农产品加工质量监督检验测试中心（大庆）等21个国家、省部级科研平台，以及7个省高校重点实验室、工程技术研发中心。学校建有3个博士后科研流动站及1个博士后科研工作站，与地方政府合作共建“牡丹江食品与生物技术创新研究院”“大庆设施农业研究院”。“十二五”期间，学校获批国家级、省部级等各类课题300余项，科研经费2.4亿元。建校以来，学校共取得科研成果1500余项，获得省部级以上奖励157项，以“大豆三垄栽培技术”“农作物种衣剂”为代表的一批重大科研成果在生产实践中得到有效转化，创造了巨大的经济效益和社会效益，学校先后3次获得“黑龙江省省长特别奖”，2次获得“黑龙江省大专院校和科研单位振兴经济奖”。\n\n学校坚持开放办学，与美国、英国、加拿大、俄罗斯、法国、韩国、日本、印度、乌克兰等国家和地区近30所高校和科研院所开展交流与合作。目前，学校与俄罗斯符拉迪沃斯托克国立经济服务大学实施本科生中外合作办学项目，与韩国国立江陵原州大学、日本山形大学、波兰波兹南生命科学大学等国外高校实施交换生、留学生等项目，与加拿大阿尔伯塔大学实施“中加合作食品研究发展中心”项目，与印度UAS大学实施中印杂粮工程技术研究中心项目，与乌克兰哈尔科夫国立无线电电子大学、哈尔科夫国立城市规划大学实施交换生项目。\n\n学校承担了传承北大荒精神的社会责任，现建有黑龙江省学校思想政治理论课教师社会实践研修基地和黑龙江省北大荒精神学术交流基地，充分发挥学校地缘、人才和科研和资源优势，以基地为平台，切实加强北大荒精神的实践教育资源的研究工作，将北大荒精神作为一种得天独厚的文化传统和教育资源，全方位融入办学育人之中，着力培养具有北大荒精神烙印的现代农业人才。\n\n困知勉行，积厚成器。“十三五”时期，学校坚持“育人为本、质量立校、崇尚学术、特色发展”的核心办学理念，以立德树人为根本任务，以服务龙江全面振兴、服务国家农业现代化为办学使命，以产教融合发展为导向，以质量提升为核心，着力提高学校内涵发展水平和办学综合实力，向建设特色鲜明的高水平现代农业大学目标不断迈进。','HBAU','东北地区',2,'黑龙江农垦大学','农林',1958,'黑龙江省','否',0,0,0,0,14,0,0,1410,47,8,2,0,3360,0,'http://www.hlau.cn/'),(112,200,'东北农业大学','黑龙江',7,'东北农业大学是一所“以农科为优势，以生命科学和食品科学为特色，农、工、理、经、管等多学科协调发展”的国家“211工程”重点建设大学和“世界一流学科”建设高校，是黑龙江省人民政府与农业部省部共建大学、国家“中西部高校基础能力建设工程”项目入选高校、教育部本科教学工作水平评估优秀院校。\n\n学校1948年创建于哈尔滨，始称东北农学院，是中国共产党在解放区创办的第一所普通高等农业院校，知名教育家、哈尔滨市第一任市长刘达为学校第一任院长。建校以来，先后隶属于东北行政委员会农业部、国家高等教育部、国家农业部、黑龙江省农业委员会、黑龙江省教育厅，1981年被确定为黑龙江省省属重点院校，1994年与黑龙江省农业管理干部学院合并组建东北农业大学。\n\n学校设有17个学院和1个教学部。现有3个国家重点学科，3个国家重点（培育）学科，2个农业部重点学科，2个省级重点学科群，10个省级一级重点学科，农业科学、植物学与动物学2个学科进入ESI国际学科排名前1%。学校是全国首批具有博士、硕士学位授予权的高等学校之一，现有10个博士学位授权一级学科点，22个硕士学位授权一级学科点；1个博士专业学位授权类别，10个硕士专业学位授权类别；10个博士后科研流动站、2个博士后科研工作站；72个本科专业。\n\n学校现有专任教师1 386人，其中教授311人、副教授475人；具有博士学位者837人，具有硕士学位者465人。现有中国工程院院士1人，“万人计划”入选者14人（含青年拔尖人才1人），“长江学者”特聘教授1人，国务院学位委员会学科评议组成员6人，国家杰出青年科学基金获得者3人，“百千万人才工程”国家级人选3人，科技部中青年科技创新领军人才9人，教育部创新团队2个，教育部“跨世纪、新世纪优秀人才支持计划”入选者10人，国家级教学团队2个，国家级教学名师3人，教育部高等学校教学指导委员会分委员会副主任委员2人、委员12人，国家现代农业产业技术体系岗位科学家17人，“龙江学者”特聘教授18人、讲座教授3人，青年“龙江学者”2人，博士生导师228人。\n\n学校现有全日制在校生27 922人，其中研究生4 579人、本科生23 152人、外国留学生191人，是首批“卓越农林人才教育培养计划”项目试点高校。拥有国家级特色专业10个，国家级人才培养模式创新实验区2个，国家级精品课程4门、国家级精品视频公开课4门、国家级精品资源共享课2门，国家级精品在线开放课程1门，国家级实验教学示范中心4个，国家级虚拟仿真实验教学中心 1 个，国家级大学生校外实践教育基地4个，教育部、农业部农科教合作人才培养基地4个，科技部创新人才培养基地1个，全国高校学生科技创业实习基地1个；获得全国“百篇”优秀博士学位论文1篇、提名4篇。学校是国家现代远程教育试点院校，设有全国重点建设职业教育师资培养培训基地。学校是全国毕业生就业典型经验高校。建校至今已累计培养各级各类学生24万人，其中既有一批站在世界科技前沿的“创新型”人才，也有一批遍布全国的“复合型”人才，更有一大批活跃在黑龙江省和全国农业生产第一线的“应用型”人才。\n\n学校现有3个国家级工程技术研究中心，3个教育部重点实验室，5个农业部重点实验室、1个农业部区域试验站、3个农业部农业科学观测实验站，2个农业部科研基地，设有国家级大学科技园1个，是高等学校新农村发展研究院首批建设院校之一。建校至今，获得省部级以上科技奖励500余项，其中国家自然科学奖、发明奖和科技进步奖32项；先后9次获得黑龙江省省长特别奖和重大科技效益奖，18次被中宣部、教育部、团中央授予“社会实践先进集体”荣誉称号，3次获得黑龙江省“振兴经济奖”，2次获得黑龙江省“粮食生产贡献奖”，并被授予黑龙江省“小康建设红旗集体”荣誉称号。\n\n学校坚持开放办学，具有接收中国政府奖学金来华留学生资格，设有教育部赴俄留学培训基地，是中国东北地区与俄罗斯远东、西伯利亚地区大学校长论坛中方秘书处单位。建校至今，已先后与俄罗斯、日本、韩国、美国、加拿大、澳大利亚、爱尔兰等国家的60多所高校及科研单位建立了联系，广泛开展国际交流与合作，并初步形成了“以东北亚为主、博采众长”的国际交流特色。\n\n学校占地面积496.4万平方米，总建筑面积123.1万平方米，教学科研仪器设备总值5.77亿元，为首批国家级节约型公共机构示范单位。主办期刊《东北农业大学学报（自然科学版）》《东北农业大学学报（社会科学版）》《东北农业大学学报（英文版）》《中国马铃薯》《饲料博览》《中国乳品工业》《乳品与人类》《农业经济与管理》《大豆科技》，在国内外公开发行。\n\n学校高度重视党建、思想政治工作和精神文明建设。1998年、2013年先后2次荣获省级文明单位称号,2015年荣获省级文明单位标兵称号；2000年被评为全国“学校民主管理先进单位”；2003年荣获全国“五一劳动奖状”；2011年被评为全国“模范职工之家”；2012年被评为全国“创先争优”先进基层党组织，荣获全国“五四红旗团委”称号；2014年荣获全省“十佳和谐校园”称号。\n\n新时代的东北农业大学，将紧紧抓住国家“双一流”建设的有利时机，继承“艰苦奋斗，自强不息”的精神，弘扬“勤奋、求实、奉献、创新”的校风，满怀信心地向着“国际知名、国内同类院校一流、具有我国北方现代农业特色的多科性、研究教学型和开放式”的高水平农业大学奋进！',NULL,'东北地区',2,'东北农学院','农林',1948,'黑龙江省','否',0,211,0,1,17,1,1,1386,72,22,10,0,3700,0,'http://www.neau.edu.cn/'),(78,201,'哈尔滨医科大学','黑龙江',7,'哈尔滨医科大学由我国医学先驱伍连德博士于1926年创建的哈尔滨医学专门学校和兴山中国医科大学第一、二分校组建而成。学校历经92年的发展建设，传承伍连德博士“赤诚爱国、自强创业”的精神，发扬中国工农红军卫生学校“政治坚定、技术优良”的光荣传统，秉承“木直中绳、博学载医”的校训，综合实力不断增强，具有相当的发展规模和鲜明的办学特色。学校是部委省共建大学、黑龙江省高水平大学和优势特色学科建设高校、中俄医科大学联盟中方牵头单位、国家理科基础科学研究与教学人才培养基地、教育部首批试办七年制高等医学教育院校。\n\n学校设18个二级院部和8个事业单位。现有教职工16958人。有中国工程院院士1人，获南丁格尔奖1人，国务院学科评议组成员3人，教育部高等学校教学指导委员会委员18人，全国医学专业学位研究生教育指导委员会委员3人，国家级教学名师4人，国家杰出青年科学基金获得者2人，国家优秀青年科学基金获得者1人，入选“千人计划”2人、“万人计划”6人、“长江学者”8人，享受国务院政府特殊津贴94人。\n\n学校拥有博士学位一级学科授权点9个，二级学科授权点62个；硕士学位一级学科授权点11个，二级学科授权点64个；博士后科研流动站5个。校本部有本科专业18个。在校普通教育本科生7972人，研究生5799人，留学生641人。\n\n学校拥有国家重点学科2个、国家重点培育学科1个，国家临床重点专科建设项目33个，省级重点学科（群）21个，省级领军人才梯队31个。在第四轮学科评估中，1个学科进入Ａ类（A-）、8个学科进入Ｂ类（2个B+、3个B、3个B-）。有国际合作联合实验室2个，省部共建国家重点实验室培育基地1个，教育部重点实验室3个，卫健委重点实验室2个，省级重点实验室13个，省普通高校重点实验室11个。与地方政府和企业共建院士工作站2个。建立了北方医学转化研究合作中心。\n\n学校有国家级实验教学示范中心4个，国家级虚拟仿真实验教学中心1个。主编教育部、卫健委“十二五”规划教材63部。获批教育部卓越医生教育培养计划改革试点项目3项。获国家视频公开课程4门、国家资源共享课程12门、国家精品在线开放课程1门。共获国家级教学成果奖16项，省级教学成果奖139项。“十二五”以来，立项教育科研课题689项，发表教育教学论文827篇，其中SCI收录5篇。\n\n“十二五”以来，学校获得各类科研项目8528项，主持国家重点基础研究发展计划（“973”计划）1项、国家高技术发展计划（“863”计划）1项、国家科技重大专项1项、国家重点研发计划专项2项，国家自然科学基金1228项。获各种奖励876项，其中，国家科学技术进步奖二等奖1项、高等学校科学研究优秀成果奖（科学研究）一等奖5项、中华医学科技奖一等奖2项。获国家自然科学基金委员会创新研究群体项目1项、教育部科技创新团队2个。发表科学研究论文19805篇，其中SCI收录7161篇。编写出版著作628部。拥有有效专利434件，发明专利182件，软件及著作权10件。\n\n面向未来，学校以习近平新时代中国特色社会主义思想为指导，开拓进取，砥砺奋进，努力建设国内一流、国际有影响的研究教学型医科大学。','HMU','东北地区',2,'哈尔滨医学专门学校、中国医科大学分校','医科',1926,'黑龙江省','是',0,0,0,0,7,1,1,8855,17,11,8,1,1750,0,'http://www.hrbmu.edu.cn/'),(444,202,'哈尔滨学院','黑龙江',7,'哈尔滨学院是一所经国家教育部批准的综合性地方普通本科院校。2000年由原哈尔滨大学、哈尔滨师范专科学校等六所学校合并而成。学校实行“省市共建，以市为主”的办学体制。面向区域经济社会发展，致力于培养高素质、高技能的应用型人才。                                                         \n\n学校坐落于素有“东方小巴黎”美誉的冰城——哈尔滨市，位于哈西新区核心地带，地处哈尔滨高校集中的学府区域。学校占地面积65万平方米，校区错落分布、疏朗有致，中西合璧、园林景观。精致的欧亚建筑，尽显异国情调；苍挺的松树林，散发着静雅的气息和北疆的坚毅；夏季的丁香芬芳、冬季的冰雪景致，彰显浓郁独特的北国风情。学校被黑龙江省政府命名为“花园式单位”。   \n\n学校现设有12个二级学院，开设46个本科专业，涵盖了经济学、法学、教育学、文学、历史学、理学、工学、管理学、艺术学等9大学科门类。小学教育专业是国家级特色专业建设点，艺术设计是国家级专业综合改革试点项目，软件工程学科是省级重点建设学科。省级重点专业、省级重点实验室、省级实验教学示范中心、省级研究基地12个。近五年来，学校承担国家、省市级科研项目318项；获各级各类科研成果奖276项。2017年获批黑龙江省高等学校硕士学位授权立项建设单位、哈尔滨市博士后创业实践基地。    \n\n学校现有专任教师653人，其中教授、副教授 339 人，占教师总数的52%；具有博士、硕士学位的教师522 人，占教师总数的80%。享受国务院和省政府特殊津贴专家6人，省教学名师、省优秀中青年专家、市有突出贡献中青年专家14人，多名教师获国家优秀教师奖、国家优秀教育工作者奖、曾宪梓奖等奖项。国家级教学团队、省级教学团队，省级研究团队、市专业领军人才梯队12个。省精品在线开放课程9门,省线上线下精品课程1门，2门慕课被“优课联盟”评为“品牌慕课”，获教育部在线教育研究中心“慕课教学实践奖”。   \n\n学校面向全国26个省（自治区、直辖市）招生，全日制在校生11373人。建校以来，学校已为社会培养了6万余名高素质应用型人才，毕业生在教育、科技、文化及企事业等各自岗位上建功立业，为地方经济建设和社会发展发挥着积极作用。\n\n学校积极开展“校企”“校政”合作，分别与海航集团、中软集团、哈尔滨地铁集团等知名企业建立合作关系；与政府相关部门合作共建哈尔滨市党风廉政建设理论研究基地、哈尔滨市民族宗教工作研究培训基地、黑龙江湿地培训中心、哈尔滨湿地研究所等科研机构；与哈尔滨市南岗区教育局合作共建哈尔滨学院附属小学。学校坚持开放办学，先后与美国、俄罗斯、英国、法国、日本、韩国等10余所高校建立了友好合作关系，连续承办6届海外华裔青少年冬令营活动。   ',NULL,'东北地区',2,'','综合',1999,'黑龙江省','是',0,0,0,0,12,0,0,673,46,0,0,0,1719,0,'http://www.hrbu.edu.cn/'),(528,203,'绥化学院','黑龙江',7,'绥化学院是黑龙江省属公立综合性普通本科高等学校，座落在中国寒地黑土绿色物产之乡绥化市，与省会哈尔滨相距70余公里。\n\n学校办学历史悠久。1953年，经黑龙江省教育厅批准，绥化县创办了绥化师范学校；1971年，绥化地区在原绥化师范学校的基础上组建绥化地区师范学校；1978年，经国务院批准，成立绥化师范专科学校，是“文革”后国家首批设立的高等师范专科学校之一；2004年，经国家教育部批准成立绥化学院。2013年,通过教育部本科教学工作合格评估，2017年，获批硕士学位授权立项建设单位，2018年，通过教育部本科教学工作审核评估。\n\n学校学科门类多样。现设有12个学院，开设42个本科专业，涵盖教育学、文学、理学、工学、农学、经济学、管理学、艺术学等八个学科门类。现有全日制在校生11609人。\n\n学校师资队伍精良。现有教职工820人，其中专任教师525人。博士、硕士学位人员523人，外籍教师7人，享受政府特殊津贴专家2人。广大教师艰苦奋斗、无私奉献、敬业爱生，培养了一代又一代社会主义合格建设者和可靠接班人，为学校高质量发展做出了卓越贡献！\n\n学校设施条件完备。校园占地40万平方米，建筑面积22.2余万平方米，教学仪器设备总值近1.2亿元，图书馆纸质藏书108万册，建有具备多媒体演示功能的教学大楼、仪器设备先进的实验大楼、具有电子检索及阅览功能的现代化图书馆、功能齐全的大学生创新创业基地、数字化宽带校园网络等多项教学、科研设施，建有环境优良、服务和保障功能完备的学生公寓、餐饮中心、购物超市、体育场馆、人工湖泊等多项生活、休闲设施。校园景色四季如画：丁香吐芳、草木滴翠、彩叶漫舞、雾凇垂枝。\n\n学校教学质量优良。以“厚德载物、经世致用”为校训，以建设应用型大学为定位。打造精品师范，形成了“五练一熟”与“接力式”顶岗支教相结合的培养特色。学校有3个省级一流专业、5门省级精品课程、2门省级精品在线开放课程、2个省级教学团队、2个省级实验教学示范中心、1个省级人才培养创新试验区，获批省寒地黑土经济与文化学术交流基地、省特殊教育学术交流基地。\n\n学校坚持开放办学。与俄罗斯皮亚季戈尔斯克国立大学、阿穆尔国立人文师范大学，日本东亚学院、九州外国语学院，泰国西那瓦大学，意大利罗马艺术大学、那不勒斯艺术学院，加拿大道格拉斯学院，台湾彰化师范大学等国家和地区高校或教育机构开展校际交流合作，互派留学生。英语、俄语、日语等专业都有外籍教师授课，形成了寒地黑土文化与世界文化相融的教育教学特色。\n\n学校大力发展特殊教育。建有黑龙江省唯一的特殊教育本科专业，建有残疾人高等教育基础实验室和医教结合康复中心，开设了特殊教育研究专刊，学校是省特殊教育师资培训基地和省特殊教育艺术人才培养基地，省特殊教育专业委员会理事长单位。学校开设残疾人高等教育，面向全国招收计算机科学与技术、环境设计、电子商务等本科专业听障生，共计有本科在校生235人,填补了黑龙江省残疾人高等教育空白。\n\n学校贯彻“以生为本，为生尽责”理念。建有完善的“奖、贷、助、补、减、缓”助学体系，帮助学生完成学业。实施导师进宿舍和班主任制，帮助学生成长进步。重视学生心理健康教育、孝道与感恩教育、创新创业教育，广泛开展有益学生身心的文体活动。建有学生综合服务大厅，实行一站式服务。畅通学生诉求反馈机制，形成良性服务体系。\n\n学校积极服务地方发展。学校与绥化市政府共建大学生创新创业基地，成为绥化市“都城地”战略发展的崭新名片。 先后与绥棱、望奎、兰西等地开展校县共建，帮助县域进行城区规划、旅游开发、人才培训。通过绥化市科技局为辖区企事业单位提供免费技术支持，通过新农村建设研究年、寒地黑土经济与文化研究所等平台开展立地科研。学校与绥化食品药品监督管理局联合，共建了哈外最大的食品药品检验检测中心。建立了食用菌研究所，培育了食用菌新品种，完成了农作物秸秆栽培食用菌技术攻关，带动了绥棱、庆安等县食用菌产业发展。学校还建设了陶艺馆、剪纸艺术专家工作室，致力于海伦剪纸、绥棱黑陶、望奎皮影戏等地方非物质文化遗产的保护和传承。\n\n学校1989年获全国普通高校国家优秀教学成果特等奖；1998年被黑龙江省委省政府命名为“省级文明单位”；2003年获得“省级文明单位标兵”的称号；2009年获高等教育国家级教学成果二等奖；2010年获全国高校校园文化建设优秀成果二等奖；2011年荣获“国家级文明单位”称号，全省高校“先进基层党组织”称号；2008-2018年连续十一年被绥化市政府命名为“服务地方经济建设先进单位”称号；2012年获全国高校校园文化建设优秀成果二等奖；2015年荣获省五一劳动奖状；2016年获第三次全国高校校园文化建设优秀成果二等奖；2018年被评为“全省文明校园”。',NULL,'东北地区',2,'绥化师范学校、绥化地区师范学校、绥化师范专科学校','综合',1953,'黑龙江省','否',0,0,0,0,12,0,0,810,41,0,0,0,3225,0,'http://www.shxy.net/'),(326,204,'哈尔滨商业大学','黑龙江',7,'哈尔滨商业大学始建于1952年，为新中国第一所多科性商业大学，是黑龙江省重点建设的十所大学之一，学校定位为教学研究型大学。2006年在教育部普通高等学校本科教学工作水平评估中获得“优秀”。2012年入选国家“中西部高校基础能力建设工程”建设院校，2013年成为中俄经济类大学联盟成员，2014年被授予第四届“全国文明单位”称号，2017年荣获“全国五一劳动奖状”，2018年工商管理学科进入黑龙江省国内一流学科建设行列。\n\n学校占地总面积116.55万平方米，校舍总面积64.51万平方米。现有全日制本科在校生23004人，全日制博士研究生154人，硕士研究生2209人。\n\n学校师资力量雄厚，有教职员工1492人，其中专任教师1151人，教授215人、副教授496人；具有硕士、博士学位教师分别为557人、471人；研究生导师527人，其中博士生导师85人。拥有国务院学位委员会学科评议组成员1人，乌克兰经济科学院院士（外籍）1人，“新世纪百千万人才工程”国家级人选1人，教育部“新世纪优秀人才支持计划”入选者1人，国务院政府特殊津贴专家24人，“龙江学者”特聘教授7人，省政府特殊津贴专家14人，省（部）级中青年专家14人，省级领军人才梯队带头人、后备带头人37人，省级教学名师15人。\n\n学校拥有涵盖经、管、工、法、文、理、医学、艺术等八大学科门类的本科专业63个；4个一级学科博士学位授权点、20个可设置二级学科博士学位授权点，14个一级学科硕士学位授权点、73个可设置二级学科硕士学位授权点，2个独立二级学科硕士学位授权点，13个专业类别硕士学位授权点；4个省级一级重点学科和1个省级重点学科群（第三产业发展与创新）；拥有3个博士后科研流动站和2个博士后科研工作站；荣获国家级教学成果二等奖3项，拥有国家级特色专业6个、国家级专业综合改革试点项目3个、国家级精品课程3门、国家级虚拟仿真实验教学中心1个、国家级实验教学示范中心1个、国家级规划教材28部；拥有教育部全国重点建设职教师资培养培训基地1个、省级人才培养模式试验区1个、省级对俄经贸人才培养基地1个、省级重点专业15个、省级专业综合改革试点项目3个、省级精品课程35门、省级教学团队5个、省级实验教学示范中心5个。\n\n学校现拥有抗肿瘤创新药物国家工程研究中心1个、教育部天然抗肿瘤药物工程研究中心1个、省级重点实验室4个、省级工程研究中心2个、省普通高校重点实验室3个；拥有黑龙江省首批重点培育智库1个、省哲学社会科学重点研究基地1个、省教育厅人文社会科学重点研究基地3个。\n\n学校一直面向全国30个省、市、自治区招生。建校67年来，学校总计为社会培养了14万名各类专业人才，其中许多人已成为著名专家学者、成功企业家和优秀党政管理干部，人才培养成绩斐然。\n\n学校有多项科研成果填补国内空白。1959年研制出中国第一台食品机械——601型饺子机、利用小球藻解决了替代食物对人体健康危害问题，效果显著。1960年，首创我国三大会计记账法之一“财产收付复式记账法”。1960年，“读、看、论、讲、做、研、写七字一条龙”教学法研究在全国文教群英会上受到表彰并予推广，光明日报于1960年5月29日对我校的这一教学经验进行了报道。1981年，最早提出了东北亚经济圈协作开发的学术理论。1988年研制出国内首台电脑自动加油机。1996年，建成了高校第一所金融货币博物馆，推出了一批有影响的科研成果。1999年，完成国家重大科技攻关项目“商业自动化技术集成示范工程”。2000年后，学校在应用经济学、工商管理、中药学、食品科学等领域的研究成果在全国产生了重要影响。\n\n近5年，学校承担了国家自然科学基金项目、国家哲学社会科学基金项目、国家科技支撑计划项目、教育部项目等纵向项目900余项，各类横向项目300余项，申请发明专利和实用新型专利300余件，授权95项。科研成果先后获得省部级以上奖励200余项，出版著作300余部，发表论文7000余篇。学校公开编辑出版《商业研究》《哈尔滨商业大学学报（自然科学版）》《哈尔滨商业大学学报（社会科学版）》等3种学术期刊，其中《商业研究》是CSSCI来源期刊、全国中文核心期刊和中国国际影响力优秀学术期刊，《哈尔滨商业大学学报（社会科学版）》是CSSCI扩展版来源期刊和全国高校百强社科期刊。\n\n学校高度重视国际交流与合作，先后与英国、美国、俄罗斯、韩国、日本、哈萨克斯坦、荷兰、挪威等27个国家和地区的73所院校建立了教育合作及学术交流关系。是中国（教育部）留学服务中心出国留学培训基地，中国汉语水平考试（HSK）基地，商务部“援外高级学历学位教育专项计划”项目合作院校，商务部对外援助培训项目承办单位。近十年来，已有亚洲、非洲、南美洲和加勒比海地区、南太平洋地区和独联体共76个国家和地区的600余名政府官员、企业高管和学校教师来校研修和培训。',NULL,'东北地区',2,'黑龙江商学院、黑龙江省财政高等专科学校','财经',1952,'黑龙江省','否',0,0,0,0,22,0,0,1492,61,14,4,0,6219,0,'http://www.hljcu.edu.cn/'),(147,205,'上海海事大学','上海',9,'上海海事大学（Shanghai Maritime University）是一所以航运、物流、海洋为特色学科，具有工学、管理学、经济学、法学、文学、理学和艺术学等学科门类的多科性大学，也是首批获得中华人民共和国船员教育和培训质量体系证书和挪威船级社颁发的ISO9001质量保证书的高校；入选国家卓越工程师教育培养计划、中国政府奖学金来华留学生接收院校和上海市首批深化创新创业教育改革示范高校。学校原为交通运输部直属高校，2000年划归上海市管理，2008年上海市人民政府与交通运输部签订协议，共建上海海事大学','SMU','华东地区',3,'上海海运学院','理工',1909,'上海市','是',0,0,0,0,12,0,0,1000,47,78,21,0,0,0,'http://www.shmtu.edu.cn/'),(73,206,'东华大学','上海',9,'东华大学（Donghua University）简称“东华”，地处中国上海，是中华人民共和国教育部直属的全国重点大学，是国家“211工程“建设高校、世界一流学科建设高校，入选”2011计划“牵头高校、”111计划“、卓越工程师教育培养计划、国家大学生创新性实验计划、中非高校20+20合作计划、国家级大学生创新创业训练计划、国家建设高水平大学公派研究生项目、国家级新工科研究与实践项目、中国政府奖学金来华留学生接收院校，是中国首批具有博士、硕士、学士三级学位授予权的大学之一，是教育部“援疆学科建设计划”40所重点高校之一、首批28所全国来华留学质量认证院校之一、高水平行业特色大学优质资源共享联盟成员、“一带一路”世界纺织大学联盟成员、全国深化创新创业教育改革特色典型经验高校、上海市首批深化创新创业教育改革示范高校。在2016年世界大学学术排名中，东华大学名列中国大陆高校第34位、上海高校第6位。','DHU','华东地区',3,'中国纺织大学','理工',1951,'国家教育部','是',985,211,1,1,18,0,10,2209,55,87,38,1,0,0,'http://www.dhu.edu.cn/'),(189,207,'上海电力大学','上海',9,'上海电力大学是一所由中央与上海市共建、以上海市管理为主的全日制普通高等院校，其前身是创建于1951年的上海电业学校，2018年6月，更名为上海电力大学。现有平凉、学海、国东三大校区。','SUEP','华东地区',3,'上海电业学校','理工',1951,'上海市','是',0,0,0,0,13,0,0,1100,32,2,0,0,0,0,'http://www.shiep.edu.cn'),(154,208,'上海海洋大学','上海',9,'上海海洋大学','SHOU','华东地区',3,'上海水产学院','农林',1912,'上海市','否',0,0,1,0,14,0,0,1200,43,10,3,0,0,0,'http://www.shfu.edu.cn/'),(29,209,'华东师范大学','上海',9,'华东师范大学（East China Normal University），简称“华东师大”，由中华人民共和国教育部直属，是教育部、上海市人民政府和国家海洋局共建的全国重点大学。位列世界一流大学A类建设高校、“985工程”、“211工程”，入选国家“2011计划”、“111计划”和“国家建设高水平大学公派研究生项目”，“长三角高校合作联盟”、“金砖国家大学联盟”、“亚太高校书院联盟”、“中日人文交流大学联盟”成员，设有研究生院和国家大学科技园。','ECNU','华东地区',3,'上海师范大学','师范',1951,'国家教育部','是',0,211,1,0,30,0,37,3990,83,209,144,2,0,0,'http://www.ecnu.edu.cn/'),(30,210,'上海财经大学','上海',9,'上海财经大学（Shanghai University of Finance and Economics）是中华人民共和国教育部直属的一所以经济管理学科为主，经、管、法、文、理、哲等多学科协调发展的研究型重点大学，国家首批世界一流学科建设高校，国家“211工程”、“985工程优势学科创新平台”重点建设高校，入选国家“111计划”、卓越法律人才教育培养计划、国家级大学生创新创业训练计划、国家经济学基础人才培养基地、国家海外高层人才创新创业基地、全国高校实践育人创新创业基地、教育部人文社会科学重点研究基地、国家建设高水平大学公派研究生项目、中国政府奖学金来华留学生接收院校、全国深化创新创业教育改革特色典型经验高校，全国首批博士、硕士学位授予单位，中国自由贸易试验区协同创新中心牵头单位，中欧商校联盟创始会员，由教育部、财政部和上海市三方共建。','SUFE','华东地区',3,'上海财经学院','财经',1917,'国家教育部','否',0,211,1,1,12,0,9,1043,36,101,53,0,0,0,'http://www.shufe.edu.cn/'),(157,211,'上海对外经贸大学','上海',9,'上海对外经贸大学（Shanghai University of International Business and?Economics），位于中国上海，是一所有经济学、管理学、文学、法学、理学、工学6大学科门类的行业特色型重点大学，为华东地区唯一的一所涉外商科大学，入选“2011计划”、国家级人才培养模式创新实验区、国家级大学生创业教育基地、上海卓越法律人才教育培养基地、上海卓越新闻传播人才教育培养基地 ，是中国自由贸易试验区协同创新中心核心单位、中国政府奖学金来华留学生接收院校、英国特许公认会计师公会认证的“白金级”教育机构、中国-中东欧国家高校联合会创始成员以及全球外国语大学联盟创始成员和理事单位，被誉为“国际商务精英的摇篮”。','SUIBE','华东地区',3,'海对外贸易学院','财经',1960,'上海市','否',0,0,0,0,13,0,0,1000,32,41,1,0,0,0,'http://www.shift.edu.cn/'),(130,212,'华东政法大学','上海',9,'华东政法大学前身是华